INSERT INTO `broadcast_text_locale` VALUES (99756, 'enUS', 'Kayn, tell me what you know about Mardum.', 'Kayn, tell me what you know about Mardum.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99757, 'enUS', 'If this doesn\'t end soon we\'re gonna need more supplies.', 'If this doesn\'t end soon we\'re gonna need more supplies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99758, 'enUS', 'C\'mon then. Yer not goin\' out like this!', 'C\'mon then. Yer not goin\' out like this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99759, 'enUS', 'You\'ll be back on yer feet in no time.', 'You\'ll be back on yer feet in no time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99760, 'enUS', 'On the bright side - ye might get promoted after a heroic act like that!', 'On the bright side - ye might get promoted after a heroic act like that!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99761, 'enUS', 'I sold the enchanting supplies to a fence up in Dalaran. Grimbore, Boargrim... somethin\' orcish like that.$b$bNow beat it, will ya? I got a customer on the way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99762, 'enUS', 'Your souls shall suffer!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99763, 'enUS', 'The Legion conquers all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99764, 'enUS', 'Worthless species!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99765, 'enUS', 'You will burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99766, 'enUS', 'Your world is at an end, mortals!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99767, 'enUS', 'Ah, $n, it\'s good to see you again. Now that we are all gathered, let\'s begin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99768, 'enUS', 'Bow before the might of Sargeras.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99769, 'enUS', 'The outcome of this world has been decided!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99770, 'enUS', 'Puny mortals. I\'ll deal with you myself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99771, 'enUS', 'Yer not welcome here!', 'Yer not welcome here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99772, 'enUS', 'I\'ll show ya who yer messin\' with!', 'I\'ll show ya who yer messin\' with!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99773, 'enUS', 'Hold still! This\'ll only hurt a little!', 'Hold still! This\'ll only hurt a little!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99774, 'enUS', 'Today ye find out what yer insides look like!', 'Today ye find out what yer insides look like!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99775, 'enUS', 'You appear to be lost, friend.', 'You appear to be lost, friend.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99776, 'enUS', 'By the Jade Serpent! What was that?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99777, 'enUS', 'Yer messin\' with the wrong dwarf.', 'Yer messin\' with the wrong dwarf.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99778, 'enUS', 'Today is not yer day!', 'Today is not yer day!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99779, 'enUS', 'And to think I used to be afraid of Ildine back in our Ravenholdt days! She\'s gone soft!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99780, 'enUS', 'Make yourself at home, and may the wind roll with you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99781, 'enUS', 'Ready up! He\'s comin\' down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99782, 'enUS', 'This is what we\'ve been trainin\' for!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99783, 'enUS', 'I\'ve got this one, shaman. Help the others!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99784, 'enUS', 'Training seems to have paid off! Well done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99785, 'enUS', 'Whoever is meddling here shall be dealt with shortly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99786, 'enUS', 'Your soul belongs to me now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99787, 'enUS', 'The service in this place is terrible. TERRIBLE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99788, 'enUS', 'I\'m here for the enchanting supplies Belamy sold you.', 'I\'m here for the enchanting supplies Belamy sold you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99789, 'enUS', 'I got those supplies fair an\' square! I\'m an honest businessman trying to make a living!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99790, 'enUS', 'You\'re dealing in stolen goods. Hand them over!', 'You\'re dealing in stolen goods. Hand them over!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99791, 'enUS', '', 'Ah... the fog has lifted, just a bit...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99792, 'enUS', '', 'Thank you, I was... losing control.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99793, 'enUS', '', 'Ugh. I shall never grow accustomed to that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99794, 'enUS', '', 'Hanging in there, thanks to you...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99795, 'enUS', '', 'My head... still spinning...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99796, 'enUS', '', 'Oh, but for a taste of the real thing...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99797, 'enUS', '', 'Oh, how I\'ve longed for this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99798, 'enUS', 'Well timed, champion. We have cleared a path to Dargrul. This way...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99799, 'enUS', '', 'At last, I am quenched.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99800, 'enUS', '', 'Ahh, to be alight with power once again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99801, 'enUS', '', 'My mind is ablaze. I am restored.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99802, 'enUS', '', 'I surge with power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99803, 'enUS', 'I\'m here for the enchanting supplies, Bonegrim. Where are...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99804, 'enUS', '', 'This feeling... flooding back to me... I am alive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99805, 'enUS', 'Listen, I don\'t want any trouble. I\'m just gonna go...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99806, 'enUS', 'Not withering today, it seems...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99807, 'enUS', 'Phew. I was close... too close.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99808, 'enUS', 'A drop in the ocean of my thirst... But appreciated, nonetheless.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99809, 'enUS', 'Blech. How did we live before the Nightwell?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99810, 'enUS', 'Eurrghh... Still woozy...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99811, 'enUS', 'Still here... for now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99812, 'enUS', 'Oh, it burns going down. In a good way!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99813, 'enUS', 'My strength returns!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99814, 'enUS', '$ct, I\'m proud to report that my mission was easily accomplished.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99815, 'enUS', 'Ah, it is good to be in touch with my better half once again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99816, 'enUS', 'Like a breath of air! Thank you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99817, 'enUS', 'This power... Had I ever truly tasted this before?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99818, 'enUS', 'Nooo! My profits! I was gonna buy myself a boat...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99819, 'enUS', 'Station Highlord Darion Mograine.', 'Station Highlord Darion Mograine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99820, 'enUS', 'Station Thassarian.', 'Station Thassarian.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99821, 'enUS', 'Station Koltira Deathweaver.', 'Station Koltira Deathweaver.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99822, 'enUS', 'We\'ll stay here and make sure nothing more can be taken from the crater. Stay on the offensive!', 'We\'ll stay here and make sure nothing more can be taken from the crater. Stay on the offensive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99823, 'enUS', 'So much energy... We\'ve got to stay here and ensure the Demons can take no more from this crater. Keep up the fight!', 'So much energy... We\'ve got to stay here and ensure the Demons can take no more from this crater. Keep up the fight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99824, 'enUS', '|TInterface\\Icons\\spell_frost_summonwaterelemental:20|t%s begins to cast |cFFFF0000|Hspell:193051|h[Call the Seas]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99825, 'enUS', 'Push them back! Don\'t let them take yer brothers and sisters!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99826, 'enUS', '', '$n! You and your demon hunters are nothing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99827, 'enUS', '', 'The keystone was entrusted to me by Sargeras himself. You will not have it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99828, 'enUS', 'The mists are having an ill effect on my crew. They have become wild and uncontrollable. They care not for the sea and prefer to bicker and brawl with each other.$B$BKjell, the harbor master, has ordered us to depart for fresh supplies as soon as we are able. I have assigned extra duties in hopes that the work will occupy their time until we ship out.$B$BI can only hope that the sea air will calm my crew and return their vigor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99829, 'enUS', '', 'Hurry. Kayn has already launched the attack on Tyranna.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99830, 'enUS', 'We have accomplished the impossible. I must admit, I had my doubts about your leadership, $n. But no more.\n\nLet\'s get back and put an end to the attack on the Black Temple. Lord Illidan awaits.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99831, 'enUS', '', 'Your foul magic is in vain, monster! These writings are under my protection.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99832, 'enUS', '', 'Away with you, monster!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99833, 'enUS', '', 'Give up! You only strike at the wind!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99834, 'enUS', 'Maybe it\'s best that I stay here in the foothold. I still haven\'t recovered from my wounds when I was held prisoner down below.\n\nThis world is overflowing with demonic energy. It\'s making it harder for me to contain my own fel power.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99835, 'enUS', 'Make the Illidari Foothold your home.', 'Make the Illidari Foothold your home.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99836, 'enUS', '$n, you made it through!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99837, 'enUS', '', 'Good luck to all of you. I will get the rest of us up there, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99838, 'enUS', '', 'Thank you, friend! My students died to protect these scrolls. Centuries of history could have been lost!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99839, 'enUS', '', 'A curse has fallen upon our harbor.$B$BGulls drop like stones from the sky. I saw one fall on a deck hand\'s head the other day. It knocked him out cold.$B$BSchools of fish rot belly-up in the shoals. Our fishermen have not had a decent catch in weeks and the stench of dead fish is horrible.$B$BI have taken on extra shifts in the tower, as most of my watchmen have gone missing. I take respite up here, as the foul odor and stale air are not as stifling.$B$BThe harbor is dead. The mists have choked the very life from it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99840, 'enUS', '<There is a note attached to a supply requisition.>$B$BHarbor Master Kjell,$B$BI do not know what foul bane these mists carry, but barely a day after they engulfed the harbor, our entire stock of provisions spoiled. We need to resupply our stock immediately.$B$BDispatch Captain Sten on a supply run as soon as he is able, assuming he can stop his crew from bickering long enough to set sail.$B$BIn the meantime, we will all have to tighten our belts and stretch what little we have left. Since most of the town has gone missing, this shouldn\'t be too difficult.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99841, 'enUS', '<Most of the roster entries are blank. There is a sloppily written note on the corner of one of the drafts.>$B$BDamn these mists to Helheim! Not long after they arrived, many of my craftsmen have vanished, and the few that remain have not the sense in their head to work. They act as if they had run headlong into the mast.$B$BHow am I supposed to get Sten\'s ship ready to sail with a sparse bunch of loons at my disposal?$B$BI will do what I can to get the ship seaworthy, alone if I must, but I need good men to fill my work roster.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99843, 'enUS', 'You\'re in danger here. Grand Master Hight needs everyone to gather at the Shrine of the Ox.', 'You\'re in danger here. Grand Master Hight needs everyone to gather at the Shrine of the Ox.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99844, 'enUS', '', 'When Dargrul stole the Hammer of Khaz\'goroth he did so right in front of myself and the other chieftains.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99845, 'enUS', '', 'Our last High Chieftain, Ulan, tried to stop him and was slain. Mayla may have picked up her father\'s totem, but she is not High Chieftain yet.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99846, 'enUS', '', 'Dargrul didn\'t just steal a hammer, he shattered our people, driving a wedge between our tribes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99847, 'enUS', '', 'Chen Stormstout was teaching a class of youngsters when the attack began. He\'s one of the strongest monks I know, but even he won\'t be able to hold out forever. Come, we must rescue them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99848, 'enUS', 'This fel circle is similar to the ones used in Skovald\'s attempts to enslave the Thorignir dragons of Thorim\'s Peak. $B$BIt appears to be too powerful to break by normal means.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99849, 'enUS', '', 'The time for ascension is upon you. Prepare yourselves; Odyn awaits your arrival.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99850, 'enUS', 'Use the Jailer\'s Shard to break Vethir\'s prison.', 'Use the Jailer\'s Shard to break Vethir\'s prison.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99851, 'enUS', 'We are almost to the exit, $n. Our freedom is near.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99852, 'enUS', 'These beasts are quite feisty, unless you know how to deal with them. This one should listen to your commands... for a little while, at least.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99854, 'enUS', 'You\'ve already ruined my business. Can\'t you just leave me alone?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99855, 'enUS', '', 'For years we were shunned, banished to the darkest depths of the world by the Wardens. \n\nNow, we must rise to the hardest challenge our world has ever faced. Now, we must defend Azeroth against its greatest enemies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99856, 'enUS', 'Fine, $r, I\'ll send the supplies to the enchanting shop. Now leave me to drink in peace!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99857, 'enUS', '', 'It was difficult, but I\'ve managed to gather some weapons and supplies. What do you need?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99858, 'enUS', 'I will temper my differences for now. The enemy is nearly at our doorstep, and we will be more effective united as one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99859, 'enUS', 'Your world is the last standing. It, too, will be purged.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99860, 'enUS', 'I have watched many of your kind walk the path of life, but none have carried themselves with a stride such as yours.$B$BI look forward to telling your story.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99861, 'enUS', 'We are but the beginning of the tide.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99862, 'enUS', 'Your doom awaits.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99863, 'enUS', 'Prepare for your own demise!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99864, 'enUS', 'I am the avatar of your destruction!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99865, 'enUS', 'Resistance only delays the inevitable!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99866, 'enUS', 'Your world will crumble into ashes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99867, 'enUS', '[Placeholder Event] - Anduin welcomes you to the Alliance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99868, 'enUS', '[Placeholder Event] - Sylvanas welcomes you to the Horde.', '[Placeholder Event] - Sylvanas welcomes you to the Horde.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99869, 'enUS', 'Ah, Darahir, my friend! Come have a drink!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99870, 'enUS', 'Don\'t let this $c scare you away, Darahir! Wait... come back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99871, 'enUS', 'DIE!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99872, 'enUS', 'Grand Master Hight... <gasp> Grand Master Hight! A giant... THING appeared in the sky!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99873, 'enUS', '|TInterface\\Icons\\INV_Spear_07:20|t%s targets you with an |cFFFF0000|Hspell:191977|h[Impaling Spear]|h|r! Get behind someone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99874, 'enUS', 'Look what the ravens have dropped off.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99875, 'enUS', 'GAHHH!! Keep going, $n. Activate the second pool.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99876, 'enUS', 'It\'s... trying to break free! Hurry, $n, activate the final pool!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99877, 'enUS', '', '|TInterface\\Icons\\INV_Enchant_EssenceArcaneSmall:20|t%s targets |cFFFF0000$n|r with a massive |cFFFF0000|Hspell:192708|h[Arcane Bomb]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99878, 'enUS', 'Demons are appearing everywh--', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99879, 'enUS', 'You walk among the spirits of an ancient land, $r.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99880, 'enUS', 'It is done. The demon is once again within my control.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99887, 'enUS', '|TInterface\\Icons\\Spell_Shadow_ShadeTrueSight:20|t%s begins to release a |cFFFF0000|Hspell:197969|h[Roaring Cacophony]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99889, 'enUS', '', '|TInterface\\Icons\\ability_shawaterelemental_swirl:20|t|cFFFF0000|Hspell:192696|h[Raging Storms]|h|r begin while no enemies are in melee range!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99890, 'enUS', 'Ah, we meet again, weakling. Now it\'s just you and me... and my guards!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99891, 'enUS', 'Sssssth! Hatecoil, crush their bodies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99892, 'enUS', 'Hatecoil, drown them out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99893, 'enUS', 'Fools! Get out of the way!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99894, 'enUS', 'You can\'t stop.... the storm...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99895, 'enUS', 'Useless. All of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99896, 'enUS', 'Like fish in a net.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99897, 'enUS', 'That was for Athissa.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99898, 'enUS', 'Ha, nowhere to hide!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99899, 'enUS', 'Get my point?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99900, 'enUS', 'I must retrieve an artifact from Tideskorn Harbor.', 'I must retrieve an artifact from Tideskorn Harbor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99901, 'enUS', '', 'No good! We don\'t have time to fight our way through that horde!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99903, 'enUS', 'What is it you seek, $c?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99904, 'enUS', 'I\'m ready to begin the ritual.', 'I\'m ready to begin the ritual.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99905, 'enUS', 'The Swamprock murlocs\' favorite thing to do is cause shipwrecks with their makeshift lighthouse. Murlocs are fascinating, but these are ruthless.\n\nWe must save this coast! I have been raising this young murloc, Murky. We need to help him become stronger so he can teach the tadpoles to be better than their elders.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99906, 'enUS', 'Almost had me that time! Try again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99907, 'enUS', 'Did you think you could hit me with that?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99910, 'enUS', 'Hey! Over here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99913, 'enUS', 'Run, children! I\'ll protect you from this monster!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99919, 'enUS', '', 'Try not to be so conspicuous. Much more can be learned by observing than by engaging your enemy in combat.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99922, 'enUS', '\"Go here, Nicholo.\"$b$b\"Fetch me that, Nicholo.\"$b$bOne day I\'ll be giving the orders, I tell ya.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99924, 'enUS', 'I\'m ready when you are, Nicholo.', 'I\'m ready when you are, Nicholo.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99925, 'enUS', 'Stay alert, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99926, 'enUS', 'Just be ready in case anything happens, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99927, 'enUS', 'Whew, this is a lot harder than it looks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99928, 'enUS', 'This is looking pretty good so far. Don\'t stop now, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99929, 'enUS', 'I\'m almost finished, I swear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99930, 'enUS', 'Captain Bladewind! A warrior is attacking us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99931, 'enUS', 'To arms! Avenge me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99932, 'enUS', 'Yo, champ! We\'ve been researchin\' and cataloguin\' day and night to make sure every bozo in this joint knows all there is to know about the one and only Grand Assassin $n.$B$BThere\'s only one of you, right?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99937, 'enUS', 'Aaaaah!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99939, 'enUS', 'Success! We did it, $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99940, 'enUS', 'God-King Skovald has been spotted at the top of the gates. \n\nWhen you are ready, we will take the fight to him!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99941, 'enUS', 'Take me to the top, Vethir!', 'Take me to the top, Vethir!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99942, 'enUS', 'This may take longer than expected. Salvaging the necessary parts for these is quite difficult!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99943, 'enUS', '', 'ALL WILL BE ERADICATED!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99944, 'enUS', '', 'WATER... ETERNAL...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99945, 'enUS', '', 'WASHED AWAY.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99946, 'enUS', '', 'TORN ASUNDER.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99947, 'enUS', '', 'CRUSH!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99948, 'enUS', '', 'DROWN!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99949, 'enUS', '', 'TEMPEST!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99950, 'enUS', '', 'THE STORM AWAKENS.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99951, 'enUS', '<The crystal emanates a sinister energy.>', '<The crystal emanates a sinister energy.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99952, 'enUS', '<Place the dreamleaf at the foot of the crystal.>', '<Place the dreamleaf at the foot of the crystal.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99953, 'enUS', 'I\'ll see if I can scrounge up a few more parts! We need every bit of help we can get.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (99954, 'enUS', 'Evan Banlip begins to whistle a grim tune.', '', 26124);
