INSERT INTO `broadcast_text_locale` VALUES (102493, 'enUS', '', 'These guys don\'t play nice!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102494, 'enUS', 'You have bested me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102495, 'enUS', 'Congratulations, hero! The second challenge is complete.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102496, 'enUS', 'Rehgar, this may be the most powerful fighter you\'ve ever brought to the Arena.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102497, 'enUS', 'I know a champion when I see one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102498, 'enUS', 'As The Light as my witness, jewels are my business!$b$bWhat can I help you with?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102499, 'enUS', 'You have proven your worth, champion. You are ready for the final trial, which none have passed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102500, 'enUS', 'The tools of the Keeper of Storms are so powerful, even the Thunder King himself dared not wield them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102501, 'enUS', 'The Fist of Ra-den is your final challenge. If you can wield it and live, the weapon is yours to command.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102502, 'enUS', 'I believe in you, shaman!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102503, 'enUS', '', 'This is so cool.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102504, 'enUS', 'I\'ll be honest... those things scare me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102505, 'enUS', '', 'Yow! Watch it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102506, 'enUS', '', 'Hiya, $n, the name\'s Sminx. I mostly deal in fine jewelry. I leave the actual gem-cutting duties to Tiffany.$b$bIf you\'re looking for something to put your shinies into, come talk to me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102507, 'enUS', 'Focus, champion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102508, 'enUS', '', 'What\'cha lookin\' for, stranger?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102509, 'enUS', 'Stand in the light and prove your mastery over the elements.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102510, 'enUS', 'Destroy the first target with a lightning bolt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102511, 'enUS', 'Hmm. Maybe we should do this outdoors.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102512, 'enUS', 'One moment, great tiger.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102513, 'enUS', 'Stay focused, champion. You were born to command these elements. You can do this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102514, 'enUS', 'So, you are the one Ebonhorn has been talking about?$b$bIt was a strange prophecy from a strange spiritwalker.$b$bToo strange, if you ask me.', 'So, you are the one Ebonhorn has been talking about?$b$bIt was a strange prophecy from a strange spiritwalker.$b$bToo strange, if you ask me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102515, 'enUS', 'Did you hear? The High-Chieftain is dead!$b$bThe drogbar underking betrayed him.', 'Did you hear? The High-Chieftain is dead!$b$bThe drogbar underking betrayed him.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102516, 'enUS', 'Jale is going to try to negotiate with the drogbar. Without the help of the other tribes, we have no choice but to try for peace.', 'Jale is going to try to negotiate with the drogbar. Without the help of the other tribes, we have no choice but to try for peace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102517, 'enUS', 'I heard the drogbar have already begun attacking Whitewater Wash and even Thunder Totem.$b$bThese are dark days.', 'So, you are the one Ebonhorn has been talking about?$b$bIt was a strange prophecy from a strange spiritwalker.$b$bToo strange, if you ask me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102518, 'enUS', 'Use a lava burst to destroy the second target.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102519, 'enUS', 'Concentrate! Stay in control!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102520, 'enUS', 'Something is wrong... someone is here...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102521, 'enUS', 'The Legion is attacking!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102522, 'enUS', 'You will never stop the Inquisition!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102523, 'enUS', '', 'You need to get me out of this cage! We\'re in more danger than you suspect!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102524, 'enUS', 'The High Inquisitor\'s work will not be interrupted!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102525, 'enUS', '', 'Thank you, $n. Back into the fight.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102526, 'enUS', 'Thank you, $n. Back into the fight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102527, 'enUS', 'Leave the mortals alive. We must find the weapons of their so-called \'Storm-God.\'', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102528, 'enUS', 'I will feast on your soul after my triumph here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102529, 'enUS', '', 'Thank you, $n. Back into the fight.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102530, 'enUS', 'The Legion will scorch this world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102531, 'enUS', 'We will defile the very ground you stand on!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102532, 'enUS', 'Thank you, $n. Back into the fight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102533, 'enUS', 'Soon there will be no place for you to run...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102534, 'enUS', 'You rely on the elements to fight for you? Weakling!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102535, 'enUS', 'Ancestors watch over you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102536, 'enUS', 'Begone, mortal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102537, 'enUS', 'Finish... him...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102538, 'enUS', 'My master WILL possess the power of the storm!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102539, 'enUS', 'Glory to the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102540, 'enUS', '', 'I will repay my debt to you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102541, 'enUS', '', 'The Burning Legion will fall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102542, 'enUS', 'Champion, you saved us...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102543, 'enUS', '', 'I will not let you down.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102544, 'enUS', '', 'For Lord Illidan!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102545, 'enUS', 'The elements walk in harmony with this one. I\'ve not seen a better shaman since I fought alongside Thrall himself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102546, 'enUS', '', 'We WILL take the keystone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102547, 'enUS', 'Shaman, no further trials are required. The weapons of the Keeper of Storms have found their new master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102548, 'enUS', '', 'Die demons!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102549, 'enUS', 'Go forth, champion of the Earthen Ring, with my blessing. Azeroth is under siege, and heroes like you must rise to the challenge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102550, 'enUS', '', 'I did not make it this far just to die on this rock.', 26124);
