INSERT INTO `broadcast_text_locale` VALUES (102771, 'enUS', 'Be still, worm! I will deal with you shortly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102772, 'enUS', 'A fluke. Savor it, and pray you do not face me next.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102773, 'enUS', 'They will be more than prepared, for your true power rests in the Nightmare.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102774, 'enUS', 'Silence, weakling! When you open your mouth again, it will be to cry for mercy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102775, 'enUS', 'A pity you have come this far only to die at the hands of Xavius!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102776, 'enUS', 'An empty threat. You hold no sway over this realm, Nightmare Lord.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102777, 'enUS', 'Bold words for a prisoner. Even an echo of my power was enough to overwhelm you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102778, 'enUS', 'A pity you have come this far only to die at the hands of Xavius!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102779, 'enUS', 'Your voice is but a whisper in this realm.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102780, 'enUS', 'Your mind games hold no sway over me, dog!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102781, 'enUS', 'First you suffer; then you die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102782, 'enUS', 'Stand together if you hope to win!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102783, 'enUS', 'I will fracture your feeble mind!', '', 26124);
