INSERT INTO `broadcast_text_locale` VALUES (102786, 'enUS', 'Fight back! Do not let him manipulate you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102787, 'enUS', 'For every blow you strike, I will return even stronger!', '', 26124);
