INSERT INTO `broadcast_text_locale` VALUES (102807, 'enUS', 'None may challenge me and live!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102808, 'enUS', 'A well-earned victory! We must, however, stay vigilant. Deep inside the Nightmare Xavius remains, unvanquished. The enemy you fought here was merely his shadow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102809, 'enUS', 'If you truly love nature, you will find death everywhere.', 'If you truly love nature, you will find death everywhere.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102810, 'enUS', 'An early morning sacrifice is a blessing for the whole day.', 'An early morning sacrifice is a blessing for the whole day.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102811, 'enUS', 'Nature never hurries, it kills slowly... painfully.', 'Nature never hurries, it kills slowly... painfully.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102812, 'enUS', 'My profession is to always find agony in nature.', 'My profession is to always find agony in nature.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102813, 'enUS', 'To sit in the shade on a fine day and look upon a rotting corpse is the most perfect refreshment.', 'To sit in the shade on a fine day and look upon a rotting corpse is the most perfect refreshment.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102814, 'enUS', 'It seems you\'ve been followed, $c! Dark Riders!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102815, 'enUS', 'All seasons are beautiful for the person who carries cruelty within.', 'All seasons are beautiful for the person who carries cruelty within.', 26124);
