INSERT INTO `broadcast_text_locale` VALUES (102818, 'enUS', 'Try to keep up, $c!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102819, 'enUS', 'Flowers and maggots are heaven\'s masterpiece.', 'Flowers and maggots are heaven\'s masterpiece.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102820, 'enUS', 'Quickly! We don\'t have time to linger!', '', 26124);
