INSERT INTO `broadcast_text_locale` VALUES (102822, 'enUS', 'Look out behind you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102823, 'enUS', 'Never lose an opportunity to kill anything that is beautiful.', 'Never lose an opportunity to kill anything that is beautiful.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102824, 'enUS', 'Your skill in combat is impressive. We may yet survive this.', '', 26124);
