INSERT INTO `broadcast_text_locale` VALUES (102828, 'enUS', 'A burden carried by one can bring them to their knees, but carried by many and it becomes a lesson from which we learn.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102829, 'enUS', 'One day I\'ll find it...', 'One day I\'ll find it...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102830, 'enUS', 'Let us press on. We are nearly there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102831, 'enUS', 'Here! The camp is in this clearing. Let us search it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102832, 'enUS', 'The lovers... the dreamers... and me.', 'The lovers... the dreamers... and me.', 26124);
