INSERT INTO `broadcast_text_locale` VALUES (102878, 'enUS', 'You have final come out of the shadows, fiend!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (102879, 'enUS', 'Most impressive! I never thought I would meet anyone who could match the Valarjar\'s strength... and yet here you stand.', '', 26124);
