INSERT INTO `broadcast_text_locale` VALUES (103802, 'enUS', 'Me... sage... for yoooou!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (103803, 'enUS', '', 'Aye.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (103804, 'enUS', 'The vote carries. I\'m sorry, Jaina, but the stakes are higher than they\'ve ever been.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (103805, 'enUS', '', 'Modera! Vargoth! How could you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (103806, 'enUS', 'Me... go...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (103807, 'enUS', '', 'Jaina, please... we are doing what\'s best for Azeroth. Stand with us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (103808, 'enUS', '', 'This Council is blind to the truth. When the Horde betrays you again, remember who warned you. Farewell.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (103809, 'enUS', 'That was... difficult. But we must move forward. We will send our invitation to the Warchief at once. We have a war to win.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (103810, 'enUS', 'UNCROSS THE WIRES TO SAVE MY DIGITAL LIFE', 'UNCROSS THE WIRES TO SAVE MY DIGITAL LIFE', 26124);
INSERT INTO `broadcast_text_locale` VALUES (103811, 'enUS', 'But, not so fast, $n. I have another request of you.', '', 26124);
