INSERT INTO `broadcast_text_locale` VALUES (104186, 'enUS', '', 'Avenge me, Anishar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (104187, 'enUS', 'Where is the betrayer?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (104188, 'enUS', 'By the might of the Nightwell!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (104189, 'enUS', 'Foolish creature. I will show you real power.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (104190, 'enUS', 'Elisande... I have failed you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (104191, 'enUS', 'Pathetic... creature... I will...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (104192, 'enUS', 'Avenge my death, Idriana.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (104193, 'enUS', 'Evaluating target.', '', 26124);
