INSERT INTO `broadcast_text_locale` VALUES (105151, 'enUS', 'Bones suit you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105152, 'enUS', '', 'I thank you for my freedom, $n.$b$bUnfortunately, the soul harvester has taken much of my strength. I will regain the full extent of my powers shortly.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105153, 'enUS', 'I\'d like to introduce you to Calia Menethil... yes, that Menethil. She has been a great help these past few years. I\'d like her to assist you, helping the helpless, conveying your word to the world, all that.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105154, 'enUS', '', '$p! You\'re alive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105155, 'enUS', '', 'The Twinblades are even more impressive in person.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105156, 'enUS', 'They tell me to clean up Underking\'s mess. But look at \'dis. It gonna take forever.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105157, 'enUS', 'Headed down to the Broken Shore, eh? I know right where you need to go, Zeomus here will get ya there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105158, 'enUS', 'Beakbuck sends a placeholder event!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105159, 'enUS', '', 'If you wish to find me when Grimwing is slain... follow the trail of demon corpses.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105160, 'enUS', 'CHEAT - Make me eligible for Chapter 1.', 'CHEAT - Make me eligible for Chapter 1.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105161, 'enUS', 'CHEAT - Make me eligible for Chapter 2.', 'CHEAT - Make me eligible for Chapter 2.', 26124);
