INSERT INTO `broadcast_text_locale` VALUES (105290, 'enUS', 'Foolish mortals! Are you so eager to take the place of my next meal?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105291, 'enUS', 'So be it! This prisoner is useless to me anyway. Its blood is weak and stale.', '', 26124);
