INSERT INTO `broadcast_text_locale` VALUES (105294, 'enUS', 'I am ready.', 'I am ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105299, 'enUS', 'Switching', 'Switching', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105300, 'enUS', 'We\'re here to slay a demon, not rescue every failure who couldn\'t get the job done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105301, 'enUS', 'I don\'t care! Slaying the demon is our first priority. Come, we\'ve wasted enough time with this blathering.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105302, 'enUS', 'What!? Don\'t ye have any sense of duty to aid yer fellow knights?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105303, 'enUS', '', 'What!? Don\'t you have any sense of duty to aid your fellow knights?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105308, 'enUS', 'Blast ye, Sliver! Fine! I\'ll help ye, but I\'ll rescue Margrave first chance I get!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105309, 'enUS', 'Thank Odyn, I thought I was fishbait.', 'Thank Odyn, I thought I was fishbait.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105310, 'enUS', 'May Valhallas shine upon you.', 'May Valhallas shine upon you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105311, 'enUS', 'Oh my, what a situation. I\'m out of here.', 'Oh my, what a situation. I\'m out of here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105312, 'enUS', '', 'I\'ve been helping the villagers at Dai-Lo Farmstead recover from their vermin infestation.$B$BChen told me stories about Shen-zin Su, but I never expected the turtle to be so vast!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105313, 'enUS', '', 'I know only too well the pain and suffering that comes from losing a loved one. That\'s why I joined this fight, and the brew here\'s not so bad, either.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105314, 'enUS', 'What can I help you with, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105315, 'enUS', 'Can you give me an empty barrel?', 'Can you give me an empty barrel?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105316, 'enUS', 'Do you have hops and yeast?', 'Do you have hops and yeast?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105317, 'enUS', 'Chen wanted me to ask for some honey.', 'Chen wanted me to ask for some honey.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105318, 'enUS', 'We\'re leaving soon, Yalia.', 'We\'re leaving soon, Yalia.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105319, 'enUS', '', 'I will send you to the abyss.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105320, 'enUS', '', 'Jandvik is oursss.', 26124);
