INSERT INTO `broadcast_text_locale` VALUES (105322, 'enUS', '', 'Death to our enemiesss!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105323, 'enUS', '', 'You will fall before the Sashj\'tar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105324, 'enUS', '', 'Here you go. Tell Chen to be careful out there. We have already lost too many to the Legion attacks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105325, 'enUS', '', 'You\'re a cold one, Sliver. Fine! I\'ll help, but if we have any chance of getting Margrave out alive, we should take it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105326, 'enUS', '', 'That poor boy. The temple puts a heavy burden on his shoulders, yet all he wants is to settle down with Yalia.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105327, 'enUS', 'These supplies should do. Tell Chen good luck with his journey.', '', 26124);
