INSERT INTO `broadcast_text_locale` VALUES (105539, 'enUS', 'A warden tried to convince us to turn traitor. I heard screaming.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105540, 'enUS', 'Cyana Nightglaive turned traitor. She joined the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105541, 'enUS', 'Nightglaive went southwest with a warden named Cordana Felsong.', '', 26124);
