INSERT INTO `broadcast_text_locale` VALUES (105603, 'enUS', '', 'If you will follow me. The Eye of Odyn is a powerful scrying and communication device. With it you will be able to see the Broken Isles, send orders to your champions, and enact your own special operations. Skyseer Ghrent will help you manage the Eye.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105604, 'enUS', '', 'Here is Skyseer Ghrent and the Eye of Odyn. May your orders come swift and your battles victorious, $ct!', 26124);
