INSERT INTO `broadcast_text_locale` VALUES (105698, 'enUS', 'My, my... Someone has made quite a mess down here. It has been too long since we purged these catacombs of refuse. It appears we have a rat infestation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105699, 'enUS', 'Find the vermin and bring me their heads! Leave no stone unturned!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105700, 'enUS', 'I have not survived for ten thousand years just to fall to a swarm of rats.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105701, 'enUS', 'The Grand Magistrix... will have your heads for this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105702, 'enUS', 'Alas... blind courage only triumphs in the fairy tales.', '', 26124);
