INSERT INTO `broadcast_text_locale` VALUES (105730, 'enUS', 'The nerve! You must stop the Blackswell from darkening the hearts of these poor Rockaway murlocs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (105731, 'enUS', 'So brave! So cunning! I\'m glad an adventurer like you is willing to think of the murlocs! Thank you!', '', 26124);
