INSERT INTO `broadcast_text_locale` VALUES (106274, 'enUS', 'You are entering a restricted area. You will be teleported to Dalaran unless you return to your previous zone.', 'You are entering a restricted area. You will be teleported to Dalaran unless you return to your previous zone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (106275, 'enUS', 'Suffer well.', '', 26124);
