INSERT INTO `broadcast_text_locale` VALUES (107848, 'enUS', '|TInterface\\Icons\\spell_priest_shadoworbs:20|t%s begins to cast |cFFFF0000|Hspell:207439|h[Void Nova]|h|r!', '|TInterface\\Icons\\spell_priest_shadoworbs:20|t%s begins to cast |cFFFF0000|Hspell:207439|h[Void Nova]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107849, 'enUS', '|TInterface\\Icons\\Ability_Druid_Starfall:20|t%s begins to cast |cFFFF0000|Hspell:205408|h[Grand Conjunction]|h|r!', '|TInterface\\Icons\\spell_frost_ring of frost:20|t%s begins to cast |cFFFF0000|Hspell:206949|h[Frigid Nova]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107850, 'enUS', '', 'Put your back into it, Taraar. Quickly!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107851, 'enUS', 'Me?! If you would focus, we\'d be done by now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107852, 'enUS', 'At last something reasonable escapes your lips!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107853, 'enUS', 'Caria must complete her work. You will not interfere!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107854, 'enUS', 'Reesh Archim, galar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107855, 'enUS', '', 'UNGH! I\'m hit!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107856, 'enUS', '', 'You... must continue. The Warblades are worth... any sacrifice!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107857, 'enUS', '', 'I will secure a way out of here. Caria is yours.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107858, 'enUS', '', 'I\'ll meet you at your order hall once you\'ve gathered the ingredients.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107859, 'enUS', '', 'Yeah! I\'m ready for action!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107860, 'enUS', 'Use your |TInterface\\Icons\\ability_demonhunter_spectralsight.blp:20|t |cFFFF0000|Hspell:188501|h[Spectral Sight]|h|r to find Caria\'s trail!', 'Use your |TInterface\\Icons\\ability_demonhunter_spectralsight.blp:20|t |cFFFF0000|Hspell:188501|h[Spectral Sight]|h|r to find Caria\'s trail!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107861, 'enUS', '', 'I heard Angus talking about it back at home. He said it makes you a million times stronger!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107862, 'enUS', '', 'Yeahhhh! Another one bites the dust!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107863, 'enUS', '', 'Awww, is the adventure over already? Okay...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107864, 'enUS', 'Fast little wikket, you are.  Stick with me and you\'ll go far!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107865, 'enUS', 'How is it? Found many ingredients already?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107866, 'enUS', 'More recruits show up every day, seeking to learn from the Master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107867, 'enUS', 'Good luck out there, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107868, 'enUS', '', 'I can\'t take these infernals alone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107869, 'enUS', 'Ey Master, nice ta see ya again! We heard ye might need some help gathering materials fer the Storm Brew.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107870, 'enUS', 'I\'m happy ta help mastah, long as I get a taste a\' dat brew when it be done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107871, 'enUS', 'We\'ve been hearin\' reports of increased Legion activity near the Tomb of Sargeras.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107872, 'enUS', 'Legion forces are amassin\' in great concentration there. Maybe for a large attack.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107873, 'enUS', 'We\'ll fight wherever we need ta, but we\'d sure lose a lot fewer of our order if we had somethin\' like this Storm Brew.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107874, 'enUS', 'I can\'t wait to try just a little taste o\' dat brew when it\'s ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107875, 'enUS', 'That should do it! We\'ll report back to duty. Good luck, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107876, 'enUS', '', 'Unhand my father\'s body, you monster!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107877, 'enUS', '', 'Father! Fight it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107878, 'enUS', 'Oh, how Ulan\'s spirit cries out! How he weeps, knowing he will be the instrument of your destruction!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107879, 'enUS', 'How delicious... the hapless chieftain dying at the hands of her beloved father!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107880, 'enUS', '', 'Resist him, father! Do not let him control you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107881, 'enUS', 'You failed to save your father in life. Now you fail his spirit! Such a disappointing daughter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107882, 'enUS', 'Your petty weapons are no match for me here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107883, 'enUS', 'Fascinating. You still think you can win?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107884, 'enUS', 'No! Your spirits were to be mine...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107885, 'enUS', '', 'Aegira? My dear apprentice? It brings me joy to hear your voice.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107886, 'enUS', 'Welcome to MY world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107887, 'enUS', '', 'Master... I am unworthy. I wish I had more time to learn from you. But you were avenged. Mornjirn is dead, and we recovered your Storm Brew recipe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107888, 'enUS', '', 'My dear Aegira, your skills are already beginning to surpass mine. From this day forward, no longer are you my apprentice. Go forward proudly as a Brewmaster, and continue my legacy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107889, 'enUS', '', 'Thank you, Master.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107890, 'enUS', 'Greg! Dance harder! People actually showed up to this scam... I mean event!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107891, 'enUS', 'I pay you to dance Greg, not mope! So put some boogie into it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107892, 'enUS', 'Arcane energy thrums around the conduit. It seems to be linked to the Guardian Constructs somehow.\n\nUnfortunately, the intricacies of this mechanical construct are unknown to you.', 'Arcane energy thrums around the conduit. It seems to be linked to the Guardian Constructs somehow.\n\nUnfortunately, the intricacies of this mechanical construct are unknown to you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107893, 'enUS', 'This is it Greg! We finally hit the big time! People from all over Azeroth are here at OUR event!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107894, 'enUS', 'Greg. You\'re fired.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107895, 'enUS', 'Brewing', 'Brewing', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107896, 'enUS', 'Back in Dalaran from her successful world tour, it\'s... Tiffany Nelson! Today only, step up and challenge a true pet battle superstar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107897, 'enUS', 'Do you believe in miracles? Our challenger defeated Tiffany Nelson! Keep an eye on this up-and-coming superstar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107898, 'enUS', '', 'The Legion witch is dead. Grandmaster, I think this calls for a celebration!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107899, 'enUS', 'Firelord? You are nothing but a pile of rocks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107900, 'enUS', 'Careful shaman. Play with fire and you will get burned...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107901, 'enUS', 'Perhaps I underestimated your strength...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107902, 'enUS', 'Today in the arena, it\'s the mighty Sir Galveston! He may be a bit of a nut case, but he paid the entrance fee... so he\'s in! Step right up and challenge the local lunatic!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107903, 'enUS', 'Ladies and gentlemen, you just witnessed an epic victory over a guy who fights with a toy strapped to a bird. Frankly, it\'s not that impressive... but a win\'s a win!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107904, 'enUS', '', 'The ritual is nearly ready. Stop them, you mongrels!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107905, 'enUS', 'And in this corner, we have Bodhi Sunwayver... the shirtless guy! Why doesn\'t he wear a shirt? Is it being washed? Did he lose a bet? No one knows!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107906, 'enUS', '', 'I will claim every soul on this pathetic world!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107907, 'enUS', 'What a match! Bodhi may be terrible at pet battles, but he sure is easy on the eyes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107908, 'enUS', '', 'The Legion is only the beginning. My blades call upon power from beyond!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107909, 'enUS', 'What a delicious looking intruder! It has been ages since I\'ve dined on mortal flesh! Long have I hungered for the taste...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107910, 'enUS', 'I feel unwell...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107911, 'enUS', 'Ugh, what is happening?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107912, 'enUS', 'This fruit is horrible...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107913, 'enUS', 'Ugh. I must find a physician!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107914, 'enUS', 'Lower those weapons, bonemen. I am here for $p.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107915, 'enUS', 'It looks like a war zone out there. Nicely done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107916, 'enUS', 'Welcome to my web, delicious mortal! I do hope you\'ll stick around!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107917, 'enUS', 'I will rend your flesh and dine on your entrails!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107918, 'enUS', 'Avenge me my children! Avenge me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107919, 'enUS', '', 'I will need to focus on the vision to understand its message.\n\nWe have had our differences in the past, but this is bigger than both of us. Can I trust you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107920, 'enUS', 'By my honor you will come to no harm, priestess.', 'By my honor you will come to no harm, priestess.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107921, 'enUS', '', 'I pray we are not too late.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107922, 'enUS', '', 'Meet me back at the refuge and we will continue our search. Be quick about it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107923, 'enUS', '', 'Do not let anything disturb my meditation.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107924, 'enUS', '', 'Stay close. Do not let the Nightmare confound you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107925, 'enUS', '', 'The path splits. Wait, what is that?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107926, 'enUS', '', 'The corruption is thick here. What is that across the bridge? Follow closely.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107927, 'enUS', 'Lower those weapons, pale elves. I am here for $p.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107928, 'enUS', '', 'There is no time to mourn. Elothir is beyond my reach now. Come, and do not delay.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107929, 'enUS', '', 'Shala\'nir is just ahead. You should be ready for whatever foulness awaits us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107930, 'enUS', '', 'I admit, I was wary to have one of your kind accompany me. But you have fought with bravery and valor. Let us see this through together.\n\nAre you prepared to enter the epicenter of this chaos, $r?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107931, 'enUS', '', 'Return to me, warrior of the Horde! I have found something!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107932, 'enUS', 'Turns out the egg held an ancient magical creature bent on watching the world burn. Luckily our challenger was victorious!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107933, 'enUS', 'Featured in today\'s pet battle spectacular, it\'s... Hey, who put that egg in the arena? Uh... Step right up and fight a big egg! Beat up a defenseless egg, limited time only!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107934, 'enUS', 'I claim this vessel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107935, 'enUS', '', 'Show your face, Grey One!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107936, 'enUS', 'You ronir have grown strong during my long absence, Highmountain. But not strong enough.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107937, 'enUS', '', 'My people stopped you in ages past, Darkness. We will defeat you again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107938, 'enUS', 'Your ancestors were overconfident as well. It shall prove to be your undoing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107939, 'enUS', 'Still... there is power in your bloodline, child. I shall relish claiming it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107940, 'enUS', '', 'I will never serve you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107941, 'enUS', 'One day... but you are not yet a worthy vessel for my power. No... I have a more... formidable chieftain in mind!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107942, 'enUS', '', 'What could he... Oh no... My father! He means to claim my father!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107943, 'enUS', 'Due to technical difficulties, today\'s tournament is canceled! Ow! That one bit me! Ow! Err... Please stand by! This little rat problem should be resolved shortly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107944, 'enUS', 'The tournament is saved! I\'m not sure how those rodents got out of their cages. Umm... did I say cages? What cages? It\'s not like I\'m part of a magically enhanced rat smuggling ring or something!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107945, 'enUS', '', 'Watch out! Doomguard headed your way!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107946, 'enUS', '', 'Well fought, but Legion reinforcements are approaching as we speak. We must depart.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107947, 'enUS', '[PH] And then a rad finale cinematic plays! [PH]', '[PH] And then a rad finale cinematic plays! [PH]', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107948, 'enUS', 'If you are unafraid of death, meet me at my throne. You may prove useful after all...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107949, 'enUS', 'Soon, Lord Smolderon... you will kneel before the true Firelord!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107950, 'enUS', 'Foolish mortals! Soon this place will belong to the master!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107951, 'enUS', '', 'Show them no quarter!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107952, 'enUS', '', 'Smite the defiler!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107953, 'enUS', '', 'Show them nature\'s wrath!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107954, 'enUS', '', 'Andu-falah-dor!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107955, 'enUS', '', 'Lord Ursoc! Quickly $n, to his side!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107956, 'enUS', '', 'The ritual I\'m about to perform is complicated, and will take some time to finish, but it is the only way to drop the barrier.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107965, 'enUS', 'It\'s a tray of treats. It looks like this tray in particular has been infused with some strange arcane magic.', 'It\'s a tray of treats. It looks like this tray in particular has been infused with some strange arcane magic.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107966, 'enUS', 'It\'s a tray of treats. It looks like this tray in particular has been infused with some strange arcane magic.\n\nEating random magically infused food might be bad for your system... you don\'t know if it is safe or not.', 'It\'s a tray of treats. It looks like this tray in particular has been infused with some strange arcane magic.\n\nEating random magically infused food might be bad for your system... you don\'t know if it is safe or not.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107967, 'enUS', 'Years of training in the culinary arts has strongly developed your skill in Cooking. These parts here are safe to eat.', 'Years of training in the culinary arts has strongly developed your skill in Cooking. These parts here are safe to eat.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107968, 'enUS', 'As a Pandaren, all food is familiar to you. Nothing can break your iron belly. Time to eat!', 'As a Pandaren, all food is familiar to you. Nothing can break your iron belly. Time to eat!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107969, 'enUS', '|TInterface\\Icons\\spell_mage_altertime:20|tYou\'ve been given a |cFFFF0000|Hspell:206617|h[Time Bomb]|h|r.', '|TInterface\\Icons\\spell_mage_altertime:20|tYou\'ve been given a |cFFFF0000|Hspell:206617|h[Time Bomb]|h|r.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107970, 'enUS', '|TInterface\\Icons\\inv_archonpet:20|t%s begins casting |cFFFF0000|Hspell:211927|h[Power Overwhelming]|h|r!', '|TInterface\\Icons\\inv_archonpet:20|t%s begins casting |cFFFF0000|Hspell:211927|h[Power Overwhelming]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107971, 'enUS', '|TInterface\\Icons\\inv_archonpet:20|t%s reaches |cFFFF0000|Hspell:207972|h[Full Power]|h|r !', '|TInterface\\Icons\\inv_archonpet:20|t%s reaches |cFFFF0000|Hspell:207972|h[Full Power]|h|r !', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107972, 'enUS', '', 'I see you, monk! Your struggle is for naught. My forces will purge your order from the face of this world!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107973, 'enUS', '', 'You cannot hide from me, monk. Try as you might, but your monastery will burn!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107974, 'enUS', '', 'You are stronger than you look, monk, but I will not be defied! Jorvinax! Keep that portal open!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107975, 'enUS', 'Greetings, and thank you for helping to test the Emerald Nightmare!\n\nI can teleport you directly to any bosses that are currently available for testing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107976, 'enUS', 'Why defy that which devours all things?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107977, 'enUS', 'I want to teleport to Elerethe Renferal.', 'I want to teleport to Elerethe Renferal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107978, 'enUS', 'A timely demise.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107979, 'enUS', 'I want to teleport to Il\'gynoth, Heart of Corruption.', 'I want to teleport to Il\'gynoth, Heart of Corruption.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107980, 'enUS', 'So linear. So limited.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107981, 'enUS', 'Time is on MY side.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107982, 'enUS', 'The wheels of time turn against you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107983, 'enUS', 'Time races past you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107984, 'enUS', 'I want to teleport to Nythendra.', 'I want to teleport to Nythendra.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107985, 'enUS', 'Time will not be kind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107986, 'enUS', 'I want to teleport to Dragons of Nightmare.', 'I want to teleport to Dragons of Nightmare.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107987, 'enUS', 'Feel like time is standing still?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107988, 'enUS', 'Time waits for no one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107989, 'enUS', 'I want to teleport to Xavius.', 'I want to teleport to Xavius.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107990, 'enUS', 'Precise. Perfect. Inevitable.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107991, 'enUS', 'I want to teleport to Cenarius.', 'I want to teleport to Cenarius.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107992, 'enUS', 'My time has come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107993, 'enUS', 'I want to teleport to Ursoc.', 'I want to teleport to Ursoc.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107994, 'enUS', 'It was only a matter of time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107995, 'enUS', 'Your pets are so atrocious, I consider it a provocation!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107996, 'enUS', 'If only you knew what lay beyond...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107997, 'enUS', 'Such insolence... very well, I shall show you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107998, 'enUS', 'See for yourself the cruel, frozen void that lies beyond the margin of Azeroth\'s skies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (107999, 'enUS', 'The stars align! Fear their portents!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108000, 'enUS', 'No mortal may abrogate the precepts of fate!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108001, 'enUS', 'As the stars wax, so your lives wane!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108002, 'enUS', 'I have all the time in the world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108003, 'enUS', 'Lets begin your lesson in humility.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108004, 'enUS', 'Lesson over.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108005, 'enUS', 'Begone! I need time to contemplate this affront.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108006, 'enUS', 'So undisciplined.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108007, 'enUS', 'You will learn respect or I will continue to educate you in it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108008, 'enUS', 'That one isn\'t fit to clean my quarters, much less battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108009, 'enUS', 'Interesting, show me more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108010, 'enUS', 'An unfortunate miscalculation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108011, 'enUS', 'You cannot discern between a victory or a sacrifice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108012, 'enUS', '', 'These hoofprints follow the path to the left. After them, quickly!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108013, 'enUS', '', 'I cannot tell where his voice is coming from! Search the buildings to the west! Now! I will scour the east for signs of him!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108014, 'enUS', '', 'Warrior of the Horde! To me! I have found something!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108015, 'enUS', '', 'I can\'t... hold... anymore...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108016, 'enUS', '', 'This is too much to bear!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108017, 'enUS', '', 'Augghhh!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108018, 'enUS', '', 'H-help! I can\'t hold... on...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108019, 'enUS', 'I thank you, druids. These abominations of Nightmare are relentless! They will stop at nothing to snatch the claws from my lair.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108020, 'enUS', '', 'Lord Ursoc, the Burning Legion has returned. $n is our best hope to drive back the Legion AND the Nightmare, but we must have the claws if we are to succeed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108021, 'enUS', '', 'We humbly request your aid. The power of your claws could defeat our enemies once and for all.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108022, 'enUS', '', 'Lord Ursoc, I merely returned the claws to their rightful place; a place where their power would not be abused.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108023, 'enUS', '', 'Thank you, mighty Ursoc!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108024, 'enUS', 'Meeting me must be overwhelming. Understandable... the low-born are often stricken dumb in my presence. Luckily for you, I am feeling charitable and will honor you with a battle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108025, 'enUS', 'A fluke! A mishap! Someone of my station does not lose to a commoner with such boorish manners. Tell no one of this encounter.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108026, 'enUS', 'Good to see you, friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108027, 'enUS', 'I... um... misplaced that crystal thing you gave me. Do you have another?', 'I... um... misplaced that crystal thing you gave me. Do you have another?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108028, 'enUS', 'Greetings, and thank you for helping to test the Suramar Palace!\n\nI can teleport you directly to any bosses that are currently available for testing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108029, 'enUS', 'This sacred peak is for hunters alone.$bTurn back, or the eagle spirit will carry you away!', 'This sacred peak is for hunters alone.$bTurn back, or the eagle spirit will carry you away!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108030, 'enUS', 'I want to teleport to Crystal Scorpion.', 'I want to teleport to Crystal Scorpion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108031, 'enUS', 'I want to teleport to Chronomatic Anomaly.', 'I want to teleport to Chronomatic Anomaly.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108032, 'enUS', 'I want to teleport to Trilliax.', 'I want to teleport to Trilliax.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108033, 'enUS', 'I want to teleport to Spellblade Aluriel.', 'I want to teleport to Spellblade Aluriel.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108034, 'enUS', 'I want to teleport to Tichondrius.', 'I want to teleport to Tichondrius.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108035, 'enUS', 'Kayn, I need to return to the Black Temple.', 'Kayn, I need to return to the Black Temple.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108036, 'enUS', 'I want to teleport to Astromancer Etraeus.', 'I want to teleport to Astromancer Etraeus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108037, 'enUS', 'I want to teleport to High Botanist Tel\'arn.', 'I want to teleport to High Botanist Tel\'arn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108038, 'enUS', 'I want to teleport to Krosus.', 'I want to teleport to Krosus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108039, 'enUS', 'I want to teleport to Grand Magistrix Elisande.', 'I want to teleport to Grand Magistrix Elisande.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108040, 'enUS', 'I want to teleport to Gul\'dan.', 'I want to teleport to Gul\'dan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108041, 'enUS', '', 'I heard your call for aid, great one. Are there any teeny lil\' fuzzy animals in danger? Oh! Oh my!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108043, 'enUS', 'The other spiders seem wary of this one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108044, 'enUS', 'I am Grash, son of Gralashar.$b$bThe snake witch controls us with magic stick. Those who fight she takes away to kill.$b$bHave you come to kill snake witch?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108045, 'enUS', 'She calls herself \"Lady Zithreen.\" She has many snakes. Snakes who follow her. Snakes on her face.$b$bShe claims water throne for her queen, using magic stick.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108046, 'enUS', '<Grash frowns.>$b$bMagic stick is very old. Very powerful. It is made of living water.$b$bSnake witch says the magic stick came from her queen. It speaks to the sea. Sea monsters obey the stick. It heals their wounds.$b$bStick fill the heads of giants with dark and pain. The dark comes from the witch, not the stick.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108047, 'enUS', 'Sha-man? Like, little one who speaks to water and storms? $b$bYes, Grash has seen her. She fights the snake witch and loses.$b$bSnake witch keeps her alive. Says stupid people will come for her.$b$bHa hah! Snake witch is the dumb one. No one stupid enough to come for shaman lady.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108048, 'enUS', 'You will kill snake witch?$b$bYou will take away magic stick?$b$bYes! Grash will help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108049, 'enUS', 'Who is this snake witch?', 'Who is this snake witch?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108050, 'enUS', 'We seek a magic stick! What does it look like?', 'We seek a magic stick! What does it look like?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108051, 'enUS', 'We are looking for a shaman. Have you seen her?', 'We are looking for a shaman. Have you seen her?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108052, 'enUS', 'Join us! We will kill the \"snake witch\" and take this \"magic stick!\"', 'Join us! We will kill the \"snake witch\" and take this \"magic stick!\"', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108053, 'enUS', 'Let\'s go!', 'Let\'s go!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108054, 'enUS', 'We must keep these catacombs clear. Not all of us are strong enough to use the portals and must resort to other means of travel, unsettling though they may be.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108055, 'enUS', '%s\'s bark begins to splinter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108056, 'enUS', 'In times like these, everyone must get their hands dirty.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108057, 'enUS', '', 'It is finished! The claws are ours!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108058, 'enUS', 'Your allies have fallen! The ritual has failed!', 'Your allies have fallen! The ritual has failed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108059, 'enUS', 'I must go attend to my prize. Malithar, I leave the claws to you. Use them to exterminate this filth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108060, 'enUS', '', 'You did it, $n! You got him!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108061, 'enUS', '', 'We should go. There\'s nothing left for us here. Let me know when you\'re ready to leave and I\'ll take us back to our bodies in the normal world.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108062, 'enUS', '', 'Are you ready to leave?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108063, 'enUS', '', 'Champion, once again we must ask that you go where we cannot follow. Helya\'s forces must be weakened if we are to prevail.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108064, 'enUS', 'Let\'s go.', 'Let\'s go.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108065, 'enUS', '', 'Do not overextend yourself. The time will come when Helya will face Odyn\'s full wrath. But until then, you have weakened her forces.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108066, 'enUS', 'Are you sure you want to begin the encounter?', 'Are you sure you want to begin the encounter?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108067, 'enUS', 'I am ready.', 'I am ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108068, 'enUS', 'Are you ready to begin the ritual?', 'Are you ready to begin the ritual?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108069, 'enUS', 'This glowing flower very distinctly stands out. It looks to have been meticulously cared for by someone with great horticultural skills.', 'This glowing flower very distinctly stands out. It looks to have been meticulously cared for by someone with great horticultural skills.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108070, 'enUS', 'These new challengers seek to complete the trials you have bested. Show them how a true champion fights!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108071, 'enUS', 'Better you taught them their place is in this life now than delude themselves further.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108072, 'enUS', 'This glowing flower very distinctly stands out. It looks to have been meticulously cared for by someone with great horticultural skills.\n\nProbably best to leave this one to someone more in tune with nature.', 'This glowing flower very distinctly stands out. It looks to have been meticulously cared for by someone with great horticultural skills.\n\nProbably best to leave this one to someone more in tune with nature.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108073, 'enUS', 'Desmond seems to have crossed someone he shouldn\'t have.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108074, 'enUS', '<Search the body for clues.>', '<Search the body for clues.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108075, 'enUS', 'The Inkbinders have lost their way! They forget the true purpose of our runic magic traditions. They must be stopped before they pervert these customs further.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108076, 'enUS', 'To think I would live to see such profane use of runic magic. It saddens my heart.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108077, 'enUS', 'There\'s nothing here.', 'There\'s nothing here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108078, 'enUS', '', 'We shall meet again, $n. May Elune guide our paths.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108079, 'enUS', 'Picking the flower just here should release invigorating spores. You know this because you studied Herbalism and know about plant things.', 'Picking the flower just here should release invigorating spores. You know this because you studied Herbalism and know about plant things.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108080, 'enUS', 'Breathe deep of its spores. As a Druid, you are attuned to nature and can recognize this flower\'s beneficial properties.', 'Breathe deep of its spores. As a Druid, you are attuned to nature and can recognize this flower\'s beneficial properties.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108081, 'enUS', 'We are everywhere. We are Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108082, 'enUS', 'Meddling... whelp...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108083, 'enUS', 'Welcome, $p. As a subtlety rogue you specialize in striking from the shadows, dispatching foes quickly and quietly. Let\'s begin your training with your primary attack, Backstab.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108084, 'enUS', 'Use Backstab on this target dummy. Attacking from behind inflicts more damage.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108085, 'enUS', '', 'Demons continue to march on the Gates of the Valor. Your aid is required.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108086, 'enUS', '', 'You have done well this day, champion. May the Twisting Nether be filled with the lamentations of your enemies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108087, 'enUS', 'What brings you to this lawless place?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108088, 'enUS', 'The shadows reveal.', 'The shadows reveal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108089, 'enUS', 'The Burning Legion. Slayers of whole worlds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108090, 'enUS', 'Could we hope to resist? Can you?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108091, 'enUS', 'Held to the majesty of the stars, you are nothing - less than nothing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108092, 'enUS', 'What are you exactly?', 'What are you exactly?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108093, 'enUS', 'What meager petulance. I expected more from you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108094, 'enUS', 'Is that not enough? Must I show you the true horror of our reality?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108095, 'enUS', 'Very well... witness what I have seen, and TREMBLE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108096, 'enUS', 'Avatars of non-existence... knowing nothing but hunger.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108097, 'enUS', 'These are the beings who will devour the future you so futilely fight to protect!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108098, 'enUS', 'No... I cannot control... the void... it comes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108099, 'enUS', 'I provide information that no other can.$B$BThat is all you need to know.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108100, 'enUS', 'The stars have judged you, and found you wanting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108101, 'enUS', 'Let\'s get back to the matter at hand.', 'Let\'s get back to the matter at hand.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108102, 'enUS', '', 'Those creatures over there are empowered by ancient mana. It is awful to ask this of you, but I need you to dispatch them and siphon their energy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108103, 'enUS', 'It seems fate is on your side.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108104, 'enUS', '', 'Thank you. It is a horrid duty, but this work is essential for our survival.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108105, 'enUS', 'Akaari is in this very city as we speak, spying for her masters.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108106, 'enUS', 'I will gladly tell you where... in exchange for a rune of portals.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108107, 'enUS', 'Until then, our business is complete.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108108, 'enUS', '', 'We go to the Temple of Elune. We will ride more swiftly on Ash\'alah\'s back. Are you ready to depart?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108109, 'enUS', '', 'We go to the Temple of Elune. Ash\'alah will carry you this once. Prepare yourself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108110, 'enUS', 'Greetings! Are you here to see my exquisite collection?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108111, 'enUS', 'If you are here to see my most prized possession, I am afraid that I have it locked away.$b$bI might bring it out in a hundred years or so. Would you like to make an appointment?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108112, 'enUS', 'I am Mornath, a collector of fine arts and collectibles. Would you like to see my statue collection?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108113, 'enUS', 'Lespin wants his sculpture back.', 'Lespin wants his sculpture back.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108114, 'enUS', 'Return to nothing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108115, 'enUS', 'You can not avoid annihilation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108116, 'enUS', 'I was beginning to doubt you would come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108117, 'enUS', 'I carry only the highest quality products.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108118, 'enUS', 'Show me your latest wares.', 'Show me your latest wares.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108119, 'enUS', 'The shadows reveal.', 'The shadows reveal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108120, 'enUS', 'Nothing to share, I\'m afraid. The wine gets people talking, but sometimes they just don\'t have anything useful to say.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108121, 'enUS', 'Say, care for some Moist Azsunian Feta while you\'re here? It has a lovely tangy flavor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108122, 'enUS', 'This will remove a |cFF0070dd|Hitem:124441:0:0:0:0:0:0:0:90:0|h[Leylight Shard]|h|r from your inventory. Are you sure?', 'This will remove a |cFF0070dd|Hitem:124441:0:0:0:0:0:0:0:90:0|h[Leylight Shard]|h|r from your inventory. Are you sure?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108123, 'enUS', '', 'We\'re finally closing in on her after all these years...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108124, 'enUS', '', 'I\'ll meet you upstairs in Glorious Goods when you\'re done. Another one of Khadgar\'s dogs has been nosing around, asking too many questions...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108125, 'enUS', '', 'If you don\'t have any leylight shards on you, I\'m afraid I can\'t finish the ritual.$b$bMaybe you left some in your bank?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108126, 'enUS', 'Do not think... the future will be so easy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108127, 'enUS', 'I heard you might have a tome I\'m looking for.', 'I heard you might have a tome I\'m looking for.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108128, 'enUS', 'Ah, yes, I know the one. It should be on one of the shelves upstairs. Feel free.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108129, 'enUS', 'I haven\'t exploded anybody yet today. It\'s a good day!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108130, 'enUS', 'Hmm... you didn\'t see any nuclear bombs on your way in, did you? No? Alright, I\'ll keep looking.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108131, 'enUS', 'This Oshenko fella\' is always using \"screwdrivers\" on his screws. Hasn\'t he ever heard of a hammer?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108132, 'enUS', 'As a retribution paladin you are a stalwart protector of the weak, a bringer of punishment to the unjust!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108133, 'enUS', 'Use Judgment on this target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108134, 'enUS', 'Musken will trample your bones to dust!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108135, 'enUS', 'Bears will rend the flesh from your bones!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108136, 'enUS', 'You\'ve been detected!', 'You\'ve been detected!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108137, 'enUS', 'Musken! Bless me with fortitude!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108138, 'enUS', 'Ha! Musken prove to be the strongest once again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108139, 'enUS', 'I am disgraced.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108140, 'enUS', 'Crushed under hoof!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108141, 'enUS', 'More! Bring me more to crush!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108142, 'enUS', 'Glory in battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108143, 'enUS', '', 'Stay in the shadows to avoid detection. The Kirin Tor are no fools.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108144, 'enUS', 'Looting', 'Looting', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108145, 'enUS', 'Bjorn! Bless me with savagery!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108146, 'enUS', 'Ha! Bears prove to be the strongest once again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108147, 'enUS', 'I am disgraced.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108148, 'enUS', 'Torn by claws!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108149, 'enUS', 'More! Bring me more to rip apart!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108150, 'enUS', 'Glory in battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108151, 'enUS', '[PH] You are about to drop Poop, get out of the raid!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108152, 'enUS', '', 'The captain will have my head if I do not return soon...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108153, 'enUS', '', 'Yes, citizen?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108154, 'enUS', '', 'State your business, or move along. I am expecting someone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108155, 'enUS', '', 'Finally, the diamond stabilizers we commissioned. Maybe now the power conduits will stop shorting out.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108156, 'enUS', '', 'My sparring partner Naltethis told me what you did. You would make a good soldier.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108157, 'enUS', 'I have your package from Lespin.', 'I have your package from Lespin.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108158, 'enUS', '', 'Eyir! Witness my deeds as I slice down this so-called champion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108159, 'enUS', 'Millenia without so much as a barge to log, and now I am up to my ears in manifests and shipping records.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108160, 'enUS', 'Make it quick. The seas wait for no one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108161, 'enUS', 'I am busier now than I have been in almost ten thousand years. What do you want with me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108162, 'enUS', 'Excellent, my gold pin is here. After all, an illustrious official deserves an equally illustrious badge of office, does he not?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108163, 'enUS', 'I have seen Anastae\'s newest coat. Tell me, are you going to be getting more pelts? I will pay you more than what you are currently making running errands for Lespin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108164, 'enUS', '', 'There are those among the Valkyra who wish to test you in single combat. Go to Skold-Ashil and face them in battle. None shall attack until you strike the first blow.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108165, 'enUS', 'My birds sing such lovely songs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108166, 'enUS', 'What a lovely day.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108167, 'enUS', '\'Borrowing\'', '\'Borrowing\'', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108168, 'enUS', 'You have something for me, yes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108169, 'enUS', 'Ah, what a beautiful perch. A perfect replacement for the broken one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108170, 'enUS', 'Lespin is reaching the end of his usefulness, I think. You would be wise to be careful.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108171, 'enUS', '', 'I go... to the halls... of valor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108177, 'enUS', 'Reveal yourself!', 'Reveal yourself!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108178, 'enUS', 'Archmage Celindra must not be disturbed!', 'Archmage Celindra must not be disturbed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108179, 'enUS', '', 'I definitely heard something. Don\'t worry, Archmage, I\'ll find them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108180, 'enUS', 'I heard it, too, Archmage. I will expel them at once!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108181, 'enUS', 'There you are!', 'There you are!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108182, 'enUS', '', 'My wards detected someone entering the study. Find them at once!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108183, 'enUS', '', 'Champion! Know that you face Bretta, the herald of your doom.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108184, 'enUS', '<Hand the Rune of Portals to Val\'zuun.>', '<Hand the Rune of Portals to Val\'zuun.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108185, 'enUS', 'Excellent... you will find Akaari in the Legerdemain Lounge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108186, 'enUS', '', 'Let us have a glorious battle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108187, 'enUS', '', 'I shall claim the honor of your death.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108188, 'enUS', '', 'I shall stain this ground with your blood.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108189, 'enUS', '', 'Akaari... your time has come.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108190, 'enUS', '', 'Eyir... guide me to your side.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108191, 'enUS', '', 'I go to face my destiny...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108192, 'enUS', '', 'Only the greatest of the Valkyra are worthy of Eyir\'s gift.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108193, 'enUS', '', 'The three fallen brothers of God-King Skovald have arisen. Find them, champion, and remind them why they should stay dead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108194, 'enUS', '', 'Four brothers. Each raised a hand in slaying his father. Each raised a hand in slaying each other. And so Skovald became king, for his blade was the fastest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108195, 'enUS', '', 'The lounge is secure, $p.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108196, 'enUS', '', 'All clear, $p.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108197, 'enUS', '', 'Akaari retreated upstairs as we arrived. She\'s all yours.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108200, 'enUS', '', 'I should thank you. You\'ve saved me the effort of finding you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108201, 'enUS', '', 'Kil\'jaeden will be pleased when I bring him your head.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108202, 'enUS', '', 'You think my master so foolish...?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108203, 'enUS', 'There is snake-man in next room. Big snake-man.$b$bGrash can smell the fish-breath.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108204, 'enUS', 'Everyone\'s ready, Grash. Smash the ice wall!', 'Everyone\'s ready, Grash. Smash the ice wall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108205, 'enUS', 'I will drag you and your pets into the ocean depths.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108206, 'enUS', 'Muster your defenses mortal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108207, 'enUS', 'Join me in the depths.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108208, 'enUS', 'Drowning. I am drowning.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108209, 'enUS', 'Another soul soon joins us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108210, 'enUS', 'Lay still and let the water take you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108211, 'enUS', 'Death comes quickly to the weak.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108212, 'enUS', 'Another takes its place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108213, 'enUS', 'You begin to irritate me mortal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108214, 'enUS', 'Your death will be slow and savored.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108216, 'enUS', '', 'Do you have the fangs?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108220, 'enUS', '', 'Val\'zuun, I will be your death!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108221, 'enUS', '', 'One by one, you will all fall to the Fangs.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108222, 'enUS', '', 'Kirel volosh!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108223, 'enUS', '', 'Miserable worm. Die!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108224, 'enUS', '', 'I strike from the shadows.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108225, 'enUS', 'Something is near. Something big.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108226, 'enUS', 'Stop! Stop! I did not know! I swear it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108227, 'enUS', 'I can still help you! I can take you to Akaari\'s stronghold!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108228, 'enUS', 'Judgment can be used on enemies at a distance or up close whenever it is available.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108229, 'enUS', 'Use Blade of Justice on this target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108230, 'enUS', 'Yes, Lord Xavius! I\'ll be happy to tear them apart in your name!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108231, 'enUS', 'AAARRRRGGGHHHH!!! THE CLAWS! THEY THIRST FOR BLOOD!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108232, 'enUS', 'Blade of Justice is an excellent tool to deal damage as your opponent approaches.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108233, 'enUS', 'Use Crusader Strike on this target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108247, 'enUS', 'Are you ready to begin?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108248, 'enUS', 'Open the portal.', 'Open the portal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108249, 'enUS', 'Thank you for seeing reason.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108250, 'enUS', 'Ered\'achor, shudas\'urzul... kurai.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108251, 'enUS', '', 'Good luck on the Trials, champion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108252, 'enUS', '', 'For the Alliance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108253, 'enUS', 'My musken will trample your tiny bear!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108254, 'enUS', 'The puny one has great beasts!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108255, 'enUS', 'I\'ll drag your beasts to Helheim to join Helya\'s cursed stable!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108256, 'enUS', 'Sleep lightly, no beast eludes me long...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108257, 'enUS', 'It is done. The way to Shadowgore Citadel is open.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108258, 'enUS', 'You feel a foreboding presence - you are not welcome here.', 'You feel a foreboding presence - you are not welcome here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108259, 'enUS', 'Move out and support the Farseer! Show the Firelands the strength of the Earthen Ring!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108260, 'enUS', '', 'Cleanse the staff, my children. I will keep the beast at bay!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108261, 'enUS', '', 'Quickly, hero! Our work is nearly complete. You must give me the staff!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108262, 'enUS', 'I was hopin\' for a warm welcome... but this ain\'na what I had in mind!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108263, 'enUS', '', 'The Farseer approaches! Let\'s show these blasted bugs what we\'re made of!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108264, 'enUS', 'The Earthen Ring stands with you, Farseer!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108265, 'enUS', 'Demons besiege my peak! We need your skills in battle, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108266, 'enUS', 'Well done. You have given us the edge we need against these foul creatures. You have my thanks, mortal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108267, 'enUS', 'Hail, friend. The drekirjar filth continue to slay and enslave my kin. Come, fly with me, and together we shall heap vengeance upon Hrydshal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108268, 'enUS', 'Would that we could level all of Hrydshal in one fell swoop! Alas, vengeance must come in waves.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108269, 'enUS', '<Give Lyessa the staff>.', '<Give Lyessa the staff>.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108270, 'enUS', 'Put your backs into it! Let the heat of combat fuel your resolve!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108271, 'enUS', '$n. I\'m just checking in before I return to the Fel Hammer.\n\nIf there\'s a mission you\'d rather I go on, I await your orders.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108273, 'enUS', '', 'Your presence is requested in the Halls of Valor. Are you ready?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108274, 'enUS', 'Take me to the Halls of Valor.', 'Take me to the Halls of Valor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108275, 'enUS', '|TInterface\\Icons\\ability_felarakkoa_feldetonation_red:20|t %s gains |cFFFF0000|Hspell:206221|h[Empowered Bonds of Fel]|h|r.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108276, 'enUS', '|TInterface\\Icons\\Spell_Fire_FelFlameStrike:20|t %s gains |cFFFF0000|Hspell:206220|h[Empowered Liquid Hellfire]|h|r.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108277, 'enUS', '|TInterface\\Icons\\inv_misc_trinket6oih_orb1:20|t %s gains |cFFFF0000|Hspell:211152|h[Empowered Eye of Gul\'dan]|h|r.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108279, 'enUS', '|TInterface\\Icons\\Spell_Shadow_MindRot:20|t %s summons $n for aid|h|r.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108280, 'enUS', 'Now, let\'s destroy the rest of these demons!', 'Now, let\'s destroy the rest of these demons!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108281, 'enUS', '', 'Have you come to be judged?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108282, 'enUS', '', 'Let us see if those you have brought are worthy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108283, 'enUS', '', 'These trials are not to be taken lightly.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108284, 'enUS', '', 'The hunting ground awaits.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108285, 'enUS', '', 'Death should not be feared.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108286, 'enUS', 'Their numbers seem endless, but we will prevail.', 'Their numbers seem endless, but we will prevail.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108287, 'enUS', '', 'Life is fleeting.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108288, 'enUS', '', 'Only greatness endures.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108289, 'enUS', '', 'There is another.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108290, 'enUS', '', 'That was your best attack? Honestly?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108291, 'enUS', 'Hey $n! Yeah, you! Ignore all that blah about cleansing the land for now and kill this big guy over here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108292, 'enUS', 'I think Jace likes his toys a bit too much.', 'I think Jace likes his toys a bit too much.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108293, 'enUS', 'Don\'t get used to this.$b$bAs soon as we finish up with this construction gig we\'re clearing out of here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108294, 'enUS', '', 'Odyn\'s hunting grounds contain the all manner of great beasts. Let us see if any of your creatures are worthy to be counted among them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108295, 'enUS', '', 'Though your beasts have been proven worthy, it is not their time to join our ranks. They are destined to enjoy many more glorious battles.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108296, 'enUS', 'Hey, you\'re not supposed to be in here!$b$bLook, we don\'t have the insurance and you\'re a liability.$b$bI\'m gonna have to ask you to leave.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108297, 'enUS', 'Akaari is just across the citadel from you. With your skill, you should be able to reach her undetected.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108298, 'enUS', 'Think you could get me out of here?', 'Think you could get me out of here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108299, 'enUS', 'Hey, you\'re not supposed to be here!$b$bLook, we don\'t have the insurance to cover you and you being here is a liability. I bet you\'re already stuck even!$b$bI\'m going to spring you out but do yourself a favor and let us finish before you go exploring again, alright?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108300, 'enUS', 'Got it, thanks.', 'Got it, thanks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108301, 'enUS', 'Everything you love will burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108302, 'enUS', 'I will shatter your world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108303, 'enUS', 'I\'ll pound you to dust!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108304, 'enUS', 'You will break...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108305, 'enUS', 'Laughable! A wonder this world survived so long.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108306, 'enUS', '', 'I think you\'re ready for more!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108307, 'enUS', '', 'Shall we raise the stakes?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108308, 'enUS', 'You may enter, but spare me your pleas for help, shaman. I know of the Earthen Ring\'s plan to unite the elements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108309, 'enUS', 'The Burning Legion threatens us all, but the power of the Firelands must be earned.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108310, 'enUS', 'Will your kind never learn?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108311, 'enUS', 'Approach my throne and we shall see just how far you will go to save this world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108312, 'enUS', '', 'Feel the fury of a thousand blades!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108313, 'enUS', '', 'Behold a true mistress of the blade!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108314, 'enUS', '', 'Prepare for the deepest cut of all!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108315, 'enUS', '', 'It would be unfair for me to have all the fun...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108316, 'enUS', '', 'Teach these upstarts a lesson, my minions!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108317, 'enUS', '', 'Amateurs! You are your own worst enemies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108318, 'enUS', '', 'Watch your footing! I\'d hate to see you slip up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108319, 'enUS', '', 'I give you a chance for glory, and you make a mess of it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108320, 'enUS', '', 'I must... concede...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108321, 'enUS', '', 'Consider this a lesson in swordplay!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108322, 'enUS', '', 'You cannot hope to match my skill!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108323, 'enUS', '', 'How fortunate they were to face so skilled a foe! I would envy them... if they weren\'t all dead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108324, 'enUS', '', 'How surprising... another kill for me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108325, 'enUS', '', 'Let your fates be bound together!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108326, 'enUS', '', 'Feel the icy grip of defeat!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108327, 'enUS', '', 'You have my attention!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108328, 'enUS', '', 'I\'m coming for you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108329, 'enUS', '', 'Death comes from above!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108330, 'enUS', '', 'A bit of magic could make this more interesting!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108331, 'enUS', '', 'Time to clear away the debris!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108332, 'enUS', '', 'I control this battlefield!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108333, 'enUS', '|TInterface\\Icons\\INV_Misc_Rune_10:20|t Shatter the runestone to interrupt Runeseer Faljar\'s |cFFFF0404|Hspell:208544|h[Runic Bulwark]|h|r!', '|TInterface\\Icons\\INV_Misc_Rune_10:20|t Shatter the Empowered Runestone to interrupt Runeseer Faljar\'s |cFFFF0404|Hspell:208544|h[Runic Bulwark]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108334, 'enUS', 'Using Blade of Justice and Crusader Strike in combat builds up Holy Power.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108335, 'enUS', 'Build up Holy Power by using your attacks, then use Templar\'s Verdict on this target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108338, 'enUS', 'DO NOT LOCALIZE', 'DO NOT LOCALIZE', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108339, 'enUS', 'Emberscar has awakened and thirsts for souls. Defeat the Cinderweb Ambushers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108340, 'enUS', 'The master has returned! We must ensure the household is immaculate.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108341, 'enUS', 'That\'s not the master, imbecile! Unless he managed to claw his way out from under two tons of solid stone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108342, 'enUS', 'Ooo, that would be impressive... considering I still have his hands in a jar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108343, 'enUS', 'Please don\'t say such things! The master is back, and things need to be kept tidy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108344, 'enUS', 'You\'re tracking mud everywhere!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108345, 'enUS', 'Did you receive satisfactory service?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108346, 'enUS', 'Have a bite master, I know it\'s your favorite.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108347, 'enUS', '%s writhes in agony.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108348, 'enUS', 'You dare leave crumbs on my floor?! Die!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108349, 'enUS', 'Filthy, filthy, filthy!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108350, 'enUS', 'Hold still, master, for incineration!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108351, 'enUS', 'Stop squirming! Makes it hard to stab you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108352, 'enUS', 'So good to see you master, how may I sever you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108353, 'enUS', '%s winces and mumbles incoherently.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108354, 'enUS', 'Welcome home master, may I take your... HEAD!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108355, 'enUS', 'Well done, Farseer. Emberscar is mine and those who opposed my rule lay silenced!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108356, 'enUS', 'Master, is that you?! I barely recognized you with your head attached.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108357, 'enUS', 'Can you please die without making such a mess?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108358, 'enUS', 'How dare you track blood across my floor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108359, 'enUS', 'As a symbol of my allegiance, I shall send my servant Scaldius to aid the Earthen Ring.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108360, 'enUS', 'You look famished, master. Please, have some refreshments.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108361, 'enUS', 'Well struck sir, right in my pain cluster.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108362, 'enUS', 'I\'m not sure what I\'ve done, but I\'m positive I deserve this beating.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108363, 'enUS', 'Pain is an effective teacher, and I endeavor to learn your lesson, master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108364, 'enUS', 'Apologies for the mess, master. I shall tidy up immediately.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108365, 'enUS', 'The Burning Legion shall tremble before the terror of the Firelord!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108366, 'enUS', 'Tread carefully in my domain, shaman. Even Farseers can burn...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108367, 'enUS', 'Oh, my head... where am I?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108368, 'enUS', 'The Firelands is the most fearsome of the elemental planes. We should tread lightly and respect our surroundings here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108369, 'enUS', 'Eye of Xirus looks in your direction!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108370, 'enUS', 'Eye of Xirus spots you and runs to its allies for help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108371, 'enUS', 'Greetings, Master $n. How are you today?', 'Greetings, Master $n. How are you today?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108372, 'enUS', 'It is now, among the darkness, that the light shines most brilliantly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108373, 'enUS', 'Everyone is worried about the Legion, but we believe in you, Master.', 'Everyone is worried about the Legion, but we believe in you, Master.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108374, 'enUS', 'It is good to see you, Master. You put us all in good spirits.', 'It is good to see you, Master. You put us all in good spirits.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108375, 'enUS', 'The Order of the Broken Temple will survive under your leadership. It must...', 'The Order of the Broken Temple will survive under your leadership. It must...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108376, 'enUS', 'Where are you off to today, Master?', 'Where are you off to today, Master?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108377, 'enUS', 'Our efforts are going to pay off, I know it.', 'Our efforts are going to pay off, I know it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108378, 'enUS', 'We cannot give up the fight, Master. I trust your judgment completely.', 'We cannot give up the fight, Master. I trust your judgment completely.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108379, 'enUS', 'To be a monk is to strive for harmony and balance. There is no one better to lead us than you, $n.', 'To be a monk is to strive for harmony and balance. There is no one better to lead us than you, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108380, 'enUS', 'The estate is gorgeous, is it not? Simply walking the grounds brings me peace.', 'The estate is gorgeous, is it not? Simply walking the grounds brings me peace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108381, 'enUS', 'I tell myself I will socialize at these parties, but I always end up lingering about the desserts.', 'I tell myself I will socialize at these parties, but I always end up lingering about the desserts.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108382, 'enUS', 'Yes, my costume IS the most fabulous. Thank you for noticing.$B$BIt hardly matters that I cannot move.', 'Yes, my costume IS the most fabulous. Thank you for noticing.$B$BIt hardly matters that I cannot move.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108383, 'enUS', 'Ugh, I heard someone else came as an owl.$B$BWell, ONE OF US will have to change...', 'Ugh, I heard someone else came as an owl.$B$BWell, ONE OF US will have to change...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108384, 'enUS', 'Quite the... unique... costume you have there.', 'Quite the... unique... costume you have there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108385, 'enUS', 'Security here is much tighter since the barrier went down. We cannot just let in any riff-raff, now can we?', 'Security here is much tighter since the barrier went down. We cannot just let in any riff-raff, now can we?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108386, 'enUS', 'Is that... an outlander costume beneath your normal costume?$B$BGenius!', 'Is that... an outlander costume beneath your normal costume?$B$BGenius!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108387, 'enUS', '<Sigh.>$B$BI thought for sure I would have the most exotic costume this time, but it seems I have been outdone once again.', '<Sigh.>$B$BI thought for sure I would have the most exotic costume this time, but it seems I have been outdone once again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108388, 'enUS', 'Make it quick - my costume looks more elegant in motion.', 'Make it quick - my costume looks more elegant in motion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108389, 'enUS', 'Did anyone ever find Thalyssra\'s body?', 'Did anyone ever find Thalyssra\'s body?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108390, 'enUS', '<Scoff.> Demons. Am I right? <Roll your eyes.>', '<Scoff.> Demons. Am I right? <Roll your eyes.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108391, 'enUS', 'Warlocks are not known to be trustworthy. What if Gul\'dan betrays us?', 'Warlocks are not known to be trustworthy. What if Gul\'dan betrays us?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108392, 'enUS', 'How can we be free in the Legion\'s shadow?', 'How can we be free in the Legion\'s shadow?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108393, 'enUS', 'How do you do, fellow Nightborne?', 'How do you do, fellow Nightborne?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108394, 'enUS', '<Just wink a lot.>', '<Just wink a lot.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108395, 'enUS', 'Is there nobody left who will stand up to Elisande?', 'Is there nobody left who will stand up to Elisande?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108396, 'enUS', 'What if we tried not watching the world burn? Just for once, to see if we like it.', 'What if we tried not watching the world burn? Just for once, to see if we like it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108397, 'enUS', 'What was that?', 'What was that?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108398, 'enUS', 'Hmm?', 'Hmm?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108399, 'enUS', 'Reveal yourself.', 'Reveal yourself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108400, 'enUS', 'I thought I heard something.', 'I thought I heard something.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108401, 'enUS', 'Shadowgore Stalker growls.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108402, 'enUS', 'Shadowgore Stalker sniffs the air excitedly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108403, 'enUS', 'Shadowgore Stalker\'s hairs bristle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108404, 'enUS', 'Shadowgore Stalker perks up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108405, 'enUS', 'Excuse me while I pretend we never met.', 'Excuse me while I pretend we never met.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108406, 'enUS', 'Curious...', 'Curious...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108407, 'enUS', 'You know, the Magistrix has spies everywhere.', 'You know, the Magistrix has spies everywhere.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108408, 'enUS', 'This conversation is boring me.', 'This conversation is boring me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108409, 'enUS', 'Hm. Yes. Fascinating.', 'Hm. Yes. Fascinating.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108410, 'enUS', 'Scandalous!', 'Scandalous!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108411, 'enUS', 'Who let you in here?', 'Who let you in here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108412, 'enUS', '', 'Try to keep up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108413, 'enUS', '', 'You were dead the moment you arrived here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108414, 'enUS', '', 'Enough!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108415, 'enUS', '', 'Take them to the holding cell. Lord Kil\'jaeden will be most pleased.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108416, 'enUS', 'Send reinforcements north of Crossroads! Tower\'s under attack! We\'re surrounded!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108417, 'enUS', 'A full-body outlander transformation? Ghastly.', 'A full-body outlander transformation? Ghastly.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108418, 'enUS', 'What kind of costume is THAT? I cannot be seen with you.', 'What kind of costume is THAT? I cannot be seen with you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108419, 'enUS', 'Put on a proper costume or I will have you thrown out.', 'Put on a proper costume or I will have you thrown out.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108420, 'enUS', 'Thank you! When this is over you\'ll have to come back and fish sometime!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108421, 'enUS', 'When you deliver the letter, please give Prin... King Anduin my condolences.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108422, 'enUS', 'These ancient stones have not been animated in eons. Once again you must be tested. Place your hand on the stone and prepare.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108423, 'enUS', 'Eyir has taken notice of your deeds. Be careful to not disappoint.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108424, 'enUS', 'Skywall is truly a wondrous place, $n. I have seen nothing like it in all my life.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108425, 'enUS', 'Wet!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108426, 'enUS', '', 'NO ONE can be allowed to become a Felsworn traitor working for the Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108427, 'enUS', '', 'Thank you, $pc. I am Stellagosa.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108428, 'enUS', '', 'Prepare yourselves, minions. Our lord requires us on the field of battle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108429, 'enUS', '', 'Our enemies will feel the fangs\' agonizing bite! They will all perish!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108430, 'enUS', 'Tread lightly, lest you prefer to be my beast\'s dinner.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108431, 'enUS', 'You\'re a true alpha.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108432, 'enUS', '', 'Zithreen is trying to crush us beneath the waves!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108433, 'enUS', 'Shaman, lead Grash forward. He can act as a breakwater for us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108434, 'enUS', 'I won\'t say it again! Get... off... my... dock!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108435, 'enUS', 'You\'ll never board one of my ships, demon! Not while I\'m alive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108436, 'enUS', '', 'Move out, armies of Kil\'jaeden! Glory awaits!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108437, 'enUS', 'That jailer presents an opportunity for your escape.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108438, 'enUS', 'Use |TInterface\\Icons\\inv_misc_bag_10_red.blp:20|t |cFFFF0000|Hspell:185438|h[Pick Pocket]|h|r to escape!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108439, 'enUS', '', 'Come to visit ol\' Scowling Rosa, have you?$B$BWhat can I do for you hun?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108440, 'enUS', '', 'Once again the accursed mists creep in from the sea. Venture forth and vanquish the kvaldir there before their darkness spreads further.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108441, 'enUS', '', 'The Tideskorn earned their name the day the mists took their greatest harbor. Since then, no vrykul loyal to Odyn has willingly entered that cursed place.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108442, 'enUS', 'I hear you might be selling something I\'m looking for...', 'I hear you might be selling something I\'m looking for...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108443, 'enUS', 'Psst. Table behind the house. Lady Ly\'leth is waiting for you.', 'Psst. Table behind the house. Lady Ly\'leth is waiting for you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108444, 'enUS', 'My mistress! The prisoner has escaped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108445, 'enUS', '', 'Xirus, deal with them. We have nearly made contact.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108446, 'enUS', 'Fool! He comes...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108447, 'enUS', 'An entire plane of existence, dedicated to the element of Air. It is truly wondrous...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108448, 'enUS', 'Xirus is here. Run!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108449, 'enUS', 'I have seen nothing like it on my own world. I wonder if such a place exists beyond Azeroth?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108450, 'enUS', '', 'Get out while you still can!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108451, 'enUS', 'I do not envy you, $n. To lead our order through this crisis must weigh heavily on your shoulders.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108452, 'enUS', 'When the Burning Legion invaded Draenor, I failed to save my people... to save my home.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108453, 'enUS', 'We must unite the elements to our cause... else our worlds share the same fate.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108454, 'enUS', 'The Heart of Skywall holds the power to recharge the blade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108455, 'enUS', 'We should continue onward with great haste!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108456, 'enUS', 'Thanks!', 'Thanks!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108457, 'enUS', 'These are dark times, $n, but you must never lose hope.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108458, 'enUS', 'There was a time when I wandered the wastes of my world, hopeless and broken.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108459, 'enUS', 'It was in my darkest hour, that the elements called out to me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108460, 'enUS', 'You too will be guided, Farseer. You need only listen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108461, 'enUS', 'The Staff of Four Winds is almost complete. We must move swiftly, much is at stake.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108462, 'enUS', 'The elements here are in a state of total chaos... they require a Windlord to guide them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108463, 'enUS', 'It is time, $n. We have all that we need to resurrect Prince Thunderaan. We must hurry.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108464, 'enUS', 'None can escape my gaze...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108465, 'enUS', '', 'I... I\'m alive! By the Light, I live again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108466, 'enUS', '', 'Help! Please...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108467, 'enUS', '', 'They came outta nowhere! Mind lending me a hand?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108468, 'enUS', 'What are you doing in here? Can\'t you see I am busy preparing meals that will astonish and delight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108469, 'enUS', 'I must get back to my cooking!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108470, 'enUS', 'Finally. Now, back to my preparations.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108471, 'enUS', 'I cannot work in these conditions!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108472, 'enUS', 'If you can\'t take the heat...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108473, 'enUS', 'Get out of the kitchen!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108474, 'enUS', 'I herald your endless torment.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108475, 'enUS', 'I have superior ingredients!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108476, 'enUS', 'Everyone is a critic.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108477, 'enUS', 'Smells awful.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108478, 'enUS', 'I have to recheck the recipe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108479, 'enUS', 'The snake witch. She is inside.$b$bGrash is... afraid.$b$bThe snake witch will try to control Grash with her magic stick.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108480, 'enUS', 'No trespassers allowed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108481, 'enUS', 'I promise to protect you, Grash. Smash your way inside!', 'I promise to protect you, Grash. Smash your way inside!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108482, 'enUS', 'That should teach you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108483, 'enUS', 'Just what you deserved!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108484, 'enUS', 'My watch... is ended.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108485, 'enUS', 'A job well done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108486, 'enUS', 'I need reinforcements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108488, 'enUS', 'This will even the odds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108489, 'enUS', 'Basely done... cowards!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108490, 'enUS', 'Stay where you are!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108491, 'enUS', 'You will face many dangerous situations, and preparation can be the key to your survival.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108492, 'enUS', 'Use Divine Shield to make yourself temporarily invulnerable to all damage!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108493, 'enUS', 'This is only the beginning.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108494, 'enUS', 'The end is inevitable.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108495, 'enUS', 'Welcome the embrace of the twisting dark.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108496, 'enUS', 'The Nightmare consumes me... compels me... to serve... to kill!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108497, 'enUS', 'My sacred gift from the Watchers! Yes, Nightmare Lord... yes... the fury of the Claws must be mine once more!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108498, 'enUS', 'You have released me from the Nightmare. The Claws... have served you well...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108499, 'enUS', 'The Watcher\'s gift is mine once more! No... not mine... it belongs to the Nightmare.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108500, 'enUS', 'I WILL FLATTEN YOU LIKE SAPLINGS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108501, 'enUS', 'THE WILDS TREMBLE BEFORE MY FURY!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108502, 'enUS', 'RRAAAUUUGHHHH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108503, 'enUS', 'They will devour your flesh until nothing remains...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108504, 'enUS', '', 'Not very clever of you to challenge all of us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108505, 'enUS', '', 'Scorched bones... so delightful!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108506, 'enUS', '', 'More kindling for the bonfire!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108507, 'enUS', '', 'My funeral pyre... will be... glorious...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108508, 'enUS', '', 'Looks like I have a roast to prepare!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108509, 'enUS', '', 'Make yourself useful and take a look around!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108510, 'enUS', '', 'What are you waiting for? Check out that noise!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108511, 'enUS', '', 'A great banquet will be held tonight for the city\'s nobles. The chef is having all the Arcwine reserves brought to his kitchen. We need you to distract him while we \"liberate\" as much of it as we can.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108512, 'enUS', '', 'You! Have a look over there!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108513, 'enUS', '', 'Excellent! You bought my agents enough time to secure almost all of their Arcwine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108514, 'enUS', '', 'Mortal flesh... so pretty when it burns!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108515, 'enUS', '', 'I\'m feeling positively radiant!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108516, 'enUS', '', 'It will amuse me to watch you melt!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108517, 'enUS', '', 'Who needs henchmen? I\'ll burn you myself!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108518, 'enUS', '', 'I believe my brother Mardranel is prepared to accompany you. Thank you for your help, friend.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108520, 'enUS', 'Yowlon purrs quietly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108522, 'enUS', 'Divine Shield protected you from the mage\'s first attack, but the second attack has left you wounded.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108523, 'enUS', 'Use Flash of Light to heal your wounds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108525, 'enUS', 'I had such high hopes for you. An amateur merchant believes that a partner is a valuably ally. A master knows that they are competition and a liability.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108526, 'enUS', 'I always suspected something was amiss with you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108537, 'enUS', 'Redhoof carefully digs into the soil with a hoof.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108538, 'enUS', 'It appears as though we have arrived, $n. Let us see what Celestos would have us do next.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108539, 'enUS', 'Fresh green food grows and your enemies are fewer. We seek only your blessing in return.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108540, 'enUS', 'I suppose I should clear away this mess and make room for the new instruments.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108541, 'enUS', 'Startalon settles and tucks her head under her wing for a moment. She straightens again and offers a dry owl talon out to Mayruna.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108542, 'enUS', 'Startalon dips her head in silent thanks and takes to the skies once more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108544, 'enUS', 'Would you be able to repair this flask for me?', 'Would you be able to repair this flask for me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108545, 'enUS', 'Silverfang deposits a shiny, smooth Nightsaber Fang onto the dirt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108546, 'enUS', 'Silverfang lets out a low, passive growl.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108547, 'enUS', 'You ask for my help, yet you have nothing to offer in return, $r. If that\'s the case, then perhaps you can do my tribe a favor.$b$bIt\'s been years since we\'ve tasted the Rivermane\'s brew. Their remedy is known to have revitalizing powers and it does wonders for the teeth. And fortunately I happen to know how to brew it.$b$bI\'ll give you my mortar and pestle, along with the recipe for creating the remedy. You bring back enough for my tribe and I\'ll fix your flask.$b$bAgreed?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108548, 'enUS', 'Agreed.', 'Agreed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108550, 'enUS', '', 'Our efforts were not in vain. The Moonwhisper Matriarch watches from afar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108551, 'enUS', '', 'May your hunt be ever fruitful, my sister.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108552, 'enUS', 'You\'ll find a pumpkin patch at the Rivermane\'s camp. Choose the ripest ones for their juice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108553, 'enUS', 'And foxflower flourishes all around Highmountain. Good luck, $r.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108554, 'enUS', 'You fail to amuse me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108555, 'enUS', '', 'No one resists the Nightmare!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108556, 'enUS', 'A priest! Thank the Light!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108557, 'enUS', '', 'Give yourself over, as I did.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108558, 'enUS', '', 'At last... the long dream comes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108559, 'enUS', 'Begone mortals! The time of the Windlords has passed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108560, 'enUS', 'Foolish shaman! You really think that Thunderaan can save your world?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108561, 'enUS', 'I will devour you and take the Staff of Four Winds for myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108562, 'enUS', '', 'The Nightmare is inescapable.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108563, 'enUS', 'Listen well, Farseer... I will aid the Earthen Ring for now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108564, 'enUS', 'The armies of Skywall shall stand shoulder to shoulder with the mortal races in battle against the Burning Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108565, 'enUS', 'When the fog of battle clears, and the Burning Legion is defeated, we shall stand victorious!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108566, 'enUS', '', 'Spirits of the forest! Guardians of nature! Hear our plea!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108567, 'enUS', '', 'On this day, the mother tree is reborn! G\'Hanir is whole once more!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108568, 'enUS', 'Come on, $p, talk with Sternfathom. Let\'s get this fight started!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108569, 'enUS', 'AAAAHHHHHHHHhhhh...!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108570, 'enUS', 'Let\'s give them a moment to regroup and prepare, $n.\n\nWhen they\'re ready, speak with me again, and I\'ll throw you into the arena to get this fight started.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108571, 'enUS', 'What\'re you lookin\' at? Ain\'t ye ever seen a sea lion before?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108572, 'enUS', 'Not like this...', 'Not like this...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108573, 'enUS', 'Yes, I\'m a druid and... well... I\'m kind of stuck like this for a while.$b$bWhile patrolling the sea around the Broken Shore, an infernal crashed into the water right on top of me. The blast instantly knocked me unconscious.\n$b$bWhen I woke I was here in the care of the nurses. They say after a few days I should have the strength to shift back. In the meantime, I\'m stuck in this contraption to keep me from drying out.\n$b$bDo me a favor. Don\'t mention this to any of the other druids. They\'ll never let me live it down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108574, 'enUS', 'We shall use the garden entrance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108575, 'enUS', 'Enchanted masks. Simply don the mask, and a magical costume will envelop your body.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108576, 'enUS', 'I will find Ly\'leth. You stay out of trouble.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108577, 'enUS', 'It should be enough to fool the other guests, but stay clear of the guards.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108578, 'enUS', 'The fangs are within your grasp!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108579, 'enUS', 'Use the signal lantern to alert Ly\'leth Lunastre of your arrival.', 'Use the signal lantern to alert Ly\'leth Lunastre of your arrival.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108580, 'enUS', 'This lantern appears to have been slightly modified. It must be the one you need to use to send a signal.', 'This lantern appears to have been slightly modified. It must be the one you need to use to send a signal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108581, 'enUS', 'Send a signal to Ly\'leth Lunastre.', 'Send a signal to Ly\'leth Lunastre.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108582, 'enUS', '', 'Your arrival is fortuitous. Tonight, the nobles have gathered in honor of the Grand Magistrix.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108583, 'enUS', '', 'Our spies report that Elisande herself will attend. This presents a rare opportunity to catch her away from her source of power.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108584, 'enUS', '', 'We\'ll take a side entrance to the grounds... too many eyes are watching the front.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108585, 'enUS', '', 'You\'ve done well so far, but we must ensure you blend in at the party. I\'ll enhance your disguise, infusing it with latent magic to make you appear as one of the nobles.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108586, 'enUS', '', 'This is your best chance to eliminate the Grand Magistrix. Good luck!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108587, 'enUS', '', 'Kill them, you fools! I will not be denied my destiny!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108588, 'enUS', 'I... I need fresh air!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108589, 'enUS', 'Among the souls you have rescued, there are some who wish to regain their honor fighting alongside you against the kvaldir.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108590, 'enUS', 'We must not suffer Helya\'s minions to gain territory unfought. Your loyalty shall be rewarded.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108591, 'enUS', 'It\'s over, Zithreen! Tell us... what did Azshara plan to do with this realm?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108592, 'enUS', '', 'I will tell you NOTHING!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108593, 'enUS', 'Agraaahh!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108594, 'enUS', 'Well then... I guess... I guess we\'re done talking here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108595, 'enUS', 'Dead now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108596, 'enUS', 'Legion ships on the horizon! All forces to Sentinel Hill!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108597, 'enUS', 'Barrem!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108598, 'enUS', 'I greet you, $n. I am glad to help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108599, 'enUS', 'Orik says you can speak to these spirits, how do we begin?', 'Orik says you can speak to these spirits, how do we begin?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108600, 'enUS', 'I misjudged how long ago this happened. I will need more time to prepare myself for the ritual.$b$bIn the meantime, I have attuned this stone to the scroll Orik brought. Certain graves will glow to your mind\'s eye. Go to them and concentrate on the stone to see if you have found the correct grave. We want the writer himself, not a descendent or ancestor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108601, 'enUS', 'The stone grows dull, this is the wrong grave. A relative probably.', 'The stone grows dull, this is the wrong grave. A relative probably.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108602, 'enUS', 'Concentrating', 'Concentrating', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108603, 'enUS', 'The stone glows brightly, this is the correct grave!', 'The stone glows brightly, this is the correct grave!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108604, 'enUS', '', 'Yes, what is it? I am very busy planning my next gala.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108605, 'enUS', '', 'Oh, you again! I cannot thank you enough for what you did, dear.$b$bI am already hearing good things about the new jeweler. Perhaps I should pay him a visit and get a new piece commissioned...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108606, 'enUS', 'I wanted to speak with you about a gem design.', 'I wanted to speak with you about a gem design.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108607, 'enUS', 'Champion, a great malevolence has risen in the mists. Gather your most powerful allies and meet the kvaldir in battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108608, 'enUS', 'Even my most loyal vrykul dare not enter the mists for fear of their souls. I commend your bravery, mortal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108609, 'enUS', 'The tower in the middle o\' Dalaran has a portal to Wyrmrest Temple. Should get ye most o\' the way there. Tahu, thanks again fer helping us paladins.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108610, 'enUS', 'You asked the Sunwalkers for aid, and I have come. May my presence bring success to your quest, Orik.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108611, 'enUS', 'Ye made it! This land haunts me still... so much pain and death. Well, let\'s get on with it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108612, 'enUS', 'Find them. Akaari will not be disturbed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108613, 'enUS', 'Light give me strength!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108614, 'enUS', 'For the Alliance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108615, 'enUS', 'Begone foul beast!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108616, 'enUS', '', 'Are you in need of healing?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108617, 'enUS', 'I\'m looking for some Shaldorei Silk. Do you have any to spare?', 'I\'m looking for some Shaldorei Silk. Do you have any to spare?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108618, 'enUS', '', 'Why yes, we do. There\'s some on that chest over there. But we\'re in short supply, so please don\'t take too much.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108619, 'enUS', 'Would you happen to know of a tailor in these parts?', 'Would you happen to know of a tailor in these parts?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108620, 'enUS', '', 'A leyweaver from Suramar came through here not too long ago. I believe she headed east towards the Sylvan Falls.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108621, 'enUS', 'My beasts have been blessed by the Light to enhance their speed and agility. You\'ll not find a swifter ride.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108622, 'enUS', 'I\'d like to travel to Dalaran.', 'I\'d like to travel to Dalaran.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108623, 'enUS', '', 'Find somewhere safe! I\'ll find you when this is over.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108624, 'enUS', 'Those tendrils are using dark magic to become immune to attack. Interrupt their casting so you can strike!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108625, 'enUS', 'I don\'t have time for idle chatter, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108626, 'enUS', 'To arms!', 'To arms!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108627, 'enUS', 'If it\'s not too much trouble, could you mend this cloth for me?', 'If it\'s not too much trouble, could you mend this cloth for me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108629, 'enUS', 'What do you want, warlock? Can\'t you see I\'m busy?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108630, 'enUS', 'Who summoned all these imps?', 'Who summoned all these imps?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108631, 'enUS', '', 'You dare request such a menial task from me? Surely you must be joking.$b$bYou\'re not? I see. Well, I have been away from Suramar for some time now and from the look of you, I take it you\'re an alchemist, yes?$b$bI\'d be willing to trade my services with you for a price that I deem is fair.$b$bI long for an Ysera\'s Dream Draught. It\'s an ancient elven drink. To concoct it, you\'ll need some dreamleaf as well as the leaves of a Dreamer\'s Tear.$b$bOne grows not to far from here in the clearing of some ruins. Take my flask, make this drink for me, and I\'ll do as you ask.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108632, 'enUS', '', 'You are of no interest to me. I come from an accomplished lineage of Suramar tailors. You, obviously, do not.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108633, 'enUS', 'Confess your crimes!', 'Confess your crimes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108634, 'enUS', '', 'Hello, leyweaver. What can I do for you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108635, 'enUS', '', 'How fortuitous! A fellow tailor!$b$bWelcome to the leyweavers\' guild of Suramar. Perhaps you have heard of us?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108636, 'enUS', '', 'Have you brought me my draughts?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108637, 'enUS', 'Here are your draughts.', 'Here are your draughts.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108638, 'enUS', 'Crimes? The only thing I am guilty of is being the most powerful warlock in all of Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108639, 'enUS', '', 'Yes, this will be sufficient. Now let me see that silk of yours.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108640, 'enUS', 'Lespin wants his brooch back.', 'Lespin wants his brooch back.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108641, 'enUS', 'Come minions, aid me in battle against this accuser!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108642, 'enUS', 'Minions...?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108643, 'enUS', '', 'Here you are. Now, run along.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108644, 'enUS', 'Progress is slow, but steady. I will be back in the good graces of the courts soon, and it is all thanks to you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108645, 'enUS', 'He\'s just inside. Please, do whatever you can to save him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108646, 'enUS', 'What do you want? I have few friends in these dark days. I am prepared to defend myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108647, 'enUS', 'The Fel Magic reacts violently to your spell!', 'The Fel Magic reacts violently to your spell!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108648, 'enUS', 'We have found you... and you have found the grave.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108649, 'enUS', 'What\'s the next step?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108650, 'enUS', 'I will begin the ritual. Focus your will upon the spirit while our friend keeps us safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108651, 'enUS', 'Let\'s get to it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108652, 'enUS', 'I died to ease the pain of my failure. Why do you disturb me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108653, 'enUS', 'I\'ve read yer saga. Where did ye find the shield?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108654, 'enUS', 'It seems you compel me to answer... My journey took me to Stormheim, to a tomb of warrior kings.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108655, 'enUS', 'We will finish what ye started.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108656, 'enUS', 'Return to your rest, and be disgraced no more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108657, 'enUS', 'I\'m coming with you, $n. You helped one of my men, so it\'s only fitting that I return the favor.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108658, 'enUS', 'I\'ll meet you at Krasus\' Landing. We can take my gryphon down to the island.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108659, 'enUS', 'Thanks for yer help, Tahu. I won\'t forget this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108660, 'enUS', 'It was my honor to serve. I must go to Netherlight Temple and inform the Conclave of our success. Good luck.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108661, 'enUS', 'As for us, we should get back to Dalaran. I\'ll meet ye there!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108662, 'enUS', 'Stop, please! I\'ll pen a confession, assuming you didn\'t break my writing hand...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108663, 'enUS', '', 'I foresaw your coming, of course. The threads of fate that led you to this place. Your desperate attempt to stop the Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108664, 'enUS', '', 'My people faced a similar dilemma. I peered into all possible futures in search of an answer... and found only one.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108665, 'enUS', '', 'The Legion\'s victory is inevitable. Every time stream shows it to be so. Had we resisted, they would have taken the Nightwell by force and left us bereft of it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108666, 'enUS', '', 'I could not allow my people to starve, to waste away without its energies sustaining us. So a bargain was struck.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108667, 'enUS', '', 'Now I see a future where the Legion is victorious and my people endure. This is the thread that must be preserved. I will do everything in my power to make it so!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108668, 'enUS', '', 'Your deaths ensure the future of the Nightborne. Come forward, then. Let us speed you toward your fate.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108669, 'enUS', '', 'Your timelines end here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108670, 'enUS', '', 'The threads of time answer to me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108671, 'enUS', '', 'Will you race through time? Or be trapped in an endless eternity?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108672, 'enUS', '', 'Time itself heeds my call!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108673, 'enUS', '', 'I control the battlefield, not you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108674, 'enUS', '', 'Let the waves of time crash over you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108675, 'enUS', '', 'You\'ll find time can be quite volatile.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108676, 'enUS', '', 'You exist in but a single moment. I am far less limited!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108677, 'enUS', '', 'Time shifts again, and your doom draws near!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108678, 'enUS', '', 'The time streams are mine to command! You cannot win!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108679, 'enUS', '', 'Time is... slipping away! How can it be so?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108680, 'enUS', '', 'Just as I foretold.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108681, 'enUS', '', 'An inevitable outcome.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108682, 'enUS', '', 'You were destined to die.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108683, 'enUS', '', 'It\'s... not possible! I... I was destined to win...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108684, 'enUS', '', 'I take no joy in this victory, but it was inevitable.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108685, 'enUS', '', 'In all the possible futures I scryed, I did not foresee one in which you were victorious. I wonder...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108686, 'enUS', '', 'Will you defeat the Legion? Will you fail? Time eddies about you in fits and starts. Nothing is certain!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108687, 'enUS', '', 'Perhaps you will win. And in saving your world, my people would be freed from the terrible bargain we were forced to make.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108688, 'enUS', '', 'Fates be damned! I cast my lot in with yours, champions. Go now, face Gul\'dan. I will aid you as best I can, and perhaps together we can save us all.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108689, 'enUS', 'I can see your future mortal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108690, 'enUS', 'Want to know the exact time and date of your death?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108691, 'enUS', 'Just as I foresaw it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108692, 'enUS', 'My sight diminishes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108693, 'enUS', 'Sacrifices must be made.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108694, 'enUS', 'All will turn to ash.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108695, 'enUS', 'I already know your next move.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108696, 'enUS', 'All answer to their fate.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108697, 'enUS', 'Your fate betrays you mortal', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108698, 'enUS', 'Hello? Is anyone out there? I can\'t see...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108699, 'enUS', 'Uh... not me. Definitely not me.\n\nLike I told the guards in Dalaran, it\'s probably the Fizzlebang girl. Just like her brother, always summoning a mess of demons she can\'t control.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108700, 'enUS', 'Be quick! I wouldn\'t want us to be caught off guard while you\'re browsing my wares.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108701, 'enUS', 'Lulubelle says you\'re to blame.', 'Lulubelle says you\'re to blame.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108702, 'enUS', 'She would say that, wouldn\'t she? Further proof that you can\'t trust a Fizzlebang!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108703, 'enUS', '', 'Welcome, Lord of Flame! Welcome, Kil\'jaeden!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108704, 'enUS', 'You have done well, Akaari. You have earned my blessing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108705, 'enUS', 'Azhir manari kirel. Akim manari galar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108706, 'enUS', '', 'The fangs... their power is beyond comprehension! I am your humble servant, my lord!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108707, 'enUS', 'It is time for your true test. Use the Fangs to dismantle Azeroth\'s kingdoms from within. Let their shattered husks pave our path to victory!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108709, 'enUS', 'My brooch? Bah, I have bigger things to worry about than Lespin. Unfortunately for you, I no longer have the brooch.$b$bIt was stolen by Venlis Swiftsea, undoubtedly part of some plot to assassinate my reputation. It is a clever move, since now I cannot be seen near him.$b$bIf you were to go \"speak\" with him and recover the brooch, I think we would both benefit from this mess.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108711, 'enUS', 'I see your future, mortal...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108712, 'enUS', 'I did not foresee this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108714, 'enUS', '', 'Who knows when this will end?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108715, 'enUS', 'Has it passed? I can\'t wait to warm up baked goods for our troops once this is all over.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108716, 'enUS', '', 'They will taste blood in your name!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108717, 'enUS', 'The specters here continue to concoct a wine so foul it serves only to poison the drinker. Azsuna would be better served with them gone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108718, 'enUS', 'That should help to contain the spread of their corrupted... spirits. You have my thanks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108719, 'enUS', '$n! You can\'t just sneak up on someone like that. Not while this is going on!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108720, 'enUS', '', 'Imps aren\'t ideal test subjects. They\'re easy enough to summon, but often too fragile for my experiments.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108721, 'enUS', 'Feel its power coursing through you, shaman. The waters that Azshara tried to turn against us are now yours to command.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108722, 'enUS', 'Magic stick!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108723, 'enUS', 'Come, let us return to the others.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108724, 'enUS', '', 'I never thought I would someday long for the peace and quiet of the Maelstrom.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108725, 'enUS', 'Ularogg enters the |cFFFF0000|Hspell:198510|h[Stance of the Mountain]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108726, 'enUS', 'It\'s a book. That floats. Why did the demons leave it here?', 'It\'s a book. That floats. Why did the demons leave it here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108727, 'enUS', 'It\'s a book. That floats. Why did the demons leave it here?\n\nIt looks sinister, but probably not worth your time.', 'It\'s a book. That floats. Why did the demons leave it here?\n\nIt looks sinister, but probably not worth your time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108728, 'enUS', 'Dedication to your holy Paladin order demands that you purify this fel taint.', 'Dedication to your holy Paladin order demands that you purify this fel taint.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108729, 'enUS', 'This book is unnatural to Priests and must be destroyed.', 'This book is unnatural to Priests and must be destroyed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108730, 'enUS', 'Let\'s fly, $n. Lightfeather is saddled and ready to go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108731, 'enUS', 'The demons of Faronaar seek to bend the Illidari to their will. This must not be allowed to transpire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108732, 'enUS', 'With the Illidari freed, the demons\' threat is contained. You have done a great service to our land, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108733, 'enUS', '', 'These demons are even more persistent than the wretched naga! We must drive them back! My Nightwatchers will aid you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108734, 'enUS', '', 'Ha! That should teach them not to tangle with the Nightwatchers!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108735, 'enUS', 'It pains me to see my brethren reduced to such a state. There is nothing left for them now but hunger. Please, put them to rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108736, 'enUS', 'It was a dark task, but one that needed to be done. You have my gratitude, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108737, 'enUS', '', 'First the naga assault our city! Now it looks like they\'re trying to make off with our relics as well. Show them what it means to incur the wrath of the Nightwatchers, champion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108738, 'enUS', '', 'That should teach the naga to stay out of Azsuna! The citizens of Nar\'thalas owe you a debt of gratitude, $c, as do the Nightwatchers.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108739, 'enUS', 'Bad enough that naga and demons invade our home... now these makrura plague our shores! We must find out what dark force is driving them to madness. Will you help?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108740, 'enUS', 'So the foul demons were behind it all! You have my thanks for foiling their scheme. Without the demons inciting them, the makrura threat should subside.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108741, 'enUS', '', 'The audacity of these scaly cretins is truly limitless! Now they dare disgrace the city of Nar\'thalas with their presence? As a friend of the Nightwatchers, you must not let this stand!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108742, 'enUS', '', 'I commend your bravery, champion. The Nightwatchers will take care of their remaining forces. We are in your debt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108743, 'enUS', 'If the sea giants wish to ally with the naga, then they have become our enemy. Champion, show them that the Highborne are not to be trifled with!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108744, 'enUS', 'Let that be a lesson they will not soon forget. Thank you, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108745, 'enUS', 'I just soiled myself...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108748, 'enUS', 'Rokmora rears back for |cFFFF0000|Hspell:188169|h[Razor Shards]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108749, 'enUS', 'Fish-breath still alive.$b$bYou ready for fighting?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108750, 'enUS', 'Let\'s do it!', 'Let\'s do it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108751, 'enUS', 'Snake witch powerful, but with Grash help kill!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108752, 'enUS', 'Let\'s finish this, Grash!', 'Let\'s finish this, Grash!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108753, 'enUS', 'They\'ve taken the fields!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108754, 'enUS', 'Protect the Hill at all costs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108755, 'enUS', 'Everything I\'ve worked for... gone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108756, 'enUS', 'All of our crops were destroyed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108757, 'enUS', 'Ain\'t never been formally trained, but I\'m sure this here pointy end means trouble.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108758, 'enUS', 'There! In the skies! More of our reinforcements! Hold strong!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108759, 'enUS', '', 'Let us finish this once and for all.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108760, 'enUS', '', 'Do you feel the fangs\' bite withering the flesh from your very bones?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108761, 'enUS', '', 'Mephistroth guide my hand! I will not fail!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108762, 'enUS', '', 'I have broken entire worlds from the inside out. Azeroth will crumble!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108763, 'enUS', '', 'No more games.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108764, 'enUS', '', 'Join me... in the Nether...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108765, 'enUS', 'The citadel has begun a self-destruct sequence. Make your way to the portal once you have the fangs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108766, 'enUS', '', 'Can you feel your mind slipping away?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108767, 'enUS', '', 'I am your end!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108768, 'enUS', 'This creature is using its own life essence to harm us! He dies a little more with every attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108769, 'enUS', 'Clever. He\'s absorbing the flame back into his body to become whole again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108770, 'enUS', 'Priest, perhaps you can use your magic to influence his mind... Trick him into snuffing out his own flame!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108771, 'enUS', 'Ah, you made it. Well done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108772, 'enUS', 'Valeera said you would know where to meet her upon your return. Not wise to keep her waiting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108773, 'enUS', 'Until our paths cross again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108774, 'enUS', 'Come with me. The depths await!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108775, 'enUS', 'That beast is trying to take her soul! Hurry, we can still save her!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108776, 'enUS', 'Jump on Azuregos\'s back to avoid |cFFFF0404|Hspell:209863|h[Force of Gravity]|h|r!', 'Jump on Azuregos\'s back to avoid |cFFFF0404|Hspell:209863|h[Force of Gravity]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108777, 'enUS', 'Mage reinforcements from Stormwind! We fight as one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108778, 'enUS', 'Close ranks! We make our stand here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108779, 'enUS', 'Sir.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108780, 'enUS', 'For the Alliance!', 'For the Alliance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108781, 'enUS', 'Hold the gates!', 'Hold the gates!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108782, 'enUS', 'Reporting for duty!', 'Reporting for duty!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108783, 'enUS', 'Point me to the nearest demon!', 'Point me to the nearest demon!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108784, 'enUS', 'Mum.', 'Mum.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108785, 'enUS', '', 'I\'m sorry, but what does, \"Mum\" mean? Is there something you need?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108786, 'enUS', 'Oh, uh... well I\'m looking for some burettes?', 'Oh, uh... well I\'m looking for some burettes?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108787, 'enUS', '', 'Well why didn\'t you just say so to begin with? There aren\'t any guards around, we\'re perfectly safe to speak freely.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108788, 'enUS', 'How in the light am I supposed to get a hair from an archmage?!', 'How in the light am I supposed to get a hair from an archmage?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108789, 'enUS', '', 'Judging by what you\'re asking for I assume you\'re an alchemist?$b$bAnd if that\'s true, isn\'t there some sort of invisibility potion you can brew? Skaggldrynk I believe it\'s called.$b$bHe\'ll never see you coming.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108790, 'enUS', 'Carefully pluck a single strand of hair from Khadgar\'s head.', 'Carefully pluck a single strand of hair from Khadgar\'s head.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108791, 'enUS', 'OW!! What in the light was that?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108792, 'enUS', 'Something... bit my head I think.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108793, 'enUS', 'If you can pay, I can supply.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108794, 'enUS', 'Show me what you have available.', 'Show me what you have available.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108795, 'enUS', '', 'Khadgar, what\'s wrong? What happened?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108796, 'enUS', '<Lay your insignia on the table.>', '<Lay your insignia on the table.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108797, 'enUS', '', 'Something bit your head? That\'s all? Would you like me to kiss it for you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108798, 'enUS', 'Just mount the wee birdy here and follow me! To Stormheim!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108799, 'enUS', 'Your order\'s in the back room, $Gsir:madame;.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108800, 'enUS', 'Right this way, $GMister:Miss; $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108801, 'enUS', 'Hm, |5$r of your tastes might find our private selection in the back to your liking.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108802, 'enUS', 'Welcome back, $Gsir:madame;. Might I suggest you check out our new arrivals in the VIP room?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108803, 'enUS', 'Use |TInterface\\Icons\\spell_warlock_demonicportal_green.blp:20|t |cFFFF0000|Hspell:111771|h[Demonic Gateway]|h|r to get past Ariden\'s barrier!', 'Use |TInterface\\Icons\\spell_warlock_demonicportal_green.blp:20|t |cFFFF0000|Hspell:111771|h[Demonic Portal]|h|r to get past Ariden\'s barrier!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108804, 'enUS', 'The Darkbrul Arena is a place for the clans to challenge each other. Get in there and prove that the Stonedark and their allies are the toughest!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108805, 'enUS', 'Please, keep your voice down!$b$bIf they catch me talking to you... they\'ll kill me!', 'Please, keep your voice down!$b$bIf they catch me talking to you... they\'ll kill me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108806, 'enUS', 'I\'ve cleared a path for you. You can escape if you hurry!', 'I\'ve cleared a path for you. You can escape if you hurry!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108807, 'enUS', 'Good work! You\'ve pushed back the Naga and cleared out their Murloc slaves sufficiently! If you have any Timeworn Artifacts on you, bring them on by!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108808, 'enUS', '', 'That was a legendary performance! Thank you for proving that friendship is overrated.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108809, 'enUS', 'The raven calls.', 'The raven calls.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108810, 'enUS', '|TInterface\\Icons\\ability_fomor_boss_shout:20|t%s begins a |cFFFF0404|Hspell:209748|h[Deafening Roar]|h|r. Stop casting!', '|TInterface\\Icons\\ability_fomor_boss_shout:20|t%s begins a |cFFFF0404|Hspell:209748|h[Deafening Roar]|h|r. Stop casting!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108811, 'enUS', 'And we will answer from the shadows.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108812, 'enUS', 'I never thought I would make it out alive!', 'I never thought I would make it out alive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108813, 'enUS', 'The Twilight\'s Hammer must be stopped!', 'The Twilight\'s Hammer must be stopped!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108814, 'enUS', 'I never thought I\'d see my children\'s faces again!', 'I never thought I\'d see my children\'s faces again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108815, 'enUS', 'I can\'t wait to take a bath!', 'I can\'t wait to take a bath!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108816, 'enUS', 'Please save the others, hero!', 'Please save the others, hero!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108817, 'enUS', 'Be careful. I\'ve seen The Master... and she\'s powerful!', 'Be careful. I\'ve seen The Master... and she\'s powerful!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108818, 'enUS', 'We\'re saved!', 'We\'re saved!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108819, 'enUS', 'You fight with a fury worthy of a vrykul! An infant or elderly vrykul, but a vrykul all the same! Well done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108820, 'enUS', 'You\'ve saved me. I won\'t forget this!', 'You\'ve saved me. I won\'t forget this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108821, 'enUS', 'These Naga are up to no good, enslaving the nearby Murlocs to invade the shore. Take them out at all costs! Oh, and if you find any Timeworn Artifacts I could really use them for my research.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108822, 'enUS', '', 'Welcome to the Black Rook Rumble! Did you come here to fight alongside friends or allies? Well, send them away! Within this arena, you stand alone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108823, 'enUS', 'Looking for something specific today?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108824, 'enUS', 'Battle?! Battle first, then eats the pets! Battle then eats!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108825, 'enUS', 'Oh! Times to eats the pets! Where you goin\'? We can shares!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108826, 'enUS', 'Oh $p, I didn\'t realize it was you... my apologies. The Uncrowned await your arrival.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108827, 'enUS', 'This a placeholder event for imbuing the Moonwell. Spell effects will be added at a later date.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108828, 'enUS', 'That there be $p. I heard $Ghe:she; took down Deathwing with nothin\' but the blades in $Ghis:her; hands!', 'Looking for something specific today?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108829, 'enUS', 'We would like you to do the honors, friend. Come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108831, 'enUS', 'Ey mon, dat there be da one Garona spoke of.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108832, 'enUS', '', 'Yes, thank you for all you have done.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108833, 'enUS', '', 'Mayruna kneels and arranges a silver basin on the ground, then pours a small amount of softly glowing liquid into it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108834, 'enUS', '', 'Whenever you are ready, simply submerge the ritual bundle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108837, 'enUS', '', 'Mother Moon, goddess of my heart, bless this place with your light.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108838, 'enUS', 'On behalf of the Sablehorn, let this moonwell be blessed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108839, 'enUS', 'On behalf of the Feathermane, let this moonwell be blessed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108840, 'enUS', '', 'On behalf of the Moonwhisper, let this moonwell be blessed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108841, 'enUS', '', 'It is done!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108842, 'enUS', '|TInterface\\Icons\\spell_shaman_lavaflow.blp:20|t %s readies |cFFF00000|Hspell:200404|h[Magma Wave]|h|r. Hide!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108843, 'enUS', '', 'I will not... give in!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108844, 'enUS', 'Your torment only feeds the nightmare, weakling!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108845, 'enUS', '', 'The power of the Scepter is MINE to command!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108846, 'enUS', 'Cato! I\'m glad ye could make it. The shield is close... I can feel it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108847, 'enUS', '', 'You will drown in this place!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108848, 'enUS', '', 'The Earthen Ring will never have Azshara\'s power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108849, 'enUS', 'I\'m here, aren\'t I? Let\'s find this shield. Some of us have a war to get back to!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108850, 'enUS', 'Cato\'s the best healer around. We\'re lucky we didn\'t have to wait in a queue for his services!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108851, 'enUS', '|TInterface\\Icons\\Spell_Nature_LightningOverload:20|t You are afflicted with |cFFFF0000|Hspell:204275|h[Arcane Tether]|h|r!', '|TInterface\\Icons\\Spell_Nature_LightningOverload:20|t You are afflicted with |cFFFF0000|Hspell:204275|h[Arcane Tether]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108852, 'enUS', '', 'I owe you a great debt, druid. I will not forget this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108853, 'enUS', 'Ummm...?', 'Ummm...?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108854, 'enUS', 'Look, this one is not my fault. How was I supposed to know those demons would believe me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108855, 'enUS', 'We must drive them back! Focus on the commanders!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108856, 'enUS', 'Petty mortals. Your world will end soon enough!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108857, 'enUS', 'They aren\'t letting up! Hold strong!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108858, 'enUS', 'Watch the skies, I sense them coming.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108859, 'enUS', 'Don\'t waste your breath, druid. Malorne\'s gift is ours. Now, join your ancestors!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108860, 'enUS', '', 'The Bloodstone is ready. Handle it with care, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108861, 'enUS', 'Nooooooooo!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108862, 'enUS', 'Cannot. Control. Must. KILL!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108863, 'enUS', 'Grash no let puny shaman hurt snake lady!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108864, 'enUS', 'Thank you again for giving me the honor of sacrificing my life for the cause. Now you know that I\'d willingly give everything to further Lord Illidan\'s plan. The Burning Legion must be destroyed at any cost.\n\nI owe you everything, $n.\n\nNow, you complete what we started, gain the Sargerite Keystone, and return triumphant to the Black Temple.\n\nLord Illidan awaits.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108867, 'enUS', 'Well I didn\'t think they would actually believe me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108868, 'enUS', 'You demon toying fool! You have compromised our position.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108869, 'enUS', 'Affliction warlocks use shadow magic and disease to wear down their enemies, then drain their very life essences to empower themselves.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108870, 'enUS', 'Use Summon Voidwalker to summon your demonic minion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108871, 'enUS', 'Agony is an appropriate name for your first attack ability.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108872, 'enUS', 'Use Agony on the target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108873, 'enUS', 'You will inflict suffering on anyone foolish enough to fight you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108874, 'enUS', 'Use Corruption on this target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108875, 'enUS', 'After afflicting your target with Agony and Corruption, you can begin to drain the very life from them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108876, 'enUS', 'Use Drain Soul on the target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108877, 'enUS', 'As Agony damages your enemy, it has a chance to generate a Soul Shard. You can use these shards to empower new abilities.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108878, 'enUS', 'Use Unstable Affliction on this target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108879, 'enUS', 'Now that you have all your primary attack abilities, we will see how they work together!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108880, 'enUS', 'Practice using your attacks on this target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108881, 'enUS', 'Many spells have a heavy mana cost. You have a clever way around this problem.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108882, 'enUS', 'Use Life Tap to replenish your mana reserves.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108883, 'enUS', 'At times you may find yourself overwhelmed by multiple enemies, or a single enemy that is too strong for you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108884, 'enUS', 'Use Fear to frighten my soldier.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108885, 'enUS', 'Well done, $p! You are now prepared to do battle as an affliction warlock.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108886, 'enUS', 'Council members and allies, it is time to begin the summoning.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108887, 'enUS', 'We employ a variety of methods to ensure the safety of Azeroth... and to secure our rightful place within it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108888, 'enUS', 'But the events at the Broken Shore have forced our hand. We haven\'t the time for subtlety.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108889, 'enUS', 'T... T\'uure... Alora... escape...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108890, 'enUS', 'You know the deal. We kill $Ghim:her;, our debts are clear!', 'You know the deal. We kill $Ghim:her;, our debts are clear!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108891, 'enUS', 'Nothing personal, friend. Just business.', 'Nothing personal, friend. Just business.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108892, 'enUS', 'Consider your choice carefully.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108893, 'enUS', 'The Uncrowned will do what no others can: finish this war.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108894, 'enUS', 'I\'m ready to make a decision.', 'I\'m ready to make a decision.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108895, 'enUS', '', 'What\'s the matter? Never seen a princess who knows how to twist a blade?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108896, 'enUS', 'Tell me about your plan.', 'Tell me about your plan.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108897, 'enUS', '', 'I plan to acquire the cursed daggers that took down King Llane Wrynn. Garona hid the Kingslayers for good reason. Their power is not to be underestimated.$B$BI need your help to retrieve them. These storied blades are just what we need to destroy Gul\'dan and his foul allies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108898, 'enUS', 'Gimme yer ear, $n. I got a plan that\'s guaranteed to put worthy blades in them hands!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108899, 'enUS', 'What can I do for ya?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108900, 'enUS', '', 'The Kingslayers are the only way to ensure the Legion\'s end. Hear me out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108901, 'enUS', '', 'I\'m close to locating a foe we\'ve been hunting for years. With a bit of help, we can take her out and lay claim to her daggers.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108902, 'enUS', 'Just after the Cataclysm, one o\' me best discovered a pair o\' golden cutlasses unlike any ye\'ve e\'er seen. The moment Eliza touched \'em, she had a vision o\' herself conquerin\' city after city, commandin\' a crew o\' lost souls.$B$BYe may doubt such a grand tale, but it indeed came t\' pass. The Dreadblades \'ave claimed countless souls, and now we\'re comin\' for \'em!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108903, 'enUS', '', 'Jorach spoke highly of you. He\'s a good friend to have.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108904, 'enUS', '', 'After the fall of Sargeras\' hound Goremaw, his dreadlords took the beast\'s fangs and forged them into a pair of daggers known as the Fangs of the Devourer. It\'s said they still carry the monster\'s bite.$B$BSargeras called upon one of his top assassins to wield the fangs, an eredar named Akaari Shadowgore. Despite our best attempts, we haven\'t been able to stop her.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108905, 'enUS', 'You won\'t escape me this time, Valith! Tell me where your master is and I may grant you a quick death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108906, 'enUS', 'Your time on this world ends now, demon! Allies, gather at Sentinel Hill!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108907, 'enUS', '|TInterface\\Icons\\Spell_DeathKnight_MindFreeze:20|t%s casts |cFFFF0404|Hspell:209983|h[Control Thoughts]|h|r on Grash!', '|TInterface\\Icons\\Spell_DeathKnight_MindFreeze:20|t%s casts |cFFFF0404|Hspell:209983|h[Control Thoughts]|h|r on Grash!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108908, 'enUS', 'That insignia marks you as one of us. Carry it with you always.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108909, 'enUS', 'My work will shine for eternity...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108910, 'enUS', 'A Rotdrool Grabber bursts from the husk of the grub!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108911, 'enUS', 'Go deal with that demon, $n! I\'ll tend to Alora.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108912, 'enUS', 'I seem to have misplaced my insignia.', 'I seem to have misplaced my insignia.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108913, 'enUS', 'Knocking', 'Knocking', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108914, 'enUS', '', 'Use your feral form to heighten your senses. Between the three of us, we can pick up the satyr\'s trail.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108915, 'enUS', 'The barrier you placed here with Ysondre\'s Tear still stands.\n\nIf you wish, you can remove it.', 'The barrier you placed here with Ysondre\'s Tear still stands.\n\nIf you wish, you can remove it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108916, 'enUS', 'Remove the seal.', 'Remove the seal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108917, 'enUS', '', 'Foolish warlocks, summoning what you cannot control.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108918, 'enUS', 'The doomguards make their descent! Prepare yourselves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108919, 'enUS', '', 'You will meet your death by our hands!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108920, 'enUS', 'Sinu a\'manore. You were fortunate I was in the area. What brings you to this forsaken place?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108921, 'enUS', 'Lookee! Seems Yrgrim was the champion, and he came here after Tyr\'s fall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108922, 'enUS', 'I had thought my battle over. You have my gratitude.', 'I had thought my battle over. You have my gratitude.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108923, 'enUS', 'Thank you. I shall return to the Illidari to recover.', 'Thank you. I shall return to the Illidari to recover.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108924, 'enUS', 'These foul demons sap our strength and seek to bend our will. Show them no mercy!', 'These foul demons sap our strength and seek to bend our will. Show them no mercy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108925, 'enUS', 'When that brute Kraggosh and his overseers moved into the Undercity, we were forced to do our real work in secret.$B$BSurely you can see how such an arrangement inevitably led me here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108926, 'enUS', 'It shames me to have been taken alive by these creatures. Thanks to you, I will fight another day.', 'It shames me to have been taken alive by these creatures. Thanks to you, I will fight another day.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108927, 'enUS', 'Thank you for freeing me, $c. The Illidari must learn what the Legion is planning in Faronaar!', 'Thank you for freeing me, $c. The Illidari must learn what the Legion is planning in Faronaar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108928, 'enUS', 'Out here, no one will hear your screams!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108929, 'enUS', 'Your god is no match for ours, druid!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108931, 'enUS', 'Oh, that reminds me. While you were off doing the easy work, I was out taking care of the other part that was needed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108932, 'enUS', 'Just... resting my eyes... for a moment...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108933, 'enUS', 'Come, we have much to discuss.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108934, 'enUS', 'I had to deal with those awful scribes over at The Scribe\'s Sacellum. But, ultimately, it was worth the effort.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108935, 'enUS', 'That should do it. I think we both deserve a pat on the back, $n. Wouldn\'t you agree?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108936, 'enUS', 'Give up, druid. Your ancient god... cannot... be saved...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108937, 'enUS', 'It\'s no use, druid. You cannot fight us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108939, 'enUS', 'Against us, your strength is nothing. All you love will turn against you. You will face your greatest fears.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108940, 'enUS', 'Who goes there?', 'Who goes there?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108941, 'enUS', 'Thank you for that welcome distraction.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108942, 'enUS', 'Sylendra, can we still recover the idol?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108943, 'enUS', 'Just enjoy the party for a bit. You know... mingle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108944, 'enUS', 'Let us gather and give our oaths!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108945, 'enUS', '<The attendant is silent.>', '<The attendant is silent.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108946, 'enUS', '<Take a drink.>', '<Take a drink.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108947, 'enUS', '', 'There are others trapped inside. Please, rescue them if you can. Thank you again, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108948, 'enUS', 'I choose you to be the Sixth.', 'I choose you to be the Sixth.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108949, 'enUS', '', 'I can see the times are changing.  \n\nOne warlock, no matter how powerful, can\'t take on the Burning Legion alone.\n\nWhile I\'ll never believe anything Ritssyn does is selfless, it\'s clear that rebuilding the council is the right thing to do.\n\nI would be honored to count myself one among the six, if you\'ll have me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108950, 'enUS', '', 'I\'ll do whatever it takes to restore pride to the Fizzlebang name.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108951, 'enUS', '', 'It takes many years to build a reputation, and only one mistake to tarnish it.\n\nI have my work cut out for me, but I hope that serving on the council will help towards restoring pride to the Fizzlebang name.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108952, 'enUS', 'Let\'s get going. The portal is just ahead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108953, 'enUS', 'The spirits, the elements, they are but pitiful shadows compared to the power of the legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108954, 'enUS', 'Bow, bow to the might of Sargeras. Bow and receive his gifts.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108955, 'enUS', 'The forces of Highmountain, the drogbar, even the elves, will be swept away in the flames of our wrath.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108956, 'enUS', 'Embrace the fel energies of our new masters. Kneel, and you shall rise stronger than ever.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108957, 'enUS', '', 'Too many of the Bloodtotem have gone over to the legion. Now, these Feltotem are gathering their strength. I do not intend to let their betrayal go unpunished.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108958, 'enUS', '', 'The Bloodtotem had been growing more distant for years now. But to side with the Legion? None of us could have foreseen that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108959, 'enUS', 'More camps on the horizon! Bring them down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108960, 'enUS', 'Fools! Our forces have spread across these foul lands. We have defiled and corrupted your precious soil... Let us see how you deal with our legion now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108961, 'enUS', 'All forces, spread out! Legion corruption rises in all directions!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108962, 'enUS', 'Don\'t bother reading it or anything.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108963, 'enUS', 'Just go ahead and sign it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108964, 'enUS', 'Right there on the line.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108965, 'enUS', 'This metal is all bent out of shape. A strong Blacksmith could hammer these pieces back into working order.', 'This metal is all bent out of shape. A strong Blacksmith could hammer these pieces back into working order.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108966, 'enUS', 'Well done, Archdruid. Take the Idol back to the Dreamgrove, and we will meet you there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108967, 'enUS', 'Someone left some goods out in the open...', 'Someone left some goods out in the open...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108968, 'enUS', 'It would be a pity if a Warrior were to smash them.', 'It would be a pity if a Warrior were to smash them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108969, 'enUS', 'If a Rogue were to pilfer some of these, you\'re sure someone would take notice.', 'If a Rogue were to pilfer some of these, you\'re sure someone would take notice.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108970, 'enUS', 'Someone left some goods out in the open...\n\nThey seem to be monitored by magical means.', 'Someone left some goods out in the open...\n\nThey seem to be monitored by magical means.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108971, 'enUS', 'Halt!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108972, 'enUS', '', 'Betrayers! Liars! Murderers! Has Hamuul sent his lapdogs to fetch the knife from my back?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108973, 'enUS', 'Guards! To me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108974, 'enUS', '', 'Do you remember murdering me? Or have you grown so used to slaughter that my cries of agony are long forgotten?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108975, 'enUS', '', 'I recall my final moments surrounded by those I had trusted. Your satisfied sneers as I drew my last breath.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108976, 'enUS', '', 'Revenge is said to be sweet, but I taste only bitterness.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108977, 'enUS', '', 'Your faces no longer appear familiar. Did you truly deserve my wrath?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108978, 'enUS', '', 'Now you have become prey!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108979, 'enUS', '', 'Scurry, scurry, little rats!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108980, 'enUS', '', 'There is nowhere left to hide!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108981, 'enUS', '', 'Such juicy morsels!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108982, 'enUS', '', 'Scream for me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108983, 'enUS', '', 'I\'ll adorn my web with your bones!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108984, 'enUS', '', 'Feel the crush of my talons!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108985, 'enUS', '', 'My beak will tear your flesh, vermin!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108986, 'enUS', '', 'Not afraid of spiders, are you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108987, 'enUS', 'Finally. I\'ll make this quick.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108988, 'enUS', '', 'This time I\'m ready for your treachery!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108989, 'enUS', '', 'Mercy? None for you! Only gushing blood and broken bodies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108990, 'enUS', 'How dare you interrupt your betters!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108991, 'enUS', '', 'Betrayers, will you remember killing me now?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108992, 'enUS', '', 'The Twilight\'s Hammer is all but destroyed. This once great order has been laid low... scattered and leaderless.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108993, 'enUS', 'The wind slices to the bone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108994, 'enUS', 'Death by a thousand cuts!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108995, 'enUS', 'Bleed for the Grand Magistrix!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108996, 'enUS', 'You can\'t counter what you can\'t see!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108997, 'enUS', 'I will speed you towards your demise!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108998, 'enUS', 'If you blink... you bleed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (108999, 'enUS', 'Another victim of my artistry.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109000, 'enUS', 'Never heard my blade coming!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109001, 'enUS', '', 'But I have heard the prayers of their faithful... crying out for destruction!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109002, 'enUS', 'Elisande... forgive me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109003, 'enUS', 'Let your names be forgotten.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109004, 'enUS', '', 'I will take these poor, pathetic creatures under my wing and lead them to the glorious destiny they yearn for!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109011, 'enUS', '', 'Breathe deeply. Embrace your fate.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109012, 'enUS', '', 'Dark secrets have been revealed to me through the void... ways of subjugating the elements to my will!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109013, 'enUS', '', 'You have no more need of this life!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109014, 'enUS', '', 'No... I still have much to do... RETURN TO ME!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109015, 'enUS', '', 'My children will learn these secrets and together we shall tear this world asunder!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109016, 'enUS', '', 'Consumed from within!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109017, 'enUS', '', 'Your wakened life is a disease!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109018, 'enUS', '', 'My little ones learn quickly... they drink from the void and thirst endlessly for more!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109020, 'enUS', '', 'The destruction of Azeroth is at hand! Soon... the very elements that shape this world will cause its demise!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109021, 'enUS', 'And so we enter the lair of the beast. May the Light protect us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109022, 'enUS', 'Good luck with the Trials... \"hero.\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109023, 'enUS', 'Do not disappoint the Banshee Queen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109024, 'enUS', 'You succumbed to the Nightmare! Defeat your fears to wake up.', 'You succumbed to the Nightmare! Defeat your fears to wake up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109025, 'enUS', 'Our leader has done what no other could do.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109026, 'enUS', 'The Council of the Black Harvest lives once again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109027, 'enUS', 'Under the First, our power will reach new heights.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109028, 'enUS', 'The secrets of the Burning Legion will be ours to unlock!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109029, 'enUS', '', 'Over here! I haven\'t much time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109030, 'enUS', 'A glowing door... Wee bit obvious, don\'t ye think? What have we here?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109031, 'enUS', 'This tablet says ye need to withstand the door\'s \"gaze\". I\'m bettin\' that\'s not gonna be pleasant. Care to give it a try?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109032, 'enUS', 'This magical lantern is different in some way. Almost as if it is leaking.', 'This magical lantern is different in some way. Almost as if it is leaking.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109033, 'enUS', 'As a Mage, you are long familiar with the intricacies of ley magic. It can be repurposed to a... better cause.', 'As a Mage, you are long familiar with the intricacies of ley magic. It can be repurposed to a... better cause.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109034, 'enUS', 'This faulty device of Elven construct is familiar to you and can be altered to provide magical empowerment.', 'This faulty device of Elven construct is familiar to you and can be altered to provide magical empowerment.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109035, 'enUS', 'This magical lantern is different in some way. Almost as if it is leaking.\n\nYou\'re sure someone will come along and fix it in due time.', 'This magical lantern is different in some way. Almost as if it is leaking.\n\nYou\'re sure someone will come along and fix it in due time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109036, 'enUS', 'The cultists are blinded by the twilight... deprived and recluse, taking cover in the night.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109037, 'enUS', 'Beast master hunters possess a deep bond with their pets, calling and bolstering them in battle while striking at foes with ranged attacks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109038, 'enUS', 'Use Call Pet to bring your pet to battle.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109039, 'enUS', 'Sending your pet to attack and distract an enemy gives you more time to line up your ranged attacks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109040, 'enUS', 'Use Kill Command multiple times on the target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109041, 'enUS', 'Now let\'s see what other forces of nature you command.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109042, 'enUS', 'Use Dire Beast on the target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109043, 'enUS', 'I\'m sure you\'re ready to join in on the action. Cobra Shot is your primary ranged attack, pummeling your enemy while your pet keeps their attention.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109044, 'enUS', 'Use Cobra Shot on the target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109045, 'enUS', 'Use Bestial Wrath while attacking the target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109046, 'enUS', 'When in combat, Bestial Wrath sends you and your pet into a frenzy of rage that increases your damage for a short time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109049, 'enUS', 'Sometimes you\'ll find yourself outnumbered. When you do, you can unleash destruction upon a host of adversaries at once!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109050, 'enUS', 'Use Multi-Shot to dispatch all the enemies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109051, 'enUS', 'Even the greatest adventurers sometimes find themselves in over their heads.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109052, 'enUS', 'Use Feign Death to trick my soldier into stopping his attack.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109053, 'enUS', 'Prove to me you have the necessary skill and cunning required to hunt any prey!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109054, 'enUS', 'Show me your courage and zeal in the heat of combat!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109056, 'enUS', 'Target practice is over, $p. It\'s time to prove your mettle. Combine all the lessons you\'ve learned and use them to defeat my men in combat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109057, 'enUS', 'Well done, $p! You are now prepared to do battle as a beast master hunter.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109058, 'enUS', 'Golarb is unresponsive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109059, 'enUS', 'Mistress is gone...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109060, 'enUS', 'The dreadlord came for her. Golarb tried to help...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109061, 'enUS', 'Golarb failed...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109062, 'enUS', 'We\'re on the right track. Keep searching.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109063, 'enUS', '', 'Their hideout should be close. I smell the stench of satyr to the east.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109064, 'enUS', 'Ye did it! Better\'n being a pile of ash on the ground, ain\'t it?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109065, 'enUS', 'Zinnin Smythe bows his head in silent thanks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109066, 'enUS', '', 'You have my thanks. I can find my own way back to Dreadscar Rift.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109067, 'enUS', '', 'Archdruid! Watch out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109068, 'enUS', 'Don\'t worry!  I\'m here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109069, 'enUS', '', 'I am not leaving without my Kaya!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109071, 'enUS', '', 'She fought with everything she was, but there were simply too many. What are we going to do, Galar\'thus?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109073, 'enUS', 'Another tablet! This champion is still forged, never touched by the curse o\' flesh! We should take care.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109074, 'enUS', '', 'My home! Dargrul blew up my home. I... I don\'t... I can\'t even...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109075, 'enUS', '', 'They did not give me enough time to gather my things. Do you think they will be safe? I don\'t want those drogbar rummaging through my personal stuff.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109076, 'enUS', 'There is no time left. Run!', 'There is no time left. Run!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109077, 'enUS', '$n! Bless the Light, you made it! This place is crawling with evil!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109078, 'enUS', 'These braves are near death. They will not be able to move themselves.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109079, 'enUS', 'I bid you greetings, Opener of Doors. You are the first.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109080, 'enUS', 'I am Yrgrim, Champion of Tyr! Let us see if you are worthy to bear his shield.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109081, 'enUS', 'Your arrival was timely. Had we waited even a few more moments I would not be here today to thank you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109082, 'enUS', 'It\'s a barrel of Starlight Rose tea. It appears to have been aged such that the brew is sickeningly strong.', 'It\'s a barrel of Starlight Rose tea. It appears to have been aged such that the brew is sickeningly strong.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109083, 'enUS', 'As a Death Knight, tea strong enough to wake the dead is just what you need.', 'As a Death Knight, tea strong enough to wake the dead is just what you need.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109084, 'enUS', 'A strong tea is merely a challenge to the auspicious Monk. Down the hatch!', 'A strong tea is merely a challenge to the auspicious Monk. Down the hatch!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109085, 'enUS', 'It\'s a barrel of Starlight Rose tea. It appears to have been aged such that the brew is sickeningly strong.\n\nJust the smell of it nearly causes you to retch. Clearly it has been left here for too long.', 'It\'s a barrel of Starlight Rose tea. It appears to have been aged such that the brew is sickeningly strong.\n\nJust the smell of it nearly causes retching. Clearly it has been left here for too long.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109086, 'enUS', 'So be it. Come my wounded friends, let us see if I might have the strength that is needed to carry you to safety.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109087, 'enUS', '', 'This is not up for debate, Anarys. You are either with us, or against us. Choose wisely.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109088, 'enUS', '', 'I will hear no more of your scheming! You endanger everything our father worked for!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109089, 'enUS', '', '<sigh> Dear child... Do you not realize what he died for?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109090, 'enUS', 'Thank you, champion. With my strength renewed, I am able to dispel this foul magic.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109091, 'enUS', 'Come, hero! Jace was heading this way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109092, 'enUS', 'Long have I waited for one who proves worthy. Face me in battle that we might test your heart!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109093, 'enUS', 'We fight at your side, demon hunter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109094, 'enUS', 'I must see if you possess the will, the strength, and the stamina to wield this shield. Will you face me in a trial of combat?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109095, 'enUS', 'I am ready for your trial, Yrgrim.', 'I am ready for your trial, Yrgrim.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109096, 'enUS', 'A realistic and ornate statue of a Suramar noble.', 'A realistic and ornate statue of a Suramar noble.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109097, 'enUS', 'Your experience with Mining grants you the knowledge to work... and deface stone.', 'Your experience with Mining grants you the knowledge to work... and deface stone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109098, 'enUS', 'Jewelcrafting grants you the skill to rework and \"improve\" this statue.', 'Jewelcrafting grants you the skill to rework and \"improve\" this statue.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109099, 'enUS', 'A realistic and ornate statue of a Suramar noble.\n\nIt appears solid and very heavy.', 'A realistic and ornate statue of a Suramar noble.\n\nIt appears solid and very heavy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109100, 'enUS', 'Let us begin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109101, 'enUS', 'Studying', 'Studying', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109102, 'enUS', 'I am beaten! You are indeed worthy. Were Tyr alive, he would make you his champion. I hereby bestow Truthguard upon you. Use it to defend Azeroth from its enemies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109103, 'enUS', 'I am weary. With a worthy successor, my long vigil is at an end. Fight for truth and justice, champion. In Tyr\'s name...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109104, 'enUS', 'This has been our home since the days of my grandfather\'s grandfather. We have stood against monsters worse than this villain that faces us now and will not be intimidated.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109105, 'enUS', 'I am not leaving, child. And that is that. No underking is going to scare me out of MY home.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109106, 'enUS', 'I name you, $n, the First of the Council of the Black Harvest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109107, 'enUS', 'Fah! This is OUR mountain. Let the drogbar savages come. We have the power of the spirits, of this mountain. All they have are sticks and stones and anger.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109108, 'enUS', 'Alright $n, you may want to stand back. Things are going to get hot.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109109, 'enUS', 'Adding the sulfate now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109110, 'enUS', 'And last but not least, the dust.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109111, 'enUS', 'Success! I have to say, this is some of my finest work. I guess some credit goes to Deucus and his detailed design.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109112, 'enUS', 'Jace went to scout ahead, but when these demons attacked and put up this barrier, we were separated. He\'s still out there!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109113, 'enUS', 'Look at what I can do!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109114, 'enUS', 'I can still see you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109115, 'enUS', 'Invisibility!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109116, 'enUS', 'Amazing! Good to see the shield in your hands. Me \'n Cato will head to Dalaran and report to Tyrosus. If ye need a ride, my hippogryph\'s outside.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109117, 'enUS', 'A presence. Something new... yet familiar. Yes...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109118, 'enUS', 'Your coming was foretold in the rings. The long circle is nearly complete.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109119, 'enUS', 'Come, little ones. Savor your fear as we feast upon it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109120, 'enUS', 'Agh!', 'Agh!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109121, 'enUS', 'At last, the feeding hour!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109122, 'enUS', '|TInterface\\Icons\\Spell_Fire_FelFlameStrike:20|t Krosus targets |cFFFF0404$n|r with an |cFFFF0404|Hspell:205344|h[Orb of Destruction]|h|r!', '|TInterface\\Icons\\Spell_Fire_FelFlameStrike:20|t Krosus targets you with an |cFFFF0404|Hspell:205344|h[Orb of Destruction]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109123, 'enUS', 'Disgusting!!', 'Disgusting!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109124, 'enUS', 'Rejoice! The end comes soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109125, 'enUS', 'Oblivion offers solace. Take the gift! Take it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109126, 'enUS', 'You have failed those who needed you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109127, 'enUS', 'Your light sputters out...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109128, 'enUS', 'Welcome death. Do not fight it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109129, 'enUS', 'How DARE you!!', 'How DARE you!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109130, 'enUS', 'You\'ll pay for this!', 'You\'ll pay for this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109131, 'enUS', 'My eyes!', 'My eyes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109132, 'enUS', 'Someone appears to have dropped a scroll in this fountain. The writing looks like it is blurred from the water.', 'Someone appears to have dropped a scroll in this fountain. The writing looks like it is blurred from the water.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109133, 'enUS', 'Return the sheep to their pens in Bradensbrook!', 'Return the sheep to their pens in Bradensbrook!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109134, 'enUS', 'As a Shaman, you can use the aid of the elements to garner the information that was present.', 'As a Shaman, you can use the aid of the elements to garner the information that was present.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109135, 'enUS', 'Mark Black Rook Falcons for Granny Marl.', 'Mark Black Rook Falcons for Granny Marl.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109136, 'enUS', 'Your training in Inscription allows you to grasp the knowledge behind the scroll despite the obvious water damage.', 'Your training in Inscription allows you to grasp the knowledge behind the scroll despite the obvious water damage.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109137, 'enUS', 'Someone appears to have dropped a scroll in this fountain. The writing looks like it is blurred from the water.\n\nIt was probably important at some point in time, but has obviously been in there too long to remain legible.', 'Someone appears to have dropped a scroll in this fountain. The writing looks like it is blurred from the water.\n\nIt was probably important at some point in time, but has obviously been in there too long to remain legible.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109138, 'enUS', 'Release your grip on hope.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109139, 'enUS', 'The pain of flesh is fleeting. True torment lasts forever.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109140, 'enUS', 'Graagh! What pointless impudence! We share a master!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109141, 'enUS', 'Every blow struck at our heart will be repaid a thousand fold!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109142, 'enUS', 'Out! Out! You are not yet worthy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109143, 'enUS', 'Still you fight? Still you refuse the gift?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109144, 'enUS', 'N\'zoth... I journey... to Ny\'alotha...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109145, 'enUS', '', 'Did you really think you could stop me? Foolish children!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109146, 'enUS', '', 'The blood of Sintharia flows through my veins and  her voice whispers to me from the Dark Beyond!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109147, 'enUS', '', 'The Twilight Dragonflight lives and our destiny is at hand!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109148, 'enUS', '', 'Cower, mortals! Obliteration is upon you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109149, 'enUS', '', 'Thanks for everything, Beakbuck. I hope we meet again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109150, 'enUS', '', 'Don\'t worry about me. Just focus on finding Lothrius.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109151, 'enUS', '', 'Even in death we shall endure... mother I return...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109152, 'enUS', 'Zinnin Smythe bows before you in silent gratitude.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109153, 'enUS', 'You will suffer horrors...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109154, 'enUS', 'Thank you, friends. I admit, I doubted you\'d survive the demon\'s trap, Boros. Thought it would be just me and $n now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109155, 'enUS', 'They closed the gates to the upper levels. $n, use your holy magic to destroy the anchoring crystal. That should open our way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109156, 'enUS', '', 'Howww did it get passst the daughters?! We will feassst on its insssides!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109157, 'enUS', 'Thank you. Now get out of here! It\'s not safe!', 'Thank you. Now get out of here! It\'s not safe!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109158, 'enUS', 'Brave of you to come, but we need to leave!', 'Brave of you to come, but we need to leave!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109159, 'enUS', 'Never thought I\'d see the day...', 'Never thought I\'d see the day...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109160, 'enUS', 'The end is coming!', 'The end is coming!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109161, 'enUS', 'A little bruised, but alive. Thanks.', 'A little bruised, but alive. Thanks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109162, 'enUS', 'Garuhod once mentioned that he learned most of his recipes from the priestesses here. They\'ve since gone mad and their writings are all gibberish to me, but it seems their oracle can still understand them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109163, 'enUS', 'With this scrying orb, all their writings are clear as day! This should point us in the right direction to continue Garuhod\'s research.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109164, 'enUS', 'Easy there, Huey! Easy griffy boy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109165, 'enUS', 'Perhaps she possesses the key to unlocking their knowledge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109166, 'enUS', 'Garuhod may have been onto something, but I think he got his ingredients mixed up. We should try a different combination. Maybe hydra eggs and murloc eyes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109167, 'enUS', 'Yes, these should do nicely. I\'ll have the court alchemist test this at once!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109168, 'enUS', '', 'Enjoying the party?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109169, 'enUS', '', 'Ah, you must be the outlander Silgryn has told me about.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109170, 'enUS', 'Pleased to meet you, Lady Ly\'leth.', 'Pleased to meet you, Lady Ly\'leth.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109171, 'enUS', 'Sup.', 'Sup.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109172, 'enUS', '', 'Well, aren\'t you charming?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109173, 'enUS', 'Excellent! Now let\'s get out of here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109174, 'enUS', 'Be on your guard. I sense a powerful demon ahead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109175, 'enUS', 'UNUSED - Nonsense. What good are my works on the necks of those abominations? You would do well to learn from our friend here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109176, 'enUS', 'Hop on and let\'s get movin\'. Ol\' Huey don\'t bite... much!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109177, 'enUS', 'Shoot her! SHOOT HER!', 'Shoot her! SHOOT HER!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109178, 'enUS', 'If you had faith in the Light, demon hunter, there would be nothing to fear!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109179, 'enUS', 'It does not matter what could happen to his reputation. He has already ruined it. This is why you are still an apprentice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109180, 'enUS', 'But master, this man was innocent! His name was cleared, surely he deserves a second chance?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109181, 'enUS', 'You have done everyone a great service, friend. Give cousin Ly\'leth my regards.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109182, 'enUS', 'To Shield\'s Rest!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109183, 'enUS', 'Make yourself comfortable. It\'s a bit of a ride to Stormheim.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109184, 'enUS', '|TInterface\\Icons\\Spell_Holy_FistOfJustice:20|t Krosus is about to destroy the bridge with a |cFFFF0404|Hspell:205875|h[Slam]|h|r!', '|TInterface\\Icons\\Spell_Holy_FistOfJustice:20|t Krosus is about to destroy the bridge with a |cFFFF0404|Hspell:205875|h[Slam]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109185, 'enUS', 'My wounds were severe, but $n arrived just in time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109186, 'enUS', 'The Light has come to vanquish you, demon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109187, 'enUS', 'Brann introduced me to Prustaga not long ago. That dwarf sure has a way with the words... he can even charm a vrykul!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109188, 'enUS', 'A prisoner! We could use her help, $n. Free her!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109189, 'enUS', '|TInterface\\Icons\\Achievement_Boss_YoggSaron_01:20|tA Dominator Tentacle faces you and readies |cFFFF0000|Hspell:208689|h[Ground Slam]|h|r!', '|TInterface\\Icons\\Achievement_Boss_YoggSaron_01:20|tA Dominator Tentacle faces you and readies |cFFFF0000|Hspell:208689|h[Ground Slam]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109190, 'enUS', 'Accordin\' to ancient texts we translated back in the Explorer\'s League, Titanstrike focuses the power of storms into blasts of energy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109191, 'enUS', 'If that\'s true, it could do some serious damage to the Legion. S\'pose we\'ll find out soon enough!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109192, 'enUS', 'Druids of Azeroth, protectors of the Dream, my sons and daughters...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109193, 'enUS', 'I must speak quickly, for my mind grows clouded.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109194, 'enUS', 'I sensed Shaladrassil\'s corruption, and entered the Emerald Dream to destroy the Nightmare\'s source.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109195, 'enUS', 'Deeper and deeper I fought. When I arrived at the Rift, I saw their true strength.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109197, 'enUS', 'The Nightmare will spread like a disease, festering and multiplying. It will engulf all of Azeroth in its twisted reality.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109198, 'enUS', 'I can no longer see the way back to the Dream. I am trapped in a Nightmare of my own making.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109199, 'enUS', 'Druids, my time is done. It is up to you now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109200, 'enUS', '', 'Taz\'dingo! T\'ank ya for gettin\' me outta dere!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109201, 'enUS', 'I\'ll introduce ye to Prustaga when yer ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109202, 'enUS', '', 'I am Mayla, daughter of the late High Chieftain Ulan, and I... I speak for the tribes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109203, 'enUS', '', 'Don\'t worry, they\'re friendlier than they look.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109204, 'enUS', 'I\'m pleased you chose to heed our call. We\'ve discussed inviting you into our ranks for some time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109206, 'enUS', 'None taken... so long as ye don\'t insult me face!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109207, 'enUS', '', 'I\'ve heard many tales of your accomplishments. I can\'t imagine half of them could be true.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109208, 'enUS', '', 'It\'s good to finally see you again. I have missed our adventures together.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109209, 'enUS', 'Such power! I feel the demon\'s essence infusing every fiber of my being!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109210, 'enUS', 'I fear for your soul, demon hunter. It\'s not too late! Turn to the Light and be cleansed of your corruption!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109211, 'enUS', 'Spare me your platitudes, paladin! Your people know well the horrors that the Legion plans to unleash.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109212, 'enUS', '', 'Many t\'anks again, mon, for gettin\' me outta dere!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109213, 'enUS', 'We seek the leader of this den of horrors, an eredar called Lady Calindris.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109214, 'enUS', '', 'Aye, she be outside directin\' her demons ta load up more captives. I be havin\' a score to settle wid her, so if ya be aimin\' ta take her out, count me in!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109215, 'enUS', 'Lead on, Farseer, and I shall follow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109216, 'enUS', 'Come, let\'s move before we encounter anymore surprises.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109217, 'enUS', 'These are troubling words, Farseer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109218, 'enUS', 'We must defeat the Twilight\'s Hammer and restore balance to Deepholm.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109219, 'enUS', 'The servant of Therazane is bound by the dark powers of the void. I hope we are not too late!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109220, 'enUS', 'The horrors... these fiends must be destroyed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109221, 'enUS', 'Twilight whelps? I thought the Twilight Dragonflight were extinct...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109222, 'enUS', 'The Fal\'dorei have taken everything... from me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109223, 'enUS', 'I still not stand by and watch as demons tear this world apart.$B$BThe time has come for the nightborne to earn their place on Azeroth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109224, 'enUS', 'This one is almost lost to the corruption! We must hurry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109225, 'enUS', 'We have put an end to the rituals, but I fear the worst is yet ahead of us...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109226, 'enUS', 'We should return to Therazane and inform her of what has transpired here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109227, 'enUS', 'Our prey stands before us! We must strike now, before she can summon her defenses!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109228, 'enUS', 'Join me in raising a glass not only to our newest Shadow, but to the dawn of a new era for the Uncrowned.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109229, 'enUS', 'Somethin\'s off about this here brew...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109230, 'enUS', '', 'Vanessa...?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109231, 'enUS', 'Today, $p secured the legendary Fangs of the Devourer, and in doing so has enabled us to strike at the Legion directly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109232, 'enUS', 'Today, $p secured the legendary Kingslayers, and in doing so has enabled us to strike at the Legion directly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109233, 'enUS', '', 'Get away from there!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109234, 'enUS', '', 'You have come to help Ly\'leth destroy our family! Be gone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109235, 'enUS', '', 'Ngh! Who ARE you?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109236, 'enUS', '', 'Well done!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109237, 'enUS', 'Just... finish me off.', 'Just... finish me off.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109238, 'enUS', 'Come with me. Ly\'leth wants to speak with you.', 'Come with me. Ly\'leth wants to speak with you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109239, 'enUS', '', 'She wants to preach her traitorous ideals to me further? Fine. Let us go.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109240, 'enUS', '', 'You\'re more resilient than I thought. I was hoping to take you somewhere a little less... populated.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109241, 'enUS', '', 'Come on, show me the \"legendary fighter\" I\'ve heard so much about!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109242, 'enUS', '', 'No! I\'ve worked too hard to be undermined by the likes of you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109243, 'enUS', '', 'Oh, my head... Valeera, you okay?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109244, 'enUS', '', 'Valeera nods.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109245, 'enUS', 'I was not successful in getting the Bloodtotem tribe to help us with this. I hope Jale has enough leverage.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109246, 'enUS', 'I have only just returned from Bloodtotem lands, but I have heard of the aid you lent our people. \n\nIt shames me to see the day when outsiders like yourself are more willing to help us than our fellow tribes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109247, 'enUS', 'We need your help negotiating with the Underking.', 'We need your help negotiating with the Underking.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109248, 'enUS', 'Jale wishes to negotiate with these savages? A bold move, though probably a foolish one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109249, 'enUS', 'By the Earthmother! It is just as a I feared... the Twilight Dragonflight has returned!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109250, 'enUS', 'Take good care of it.', 'Take good care of it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109251, 'enUS', 'Don\'t get too reckless!', 'Don\'t get too reckless!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109252, 'enUS', 'Bring her back in one piece!', 'Bring her back in one piece!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109253, 'enUS', '', 'Our work is nearly finished... do not allow these meddling shaman to interfere with the rituals!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109254, 'enUS', '', 'Find them! Leave no stone unturned!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109255, 'enUS', '', 'None survive, so says the Grand Magistrix!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109256, 'enUS', '', 'The Moon Guard are finished! Slay them all.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109257, 'enUS', '|TInterface\\Icons\\inv_feather_05.blp:20|t %s readies |cFFF00000|Hspell:210547|h[Razor Wing]|h|r.', '|TInterface\\Icons\\inv_feather_05.blp:20|t %s readies |cFFF00000|Hspell:210547|h[Razor Wing]|h|r.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109258, 'enUS', '', 'Scour the stronghold. None escape alive.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109259, 'enUS', 'I heard what you did for the Rivermane tribe.$b$bWhen we first met in Dalaran I did not expect that you would be as helpful to us as you have been.$b$bIt is much appreciated.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109260, 'enUS', '', 'Wipe this place clean of their inferior magics. The Moon Guard is finished.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109261, 'enUS', 'For Nar\'thalas!', 'For Nar\'thalas!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109262, 'enUS', 'Drive back the scaled menace!', 'Drive back the scaled menace!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109263, 'enUS', 'Show the naga no mercy!', 'Show the naga no mercy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109264, 'enUS', 'Face the wrath of the Nightwatchers!', 'Face the wrath of the Nightwatchers!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109265, 'enUS', 'Strike down the naga filth!', 'Strike down the naga filth!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109266, 'enUS', '', 'Our soldiers grow weary of the naga\'s relentless assault. Rally the Nightwatchers, champion, and drive the naga back to the depths where they belong!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109267, 'enUS', '', 'We appreciate your help, champion. You\'ve allowed my people a moment\'s respite from the naga threat.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109269, 'enUS', '|TInterface\\Icons\\INV_Misc_Bell_01:20|tA %s has been alerted to your presence!', '|TInterface\\Icons\\INV_Misc_Bell_01:20|tA %s has been alerted to your presence!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109270, 'enUS', '|TInterface\\Icons\\INV_Misc_Bell_01:20|tAn alarm has been sounded! A reinforcement has entered the fray!', '|TInterface\\Icons\\INV_Misc_Bell_01:20|tAn alarm has been sounded! A reinforcement has entered the fray!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109271, 'enUS', 'The boy-king serves at the master\'s table. Three lies will he offer you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109272, 'enUS', 'Her heart is a crater, and we have filled it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109273, 'enUS', 'The king of diamonds has been made a pawn.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109274, 'enUS', 'At the hour of her third death, she ushers in our coming.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109275, 'enUS', 'From the earth, he draws strength. Our earth. Our strength.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109276, 'enUS', 'Its surface blazes bright, masking shadows below.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109277, 'enUS', '', 'Thanks, now its time for some payback!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109278, 'enUS', 'To find him, drown yourself in the circle of stars.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109279, 'enUS', 'Five keys to open our way. Five torches to light our path.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109280, 'enUS', 'Flesh is his gift. He is your true creator.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109281, 'enUS', 'The lord of ravens will turn the key.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109283, 'enUS', '', 'Hmph... Someone will notice I am missing! They will come looking for me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109284, 'enUS', 'I will start the ritual at your command.$B$BThe twins won\'t take too kindly to being summoned, so be prepared for a fight.$B$BOnce they are weakened, use the Bloodstone to enslave them.$B$BAre you ready to begin?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109285, 'enUS', '', 'True. You will have to make an appearance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109286, 'enUS', '', 'Use the Bloodstone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109287, 'enUS', 'Welcome to Mornath\'s Manor of Mystery! I have all manner of mysterious curios from before and after the Sundering.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109288, 'enUS', 'He insists that you return it, and that I may use force if necessary.', 'He insists that you return it, and that I may use force if necessary.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109289, 'enUS', '', 'Friend, the withered are relentless in their attacks on our whelplands. They\'ve left our captive young starving for mana!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109290, 'enUS', '', 'Take back the mana crystals the withered stole and use them to revive the whelplings. Move quickly, friend!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109291, 'enUS', '', 'Your actions have ensured a future for the blue dragons in Azsuna. With the withered\'s threat subsided, you\'ve given us the time we need to recover.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109292, 'enUS', 'How brave of you, $n.', 'How brave of you, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109293, 'enUS', 'I owe you a debt of gratitude.', 'I owe you a debt of gratitude.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109294, 'enUS', 'These bandages... thank you.', 'These bandages... thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109295, 'enUS', 'I... I must get back to battle!', 'I... I must get back to battle!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109296, 'enUS', 'A little shaken, but I\'ll be alright.', 'A little shaken, but I\'ll be alright.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109297, 'enUS', 'For the Alliance!', 'For the Alliance!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109298, 'enUS', '', 'You will join the eternal hunt!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109299, 'enUS', 'Ah yes, the heroes have arrived. So persistent. So confident. But your arrogance will be your undoing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109300, 'enUS', 'Have you forgotten your humiliation on the Broken Shore? How your precious high king was bent and broken before me? Will you beg for your lives as he did, whimpering like some worthless dog?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109301, 'enUS', 'Have you forgotten your humiliation on the Broken Shore? How your mighty warchief was stuck in the belly like a helpless piglet? Will you die slowly as he did, consumed by fel corruption and squealing for a merciful end?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109302, 'enUS', 'Your pathetic Alliance will fall to dust. Your new boy-king will bow down and serve me... as will all of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109303, 'enUS', 'Your pathetic Horde will fall to dust. In the end, death will inherit this world and she will be waiting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109304, 'enUS', 'The husk of Illidan will prove an ideal vessel for my master\'s glory. Sargeras will rise, and together we will watch your world burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109305, 'enUS', '', 'None will defile the warlord\'s hoard!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109306, 'enUS', 'Let the endgame begin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109307, 'enUS', 'You will not stop the ritual!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109308, 'enUS', 'The vessel must be made ready!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109309, 'enUS', 'One by one you fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109310, 'enUS', 'You should have bent the knee!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109311, 'enUS', 'Surrender! I may yet show mercy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109312, 'enUS', 'Thus do the champions of this world fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109313, 'enUS', 'No more distractions! I have work to do...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109314, 'enUS', 'Come forth and serve me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109315, 'enUS', 'Strike for Sargeras!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109316, 'enUS', 'Rid me of these pests!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109317, 'enUS', 'Kill them in my master\'s name!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109318, 'enUS', 'I\'ll make ashes of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109319, 'enUS', 'You dare trespass at the heart of the Nightmare itself? Foolish mortals! Here, my will is absolute. Here, I am the master!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109320, 'enUS', 'My will is unbreakable!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109321, 'enUS', 'You\'ll never escape my grasp!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109322, 'enUS', 'Behold... power given form!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109323, 'enUS', 'Drink in the glory!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109324, 'enUS', 'Burn from within!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109325, 'enUS', 'A taste of the master\'s might!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109326, 'enUS', 'The Nightmare has corrupted dragons, gods, and legends. Your downfall will be simple and swift!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109327, 'enUS', 'Let the storm rain death upon you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109328, 'enUS', 'Never underestimate a master warlock!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109329, 'enUS', 'Another dreamer for my Nightmare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109330, 'enUS', 'You failed, heroes! The ritual is upon us! But first, I\'ll indulge myself a bit... and finish you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109331, 'enUS', 'You will never stir again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109332, 'enUS', 'Why won\'t you die? Why do you keep resisting?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109333, 'enUS', 'Eternal slumber! Eternal madness!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109334, 'enUS', 'This fate awaits you all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109335, 'enUS', 'My victory draws near!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109336, 'enUS', 'Your souls are mine, woven for all time into the fabric of the Nightmare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109337, 'enUS', 'Those ghosties are stirrin\' up the winds! Might need ta stop \'em the ol\' fashioned way!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109338, 'enUS', 'I will not fail you, master! More power... just need more power...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109339, 'enUS', 'My victory is absolute. The power of Nightmare grows!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109340, 'enUS', 'NO! I will not permit it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109341, 'enUS', 'They are mine, Ysera! You cannot save them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109342, 'enUS', 'Embrace the Dreaming, champions! It can save you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109343, 'enUS', 'Let the Dreaming wash away his darkness!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109344, 'enUS', 'No more dreams! Only Nightmare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109345, 'enUS', 'Your respite is over! Awaken and face your doom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109346, 'enUS', 'My work... left... unfinished...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109347, 'enUS', 'Nightmares are... never... forgotten...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109348, 'enUS', 'I\'ll slice into the heart of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109349, 'enUS', 'I will sever you from the waking world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109350, 'enUS', 'Time to cut the last tether to your mortal life!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109351, 'enUS', 'Feel the devastation of the Nightmare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109352, 'enUS', 'I\'ll level you one by one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109353, 'enUS', 'Time to crush your dreams!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109354, 'enUS', 'Succumb to the madness!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109355, 'enUS', 'Give in to the Nightmare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109356, 'enUS', 'Your dreams are mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109357, 'enUS', 'Your torment will be unending!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109358, 'enUS', 'There is no escape!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109359, 'enUS', 'Heroes! Champions! Step forth and hear me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109360, 'enUS', 'The Nightmare has long been a cancer eager to consume the Emerald Dream. Had Xavius succeeded, his corruption would have overwhelmed Azeroth, giving rise to a living Nightmare.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109361, 'enUS', 'Your triumph restores the Dream and safeguards your world. The allies of Xavius will never relent, but this road, at least, is closed to them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109362, 'enUS', 'To honor your courage, I offer passage into the pure heart of the Dream. As you feel the essence of life blooming beneath your feet, know that it endures thanks to what you achieved this day.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109363, 'enUS', 'Out of the rolling ocean I rise. The Lord of Tides has answered your call, Farseer of the Earthen Ring.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109364, 'enUS', '', 'Heretic! I will slay you in Volund\'s name!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109365, 'enUS', 'The time has come to use the Idol of the Wilds and call upon the great god, Malorne. Never did I believe I would see the great ancient himself. This is a moment that we will speak of for ages to come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109366, 'enUS', 'I am ready to begin.', 'I am ready to begin.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109367, 'enUS', 'My chosen will protect me for all eternity!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109368, 'enUS', 'You are not worthy of Titanstrike\'s might!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109369, 'enUS', '', 'I grow tired of this! Be consumed by the darkness!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109370, 'enUS', '', 'It\'s just you and me now, little priest! Your pathetic Light won\'t save you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109371, 'enUS', 'It seems Garuhod\'s recipe was incomplete. My alchemists think some bile might be needed to balance this out. It\'s supposed to have some amazing properties!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109372, 'enUS', 'Fetch me some livers from the bears in the highlands. Only the fat ones will do! Oh, and some hippogryph eggs as well. A little variety can\'t hurt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109373, 'enUS', 'Wonderful! I feel like we\'re on the cusp of discovering the cure to this curse! Who would\'ve thought that a lush like Garuhod might have held the key to our freedom?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109374, 'enUS', '', 'What is that noise? Naranoth! If you allow another jailbreak, I\'ll strand you on this pathetic rock and leave you to rot!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109375, 'enUS', '', 'Are you truly so stupid? You presume to challenge an eredar sorceress of the Burning Legion?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109376, 'enUS', '', 'My darkness will consume you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109377, 'enUS', '', 'No! My staff is absorbing the Light! Damn you, priest, and your accursed magic!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109378, 'enUS', '', 'My staff... it senses the Light within you! What is it... Nooooo!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109379, 'enUS', 'There, up ahead! That door\'s glowin\' with the same energy as all o\' these spirits.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109380, 'enUS', 'He dares to send someone in his stead? That accursed wretch.', 'He dares to send someone in his stead? That accursed wretch.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109381, 'enUS', '', 'I will dispel the runes. Protect me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109382, 'enUS', 'Will do, Prust!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109383, 'enUS', '', 'Despoilers! Halt!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109384, 'enUS', '', 'You pollute this hallowed ground with your greed. Begone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109385, 'enUS', '', 'Prophet\'s mercy! What is that?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109386, 'enUS', 'What are you filthy mortals doing near the master\'s portal? I\'ll rip the answer from your souls!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109387, 'enUS', 'Salma! Where\'s my Salma!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109388, 'enUS', '', 'My husband! Is he alright?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109389, 'enUS', '', 'I will make this lake beautiful...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109390, 'enUS', 'Do you even call this laboring? The master will have your hides!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109391, 'enUS', 'If I catch you drinking the reagents again, I\'ll turn you over to master!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109392, 'enUS', 'Hello. Have you come to join the Empyrean Society of Exotic Magical Arts?', 'Hello. Have you come to join the Empyrean Society of Exotic Magical Arts?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109393, 'enUS', 'That\'s the spirit! One down, two to go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109394, 'enUS', '', 'They grow in strength. Be ready!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109395, 'enUS', 'Look! The door\'s openin\'!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109397, 'enUS', 'Keep them from entering the chamber! The ritual is almost complete!', 'Keep them from entering the chamber! The ritual is almost complete!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109398, 'enUS', '', 'You two can yell at each other later! It be time ta get outta dis place!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109399, 'enUS', 'As an elemental shaman you commune with ancient spirits and command primal elemental forces.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109400, 'enUS', 'Use Flame Shock on the target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109401, 'enUS', 'Why stop with fire? Once your enemies are burning, you can hurl molten lava at them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109402, 'enUS', 'Use Lava Burst on the target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109403, 'enUS', 'Hurling lightning from your hands is your main weapon for overcoming enemies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109404, 'enUS', 'Use Lightning Bolt on the target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109405, 'enUS', 'Be wary, my friend- their constructs do not seem at all effected by my runes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109406, 'enUS', 'Shaman of the Earthen Ring, let our powers combine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109407, 'enUS', 'We call upon Neptulon, the great Lord of Tides, in the name of Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109408, 'enUS', '', 'If it weren\'t for Beakbuck I would most certainly be dead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109409, 'enUS', 'I need another invisibility spell applied.', 'I need another invisibility spell applied.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109410, 'enUS', 'Hear our words and answer the call of the Earthen Ring!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109411, 'enUS', 'You can make your way there through the portal to Dalaran Crater. I will meet you there with additional forces.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109412, 'enUS', 'Enchanters all have a deep understanding of the arcane. This magic can be rerouted to provide assistance.', 'Enchanters all have a deep understanding of the arcane. This magic can be rerouted to provide assistance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109413, 'enUS', '', 'Titanstrike is close.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109414, 'enUS', 'By Magni\'s beard! This Volund sure had a taste for titan relics!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109415, 'enUS', 'Lookee here... a mechagnome! Brann told me all about these things. It might be able to help us find Titanstrike!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109416, 'enUS', 'As a Leatherworker, you recognize this type of tannin. Exposing it to the night air should ruin it for any future use.', 'As a Leatherworker, you recognize this type of tannin. Exposing it to the night air should ruin it for any future use.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109417, 'enUS', 'Now let\'s see... plug this in over here... realign the power unit...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109418, 'enUS', 'Remote relay observer unit XR9-47 active. System status: damaged. Rebooting. Please stand by.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109419, 'enUS', 'Efrina, find your brother and rally at the tomb.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109420, 'enUS', '', 'They\'re coming to life!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109421, 'enUS', 'By the Earthmother!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109422, 'enUS', 'Back to the depths with you, Ozumat. Your master commands it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109423, 'enUS', 'Shaman of the Earthen Ring, you have called, but I already know the threat we face.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109424, 'enUS', 'Earth Shock is one of your most powerful spells. Casting it requires Maelstrom, a resource generated by your other attacks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109425, 'enUS', 'Use Earth Shock on this target dummy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109426, 'enUS', 'The Burning Legion has come. You should make use of the little time you have left, mortals.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109429, 'enUS', 'Foolish mortals! There is no unity among the Lords of Elements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109430, 'enUS', 'What do you suggest, little orc? Shall I fight the Burning Legion myself?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109431, 'enUS', '', 'An\'anora!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109432, 'enUS', 'Come forward then. Let me look upon this champion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109433, 'enUS', 'Ahh yes... I have seen you before! There is power in you... but not enough to defeat the Burning Legion alone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109434, 'enUS', 'Ever wanted to turn someone into a frog? Hex allows you to do just that!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109435, 'enUS', 'Turn my soldier into a frog!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109436, 'enUS', 'There are times you\'ll face multiple opponents on the battlefield. Fortunately, you have a potent ability called Chain Lightning.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109437, 'enUS', 'Use Chain Lightning against several enemies at once.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109438, 'enUS', 'There was a time, long ago, when the elements fought as one... perhaps there is still hope.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109439, 'enUS', 'Very well, shaman. I will join the fight to save this world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109440, 'enUS', 'You\'ve taken damage, but this presents no problem for you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109441, 'enUS', 'Use Healing Surge to replenish your health to full.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109442, 'enUS', 'System status: functional. Rerouting commands to maker\'s console.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109443, 'enUS', 'Unleash the elements to achieve victory, $p!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109444, 'enUS', 'Well done, $p! You are now prepared to do battle as an elemental shaman.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109445, 'enUS', 'Maker? Oh no... please don\'t be him...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109446, 'enUS', 'Hmmpf! Would you bow to Sargeras and be his slave instead? Pathetic!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109447, 'enUS', 'You will not fight alone. The Earthen Ring is with you, and we will gather the other Elemental Lords to our cause!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109448, 'enUS', 'Bronzebeard? Ah yes, there you are! My records show this unit has been inactive for millennia. It was stolen from my lab long ago.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109449, 'enUS', 'For the last time, I\'m not Brann!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109450, 'enUS', '', 'Where is Titanstrike, little tinkerer?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109451, 'enUS', 'The gun is there? Most curious! Telemetry coming in... the Broken Isles... vrykul tomb... Ah yes, I see it now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109452, 'enUS', 'Oh my stars! Yes, this is it! The lid is attuned to a particular frequency. I just need to transmit a signal like so, and...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109453, 'enUS', 'All is not lost, Tidehunter. If the Elemental Lords unite...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109454, 'enUS', 'Your skill in Skinning allows you to recognize the vellum this is inscribed upon despite the water damage. Releasing the magic within should be no problem.', 'Your skill in Skinning allows you to recognize the vellum this is inscribed upon despite the water damage. Releasing the magic within should be no problem.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109455, 'enUS', 'This citizen appears to have been injured by fel magic... his fel-seared wounds look to resist normal recovery.', 'This citizen appears to have been injured by fel magic... his fel-seared wounds look to resist normal recovery.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109456, 'enUS', 'Your skill in First Aid allows you to recognize what is needed to ensure this one does not perish.', 'Your skill in First Aid allows you to recognize what is needed to ensure this one does not perish.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109457, 'enUS', 'While not the best solution, your Tailoring allows you to wrap up these wounds and at least stabilize this civilian.', 'While not the best solution, your Tailoring allows you to wrap up these wounds and at least stabilize this civilian.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109458, 'enUS', 'This citizen appears to have been injured by fel magic... his fel-seared wounds look to resist normal recovery.\n\nWith no good way to aid this civilian, you must unfortunately continue along.', 'This citizen appears to have been injured by fel magic... his fel-seared wounds look to resist normal healing.\n\nWith no good way to aid this civilian, you must unfortunately continue along.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109459, 'enUS', 'Not down here? Alright then, go ahead and use the ladder to check the upper scribrary.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109460, 'enUS', 'Don\'t give up hope, $n. Keep milling, and inspect your inks for unusual properties. Together, you and I may be able to fill in the gaps where this book is lacking.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109461, 'enUS', 'Prepare to meet your end, mortals!', 'Prepare to meet your end, mortals!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109462, 'enUS', 'Your end is only the beginning!', 'Your end is only the beginning!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109463, 'enUS', 'The power of Sargeras courses through our veins!', 'The power of Sargeras courses through our veins!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109464, 'enUS', 'By the might of Sargeras!', 'By the might of Sargeras!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109465, 'enUS', 'Your world will burn!', 'Your world will burn!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109466, 'enUS', 'Protectors - remove these unwelcome guests!', 'Protectors - remove these unwelcome guests!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109467, 'enUS', 'These mortals disrupt our ritual! Put an end to them at once.', 'These mortals disrupt our ritual! Put an end to them at once.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109468, 'enUS', 'The ancient grounds of Llothien have long been abandoned. Now our ancestral land is overrun with beasts. I cannot bear to see it in such a state! Please make it right again, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109469, 'enUS', 'You have my gratitude. The stones of Llothien may once again know peace, thanks to you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109470, 'enUS', '|TInterface\\Icons\\Ability_Rogue_EnvelopingShadows:20|tIl\'gynoth has almost completed his |cFFFF0000|Hspell:210781|h[Dark Reconstitution]|h|r!', '|TInterface\\Icons\\Achievement_Boss_YoggSaron_01:20|tA Dominator Tentacle faces you and readies |cFFFF0000|Hspell:208689|h[Ground Slam]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109471, 'enUS', 'The drogbar are coming?', 'The drogbar are coming?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109472, 'enUS', 'But... this is our land.', 'But... this is our land.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109473, 'enUS', 'What? The Underking is coming? Oh no!', 'What? The Underking is coming? Oh no!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109474, 'enUS', 'We need to get out of here!', 'We need to get out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109475, 'enUS', 'Argh! Blast this wind!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109476, 'enUS', 'Evacuate? But we have tilled this soil for generations.', 'Evacuate? But we have tilled this soil for generations.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109477, 'enUS', 'Bah! The drogbar can\'t win. They are just a bunch of savages.', 'Bah! The drogbar can\'t win. They are just a bunch of savages.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109478, 'enUS', 'Drogbar do not come above ground. That is something they just don\'t do.', 'Drogbar do not come above ground. That is something they just don\'t do.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109479, 'enUS', 'Ah, there you are. The area was secured at great cost. This cannot continue, we must retrieve the Silver Hand and destroy the entrance to the tomb.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109480, 'enUS', 'I will complete the ritual to release the Hammer and collapse the tomb, but we must be quick. I can sense evil emanating from the tomb.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109481, 'enUS', 'You and I shall lead a small team in first. Let me know when you are ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109482, 'enUS', 'Danger still lurks within the tomb. Curse all the meddlers! If you keep us alive, we will clear the way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109483, 'enUS', 'I\'m ready.', 'I\'m ready to enter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109484, 'enUS', '%s falls to one knee, gravely wounded.', '%s falls to one knee, gravely wounded.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109485, 'enUS', '%s is |cFFFF0404|Hspell:204237|h[Gravely Wounded]|h|r and cannot fight until healed!', '%s is |cFFFF0404|Hspell:204237|h[Gravely Wounded]|h|r and cannot fight until healed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109491, 'enUS', 'Tell me about Latosius.', 'Tell me about Latosius.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109492, 'enUS', 'Do you remember Suramar before the Sundering?', 'Do you remember Suramar before the Sundering?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109493, 'enUS', 'We seek a powerful artifact. What do you know?', 'We seek a powerful artifact. What do you know?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109497, 'enUS', '', 'Jale! You have returned. Thank the spirits.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109498, 'enUS', '', 'Destroyed?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109499, 'enUS', '', 'Yes. This hero got us to safety, but we MUST get the other tribes back here to help us. We cannot stand divided.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109500, 'enUS', 'There it is! Mission accomplished!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109501, 'enUS', 'Thieves! Your heads will decorate my throne!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109502, 'enUS', 'None challenge me and live.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109503, 'enUS', 'You of flesh are not worthy to wield the storms!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109504, 'enUS', 'What... what happened?', 'What... what happened?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109505, 'enUS', 'I couldn\'t stop! I swear it!', 'I couldn\'t stop! I swear it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109506, 'enUS', 'I feel so weak...', 'I feel so weak...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109507, 'enUS', 'My... is this me? Am I myself again!?', 'My... is this me? Am I myself again!?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109508, 'enUS', 'They know our secrets! Our thoughts are not our own!', 'They know our secrets! Our thoughts are not our own!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109509, 'enUS', 'I felt fear like never before...', 'I felt fear like never before...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109510, 'enUS', 'So helpless... where am I?', 'So helpless... where am I?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109511, 'enUS', 'You are weak!', 'You are weak!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109512, 'enUS', 'We are everywhere!', 'We are everywhere!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109513, 'enUS', 'You cannot escape!', 'You cannot escape!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109514, 'enUS', 'By the might of the Legion!', 'By the might of the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109515, 'enUS', 'The Legion cannot be stopped!', 'The Legion cannot be stopped!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109516, 'enUS', 'Fragile mortal! Your end has come!', 'Fragile mortal! Your end has come!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109517, 'enUS', 'Your soul shall suffer!', 'Your soul shall suffer!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109518, 'enUS', 'Come forth and fight for the Dream!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109519, 'enUS', 'Allies! To my side!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109520, 'enUS', 'Nature will rise against you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109521, 'enUS', 'We must strike back at the Nightmare!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109522, 'enUS', 'Attack the corruptors!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109523, 'enUS', 'The battle is joined!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109524, 'enUS', 'Noble ancient, stomp out the stain of their evil!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109525, 'enUS', 'Seeds of life, purge the wicked from this grove!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109526, 'enUS', 'Champion of the Dream, strike with wing and claw!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109527, 'enUS', 'Beloved daughter, restore my strength!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109528, 'enUS', 'Sacred vines, entangle the corrupted!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109529, 'enUS', 'Die, corrupter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109530, 'enUS', 'The Dream must endure!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109531, 'enUS', 'Your death fortifies the Dream!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109532, 'enUS', 'Your Nightmare ends here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109533, 'enUS', 'The impurity must be cleansed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109534, 'enUS', 'And so the Dream is preserved!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109535, 'enUS', 'I mourn your deaths, but I could not let the Nightmare win.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109536, 'enUS', 'Father! I have... failed you...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109537, 'enUS', 'Thero\'shan... how... could you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109538, 'enUS', 'Malfurion! No, it cannot be! I prayed my thero\'shan would never fall to the Nightmare. Yet here you stand, casting a shadow upon the purity of the Dream!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109539, 'enUS', 'Liars! Servants of darkness! It will give me no pleasure to destroy you, but my duty is to the Dream. Andu-falah-dor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109540, 'enUS', 'Insolent whelps. Taste my steel!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109541, 'enUS', 'Is that you, Thero\'shan?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109542, 'enUS', 'Aaargh! Enough of your lies! The Nightmare must be ended!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109543, 'enUS', 'Aid me, brothers and sisters. We stand as one!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109544, 'enUS', 'You fight with noble hearts! Could it be true? Could the darkness be inside of... me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109545, 'enUS', 'I am the scourge of a thousand plains. The terror of the isles!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109546, 'enUS', 'Father? Father, is that you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109547, 'enUS', 'Yes, father! I will not fail you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109548, 'enUS', 'You... cannot control...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109549, 'enUS', 'Deceiver! You corrupt the servants of the Dream!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109550, 'enUS', 'Filth! Defiler!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109551, 'enUS', 'What wickedness is this, Malfurion?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109552, 'enUS', 'Villains! You poison the children of the Dream!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109553, 'enUS', 'I have purified one of them! They fight for us now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109554, 'enUS', 'Let the Nightmare be cleansed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109555, 'enUS', 'I will not lose you, shan\'do!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109556, 'enUS', 'Master, we fight to save them! To save you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109557, 'enUS', 'Cenarius! It breaks my heart to see my shan\'do twisted and torn this way. If we can subdue him, I will do everything in my power to purge the vile touch of Xavius from his spirit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109558, 'enUS', 'Can it be? My old master is blind to the lies of Xavius and thinks WE are the source of the infestation!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109559, 'enUS', 'You have been deceived, my teacher! Xavius has twisted your perceptions! I beg of you, let me cleanse you of his evil!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109560, 'enUS', 'Yes, Cenarius! We are here to save you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109561, 'enUS', 'Hold your weapons! He begins to see the truth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109562, 'enUS', 'Cenarius! Do not listen to him!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109563, 'enUS', 'It is done! Cenarius is beaten, but my great teacher lives. Now I must focus all of my strength to cleanse him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109564, 'enUS', 'Xavius waits ahead in the Rift of Aln, the true heart of the Nightmare. Would that I could fight at your side, heroes, but... to this place, I cannot go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109565, 'enUS', 'My bond with the Emerald Dream gives me great power... but it also makes me vulnerable to the rift\'s darkness. I have peered inside it and know this to be true. Should I step within, I would be lost... just as Cenarius was.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109566, 'enUS', 'Go. Destroy the Nightmare Lord. Only this way can the Dream be restored... and Azeroth be saved.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109567, 'enUS', 'They are lying to you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109568, 'enUS', 'Yes, my son! It is your father, Malorne! Do not trust these invaders! You must purge them from the Dream!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109569, 'enUS', '|TInterface\\Icons\\Spell_Fire_FelFire:20|t Krosus targets |cFFFF0404you|r with an |cFFFF0404|Hspell:206677|h[Fel Brand]|h|r!', '|TInterface\\Icons\\Spell_Fire_FelFlameStrike:20|t Krosus targets you with an |cFFFF0404|Hspell:205344|h[Orb of Destruction]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109570, 'enUS', 'Just a brief swim and we\'ll reach the tomb.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109571, 'enUS', 'Behold the Tomb of Tyr, built by the mighty... wait! These monsters dare befoul the tomb?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109572, 'enUS', 'This cannot stand! Destroy these abominations!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109573, 'enUS', 'That wasn\'t so bad! Okay, $n, grab Titanstrike so we can get out of here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109574, 'enUS', '', 'You\'ve played your part. Now I shall play mine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109575, 'enUS', 'It pains me to see such destruction threaten the resting place of the Silver Hand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109576, 'enUS', '', 'The heart of this weapon will infuse me with the might of the titans. The vrykul shall bow before me... their new god-queen!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109577, 'enUS', '', 'The Legion.. will prevail!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109578, 'enUS', '', 'Intruders are here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109579, 'enUS', '', 'The Emissary must be notified!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109580, 'enUS', '', 'You will never stop our plans!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109581, 'enUS', 'Rest your hands upon the Idol and we will begin the ritual.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109582, 'enUS', '', 'The fires will come for you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109583, 'enUS', 'I thank you all.  You have done what few others could.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109584, 'enUS', 'Thank you, $p.  You have done what no others could.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109585, 'enUS', '', 'Who DARES challenge the Truthseeker?! I will freeze you where you stand!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109586, 'enUS', '', 'Protected in ice!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109587, 'enUS', '', 'Freeze!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109588, 'enUS', '', 'I have... failed...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109589, 'enUS', 'For Gul\'dan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109590, 'enUS', 'The Legion claims you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109591, 'enUS', 'You can\'t... beat us all...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109592, 'enUS', 'Your soul belongs to the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109593, 'enUS', 'Our power will crush you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109594, 'enUS', 'Die, mortal scum!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109595, 'enUS', 'I\'ll break you all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109596, 'enUS', 'More bones to play with!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109597, 'enUS', 'I am one... of many...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109598, 'enUS', 'I have you now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109599, 'enUS', 'Your essence is mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109600, 'enUS', 'No escape!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109601, 'enUS', 'This one is mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109602, 'enUS', 'Mortal flesh... so very weak!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109603, 'enUS', 'My part... is played.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109604, 'enUS', 'Give me your secrets!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109605, 'enUS', 'Whisper your soul to me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109606, 'enUS', 'Your world will fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109607, 'enUS', 'Keep pressing, champions! Gul\'dan wavers! He WILL fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109608, 'enUS', 'They will come no farther!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109609, 'enUS', 'Fight for Azeroth! For all you hold dear!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109610, 'enUS', 'We can stop him! I know it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109611, 'enUS', 'This is our world, not the Legion\'s!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109612, 'enUS', 'Yessss. It stays in the cage, it does. It stays until master wants it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109613, 'enUS', 'It loses hope, soon it loses its soul! Master will claim you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109616, 'enUS', 'Not good, not good at all! You should both join me in my workshop in Ulduar at once. We have a problem to solve!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109617, 'enUS', '<Plant a flower?>', '<Plant a flower?>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109618, 'enUS', 'Oh, this is fortunate! Titanstrike\'s power has energized this teleportation pad. I should be able to reroute its destination... Success!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109619, 'enUS', 'I would like to continue with a more difficult challenge, Latosius.', 'I would like to continue with a more difficult challenge, Latosius.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109620, 'enUS', 'I believe I am prepared for a very difficult challenge, Latosius.', 'I believe I am prepared for a very difficult challenge, Latosius.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109621, 'enUS', 'Your actions will be your undoing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109622, 'enUS', 'Well, we can\'t stop now! What\'re ye waitin\' for? C\'mon, Theodore!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109623, 'enUS', '|TInterface\\Icons\\INV_Misc_Bell_01:20|tAs the |cFFFF0000Bazaar Goods|r are tampered with, a magical alarm rings out!', '|TInterface\\Icons\\INV_Misc_Bell_01:20|tAs the |cFFFF0000Bazaar Goods|r are tampered with, a magical alarm rings out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109624, 'enUS', '|TInterface\\Icons\\inv_misc_statue_04:20|tThe destruction of the |cFFFF0000Lifesized Nightborne Statue|r draws the attention of enemies!', '|TInterface\\Icons\\inv_misc_statue_04:20|tThe destruction of the |cFFFF0000Lifesized Nightborne Statue|r draws the attention of enemies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109625, 'enUS', '', 'Your bladework is slow and thoughtless. Train harder!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109626, 'enUS', '', 'So slow, so predictable! Your swordsmanship is weak!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109627, 'enUS', 'Bagging', 'Bagging', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109628, 'enUS', '|TInterface\\Icons\\inv_gizmo_03:20|tThe clamor of tools repairing the pile of |cFFFF0000Discarded Junk|r echoes through the district!', '|TInterface\\Icons\\inv_gizmo_03:20|tThe clamor of tools repairing the pile of |cFFFF0000Discarded Junk|r echoes through the district!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109629, 'enUS', 'Abra Cadabra!', 'Abra Cadabra!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109630, 'enUS', 'You hear footsteps behind you!', 'You hear footsteps behind you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109631, 'enUS', 'Hey! Tha...  thash MY drink! <hic!>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109632, 'enUS', 'Plant aethril', 'Plant aethril', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109633, 'enUS', 'Plant dreamleaf', 'Plant dreamleaf', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109634, 'enUS', 'Plant foxflower', 'Plant foxflower', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109635, 'enUS', 'Plant fjarnskaggl', 'Plant fjarnskaggl', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109636, 'enUS', 'Plant starlight rose', 'Plant starlight rose', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109637, 'enUS', 'Plant felwort', 'Plant felwort', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109638, 'enUS', 'S... sleepy time... <hic!>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109639, 'enUS', 'Over here! There\'s no time to wind your way through the entire complex.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109640, 'enUS', 'Well done, soldiers! Three of you, on me. We\'ll warn the others. The rest of you hold your -', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109641, 'enUS', 'Watchout! Another!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109642, 'enUS', '', 'Ivanel Meadowsong reaches out toward the nearby hippogryph and weakly tries to shoo it away.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109643, 'enUS', '', 'Go on, fly away!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109644, 'enUS', 'Wild Moonfall Hippogryph claws defiantly at the air and remains unmoved.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109645, 'enUS', '', 'Do not come back for us. We are already doomed by the Nightborne\'s betrayal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109646, 'enUS', '', 'We need to - Beakbuck, no!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109647, 'enUS', 'Beakbuck turns and charges toward you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109648, 'enUS', '<Vanthir halts his conversation abruptly as you approach and eyes you suspiciously.>$B$BAnarys. What do you want? Are there no more demon boots left for you to lick?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109649, 'enUS', '', 'Training starts now. Are you ready?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109650, 'enUS', '', 'Ignore him. I need you to find Lothrius. We were separated in the attack- I think he is still holding out at the Athenaeum.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109651, 'enUS', '', 'Formations!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109652, 'enUS', 'Ly\'leth would like you to visit her regarding a shipment of dusk lily pods.', 'Ly\'leth would like you to visit her regarding a shipment of dusk lily pods.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109653, 'enUS', '', 'Again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109654, 'enUS', '', 'Well done. Make sure to practice every day.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109655, 'enUS', '', 'You need to riposte!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109656, 'enUS', '', 'Keep at it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109657, 'enUS', '', 'Practice the basics.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109658, 'enUS', '', 'Nicely done.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109659, 'enUS', '', 'Now for the counter-attack!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109660, 'enUS', 'Dusk lily pods? I see...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109661, 'enUS', 'Sometimes I forget how talented Serena is. Welcome, as it were. It is high time we dealt with these intruders.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109662, 'enUS', 'Please excuse me. I must address this immediately.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109663, 'enUS', '', 'Press your advantage.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109664, 'enUS', 'I have laid out a safe path for you. Touching the crystals will summon my guardians to you. Make good use of it and go swiftly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109665, 'enUS', '', 'There!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109666, 'enUS', '', 'Lothrius\' magic called to me. He must still be alive! Quickly- you can help us rejoin the others.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109667, 'enUS', '', 'We will move when the way is clear. Your bravery is worth more than you know, friend.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109668, 'enUS', 'Excellent! You made it through in one piece! You can never be sure about the reliability of stolen teleporters...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109677, 'enUS', 'I\'m preparing the diagnostic systems now. Say, you had quite an epic struggle with that ghostly vrykul warlord. The thief had it coming, if you ask me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109678, 'enUS', 'Steam pump... check. Low-ion energy converter units... check, check, and... check.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109679, 'enUS', 'There you are! Right on schedule.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109680, 'enUS', 'Now, let\'s recall that observation unit from Stormheim.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109681, 'enUS', 'Stormheim... Stormheim... Ah, there it is.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109682, 'enUS', 'Engaging processors. Initializing download relays. I don\'t anticipate any incompatibilities with my upgraded systems.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109683, 'enUS', 'Once the process kicks into gear, it should only take a minute or two to retrieve the information from XR9-47 here. Just sit back and enjoy the science!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109684, 'enUS', 'Please... someone!', 'Please... someone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109685, 'enUS', 'Anyone? Help!', 'Anyone? Help!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109686, 'enUS', 'If you wouldn\'t mind, I\'d like to help.', 'If you wouldn\'t mind, I\'d like to help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109687, 'enUS', 'Looks like this fight\'s just beginning.', 'Looks like this fight\'s just beginning.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109688, 'enUS', 'Thanks. I need find the others, but hopefully this helps.', 'Thanks. I need find the others, but hopefully this helps.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109689, 'enUS', 'Keep moving! They\'re everywhere!', 'Keep moving! They\'re everywhere!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109690, 'enUS', 'You there! Mind lending a hand?', 'You there! Mind lending a hand?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109691, 'enUS', 'My spellbook! How can I - Hey! Over here!', 'My spellbook! How can I - Hey! Over here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109692, 'enUS', 'What kind of mess have I gotten myself into?', 'What kind of mess have I gotten myself into?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109693, 'enUS', '<If you had an herb, you could plant it here.>', '<Plant a flower?>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109694, 'enUS', 'More Kirin Tor Spies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109695, 'enUS', 'Your friend has been dealt with.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109696, 'enUS', 'Now it is your turn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109697, 'enUS', 'This looks like the perfect opportunity for me to hone my skills!', 'This looks like the perfect opportunity for me to hone my skills!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109698, 'enUS', 'I seem to be missing some of my newer spells, though I\'m sure we won\'t run into too much trouble.', 'I seem to be missing some of my newer spells, though I\'m sure we won\'t run into too much trouble.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109699, 'enUS', '%s flips through the pages of his spellbook.', '%s flips through the pages of his spellbook.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109700, 'enUS', 'By the Light! What are ye doin\' all the way out here? Ne\'er mind that! Help me with this thing!', 'By the Light! What are ye doin\' all the way out here? Ne\'er mind that! Help me with this thing!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109701, 'enUS', 'Ne\'er thought I\'d see you out here! Mind lendin\' a hand?', 'Ne\'er thought I\'d see you out here! Mind lendin\' a hand?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109702, 'enUS', '<The beast looks at you for a moment and neighs.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109703, 'enUS', '<Carefully take a small blood sample.>', '<Carefully take a small blood sample.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109704, 'enUS', '<Obtain the blood with your dark magic.>', '<Obtain the blood with your dark magic.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (109705, 'enUS', 'Mind some comp\'ny? We\'re ere fer the same cause.', 'Mind some comp\'ny? We\'re ere fer the same cause.', 26124);
