INSERT INTO `broadcast_text_locale` VALUES (126410, 'enUS', 'Mistress, grant me your strength!', 'Mistress, grant me your strength!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126411, 'enUS', 'You are no match for this power!', 'You are no match for this power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126412, 'enUS', '', 'Let the blood flow!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126413, 'enUS', '', 'Welcome to the grave!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126414, 'enUS', '', 'Ah, yes. The sweet scent of death.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126415, 'enUS', '', 'Time to die!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126416, 'enUS', '', 'Death awaits!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126417, 'enUS', '', 'Time to send our foe to the gallows!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126418, 'enUS', '', 'Think I\'d let you have all the fun?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126419, 'enUS', '', 'Time to play!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126420, 'enUS', '', 'My axe grew hungry!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126421, 'enUS', 'Hail, Highlord. I am at your service.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126422, 'enUS', '', 'The grave is calling me back...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126423, 'enUS', '', 'Mind taking care of that for me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126424, 'enUS', '', 'Go ahead... I\'ll catch up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126425, 'enUS', 'Let us cast light upon this unholy place.$b$bHow may I assist you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126426, 'enUS', 'The forces within this forsaken city can only hide in the shadows for so long.$b$bI await your command.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126427, 'enUS', '', 'In the mood for some killing?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126428, 'enUS', 'Which paladin would you like to remove from your ranks?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126429, 'enUS', '', 'Do exactly as I say, child.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126430, 'enUS', 'Fiona Stormwell', 'Fiona Stormwell', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126431, 'enUS', 'Vindicator Dran\'aan', 'Vindicator Dran\'aan', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126432, 'enUS', 'Vindicator Edhri', 'Vindicator Edhri', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126433, 'enUS', 'Kralgon Lightbeard', 'Kralgon Lightbeard', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126434, 'enUS', 'Phillip Stormwell', 'Phillip Stormwell', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126435, 'enUS', 'Thelsia Oakthorn', 'Thelsia Oakthorn', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126436, 'enUS', '', 'I do not suffer fools gladly.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126437, 'enUS', '', 'Didn\'t even break a sweat.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126438, 'enUS', '', 'No match for the elements!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126439, 'enUS', '', 'You were a fool to cross paths with me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126440, 'enUS', '', 'I\'ll barely lift a finger to destroy you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126441, 'enUS', '', 'Feel the fury of the Grimtotem!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126442, 'enUS', '', 'These old bones need a rest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126443, 'enUS', '', 'I need to... catch my breath.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126444, 'enUS', '', 'I must visit the healing waters.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126445, 'enUS', '', 'Did you think you could do this alone?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126446, 'enUS', '', 'I have little time for foolishness!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126447, 'enUS', '', 'Need me to poison your blade?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126448, 'enUS', '', 'Galius my dear, you should cast the spell I taught you. AND DON\'T SCREW IT UP!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126449, 'enUS', 'Brother... why?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126450, 'enUS', 'I\'m... defeated?  That\'s... impossible...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126451, 'enUS', '', 'Hail, Highlord. I am at your service.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126452, 'enUS', '', 'Let us cast light upon this unholy place.$b$bHow may I assist you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126453, 'enUS', '', 'The forces within this forsaken city can only hide in the shadows for so long.$b$bI await your command.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126454, 'enUS', '', 'Which paladin would you like to remove from your ranks?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126459, 'enUS', 'Are ye ready to leave, High Priest? Just gimme the word an\' I\'ll point the sub back towards Dalaran.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126460, 'enUS', 'I am ready to head back to Dalaran.', 'I am ready to head back to Dalaran.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126461, 'enUS', 'I can take ye back whenever yer ready, High Priest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126462, 'enUS', 'We\'re not done yet!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126463, 'enUS', '', 'By my troth, this\'ll be the last time I go adventurin\' with a Bronzebeard!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126464, 'enUS', 'Many\'ll come this time. The machine surges with the Light.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126465, 'enUS', 'It pains me to see this ancient land tainted by such pestilent creatures. Stop them before they spread!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126466, 'enUS', 'Well done, hero! Our druids will see to the rest of this menace.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126467, 'enUS', 'Least there\'s no giant eye lasers this time, eh?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126468, 'enUS', 'Powerful warlocks serving the Legion have gathered in Felfire Pass. We must stop them and their fanatical master before they grow too strong.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126469, 'enUS', 'The allure of power is great, and many succumb to its call. Those warlocks gave us no choice but to end their sinister threat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126470, 'enUS', 'We sought to consecrate the ground where Tirion fell, but the Legion pushed us back. Your aid is needed, Highlord.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126471, 'enUS', 'Thanks to your help, we can complete our work and honor Tirion\'s life of sacrifice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126472, 'enUS', 'Good work. You have bought our forces the reprieve we needed to finish our task.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126473, 'enUS', 'The paladins of the Silver Hand are attempting a holy ritual in Felfire Pass, but the Legion is pushing back. Slay the demon leader so that we may sanctify this place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126474, 'enUS', 'This is the place. Kill abominations and smash any eggs you find!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126475, 'enUS', 'Break enough eggs, and the monstrosity that laid them will come looking for a fight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126476, 'enUS', 'Rasean Lightfall', 'Rasean Lightfall', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126477, 'enUS', 'Alawa Wisebrace', 'Alawa Wisebrace', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126478, 'enUS', 'Guldek Stoneshield', 'Guldek Stoneshield', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126479, 'enUS', 'Nalenn Heartreaver', 'Nalenn Heartreaver', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126480, 'enUS', 'Melhen Lightfury', 'Melhen Lightfury', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126481, 'enUS', 'Tayen Steelhide', 'Tayen Steelhide', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126482, 'enUS', 'Shadowblade, the demons are bringing in more hapless refugees from Suramar. You and your gallery of rogues must liberate them before they become fuel for the fel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126483, 'enUS', 'Every soul snatched from the fel forge weakens our enemy. Well done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126484, 'enUS', 'The demons are bringing in more hapless refugees from Suramar. Assist the rogues of the Council of Shadows in liberating the prisoners before they become fuel for the fel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126485, 'enUS', 'Every soul snatched from the fel forge deprives our enemy of fuel for their cause. Well done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126486, 'enUS', 'Open yer trap so I can fire, Oso!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126487, 'enUS', 'Make it stop! Please, make it stop!', 'Make it stop! Please, make it stop!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126488, 'enUS', 'My... mind... get out of my... mind.', 'My... mind... get out of my... mind.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126489, 'enUS', 'Get...out...of...my...head!', 'Get...out...of...my...head!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126490, 'enUS', 'I don\'t know anything...', 'I don\'t know anything...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126491, 'enUS', 'Why? Why are you doing this?', 'Why? Why are you doing this?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126492, 'enUS', 'Please... let me go. I have a family.', 'Please... let me go. I have a family.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126493, 'enUS', 'I will... never... submit...', 'I will... never... submit...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126494, 'enUS', 'I saw the demon slinking through a portal in all the chaos. Size of two tauren - maybe three!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126495, 'enUS', '', 'Oh, the calamity! The destruction! The lack of customer trust! $B$B<Glutonia pauses to make sure you\'re looking.>$B$BMaybe the familiar smell of ten gold coins would jog my memory!$B$BUnmarked, please.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126496, 'enUS', '', 'I\'ll tell ya what the Legion was after - my bottom line! Legion-grade protection wards ain\'t exactly cheap, sugar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126497, 'enUS', 'You got its attention! Here it comes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126498, 'enUS', 'You\'ve put a dent in their forces. Focus on the eggs now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126499, 'enUS', 'The beast that laid those eggs is in the cavern! Check the upper area!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126501, 'enUS', 'To me, heroes! Shield me with the Aegis while I tear this dreadlord from the shadows!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126502, 'enUS', 'The vaults have been breached!', 'The vaults have been breached!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126503, 'enUS', 'Doloria, my love! I have found you at last! Are you safe?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126504, 'enUS', '', 'Maximillian? How did you find me here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126505, 'enUS', 'Never fear, my love, we will hunt down these bandits and secure your freedom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126506, 'enUS', 'Come, Squire! I shall meet you in the mountains that are high.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126507, 'enUS', '', 'During our imprisonment, the Kirin Tor locked Kruul\'s heart away in Dalaran\'s vaults to prevent it from falling into Legion hands.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126509, 'enUS', 'Eitrigg, I need you to rally the other class orders to form a coalition against the Legion.', 'Eitrigg, I need you to rally the other class orders to form a coalition against the Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126510, 'enUS', 'You are saved, my love! Wait... something\'s not quite right.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126511, 'enUS', 'Horde warriors, move out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126512, 'enUS', 'Pardon me, good sir, have you seen my Doloria?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126513, 'enUS', 'Hmm, perhaps she\'s behind here...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126514, 'enUS', 'Aha! A clue! No, just some dust.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126519, 'enUS', 'Greetings, Battlelord. You are the strong leader we need in these troubling times.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126520, 'enUS', 'Lord Darius, I need you to rally the other class orders to form a coalition against the Legion.', 'Lord Darius, I need you to rally the other class orders to form a coalition against the Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126521, 'enUS', 'Greetings, Battlelord. You are the strong leader we need in these troubling times.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126522, 'enUS', 'Warriors of the Alliance! We have a job to do!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126523, 'enUS', 'We seem to be having a problem with over-sized eggs... well, not with the eggs per se, but the kraken hatching from the eggs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126524, 'enUS', 'She and her kvaldir jealously stole away many souls worthy of joining our ranks. They must be freed so they can at last assume their rightful places here in the sky.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126525, 'enUS', 'Normally I\'d let nature take its course... however, I\'d prefer not to fight an army of kraken and the Burning Legion at the same time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126526, 'enUS', '', 'Excellent! I can think of no one better suited to the task. We stand ready to inform you about all known threats.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126527, 'enUS', 'Ya must be blinder than a pirate with two eye patches.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126528, 'enUS', '', 'Umm, isn\'t that her with the mask? Oh wait, right, yes, no, haven\'t seen her.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126529, 'enUS', '', 'Hey Doloria, this one\'s looking for ya.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126530, 'enUS', '', 'Be quiet, Crixa!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126531, 'enUS', 'She is nowhere to be found. She must have been taken once more!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126532, 'enUS', '', 'In the other corner, a dwarf and his totally loyal companion who would NEVER harm its master. Ever.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126533, 'enUS', 'Cooldowns and debuffs have been reset.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126534, 'enUS', 'Good work taking care of those giants! The supplies you\'ve gathered will be a boon to the war effort.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126535, 'enUS', 'In the other corner, a dwarf and his totally loyal companion who would NEVER harm its master. Ever.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126536, 'enUS', 'What?! The gunpowder has been taken by harpies? I must gather my crew and head to Highmountain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126537, 'enUS', 'Bah! These vrykul think they can steal our gunpowder? They\'ve got another thing coming!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126539, 'enUS', '', 'Kruul returns? We must prepare an assault on the demon\'s lair. Karlain, please ensure the Illidari are given all the supplies we can spare.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126540, 'enUS', '', 'Thank you, $p. You have bought valuable time to deal with this threat.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126541, 'enUS', 'Crackers! I thought ye were me matey! Yer breakin\' my heart!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126542, 'enUS', 'These demons will pay for what they have done. This I vow!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126543, 'enUS', 'Send me to the forward camp at once! [Queue for Scenario]', 'Send me to the forward camp at once! [Queue for Scenario]', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126544, 'enUS', 'My mistress\' death will be avenged! No foul outlander shall control the kvaldir while I still draw breath!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126545, 'enUS', 'Helya... I failed you...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126546, 'enUS', 'Ahoy, $n. I\'m hopin\' fer a great explosion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126547, 'enUS', 'What\'s with the hozen?', 'What\'s with the hozen?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126548, 'enUS', 'Ye can\'t find any loyalty in th\' feathered folk. Crackers might\'ve left me shipwrecked, but I can\'t be a successful pirate captain without a proper matey! Nanners is me new partner, and he\'s been right handy!$b$bWho needs a bird, anyway? Nanners is a much more useful crewmate.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126549, 'enUS', 'How may I serve, $ct?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126550, 'enUS', 'Go gather the other class orders. We\'re going to Broken Shore.', 'Go gather the other class orders. We\'re going to Broken Shore.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126551, 'enUS', 'Of course, $ct. We will leave at once.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126552, 'enUS', 'Knights! The $ct needs us! Let\'s fly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126553, 'enUS', 'You fight with honor, Battlelord $n. I will report to Deliverance Point. See you there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126554, 'enUS', 'It was an honor to fight alongside you, Battlelord $n. I will report to Deliverance Point. See you there!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126555, 'enUS', 'First they invade our lands. Now the demons darken the skies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126556, 'enUS', 'The Burning Legion is a plague that sickens the land with its presence. The demons must be purged before their forces spread any further!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126557, 'enUS', 'Valuable supplies were lost beneath the ocean during our first assault. They would come in handy in our fight against the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126558, 'enUS', 'Unfortunately, sea giants have moved in to claim the goods. Slay the giants and scour the wreckage for our lost supplies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126559, 'enUS', 'The death of those foul creatures will stoke the fire of the Legion\'s wrath. Make haste to Black Rook Hold. We must press our attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126560, 'enUS', 'The Legion opened a portal that is spewing forth a swarm of foul imps. Champion, these tiny fiends must be eradicated!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126561, 'enUS', 'Impressive. Those impudent imposters imperiled important improvements.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126562, 'enUS', 'This always happens! I look the other way for one minute, and your goons have stolen our explosives!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126563, 'enUS', 'Hey, easy there, bub. I only swiped your bombs because you and your munchkin squad swiped ours first!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126564, 'enUS', '<Gasp!> I can\'t believe you would resort to name-calling. On BOAT DAY, of all days.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126565, 'enUS', '', 'Ahoy down there! You sailors want to party?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126566, 'enUS', 'ACH! I can\'t believe it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126567, 'enUS', 'We come all this way to celebrate Boat Day, and ye forgot the dang-blasted ALE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126568, 'enUS', 'Sorry.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126569, 'enUS', 'Hmm... I\'ll bet those pirates over yonder have some ale on \'em...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126570, 'enUS', 'The thoughtless partying of the other races has a drastic effect on this land\'s environment.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126571, 'enUS', 'All of their litter collects in this corner of the lagoon. We cannot abide this affront to nature.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126572, 'enUS', '<Sigh>... I was hoping to play some volleyball, too...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126573, 'enUS', 'You\'ve been given a free boat to use on the water\'s surface for the day. Boat safely!', 'You\'ve been given a free boat to use on the water\'s surface for the day. Boat safely!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126574, 'enUS', 'Well fought, $n. But there is more to be done before we can claim victory.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126575, 'enUS', 'Stay away, unless you want a firework to the face!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126576, 'enUS', '', 'Ack!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126577, 'enUS', 'Get off my boat, Horde scum!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126578, 'enUS', 'Get off my boat, Alliance scum!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126579, 'enUS', '', 'You want the boom? You got it, bozo!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126580, 'enUS', 'Grongo smash!', '', 26124);
