INSERT INTO `broadcast_text_locale` VALUES (126584, 'enUS', '|TINTERFACE\\ICONS\\SPELL_FIRE_FELHELLFIRE:20|t The Avatar begins to cast |cFFFF0404|Hspell:234724|h[Annihilation]|h|r!', '|TINTERFACE\\ICONS\\SPELL_FIRE_FELHELLFIRE:20|t The Avatar begins to cast |cFFFF0404|Hspell:234724|h[Annihilation]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126585, 'enUS', 'Greetings! This is the perfect place for arcane meditation, don\'t you agree?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126586, 'enUS', 'High Sorceror, I need you to persuade the other class orders to join us against the Legion.', 'High Sorceror, I need you to rally the other class orders to join us against the Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126587, 'enUS', '', 'Spirits guide you, $ct.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126588, 'enUS', 'I will open a portal to Arcanist Ryanna\'s  location. One moment.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126589, 'enUS', 'We must send news to the other class orders. We are going to the Broken Shore.', 'We must send news to the other class orders. We are going to the Broken Shore.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126590, 'enUS', '%s begins to channel |cFFFF0404|Hspell:234728|h[Arcane Annihilation]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126591, 'enUS', '', 'You have heard the $ct\'s words, children. Go to the other orders, gather their support! May the elements guide you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126592, 'enUS', 'The enemy must be stopped at all costs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126593, 'enUS', 'Let\'s begin the assault.', 'Let\'s begin the assault.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126594, 'enUS', 'The Legion will fail in its efforts.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126595, 'enUS', 'You cannot hide, Mephistroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126596, 'enUS', '|TInterface\\Icons\\ABILITY_CREATURE_CURSED_03.BLP:32|t Mephistroth uses |cFFFF0000|Hspell:233206|h[Shadow Fade]|h|r to conceal himself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126597, 'enUS', 'What can I help you with?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126598, 'enUS', 'Hmm... Laurence... let\'s see...\n\nOh yes, she was in the other day. She was looking for a staff with some very particular properties. A funny thing, really, because it seemed the kind of staff I would normally procure for one of my warlock customers.\n\nIt took me a few days to get it, but I found one and had it delivered to the Legerdemain Lounge at her request.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126599, 'enUS', '%s uses Dino Mojo on you, granting you an Adaptation!', '%s uses Dino Mojo on you, granting you an Adaptation!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126600, 'enUS', 'Here\'s the holy water, Grayson.', 'Here\'s the holy water, Grayson.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126601, 'enUS', 'Hey there. We brought some toys for you to borrow. Good luck.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126602, 'enUS', 'Uh... I don\'t think he was supposed to be so little.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126606, 'enUS', 'Free me from this torment!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126607, 'enUS', 'The hounds will devour us all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126608, 'enUS', 'Release me, stranger. I\'d rather spend eternity in Helheim than suffer the fate that awaits me here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126609, 'enUS', 'The final death... it comes for us all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126610, 'enUS', 'Freedom, at last!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126611, 'enUS', 'Thank you, stranger! May we meet again in the Halls of Valor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126612, 'enUS', 'I\'ll be food for the hounds no longer!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126613, 'enUS', 'Odyn bless you, stranger!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126614, 'enUS', 'These ancient forests must be cleansed of the Legion\'s presence. Give the word, heroes, and we shall begin the fight to save Val\'sharah!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126615, 'enUS', 'Defenders, move out! Now we take the fight to the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126616, 'enUS', 'Purge the Legion from our world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126617, 'enUS', 'You will fall, demons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126618, 'enUS', 'A wall of fel energy blocks our entry to the Warden tower. Move your team forward and destroy the Legion machines powering the barrier. We will hold this position while you strike.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126619, 'enUS', 'The fool! Her portal has overloaded and is tearing rifts into the Twisting Nether!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126620, 'enUS', 'I\'ll try to get it stable! Hold the demons off until I can stop them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126621, 'enUS', '', 'I reclaimed Arikara. Did you find the Doomstone?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126622, 'enUS', 'We sense you bear dangerous news from the Broken Shore. We await you at the Violet Citadel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126623, 'enUS', 'The barrier is down, but the Legion\'s evil still looms over us. If we secure the tower\'s apex, the Dreamweavers will provide the means to reach the demons\' vessel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126624, 'enUS', 'The key to securing the tower lies in striking down their leader within. We must take that tower!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126625, 'enUS', 'The Dreamweavers have lent their hippogryphs to carry you to the Legion\'s vessel. Eliminate the commander leading this assault, and the rest of these cowardly demons will retreat. Good luck, champions!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126626, 'enUS', 'Feel the power of the Light!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126627, 'enUS', 'In the Light, we are one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126628, 'enUS', 'We will not fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126629, 'enUS', 'I must regain my strength. Be cautious, champion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126630, 'enUS', 'Please be turnink your attenzhion to me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126631, 'enUS', 'Ve run a tight balloon here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126632, 'enUS', 'Zhere vill be no talkink, no jumpink, no basket-jostlink.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126633, 'enUS', 'NO HORSEPLAYINK!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126634, 'enUS', 'Zhese are zhe rules. Obey zhem, or be dealt vizh.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126635, 'enUS', 'Ve vill all have a very nice balloon ride, and ve are goink to enjoy it. Is zhat clear?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126636, 'enUS', 'Very goot. Now ve sit in silence for zhe next hour.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126637, 'enUS', 'The Night Watch knows a thing or two about tracking. In Duskwood, creatures lurk in every dark nook and cranny.$b$bSometimes you catch a glimpse of their beady little eyes, peering at you from the bushes.$b$b<The watcher tries to suppress a shudder.>$b$bWhy, just the other day I sensed an unfamiliar creature stalking me in the forest, watching from the shadows.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126638, 'enUS', 'I DESERVED ZHIIISSS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126639, 'enUS', 'A demon stronghold in Black Rook? Remove the fiends at once!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126640, 'enUS', 'Hurry to the northern end of the ramparts! A dark presence rises atop the keep!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126641, 'enUS', 'Farseer! Elementals of pure felfire scorch the land with their curses. Please, cleanse those who battle these creatures and purge the magic these monsters wield.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126643, 'enUS', 'Well done, Farseer. Your shaman can take it from here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126644, 'enUS', 'Hero, the shaman of the Earthen Ring are striving to counter intense fel curses plaguing this land. They need your aid in battle while they carry out their cleansing rituals.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126645, 'enUS', 'When our war against the Legion ends, let us hope the Earthen Ring finds a way to restore balance to this scarred land.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126646, 'enUS', '', 'Welcome aboard!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126647, 'enUS', '', 'Boy, are you guys in luck! This ain\'t gonna be your standard, sleepy, ultra-slow balloon ride.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126648, 'enUS', '', 'I\'ve got this baby tricked out with a chronal accelerator I bought super cheap! Time is literally gonna fly! What could go wrong?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126649, 'enUS', '', 'Uh-oh...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126650, 'enUS', '', 'I don\'t think we\'re in Nagrand anymore.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126651, 'enUS', '', 'We\'re in Nagrand? We\'re in Nagrand!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126652, 'enUS', '', 'I don\'t believe it! We traveled back in time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126653, 'enUS', '', 'Or is it forward in time? I never know. Either way, this is horrifying! I wanna go back!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126654, 'enUS', '', 'There\'s no time like home, there\'s no time like home, there\'s no time like home...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126655, 'enUS', 'Hop aboard, junior ballooners! Anybody want a balloon sticker? Here ya go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126656, 'enUS', 'We run the swankiest balloon in all of Azeroth! Ask anybody, they\'ll tell ya it\'s true!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126657, 'enUS', 'How\'s about some free drinks? Here ya go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126658, 'enUS', 'Today, we\'re flying over Azsuna, the jewel of the Broken Isles.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126659, 'enUS', 'Anybody for caviar? We got only the best!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126660, 'enUS', 'That\'s the Nar\'thalas Academy down there. A school for mages back in ancient times, I hear.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126661, 'enUS', 'Hot towel? Only the best for our customers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126662, 'enUS', 'Rungle, why don\'t ya heat up the barbecue, then get the fireworks ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126663, 'enUS', 'Sure thing, boss.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126664, 'enUS', 'Ya might be wondering how I can run such a classy balloon outfit and still turn a profit. Well, lemme tell ya.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126665, 'enUS', 'The trick is, ya don\'t reveal the price to the customer \'til they\'re up in the balloon with ya!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126666, 'enUS', 'That\'ll be 28,542 gold each--gratuity NOT included. Unless, of course, you\'d like Rungle here to break your legs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126667, 'enUS', 'Okay, boss. I put the fireworks on the barbecue, like you said.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126668, 'enUS', 'You did WHAT? Rungle, you bungled it again!!!!!!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126669, 'enUS', 'Welcome, friends, to the Nesingwary balloon tour o\' Highmountain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126670, 'enUS', 'Bought this balloon from a wee goblin. Claimed it\'d make a good perch fer shootin\' moose!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126671, 'enUS', 'Lately, though... eh, I dunno. Been feelin\' a bit down. Huntin\' don\'t bring the same joy it once did.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126672, 'enUS', 'Not even shootin\' critters from a hot air balloon can cheer me up.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126673, 'enUS', '$n, you got any?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126674, 'enUS', 'How \'bout you, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126675, 'enUS', 'Or even you, $n. Someone cheer me up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126676, 'enUS', 'Ach... looks like we\'re gonna come in pretty close. Everybody wave hello to Thunder Totem!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126677, 'enUS', 'Welcome aboard. Please sit back and enjoy the ride.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126678, 'enUS', 'Below, you will see Crystalsong Forest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126679, 'enUS', 'I\'m sad to say that despite all my travels, I never spent much time here. I couldn\'t tell you why.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126680, 'enUS', 'Ahh... but I do have fond memories of my time here in Northrend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126681, 'enUS', 'Just over that ridge lies Sholazar Basin. There I met tribes of wolvar and murloc oracles. Such strange creatures!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126682, 'enUS', 'After all the journeys I\'ve been on, I still remember that day the most clearly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126683, 'enUS', 'Please, pardon me for blathering on. No doubt the the tales of your own adventures are much more exciting!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126684, 'enUS', 'Mages of the Tirisgarde need help investigating the Felborne occupying the Weeping Terrace. This wand may come in handy for removing their magical enhancements.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126685, 'enUS', '', 'Netherlord, one of our warlocks has opened a portal to a Legion carrier brimming with demons. I suggest you show these vermin what it means to be the puppet of a greater power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126686, 'enUS', 'What about you, $n? To what exotic lands have you traveled?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126687, 'enUS', 'And you, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126688, 'enUS', 'Or you, $n? What tales of exploration do you hold dear?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126689, 'enUS', 'I\'ll let the three of you talk amongst yourselves. I need to make sure I don\'t crash this balloon into Dalaran.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126690, 'enUS', 'MORE balloon riders? Fine, come on in... I guess.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126691, 'enUS', 'This is the balloon ride to Halfhill. Please keep your hands and off-hands inside the basket at all times.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126692, 'enUS', '<Sigh>...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126693, 'enUS', '', 'Yes. Yes! YES! Not so fun to be on the receiving end, is it? Legion scum!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126694, 'enUS', 'Below us, to the right, you will see Pang\'s Stead. Be sure to keep an eye out for...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126695, 'enUS', 'You know what? That\'s it. I\'ve HAD it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126696, 'enUS', 'I have a confession I\'d like to make to the three of you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126697, 'enUS', 'I HATE balloons. Hated them all my life. All I ever wanted to do was be a shaman, but no! Apparently \"ballooning is in our blood\".', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126698, 'enUS', 'Is this good enough for you, father? Huh? ARE YOU PROUD OF ME NOW, DAD?!?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126699, 'enUS', '...Whew.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126700, 'enUS', 'Wow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126701, 'enUS', 'Feels good to get that off my chest. I\'ve never told that to anyone before.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126702, 'enUS', 'Now you know my secret. What about you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126703, 'enUS', '$n, give it a try. Tell us something you\'ve never told another soul before. Go ahead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126704, 'enUS', 'How about you, $n? Do you have any secrets burning a hole in your heart?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126705, 'enUS', 'I haven\'t forgotten about you, $n. Tell us a secret. We won\'t judge. This is a safe balloon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126706, 'enUS', 'That was the most cathartic balloon ride I\'ve ever had. It was even sort of fun. Thanks, all of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126707, 'enUS', '', 'Welcome aboard! Today I will be your guide on a tour of Stormheim.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126708, 'enUS', '', 'See all that stuff down there? That\'s... uh... that\'s Stormheim.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126709, 'enUS', '', 'Sorry, I\'m not a very experienced tour guide.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126710, 'enUS', '', 'So... any of you know some balloon party games?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126711, 'enUS', '', 'Ooh, ooh, I\'ve got one! It\'s called Kiss, Marry, Slay.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126712, 'enUS', '', 'It\'s easy. You just have to pick one person you would kiss, one you would marry, and one you would slay.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126713, 'enUS', '', 'I\'ll go first! Let\'s see...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126714, 'enUS', '', '$n, you\'ve got a nice face, and it looks like you\'re the only one here who brushes $Ghis:her; teeth. I would kiss you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126715, 'enUS', '', 'Hmm... I would slay $n. For obvious reasons.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126716, 'enUS', '', 'And that leaves $n to marry! I could live with that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126717, 'enUS', '', 'Okay, my turn\'s over. Somebody else go next!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126718, 'enUS', '', 'Alright, the balloon ride is over! That was FUN!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126719, 'enUS', 'Greetings! Please step into the basket... and prepare for the most educational balloon ride of your life.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126720, 'enUS', 'Today we will be taking a scenic balloon tour over the ancient city of Suramar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126721, 'enUS', 'But first, I will be taking attendance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126722, 'enUS', '$n? Is $n here?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126723, 'enUS', 'Please, just say \"here\".', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126724, 'enUS', 'Very well. Do we have a $n in attendance today?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126725, 'enUS', 'I see. And, last on the list is $n. That must be you, correct?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126726, 'enUS', 'Quite the talkative bunch. On with the tour!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126727, 'enUS', 'Suramar, as we all know, is home to the Nightborne elves.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126728, 'enUS', 'One must admit the magic of the Felborne possesses a certain... flare. A pity they abuse its potential. They met the end they deserved.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126729, 'enUS', 'Among other things, the Nightborne are known for being rather disagreeable.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126730, 'enUS', '$n, can you tell me what mind-altering substance the Nightborne use to relax?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126731, 'enUS', 'The substance they consume is, of course, mana.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126732, 'enUS', 'Consuming so much mana has had an effect on the Nightborne\'s bodies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126733, 'enUS', '$n. What specific feature of Nightborne anatomy has been \"enhanced\" by excessive mana use?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126734, 'enUS', 'Very close! The answer I was looking for was \"their skin\".', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126735, 'enUS', 'Final question, for $n. What is the traditional greeting used in Suramar City?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126736, 'enUS', 'Good guess! The correct answer is: \"An illusion! What are you hiding?\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126737, 'enUS', 'That\'s all for today. Thank you for flying... and study up next time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126738, 'enUS', '', 'We need a victim... er... volunteer to go through a portal and thin the ranks of demons on the other side. Talk to Tehd, he can provide a loaner demon to get you started.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126739, 'enUS', 'Hail, Highlord. Lord Grayson waits inside for your arrival.$b$bHe\'s currently overseeing the preparation for the purification of the barding.$b$bWhen you\'re ready to enter, I will escort you inside.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126740, 'enUS', 'I\'m ready to go.', 'I\'m ready to go.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126741, 'enUS', 'Spirits of the fallen cry out as Legion hounds drain their life essence. Free the spirits from their torment and purge the Legion filth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126742, 'enUS', 'The Halls of Valor echo with the songs of those you rescued. Magnificently done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126743, 'enUS', 'You must stop them...', 'You must stop them...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126744, 'enUS', 'Had to... get away! Had to... warn you...', 'Had to... get away! Had to... warn you...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126745, 'enUS', 'Take me to the top of Black Rook Hold.', 'Take me to the top of Black Rook Hold.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126746, 'enUS', 'There! The rifts are subsiding, but the portal remains too volatile to follow through.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126747, 'enUS', 'I can keep it open, but I\'ll need your help to stabilize it...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126748, 'enUS', 'Alright, I\'ll attempt to stabilize the portal. I hope you know what you\'re doing, $c!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126749, 'enUS', 'The runes seem to be working! The fel energy is subsiding...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126750, 'enUS', 'It is done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126751, 'enUS', 'Have you figured out these runes yet? We don\'t have much time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126752, 'enUS', 'The runes are activated. Stabilize the portal.', 'The runes are activated. Stabilize the portal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126753, 'enUS', 'Wait... something\'s wrong! I can\'t... control...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126754, 'enUS', 'Urgh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126755, 'enUS', 'Send me to the terrace.', 'Send me to the terrace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126756, 'enUS', 'Help us, $ct! The Legion is pushing us back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126757, 'enUS', 'Incoming!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126758, 'enUS', '', 'My \"master\" ordered me to help you, so try not to die.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126759, 'enUS', '', 'Do you expect me to do ALL of the work?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126760, 'enUS', '', 'It died. Pity. I might have had a use for that one.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126761, 'enUS', '', 'Ugh, they do not build these demons like they used to.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126762, 'enUS', '', 'It is as if these demons can\'t handle a bit of whip...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126763, 'enUS', '', 'Oh, that was a mean one.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126764, 'enUS', '', 'Mmm. I suppose I should have told him the safe word.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126765, 'enUS', '', 'Ooo... giving up already?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126766, 'enUS', '', 'If you find a cloak on this on, I want it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126767, 'enUS', '', 'I am cold. My outfit was meant for inferno worlds mostly on fire.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126768, 'enUS', '', 'For being on a ship of the Burning Legion, it is not very warm here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126769, 'enUS', '', 'Tehd promised we could burn this place to ash.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126770, 'enUS', '', 'If you see the Cazarian here speak up. He and I have unfinished...business.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126771, 'enUS', '', 'Ugh, I hate these ships. So drab, not enough chains.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126772, 'enUS', '', 'So you mortals think you can win against the Legion? That is so precious. You are like an adorable collection of rodents that haven\'t noticed the large predator stalking you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126773, 'enUS', '', 'You should join me here sometime when I am not bound by my contract. Oh, the things I could show you...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126774, 'enUS', '', 'You know, this whip isn\'t just for show. Say the word, break my contract, and I can show you what wonders it can do...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126775, 'enUS', '', 'I have waited a long time for this, Cazarian! Now the whip is in the other hand!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126776, 'enUS', 'Happy Balloon Day! Interested in a balloon ride?', 'Happy Balloon Day! Interested in a balloon ride?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126777, 'enUS', 'Yes!', 'Yes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126778, 'enUS', '|TInterface\\Icons\\spell_fire_felrainoffire:20|t The Legion ship has started a |cFFFF0404|Hspell:235085|h[Fel Bombardment]|h|r! Watch out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126779, 'enUS', 'You have dealt a heavy blow to the Legion, $n. The cleansing has begun. I await your return.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126780, 'enUS', 'Champion! We\'ve spotted the wreckage of a Horde airship. Secure its cargo before the Legion does.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126781, 'enUS', 'Our troops will make good use of these supplies. Well done, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126782, 'enUS', '[PH] A Legion ship materializes in the distance.', '[PH] A Legion ship materializes in the distance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126783, 'enUS', 'Return me to the terrace.', 'Return me to the terrace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126784, 'enUS', 'Enough of these games!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126785, 'enUS', 'Jubeka, are you there? Do not let them take me! I could not endure such a fate!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126786, 'enUS', '', 'Protect me, my children! I will give you the power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126787, 'enUS', 'Fhambar has the scent. Meet him on the other side of the portal and he\'ll lead you to your missing mage.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126789, 'enUS', '', 'Goldrinn has his prey cornered, but we should lend a hand. And by hand, I mean talon, claw... or other stabby thing!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126790, 'enUS', '', 'A fine hunt. Good work out there, Archdruid!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126791, 'enUS', '', 'Goldrinn needs our help! A demon decided to cheat the hunt by bringing in backup. Let\'s give him a taste of his own medicine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126792, 'enUS', '', 'Thanks for the help! Those demons are like gnats compared to Goldrinn, but with so many of them, who knows what could have happened...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126793, 'enUS', '', 'Kill the Imp Servants before they energize Agatha!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126794, 'enUS', '', 'Ugh! This demon invasion has destroyed so many habitats on the Broken Shore. The wildlife has nowhere to go!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126795, 'enUS', '', 'Crocolisks in Jagged Cove are encroaching on a sedge of saltwater cranes. Help me gather up those poor birdies so we can get them to safety!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126796, 'enUS', 'Enough! I return to my slumber!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126797, 'enUS', 'I tire of playing with the living.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126798, 'enUS', 'So... sleepy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126799, 'enUS', '', 'We\'ll get the cranes to safety. One day, when the Broken Shore is free of demons, their chicks\' chicks can finally be returned home.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126800, 'enUS', 'Fhambar patiently awaits the command to follow Levia\'s trail.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126801, 'enUS', 'Go find Levia, Fhambar!', 'Go find Levia, Fhambar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126802, 'enUS', 'Fhambar picks up Levia\'s trail!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126803, 'enUS', 'Attention, sporelings! Honorary sporelings! Attend the spectacle of the growing of Fshoo!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126804, 'enUS', 'Fshoo cannot survive long aboveground without your help. We need healers, medics, and mushroom-menders to protect her!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126805, 'enUS', 'Fshoo comes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126806, 'enUS', 'Fshoo has withered. May her spores sail forever on the Zangar winds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126807, 'enUS', 'Your arrival is well-timed. I expect the attack to come at any moment. Before it does, let me urge the two of you to return to Bradensbrook. This doesn\'t need to be your fight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126808, 'enUS', '', 'No way I\'m staying behind! The attacks on my town must be stopped!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126809, 'enUS', '', 'Better to kill \'em here than have \'em tramplin\' my crops! Let\'s be about it, elf.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126810, 'enUS', 'Lord Ravencrest may be derelict in his duties, but I am not. Soldiers, attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126811, 'enUS', '', 'So the Kirin Tor wishes to send me more playthings?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126812, 'enUS', '', 'While Levia has kept me quite satisfied, it would be rude of me to turn down such a gift.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126813, 'enUS', '', 'Come then, $c. Entertain me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126814, 'enUS', 'Archmage, I was impressed to learn you captured the demon Kathra\'natir within the Nightborne Soulstone. Devising a containment shield that allows us to harness his power safely was ingenious!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126815, 'enUS', '', 'NO! What have you done! I will make you suffer!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126816, 'enUS', '', 'My, you are a strong one. Breaking you will be ever so fun!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126817, 'enUS', '', 'No! It cannot be!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126818, 'enUS', 'I fear for the lives of our allies if something is not done about this fel reaver.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126819, 'enUS', 'Well done! With the fel reaver destroyed, a heavy blow has been struck to the Legion\'s forces.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126820, 'enUS', '', 'You shattered my illusion! Insolent wretch!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126821, 'enUS', '', 'I will hunt you down, $c! Mark my words!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126822, 'enUS', '', 'What... was... that!?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126823, 'enUS', 'We can expect more defenders within. With Lord Ravencrest gone, I would wager Erdris now occupies his chambers. He always thought very highly of himself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126824, 'enUS', 'Over here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126825, 'enUS', 'This gate is closing--look out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126826, 'enUS', '', 'Uhhhh! My head...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126827, 'enUS', 'You are mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126828, 'enUS', '', 'Dag blast it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126830, 'enUS', 'Though employing Kathra\'natir\'s prison to power Dalaran\'s defenses may not be a long-term solution, I am relieved that the demon\'s essence remains contained. Come, let us return to the Hall of the Guardian.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126831, 'enUS', '', 'You won\'t regret it, Archdruid! Ready to jump into action!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126832, 'enUS', 'The tormented dead are heeding the call of their demonic masters. Strike them down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126833, 'enUS', 'At every turn, the Burning Legion finds new ways to pervert the worlds they invade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126834, 'enUS', 'Drown in flame!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126835, 'enUS', 'Your pet lich professes to have a plan that requires the bones of those sacrificed to the fel. Help him if you think it will aid our cause, but keep that... thing... in check.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126836, 'enUS', 'An intriguing use of necromancy. Dark magic has its uses.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126837, 'enUS', '', 'Forgive me, Highlord. I... I need some time alone with my thoughts.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126838, 'enUS', 'Ah! Excellent specimens, Deathlord. Here, you may find this useful.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126839, 'enUS', 'Help! I am under attack!', 'Help! I am under attack!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126840, 'enUS', 'Citizens, aid me!', 'Citizens, aid me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126841, 'enUS', 'Assassins are here!', 'Assassins are here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126842, 'enUS', 'You will not take me down, villain!', 'You will not take me down, villain!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126843, 'enUS', 'Spies sent from the Legion are here!', 'Spies sent from the Legion are here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126844, 'enUS', 'Help! Help!', 'Help! Help!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126845, 'enUS', 'Death comes for me!', 'Death comes for me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126846, 'enUS', 'A rogue has infiltrated our city!', 'A rogue has infiltrated our city!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126847, 'enUS', '[OLD DNT] Quickly, $ct, we need you on the front lines!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126848, 'enUS', 'Gll grm grmurrmr?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126849, 'enUS', 'Useless! Stand aside while I do what you cannot, brother.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126850, 'enUS', 'Once again I must clean up your mess, brother!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126851, 'enUS', '', 'Be... behind you...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126852, 'enUS', '', 'Send Modera my regards, $c. And... thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126853, 'enUS', 'You will not stop us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126854, 'enUS', 'How pathetic! Syrenne has taken to compelling outsiders to do her bidding?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126855, 'enUS', '<Beckon the tadpole to follow you.>', '<Beckon the tadpole to follow you.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126856, 'enUS', 'Lexera does not take kindly to those who meddle in her business!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126857, 'enUS', 'Foolish mortals! Did you truly believe the Burning Legion would be so easily defeated?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126858, 'enUS', 'The Feltotem tauren are defiling the bodies of the fallen. No one deserves this fate. Lay them to rest, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126859, 'enUS', 'They gave their lives willingly, and have earned their rest. You have done well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126860, 'enUS', 'Prisoners are being held in this area. Do whatever you must to free them, champion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126861, 'enUS', 'Thanks to your efforts, the prisoners have both their freedom and their lives. Well done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126862, 'enUS', 'You made it! Praise Elune! I doubt we had much time left.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126863, 'enUS', 'Prepare yourselves. Erdris has been bewitched by the Legion and sees only enemies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126864, 'enUS', 'Hold a moment and catch your breath. Then we move.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126866, 'enUS', 'Behold the power of the fel!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126867, 'enUS', 'Careful, champion. Something is emerging from the portal... something powerful!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126868, 'enUS', 'Let us be about it, then.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126869, 'enUS', 'I cannot allow you to menace the living any longer, Erdris!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126870, 'enUS', 'Arise, soldiers! Embrace my gift and bring an end to the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126871, 'enUS', 'When the Legion vessel fell, it released what remained of the souls used in its creation. These souls wail in agony, lost in ceaseless pain. Please, free them from this torment!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126872, 'enUS', 'A spirit can endure much, but being consumed over time by fel energies is torment beyond the mortal mind\'s comprehension.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126873, 'enUS', 'The hold is... lost...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126874, 'enUS', 'I hope you have found peace, commander.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126875, 'enUS', 'Thank you, $n. We stopped the Legion from driving a wedge into our forces. The Kirin Tor will be pleased to learn of our victory. They gave me something to see your way back to them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126876, 'enUS', '', 'Pleasure working with you! Thanks for saving our town.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126877, 'enUS', '', 'Stop by Bradensbrook once in a while, plenty of birds left to shoot down!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126878, 'enUS', '', 'Act quickly! The Legion will soon overrun us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126879, 'enUS', '', 'Champion, I\'m glad I reached you. As Victoria no doubt informed you, we have located the imp mother, Agatha.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126880, 'enUS', '', 'By now, she has likely harnessed much of the power she stole from Levia. If so, there\'s no telling what she\'s capable of.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126881, 'enUS', '', 'Her location is well shielded, but our Kirin Tor mages should be able to open a portal to her for a short time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126882, 'enUS', '', 'The imp mother must be stopped! Good luck, champion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126883, 'enUS', 'Felbats! I detest those winged fiends! Not only do they harass our forces, they breed like rabbits. We need to thin their numbers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126884, 'enUS', '', 'You are too late! Levia\'s power is mine! Using her knowledge, my minions will infiltrate the Kirin Tor and dismantle it from the inside!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126885, 'enUS', '', 'Even now, my sayaad tempt your weak-willed mages. Your allies will surrender willingly to the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126886, 'enUS', '', 'But first, you must be punished for taking away my little pet.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126887, 'enUS', 'You have safeguarded our forces from a most unpleasant aerial assault. Hmm, perhaps the Illidari can make use of the juveniles you scared off...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126888, 'enUS', '', 'No... My pretties!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126889, 'enUS', '', 'You\'ll... pay for this! You\'ll... pay...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126890, 'enUS', 'A grim and dangerous task awaits us, $n. These two demanded to come along to \"make sure we got it done right\". They should not have to do this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126891, 'enUS', 'Let us deal with Commander Erdris. (use Jarod\'s free keystone.)', 'Let us deal with Commander Erdris. (use Jarod\'s free keystone.)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126892, 'enUS', 'Let us deal with Commander Can\'tomas. (requires Brimming Keystone)', 'Let us deal with Commander Can\'tomas. (requires Brimming Keystone)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126893, 'enUS', 'Welcome, Highlord $p. I\'ve been making preparations for the ritual to cleanse the barding. But so far, we\'ve been unable to secure the holy water.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126894, 'enUS', 'I sent in three of my best men to search for it, but they\'ve not returned. I fear for their safety.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126895, 'enUS', 'Perhaps someone of your skills would be better suited to recover the water. Please, take a pair of my paladins with you. Who knows what\'s still lurking in this place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126896, 'enUS', 'Your quest here is futile, paladin! This city shall spread death once again...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126897, 'enUS', 'This city belongs to the dead! And soon, so shall you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126898, 'enUS', 'You\'re too late to save your allies, paladin. Their souls fuel my army...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126900, 'enUS', 'Osseine shall grant you a swift death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126901, 'enUS', 'Take in the fumes of Pestulon and become one with my army!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126902, 'enUS', 'Feast on their entrails, Rotmouth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126903, 'enUS', 'They swarmed us. I... I tried to fight, but... Thank you Highlord for saving me.', 'They swarmed us. I... I tried to fight, but... Thank you Highlord for saving me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126904, 'enUS', 'It felt like my soul was being slowly ripped from my body. The necromancer, he... I must get to Grayson.', 'It felt like my soul was being slowly ripped from my body. The necromancer, he... I must get to Grayson.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126905, 'enUS', 'It\'s over?! Is this real? Highlord, it\'s you... I... I think I can make my way back. Thank you.', 'It\'s over?! Is this real? Highlord, it\'s you... I... I think I can make my way back. Thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126906, 'enUS', 'Excellent job recovering the water, Highlord. And you have my deepest thanks for saving my paladins.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126907, 'enUS', 'I\'ll apply the water to the barding. The rest, Highlord, lies with you and your weapon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126908, 'enUS', 'It is done! The barding is ready. Now we go to Rivendare\'s crypt to confront his specter.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126909, 'enUS', 'MOUNT UP, PALADINS! WE RIDE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126910, 'enUS', 'They\'ll bring up the rear while we\'re inside dealing with Rivendare.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126911, 'enUS', 'Where is his body?! It should be here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126912, 'enUS', 'Someone\'s taken it! This does not bode well...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126913, 'enUS', 'LOOKING FOR SOMETHING?! HA HA HA!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126914, 'enUS', 'You and your pathetic rabble will be added to my ranks of undead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126915, 'enUS', 'I, Raemien shall spread the plague over this land once more and all will bow before me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126916, 'enUS', 'The horse! Quickly, contain it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126918, 'enUS', 'The beast is bound, Highlord! The Light has favored us this day!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126919, 'enUS', 'Let us reconvene at the Sanctum of Light. There you can bard the beast safely.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126920, 'enUS', 'When you\'re ready to leave, speak with me and I\'ll see to our safe return.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126921, 'enUS', 'Let me know when you\'re ready to return to the order hall, Highlord and I\'ll see to our safe return there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126922, 'enUS', 'I\'m ready to return, Grayson.', 'I\'m ready to return, Grayson.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126923, 'enUS', 'Send me to Shadowsong. (100 Nethershards |TINTERFACE\\ICONS\\inv_datacrystal01.blp\n:16|t)', 'Send me to Shadowsong. (100 Nethershards |TINTERFACE\\ICONS\\inv_datacrystal01.blp\n:16|t)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126924, 'enUS', 'The naga pose a persistent threat to our efforts. We\'ve subdued one of their brutes to use against them. Ride it into battle against the naga!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126925, 'enUS', 'Well done. Turning the might of those big bruisers against their own was a... smashing idea!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126926, 'enUS', 'I... I live, but I\'m in no condition for battle. Hurry, champion, you must stop him!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126927, 'enUS', 'Alright, let\'s get this barding upgraded!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126928, 'enUS', 'First I gotta just smelt all this ore...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126929, 'enUS', 'Now if I just hammer the leystone in here... aaand here...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126930, 'enUS', 'And last but not least, the jewel!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126931, 'enUS', 'And there she is! Some of my finest work, if I do say so myself! You may take it now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126932, 'enUS', 'I\'m sure Grayson will be pleased to see what we accomplished. Send him my best.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126933, 'enUS', '', 'Train or perish. Out here, there are no second chances.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126934, 'enUS', '', 'Do you really believe your companions are ready to join our assault?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126935, 'enUS', 'While patience is a virtue, we no longer can afford its luxury. Prepare yourself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126936, 'enUS', 'Here are the materials you asked for, Alard.', 'Here are the materials you asked for, Alard.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126937, 'enUS', 'Again? It will take me ages to put this armor back on. Hmph.', 'Again? It will take me ages to put this armor back on. Hmph.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126938, 'enUS', 'The Legion is retreating! Leap from their vessel! Trust the hippogryphs to catch you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126939, 'enUS', 'Enough of this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126940, 'enUS', 'Descend upon our enemy and break their foothold on this plane! Leave them no ground to cling to and show them no quarter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126941, 'enUS', 'At most we have slowed our enemy. There will be more, and we must be ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126942, 'enUS', 'Your actions have awoken the spirits!', 'Your actions have awoken the spirits!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126943, 'enUS', 'Thank you, Grand Master.', 'Thank you, Grand Master.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126944, 'enUS', 'I was getting weaker by the second! Thank you.', 'I was getting weaker by the second! Thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126945, 'enUS', 'I\'m getting out of here!', 'I\'m getting out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126946, 'enUS', 'An aranasi has seized our troops to feed her deadly brood. Grandmaster, we must rely on your ability to slay that demon and Detox the captives to save them from the poison!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126947, 'enUS', 'Our troops are arriving back at camp safe, sound, and most importantly, free of poison. Well done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126948, 'enUS', 'Our troops were carried away by an aranasi to feed her foul brood! Kill these arachnids and gather their venom sacs so we can develop a cure to their toxins.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126949, 'enUS', 'Thanks to your help, we can now develop a reliable cure for this poison.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126950, 'enUS', 'Let us begin.', 'Let us begin.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126951, 'enUS', 'Pitiful.', 'Pitiful.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126952, 'enUS', 'It seems you are ready.', 'It seems you are ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126953, 'enUS', 'Again!', 'Again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126954, 'enUS', 'Try harder.', 'Try harder.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126955, 'enUS', 'Don\'t give up.', 'Don\'t give up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126956, 'enUS', 'Excellent strategy.', 'Excellent strategy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126957, 'enUS', 'You\'re improving.', 'You\'re improving.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126958, 'enUS', 'Good, but can you do it again?', 'Good, but can you do it again?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126961, 'enUS', 'The Burning Legion dares to assault Stormheim! Remove their foul presence from this land, champion, and glory shall be yours!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126962, 'enUS', 'The Legion tricked us! Their attacks were merely a diversion while they established a foothold upon the isle of Shield\'s Rest!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126964, 'enUS', 'Champion! Hold the line against the Legion\'s forces. Join our allies on the front and stand your ground!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126965, 'enUS', 'Rest for now, hero. You have earned it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126966, 'enUS', 'Hello, $c, do you wish to research more about your artifact? \n\nI would like to assist you, but it appears you might still be carrying Artifact Research Notes. Reading them will further your knowledge.', 'Hello, $c, do you wish to research more about your artifact? \n\nI would like to assist you, but it appears you might still be carrying Artifact Research Notes. Reading them will further your knowledge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126967, 'enUS', 'Alright, let\'s show these bozos a trick or two.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126968, 'enUS', 'I got the spells if you got the coin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126969, 'enUS', 'The Great Akazamzarak is ready for action!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126970, 'enUS', 'Ruins as old as these are packed with rarities that would delight any collector. Recover whatever relics ye can, but beware o\' the spirits still lingerin\' about!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126971, 'enUS', 'I\'ve got an ace up my sleeve!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126972, 'enUS', 'I think we\'ve done enough relic huntin\' fer one day. Let\'s give the spirits time ta settle down. Enjoy yer spoils!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126973, 'enUS', 'Let\'s go, everyone! We\'re heading to the next tavern!', 'Let\'s go, everyone! We\'re heading to the next tavern!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126974, 'enUS', 'Last call! I\'m opening a portal to the next tavern!', 'Last call! I\'m opening a portal to the next tavern!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126975, 'enUS', 'Welcome, welcome! Help yourself to some free drinks and food.', 'Welcome, welcome! Help yourself to some free drinks and food.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126976, 'enUS', 'Pick a card, any card!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126977, 'enUS', 'Need a hand?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126978, 'enUS', '', 'Damn them! Let\'s go!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126979, 'enUS', 'Prepare to eat fluffy, cotton-tailed death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126980, 'enUS', 'Oh, I got a trick for you, pal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126981, 'enUS', 'We can still be friends.', 'We can still be friends.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126982, 'enUS', 'I better be getting hazard pay for this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126983, 'enUS', 'It\'s not you, it\'s me.', 'It\'s not you, it\'s me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126984, 'enUS', 'Yeah, I know I\'m good.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126985, 'enUS', 'We should see other people.', 'We should see other people.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126986, 'enUS', 'I really should start charging by the corpse.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126987, 'enUS', 'That\'s what you get for not leaving a tip!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126988, 'enUS', 'It was only the one time.', 'It was only the one time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126989, 'enUS', 'I\'m billing you... for my repairs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126990, 'enUS', 'It was only the one time.', 'It was only the one time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126991, 'enUS', 'I thought it was you!', 'I thought it was you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126992, 'enUS', 'I\'m just not that into you.', 'I\'m just not that into you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126993, 'enUS', 'We are never ever getting back together.', 'We are never ever getting back together.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126994, 'enUS', 'I can do better.', 'I can do better.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126995, 'enUS', 'Ow! Stop hitting me! Hit the hero!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126996, 'enUS', 'I\'m charging extra for this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126997, 'enUS', 'Hope you don\'t mind if I take a cut.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126998, 'enUS', 'Screw this, I\'m making my own mage order... with Hearthstone... and succubi! On second thought, forget the mage order!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (126999, 'enUS', 'It is said that the remains of those buried in these ruins have magical properties. Let us put these rumors to the test! Recover their ancient bones and place them upon the ruined altar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127000, 'enUS', 'You should get checked.', 'You should get checked.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127001, 'enUS', 'How magnificent! Enjoy your creation before the magic fades.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127002, 'enUS', 'You ever need another one of those, come talk to me. I\'ll hook you up.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127003, 'enUS', '', 'The spirits within these ruins are bound to the Burning Legion through an ancient pact. Lay them to rest so that they may know peace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127004, 'enUS', '', 'Well done, champion. May their spirits finally know peace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127005, 'enUS', '', 'Highlord, the weapon you wield is needed to free these felbound spirits. Destroy their pendants and release my ancestors from their ancient curse.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127006, 'enUS', '', 'Thank you, Highlord. You have brought peace to my ancestors.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127009, 'enUS', '', 'You are right, S\'theno. Either we risk it or we are dead anyway. Slayer $n, we need to get to the main console upstairs!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127010, 'enUS', '', 'Cue awesome scene of the Fel Hammer destroying one of the Legion ships and the others jumping away!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127011, 'enUS', '', 'They should never have gotten sso close, Malevolence. Why did the Fel Hammer not fire on them?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127012, 'enUS', '', 'This is no time for nasty insinuations, S\'theno my dear. They took out our weapons first thing. If you help kill these foul creatures, we can see about making repairs!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127013, 'enUS', '', 'Cease your bickering! We have a ship to save!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127014, 'enUS', 'Why can\'t I send out the call for another Vanguard?', 'Why can\'t I send out the call for another Vanguard?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127015, 'enUS', 'Apologies, we only have enough troops to have a single Vanguard ready for deployment at a time. Once you have given your orders, we will be able to request another.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127016, 'enUS', '', 'Well, that was unfortunate. Surely someone with your skills can revive him?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127017, 'enUS', 'There are dangers ahead, traveler. A sickness has taken over the area.', 'There are dangers ahead, traveler. A sickness has taken over the area.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127018, 'enUS', 'Why am I unable to rally the Val\'kyr?', 'Why am I unable to rally the Val\'kyr?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127019, 'enUS', '', 'I\'m sorry, we cannot rally another assault until you have deployed your currently assembled troops.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127020, 'enUS', 'Why can we not summon another Infernal?', 'Why can we not summon another Infernal to unleash?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127021, 'enUS', '', 'Infernals are tricky things, and extremely powerful. We must be cautious with them. You still have a Phylactery in your possession, it would be foolhardy to hold more.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127022, 'enUS', 'Why are we not able to focus another crystal?', 'Why are we not able to focus another crystal?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127023, 'enUS', '', 'The Might of Dalaran can only be channeled once. You appear to still have a Focusing Crystal in your possession. Once you have unleashed it, my mage\'s will be able to form another for you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127024, 'enUS', 'You\'re too late, mortal! My command center has just arrived!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127025, 'enUS', 'Why are we unable to power the Rift Core?', 'Why are we unable to power the Rift Core?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127026, 'enUS', 'Harpies are distasteful enough, but harpies pledged to the Legion? Intolerable! Keep this new threat under control, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127027, 'enUS', '', 'We are simply not prepared to have multiple Rift Cores at once. You still have one in your possession. Once you have unleashed it, we will begin charging another.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127028, 'enUS', 'It seems you\'ve curbed their population growth. Good! We don\'t want such abominations spreading beyond these shores.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127029, 'enUS', 'Why are we unable to start another ritual?', 'Why are we unable to start another ritual?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127030, 'enUS', 'The Frost Crux is an item of immense power. It would be foolish to attempt to have more than one in your possession. Once you have unleashed the Frost Wyrm, we will be able to summon another.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127031, 'enUS', 'Prepare yourselves! The Legion will stop at nothing to capture this prize.', 'Prepare yourselves! The Legion will stop at nothing to capture this prize.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127032, 'enUS', 'We are ready.', 'We are ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127037, 'enUS', 'Gunners, open fire upon this mortal scum! Obliterate them with hellfire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127038, 'enUS', 'Ready your blades! The demons come!', 'Ready your blades! The demons come!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127039, 'enUS', 'The Aegis of Aggramar must be set in place. We begin on your signal, heroes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127044, 'enUS', 'You have my thanks.', 'You have my thanks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127045, 'enUS', 'The pain has lessened. Thank you, $n.', 'The pain has lessened. Thank you, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127046, 'enUS', 'The fog is finally lifting...', 'The fog is finally lifting...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127047, 'enUS', 'I am in your debt.', 'I am in your debt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127048, 'enUS', 'Your compassion will not be forgotten.', 'Your compassion will not be forgotten.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127049, 'enUS', 'I owe you my life, priest.', 'I owe you my life, priest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127050, 'enUS', 'I\'d better get back to the fight.', 'I\'d better get back to the fight.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127051, 'enUS', 'My vessel and I are needed elsewhere. Enjoy this... parting gift!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127052, 'enUS', 'Bring down the monstrosity! Nothing gets through!', 'Bring down the monstrosity! Nothing gets through!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127053, 'enUS', 'Champion! Our mages have pinpointed the location of Raest Magespear within the Twisting Nether!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127054, 'enUS', 'We have little information to work from, but it seems he found a way to use the energy of the Twisting Nether to enhance his necromantic magic.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127055, 'enUS', 'Karam insisted on being sent in ahead of you, but I fear he cannot face this challenge alone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127056, 'enUS', 'We will open a portal to his location, but can\'t hold it open long. Raest must be stopped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127057, 'enUS', '', 'Be shielded, heroes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127058, 'enUS', '', 'Must you always be so close, Roland?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127059, 'enUS', '', 'Surely you have other places to be, Roland?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127060, 'enUS', '', 'Doesn\'t this place remind you of home?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127061, 'enUS', 'Eredar conjurers are amassing dark forces from the Void. Stop them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127062, 'enUS', 'This world is dark enough without bringing more shadow into it. You have my thanks, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127063, 'enUS', '', 'Come, Roland. Let us leave this vile place before you get yourself sick again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127064, 'enUS', '|TInterface\\Icons\\SPELL_NATURE_UNRELENTINGSTORM.BLP:20|t%s begins casting |cFFFF0000|Hspell:235766|h[Lightning Nova]|h|r !', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127065, 'enUS', '', 'Shadowblade! Over here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127066, 'enUS', '', 'I consider myself an adept hunter of the undead, and even I didn\'t notice at first.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127067, 'enUS', '', 'But look what happens when I slice right here...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127068, 'enUS', '', 'It\'s not supposed to do that, and it\'s REALLY not supposed to be that color.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127069, 'enUS', '', 'He spoke in coherent thoughts, and his skin... so well-preserved. But a few telltale signs gave him away.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127070, 'enUS', '', 'It seems Legion dreadlords have found a way to craft undead operatives so lifelike that even I have difficulty detecting them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127071, 'enUS', '', 'The demons knew the Illidari would find them if they tried to infiltrate our cities again, so they sent in these... homunculi.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127072, 'enUS', '', 'Our agents have identified suspected homunculi in Silvermoon and the Exodar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127073, 'enUS', '', 'We can\'t risk you carrying out an assassination in an ally\'s city, but no one will question you taking down a target in Silvermoon. While you handle that mark, I\'ll head to the Exodar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127074, 'enUS', '', 'Remember... even though you\'re trying to help them, the Horde will assume you\'re attacking one of their own.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127075, 'enUS', '', 'Your first mark has established himself as a wealthy aristocrat who parades around the city in plain view. Be wary of his arcane golem! Separate them if you can... and keep it quiet.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127076, 'enUS', '', 'Ah, a clean twist of the knife... delightful! Your next mark awaits in the Undercity. I suggest entering from the sewers to the west.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127077, 'enUS', '', 'Your mark is having a tea party in the terrace above the Rogues\' Quarter. Be wary! Her allies are powerful spellcasters, as is she. You\'ll need to find a way to get up there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127078, 'enUS', '', 'That death scream... pure music! We have located the next mark in Thunder Bluff, hiding in the shadow of the tauren leader.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127079, 'enUS', '', 'There he is, by the bonfire. I hear he is a coward who trusts in his armor to protect him. Demonstrate how wrong he is!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127080, 'enUS', '', 'Mmm, delicious... like a knife in the dark! Your final mark will be found in Orgrimmar. This will be the most challenging target yet!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127081, 'enUS', '', 'There! In the most crowded place imaginable---the auction house. Be silent, strike true, and vanish quickly!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127082, 'enUS', '', 'Poor, pathetic marks! There\'s no place they can hide that the shadows cannot find them. Come, pay your colleagues a visit. Lord Ravenholdt has a gift for you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127083, 'enUS', '', 'We can\'t risk you carrying out an assassination in an ally\'s city, but no one will question you taking down a target in the Exodar. While you handle that mark, we\'ll see to the matter in Silvermoon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127084, 'enUS', '', 'Remember... even though you\'re trying to help them, the Alliance will assume you\'re attacking one of their own.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127085, 'enUS', '', 'Your first mark wears the guise of a humble priest, walking through the city in plain view. Be wary of his crystal golem! Separate them if you can... and keep it quiet.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127086, 'enUS', '', 'Ah, a clean twist of the knife... delightful! Your next mark will be found in Darnassus. I suggest entering through the village below Teldrassil.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127087, 'enUS', '', 'Your mark is throwing a garden party near the Warrior\'s Terrace. Be wary! Her allies are powerful spellcasters, as is she. And mind the guards.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127088, 'enUS', '', 'That death scream... pure music! We have located the next mark in Ironforge, hiding in the shadows of the dwarven council.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127089, 'enUS', '', 'There he is, against the wall. I hear he is a coward who trusts in his armor to protect him. Demonstrate how wrong he is!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127090, 'enUS', '', 'Mmm, delicious... like a knife in the dark! Your final mark will be found in Stormwind. This will be the most challenging target yet!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127091, 'enUS', '|TInterface\\Icons\\ABILITY_HUNTER_PET_RAPTOR.BLP:20|t%s |cFFFF0000|Hspell:235886|h[calls]|h|r to her pack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127092, 'enUS', '', 'Chin up, Roland. It appears help has arrived.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127093, 'enUS', 'What... what happened?', 'What... what happened?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127094, 'enUS', 'My head... get... get away from that thing!', 'My head... get... get away from that thing!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127095, 'enUS', 'I... I couldn\'t stop myself...', 'I... I couldn\'t stop myself...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127096, 'enUS', 'We must not falter, regardless of what the demons do.', 'We must not falter, regardless of what the demons do.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127097, 'enUS', '', 'You made a wise choice. We will accomplish much together.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127099, 'enUS', 'Raest may have escaped, but we will track him down. When that day comes, I hope I can count on your help once again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127100, 'enUS', 'Orgrimmar Invaders Slain: $1i', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127101, 'enUS', 'Defender of Orgrimmar', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127102, 'enUS', 'You will not leave here alive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127103, 'enUS', 'The Burning Legion cannot be stopped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127104, 'enUS', 'The contagion will consume you!', '', 26124);
