INSERT INTO `broadcast_text_locale` VALUES (127135, 'enUS', 'Undercity Invaders Slain: $1i', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127136, 'enUS', 'Silvermoon Invaders Slain: $1i', '', 26124);
