INSERT INTO `broadcast_text_locale` VALUES (127349, 'enUS', 'I can take you back to Skyhold when you\'re ready to leave, Battlelord.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127350, 'enUS', 'I can take you back to Skyhold when you\'re ready to leave, Battlelord.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127351, 'enUS', '', 'Welcome to the Kirin Tor Tavern Crawl, $n.  Help yourself to all the drinks and free food you can handle.  We\'ll be leaving for the next tavern in a couple minutes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127352, 'enUS', 'It is an honor to be here for you, Battlelord. I hope you are victorious in your trials.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127353, 'enUS', 'What have you brought with you?', 'What have you brought with you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127354, 'enUS', 'I\'m ready to go back to Skyhold.', 'I\'m ready to go back to Skyhold.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127355, 'enUS', 'Teleport me to the tavern crawl.', 'Teleport me to the tavern crawl.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127356, 'enUS', 'I\'m ready to begin the trial, Odyn.', 'I\'m ready to begin the trial, Odyn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127357, 'enUS', 'The Legion corrupts everything it touches! Destroy their fel corruptors before all of Stormheim is tainted!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127358, 'enUS', 'Well done! Scars of the Legion\'s presence may linger for a time, but fade they shall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127359, 'enUS', 'The shattered core of a fallen fel reaver is giving rise to a host of aberrations! Recover the core fragments before the Legion can salvage their creation!', '', 26124);
