INSERT INTO `broadcast_text_locale` VALUES (127362, 'enUS', '', 'Your kind will be cleansed from this world!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127363, 'enUS', '', 'You will burn in power you cannot comprehend!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127364, 'enUS', 'Our rumbler today is a beefy mammal with a taste for vengeance. He doesn\'t want to help you... he wants to kill you. It\'s the enmity moose, Grief Warden!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127365, 'enUS', 'Our rumbler today is pack of pecky penguins from the polar precipices of Northrend. Beware the Slippy Stampede!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127366, 'enUS', 'These three pandaren have a challenge for you... pull one of your beer carts to the middle of the ring before they can pull theirs. Winning team gets to drink it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127367, 'enUS', 'H-I-P-P-O spells death! Try not to be trampled by our next rumbler, Senya!', '', 26124);
