INSERT INTO `broadcast_text_locale` VALUES (127378, 'enUS', 'I can\'t believe it... they won! It\'s a miracle! Just kidding, this happens all the time.', 'I can\'t believe it... they won! It\'s a miracle! Just kidding, this happens all the time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127379, 'enUS', 'The rumbling has ceased... and our heroes are the champions! Big, bulging purses of gold to all involved.', 'The rumbling has ceased... and our heroes are the champions! Big, bulging purses of gold to all involved.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127380, 'enUS', 'Hmph. I\'m pretty sure more than one of you wasn\'t paying attention.', 'Hmph. I\'m pretty sure more than one of you wasn\'t paying attention.', 26124);
