INSERT INTO `broadcast_text_locale` VALUES (127524, 'enUS', 'What is it?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127525, 'enUS', 'My rage is growing...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127526, 'enUS', 'I\'m about to lose control... and I think I like it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127527, 'enUS', 'Free at last!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127528, 'enUS', 'So many things to burn, hehehe!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127529, 'enUS', 'Where to go now...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127530, 'enUS', 'I\'m gonna have so much fun! Hahaha!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127531, 'enUS', 'Do you have any Artifact Research already prepared?', 'Do you have any Artifact Research already prepared?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127532, 'enUS', 'You are not worthy of the sun!', '', 26124);
