INSERT INTO `broadcast_text_locale` VALUES (127535, 'enUS', '|TInterface\\Icons\\ABILITY_CREATURE_DISEASE_05.BLP:20|tA demonic hand from beyond begins to cast |cFFFF0000|Hspell:235578|h[Grasp from Beyond]|h|r!', '|TInterface\\Icons\\ABILITY_CREATURE_DISEASE_05.BLP:20|tA demonic hand from beyond begins to cast |cFFFF0000|Hspell:235578|h[Grasp from Beyond]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127536, 'enUS', '|TInterface\\Icons\\ability_fixated_state_red:20|tKaram starts chasing you again!', '|TInterface\\Icons\\ability_fixated_state_red:20|tKaram starts chasing you again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127537, 'enUS', '|TInterface\\Icons\\SPELL_MAGE_RUNEOFPOWER.BLP:20|tRaest projects a |cFFFF0000|Hspell:236460|h[Rune of Summoning]|h|r nearby!', '|TInterface\\Icons\\SPELL_MAGE_RUNEOFPOWER.BLP:20|tRaest projects a |cFFFF0000|Hspell:236460|h[Rune of Summoning]|h|r nearby!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127538, 'enUS', '|TInterface\\Icons\\ACHIEVEMENT_BOSS_GENERALVEZAX_01.BLP:20|tA Rune of Summoning conjures a |cFFFF0000|Hspell:236461|h[Thing of Nightmare]|h|r to chase you down!', '|TInterface\\Icons\\ACHIEVEMENT_BOSS_GENERALVEZAX_01.BLP:20|tA Rune of Summoning conjures a |cFFFF0000|Hspell:236461|h[Thing of Nightmare]|h|r to chase you down!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127539, 'enUS', 'Take down Dread Commander Arganoth! We will deal with the rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127540, 'enUS', '', '[OLD DNT] Lord Illidan, it is nice of you to finally arrive... Wardens, let\'s do the real work while the demon hunters play with their bats.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127541, 'enUS', '|TInterface\\Icons\\ability_fixated_state_red:20|tKaram starts chasing you one last time!', '|TInterface\\Icons\\ability_fixated_state_red:20|tKaram starts chasing you one last time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127542, 'enUS', 'Stay strong in this time of unrest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127543, 'enUS', 'You are a beacon of light in the darkness.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127544, 'enUS', 'We are a force of balance in a chaotic ocean.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127545, 'enUS', 'We serve all of Azeroth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127546, 'enUS', 'Evil cannot take root if our hearts stay pure.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127547, 'enUS', 'We will not rest until the Legion is defeated.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127548, 'enUS', 'Please, leave me be. I am meditating.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127549, 'enUS', 'You are making it very difficult for me to control my thoughts, little one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127550, 'enUS', 'I learned a new technique called \"Kick the Hornet\'s Nest!\" Shall I demonstrate it on you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127551, 'enUS', '', 'Have you had any brew today?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127552, 'enUS', 'Filthy slime! Slow down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127553, 'enUS', 'Come back! I need your core fragments!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127554, 'enUS', '', 'Good brew requires an abundance of love.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127555, 'enUS', 'Why slime move so fast?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127556, 'enUS', '', 'One must always make time for new brews.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127557, 'enUS', '', 'Nothing beats a brew shared between friends.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127558, 'enUS', '', 'You never know what wonders you will see!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127559, 'enUS', '', 'May you sample many brews, my friend!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127560, 'enUS', '', 'That is quite enough, thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127561, 'enUS', '', 'Is your head as empty as your mug?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127562, 'enUS', '', 'Best not test my patience! The storm answers my call!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127563, 'enUS', 'How long was I stuck like that?', 'How long was I stuck like that?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127564, 'enUS', 'Thank you, $ct. I\'m going back to camp!', 'Thank you, $ct. I\'m going back to camp!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127565, 'enUS', 'I\'m never stepping foot near another basilisk again!', 'I\'m never stepping foot near another basilisk again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127566, 'enUS', 'We sent troops to survey this area, only to have them fall prey to the stares of basilisks! Use this magical salve to free our stone-bound scouts.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127567, 'enUS', 'The salve worked? I mean... yes, of course it worked. Nicely done, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127570, 'enUS', 'You paladins are so predictable. Come, let us see what power your blade truly possesses...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127571, 'enUS', 'This land will never heal...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127572, 'enUS', 'You will not interrupt our work. This ground belongs to the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127582, 'enUS', '', 'D\'Bynn and I will make our way to the lodge, should you need us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127583, 'enUS', '', 'Look out, folks, Klunk\'s gonna klunk you up!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127584, 'enUS', 'Look out, folks, Klunk\'s gonna klunk you up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127585, 'enUS', '', 'Together we will protect our home from the Burning Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127586, 'enUS', '', 'We will not be stopped!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127587, 'enUS', 'Such massive Legion war machines are ill-suited to this terrain. They should think twice before sending another!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127588, 'enUS', 'Slayer, you are needed on the front lines. Rip the essences from the demons pouring forth from the Tomb and use these empowered souls to sunder the pit lord in two!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127589, 'enUS', 'Pit lords. Bah! They are arrogance made flesh. The essences from those fallen demons may prove useful in further battles.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127590, 'enUS', 'The fewer fel-corrupted beasts, the better.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127591, 'enUS', 'You seek the commander? Then face me, weaklings!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127592, 'enUS', '', 'A threat must be dispatched. Be swift about it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127593, 'enUS', '', 'A threat looms near. Eliminate the target.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127594, 'enUS', '', 'Time to deliver a message to the Legion. Send this one back in pieces!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127595, 'enUS', '', 'We have much work to do. Grant this demon a swift death.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127596, 'enUS', '', 'Our scouts report a significant threat in this area. Wipe it out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127597, 'enUS', '', 'Just ahead... a new target. Send it screaming back to the Twisting Nether!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127598, 'enUS', '', 'Demons multiply like rats. Slay this one before it summons more of its foul kin into our world!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127599, 'enUS', '', 'A potent threat lurks nearby. Seek it out and destroy it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127600, 'enUS', '', 'Well struck, champion. Remain on your guard.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127601, 'enUS', '', 'A fitting end to a vile invader.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127602, 'enUS', '', 'Message delivered!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127603, 'enUS', '', 'That was more mercy than the fiend deserved. On to the next target!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127604, 'enUS', '', 'Impressive! That wretch didn\'t stand a chance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127605, 'enUS', '', 'Well done! Your skills are sharper than any weapon of the Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127606, 'enUS', '', 'Victory by victory, we push back against  the tide of this invasion. Well done!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127607, 'enUS', '', 'Another menace eradicated. Excellent!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127608, 'enUS', 'This place is fraught with demon filth. A prime target approaches!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127609, 'enUS', 'The Legion has unleashed another of its pawns. Dispose of this threat at once.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127610, 'enUS', 'This agent of the Legion underestimates your might. Make plain the error of its ways.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127611, 'enUS', 'A target nears. Send this filth back to the Nether!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127612, 'enUS', 'A malignant foe is near. Do what must be done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127613, 'enUS', 'I sense a nearby target. Strike swiftly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127614, 'enUS', 'Ah yes, the unmistakable stench of Legion filth. Seek out the source and destroy it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127615, 'enUS', 'Another of the Legion\'s minions draws near. Eradicate it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127616, 'enUS', 'The growth of your power is most impressive. The Legion knows your name... and fears it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127617, 'enUS', 'Every blow struck against the Legion forces them closer to their end.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127618, 'enUS', 'This game will soon be at an end. The Burning Legion will fall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127619, 'enUS', 'Relish the satisfaction of dispatching yet another Legion lapdog!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127620, 'enUS', 'An impressive victory. Your resolve remains unwavering.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127621, 'enUS', 'The Legion cannot run or hide. We will hunt them down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127622, 'enUS', 'That fiend earned its death a thousand times over. Now it shall suffer the wrath of its vile master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127623, 'enUS', 'Savor that moment when the rage in a demon\'s eyes gives way to fear. We will break them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127625, 'enUS', 'Greetings, warlock. I come before your council with a discovery I believe you will find intriguing.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127626, 'enUS', 'Much time has passed since we first pierced the veil into the plane of Xoroth, but a new opportunity beckons us back to the realm of the dreadsteeds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127627, 'enUS', 'Meet with me in Dreadscar Rift, and I will explain.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127628, 'enUS', 'I\'m glad to make your acquaintance, Netherlord.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127629, 'enUS', 'Why have you called me here?', 'Why have you called me here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127630, 'enUS', 'Let me cut right to the chase:\n\nEver since I was \"displaced\" by the Blackrock orcs in the Burning Steppes, I have continued my research into the dreadsteeds of Xoroth in hiding. During one of my rituals, I managed to capture an odd demon from the other side - one of the ones we call a \"wyrmtongue.\"\n\nAfter several weeks of questioning, the demon revealed that Lord Hel\'nurath has been working to create a more powerful steed for the fel knights of Xoroth.\n\nHe calls it the Wrathsteed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127631, 'enUS', 'Go on...', 'Go on...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127632, 'enUS', 'While the wyrmtongue knew little of the steed itself, it is clear that Hel\'nurath has thus far only been able to create one. He has gifted it to his greatest general, the fel knight Arax\'ath. The tricky part is that Arax\'ath seldom leaves Xoroth, preferring to command his armies from afar.\n\nHowever, I think we might be able to draw him out. For that, I will require your help.\n\nAre you up to the challenge?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127633, 'enUS', 'The Wrathsteed will be mine!', 'The Wrathsteed will be mine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127634, 'enUS', 'Excellent. I knew you would not disappoint.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127635, 'enUS', 'Now we have preparations to make.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127636, 'enUS', 'In order to draw out Arax\'ath, we will need to create a portal capable of a sustained link to Xoroth. This will be far more difficult than our standard portals, and will require substantial resources.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127637, 'enUS', 'With the materials you\'ve gathered, we\'re well on our way to claiming the Wrathsteed for our own. \n\nWhile we work on constructing the portal, you should focus your efforts on obtaining a lure that will draw Arax\'ath and the Wrathsteed through.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127638, 'enUS', 'Ooh... that\'s nice... Wait, what did you want..?', 'Ooh... that\'s nice... Wait, what did you want..?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127639, 'enUS', 'Oh my... what lovely... hooves you have...', 'Oh my... what lovely... hooves you have...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127640, 'enUS', 'Well... if you put it that way...', 'Well... if you put it that way...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127641, 'enUS', 'Our leader..? I don\'t know...', 'Our leader..? I don\'t know...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127642, 'enUS', 'What? I\'m not leader enough for you?', 'What? I\'m not leader enough for you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127643, 'enUS', 'Leader, you say..? You can boss me around any time...', 'Leader, you say..? You can boss me around any time...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127644, 'enUS', '...enough of this! I\'ll end you, demon!', '...enough of this! I\'ll end you, demon!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127645, 'enUS', '...no... I will not... betray the Legion!', '...no... I will not... betray the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127646, 'enUS', '...what am I saying? Die, warlock!', '...what am I saying? Die, warlock!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127647, 'enUS', 'You want... our leader? Of course... anything for you...', 'You want... our leader? Of course... anything for you...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127648, 'enUS', 'Seek Zuriwa... She watches... from her tower.', 'Seek Zuriwa... She watches... from her tower.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127649, 'enUS', 'Wait... what am I doing!? Zuriwa will have my head! Get away from me!', 'Wait... what am I doing!? Zuriwa will have my head! Get away from me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127650, 'enUS', '', 'I will feed ya to tha dreadsteeds!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127651, 'enUS', '', 'Hel\'nurath will hunt ya, $c...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127652, 'enUS', 'Now, let us pierce through the Twisting Nether and take what is ours!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127653, 'enUS', 'Rak-ath varus mir\'enon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127654, 'enUS', 'Vae Xoroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127655, 'enUS', 'Ah, good to see you again, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127656, 'enUS', 'What is this? A mortal wishes to peer into my realm?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127657, 'enUS', 'The way is open, warlock! Activate the calling stone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127658, 'enUS', 'What?! You dare try to summon my steed?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127659, 'enUS', 'My hounds will rend the flesh from your bones!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127660, 'enUS', 'Careful, warlock! The bite of these felhunters is venomous to mortals. It should pose no problem for your demons, though!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127661, 'enUS', 'Acolytes! Rain fire upon this insect!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127662, 'enUS', 'Arax\'ath is protecting his acolytes with a fel shield! You\'ll need to dispel it to take them out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127663, 'enUS', 'The acolytes are forming a ritual circle. Whatever you do, don\'t leave enough of them alive to complete it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127664, 'enUS', 'Are they insane?! They\'ll destroy us all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127665, 'enUS', 'My worthless minions may have fallen, but I will not let you prevail.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127666, 'enUS', 'I will cut you down myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127667, 'enUS', 'The Wrathsteed will never be yours, mortal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127668, 'enUS', 'Impossible...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127669, 'enUS', 'Quickly, enslave the Wrathsteed before it escapes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127670, 'enUS', 'Excellent work! We must convene the council. Only with their combined strength can we bind a beast of such power.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127671, 'enUS', 'So, this is where these infernal pickpockets take their trinkets.$b$bWell, while we\'re here we may as well get some of it back. I made you a theft/shopping list of things to go get.\n\nThis would be the part where some magic wielding illusionist tries to make you look like one of them. Bah! Illusions. No commitment.\n\nI am going to TRANSFORM you into one of these things. How is your method acting?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127672, 'enUS', 'Transform?', 'Transform?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127673, 'enUS', 'How did you beat me through the portal?', 'How did you beat me through the portal?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127674, 'enUS', 'Because I know more about demonic travel than you. Get better.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127675, 'enUS', '', 'You are no match for me and my hounds!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127676, 'enUS', '', 'You will never stand a chance against the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127677, 'enUS', '', 'You WILL give me what I want, demon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127678, 'enUS', '', 'I will not be denied. Speak.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127679, 'enUS', '', 'What are they planning, demon?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127680, 'enUS', '', 'I tire of these games. Speak, or die.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127681, 'enUS', '', 'Speak!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127682, 'enUS', '', 'I grow bored. Tell us what we want to know or face your doom.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127683, 'enUS', 'You call that a big pack? Ha! I know squiggs and snifflebits that can carry more than you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127684, 'enUS', 'You have a nose that is ripe for biting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127685, 'enUS', 'Leave my shinies alone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127686, 'enUS', 'Shush. The auditors may hear you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127687, 'enUS', 'What is wrong with your face?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127688, 'enUS', 'Snitchlip and Flivvelskag were sent into the maze and never heard of again. Move on or the auditors will send both of us into the maze.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127689, 'enUS', '', 'You will not be rid of the Grimtotem so easily, child. We will earn our place.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127690, 'enUS', 'Who sent you? Was it Ragchomp? Tell him it was Trickletooth that took his shiny. It wasn\'t me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127691, 'enUS', 'Your treasures are small and dull. You should give them to me so that I might \"throw them away.\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127692, 'enUS', 'Your end is near.', 'Your end is near.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127693, 'enUS', 'You dare interrupt a servant of the Legion!', 'You dare interrupt a servant of the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127694, 'enUS', 'Your efforts are in vain.', 'Your efforts are in vain.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127695, 'enUS', 'Now you meet your end!', 'Now you meet your end!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127696, 'enUS', '', 'You will not regret this choice, Shadowblade. I will take out any Legion demon or conspirator who crosses my path!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127697, 'enUS', 'That\'s twice I\'ve almost been killed by demons. They will not get a third chance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127698, 'enUS', 'Ah, $n. I knew you would make it. I\'ll be ready to face the Legion in no time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127699, 'enUS', 'Get back to work.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127700, 'enUS', 'Know your place or I shall remind you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127701, 'enUS', 'You dare speak to me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127702, 'enUS', 'Get back to work or I shall have you scrubbing the cages of the pits.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127703, 'enUS', 'Are you trying to sneak away with something?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127704, 'enUS', 'Hail, $ct $n. My fury against the Burning Legion is unending!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127705, 'enUS', 'Do not even try to steal anything. We are watching. We are always watching.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127706, 'enUS', 'Something seems... off... about you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127707, 'enUS', 'You dare interrupt me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127708, 'enUS', 'The Legion is corrupting the wolves of the Howling Hills to attack Skyhorn. These foul rituals must be stopped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127709, 'enUS', 'Hail, $c. \n\nI know of Toryl\'s edict - you will know no harm by my blade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127710, 'enUS', 'Tread carefully, $c. The vrykul of Jandvik are wary of outsiders.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127711, 'enUS', 'I am searching for the vrykul mentioned in these scrolls.', 'I am searching for the vrykul mentioned in these scrolls.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127712, 'enUS', 'I know little of prophecies, but we have seen new faces in Jandvik since the fall of Skovald. \n\nYou should speak to Priestess Bryna. Her dwelling is in the center of the town. She may know of this prophecy you speak.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127713, 'enUS', 'I do not know why you dabble in vrykul prophecy, but the scrolls you carry look genuine. I will alert the vrykul of Jandvik to allow you to pass so you may search for the one you seek. \n\nPriestess Bryna is the most scholarly of the village. Perhaps you should seek her out?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127714, 'enUS', 'Poor pups! At least you saved them from becoming slaves of the Legion. Thank you, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127715, 'enUS', '', 'I heard Slodi\'s horn. I take it you are the outsider he was announcing?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127716, 'enUS', 'Slodi mentioned you could help find the vrykul mentioned in these scrolls.', 'Slodi mentioned you could help find the vrykul mentioned in these scrolls.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127717, 'enUS', '', 'I am familiar of the Valkyra prophecy of the Faldrottin, but I have not read their text. Let me see the scrolls you found.\n\n<Bryna pores through the scrolls\' text.>\n\nIf these scrolls are correct, that would mean we may have the heir of Skovald in our midst! Regardless of what the Valkyra believe, you would be doing us a service by removing her from here.\n\nSeek out Yngvild in the tower overlooking the town. She keeps a keen eye on the comings and goings of Jandvik, and would have noticed if anyone of note has visited lately.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127718, 'enUS', '', 'I\'m keeping my eye on you, outsider.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127719, 'enUS', 'Have any vrykul passed through here that struck you as odd?', 'Have any vrykul passed through here that struck you as odd?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127720, 'enUS', '', 'As a matter of fact, yes there is.\n\nA wandering shieldmaiden came through just recently, and has set up camp outside of town. She stayed only long enough to purchase supplies and avoided all eye contact while she was here.\n\nI was going to check on her to find out her business here, but you are welcome to go in my stead. You\'ll find her camped on the beach to the east.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127721, 'enUS', 'I must pay for my mistakes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127722, 'enUS', 'It is an honor to walk beside you, Highlord.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127723, 'enUS', 'I will prove myself worthy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127724, 'enUS', 'You will be judged!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127725, 'enUS', 'I will show no mercy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127726, 'enUS', 'Bandu thoribas!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127727, 'enUS', 'Judgment must be swift and true.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127728, 'enUS', 'We will prevail.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127729, 'enUS', 'Our will is strong.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127730, 'enUS', 'My spirit weakens...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127731, 'enUS', 'I require a moment\'s rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127732, 'enUS', 'You must continue on without me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127733, 'enUS', 'I fight for the Highlord!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127734, 'enUS', 'I will do what is asked of me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127735, 'enUS', 'Foolish girl. These bones answer to me, not your misguided honor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127736, 'enUS', '', 'So, you will not face me in single combat? The hallowed bones of our ancestors would balk at such dishonor!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127737, 'enUS', '', 'If you will not honor me, then I will break you! Champion, help me put down this cur!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127738, 'enUS', 'Ishnu-alah.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127739, 'enUS', 'Greetings, friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127740, 'enUS', 'It is good to see you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127741, 'enUS', 'Safe travels.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127742, 'enUS', 'Elune-adore.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127743, 'enUS', 'Ande\'thoras-ethil.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127744, 'enUS', 'Is this how I must pay for my mistakes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127745, 'enUS', 'Do you not have better things to do?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127746, 'enUS', 'Do I really deserve this?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127747, 'enUS', 'I am not amused.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127748, 'enUS', 'This needs to stop.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127749, 'enUS', '', 'What surprises await us?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127750, 'enUS', '', 'You need not do this alone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127751, 'enUS', '', 'I am here to help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127752, 'enUS', '', 'Our enemies will drown in shadows!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127753, 'enUS', '', 'Your end is nigh!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127754, 'enUS', '', 'You will feel my wrath!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127755, 'enUS', '', 'You shall be purged from this world!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127756, 'enUS', '', 'The void awaits.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127757, 'enUS', '', 'You brought this on yourself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127758, 'enUS', '', 'Your struggle is over.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127759, 'enUS', '', 'I will need just a moment.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127760, 'enUS', '', 'This was... unexpected.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127761, 'enUS', '', 'This defeat is only temporary.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127762, 'enUS', '', 'This should be interesting...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127763, 'enUS', '', 'Yes?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127764, 'enUS', '', 'What do you need?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127765, 'enUS', '', 'How can I help?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127766, 'enUS', '', 'Take care.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127767, 'enUS', '', 'May the Shadows embrace you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127768, 'enUS', '', 'Farewell.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127769, 'enUS', '', 'You are testing my patience.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127770, 'enUS', '', 'Do you think yourself clever?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127771, 'enUS', '', 'Shoo!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127772, 'enUS', '', 'Begone from my sight!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127773, 'enUS', '', 'This will not end well for you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127774, 'enUS', '', 'Silly me for thinking Roland was the annoying one.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127775, 'enUS', 'Hello.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127776, 'enUS', 'How can I help?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127777, 'enUS', 'Yes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127778, 'enUS', 'Safe travels.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127779, 'enUS', 'Farewell.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127780, 'enUS', 'Until next time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127781, 'enUS', 'What is wrong with you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127782, 'enUS', 'Surely the leader of the Black Harvest does not have time to waste like this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127783, 'enUS', 'I am not to be trifled with!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127784, 'enUS', 'Do you really want to wake the demon?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127785, 'enUS', 'Jubeka should have banished you instead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127786, 'enUS', 'It is an honor to fight alongside such power.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127787, 'enUS', 'There is so much untapped power in this world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127788, 'enUS', 'Our enemies will fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127789, 'enUS', 'Feel the power of the Black Harvest!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127790, 'enUS', 'You are no match for our power!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127791, 'enUS', 'This will be your undoing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127792, 'enUS', 'Our power cannot be matched.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127793, 'enUS', 'Our power grows!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127794, 'enUS', 'That was almost too easy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127795, 'enUS', 'Give me a moment to regain my strength.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127796, 'enUS', 'I feel powerless...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127797, 'enUS', 'Don\'t let me slow you down.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127798, 'enUS', 'You will know my power!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127799, 'enUS', 'I will do what is needed in the name of the Black Harvest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127800, 'enUS', '', 'Yes?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127801, 'enUS', '', 'Can I help you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127802, 'enUS', '', 'What do you need?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127803, 'enUS', '', 'Farewell.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127804, 'enUS', '', 'Remain vigilant.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127805, 'enUS', '', 'Be safe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127806, 'enUS', '', 'You have my attention.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127807, 'enUS', '', 'I like you better when you are silent.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127808, 'enUS', '', 'You are a persistant one, are you not?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127809, 'enUS', '', 'Surely you know other tricks?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127810, 'enUS', '', 'You are crossing a line that cannot be uncrossed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127811, 'enUS', '', 'You can stop now, or I can have D\'Bynn stop you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127812, 'enUS', '', 'Be alert, Huntmaster. We do not know what dangers lurk nearby.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127813, 'enUS', '', 'I am here to take my place at your side.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127814, 'enUS', '', 'We will protect these lands together.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127815, 'enUS', '', 'Run if you dare, though you will not get far!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127816, 'enUS', '', 'You will die by my hand!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127817, 'enUS', '', 'I will enjoy this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127818, 'enUS', '', 'This is the fate you deserve.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127819, 'enUS', '', 'There could be no other way.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127820, 'enUS', '', 'The weak will always perish.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127821, 'enUS', '', 'Go on without me. I will catch up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127822, 'enUS', '', 'This defeat will not break me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127823, 'enUS', '', 'My will cannot be broken!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127824, 'enUS', '', 'You can trust me to do whatever is needed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127825, 'enUS', '', 'Your victory brings hope to the hearts of my people. The Burning Legion will never take our lands!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127826, 'enUS', 'Our riders wage war against the Legion in the skies. Give aid to those who have fallen. We must hold this point!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127827, 'enUS', 'Our riders will take it from here. Continue your fight, champion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127828, 'enUS', 'I\'ve already completed my training.', 'I\'ve already completed my training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127829, 'enUS', 'Finish your drinks! We\'re leaving in less than a minute!', 'Finish your drinks! We\'re leaving in less than a minute!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127830, 'enUS', 'Merry is the day, for we have found the jewelry!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127831, 'enUS', 'Teleport me to the next tavern.', 'Teleport me to the next tavern.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127832, 'enUS', 'It is amusing how you continue to fight, even though you\'re bound to lose.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127833, 'enUS', 'Your fighting style does not impress, nor concern me. You will fall to my strength soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127834, 'enUS', 'You\'re so tiny! It\'s like playing with a rabbit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127835, 'enUS', 'How may I assist you?', 'How may I assist you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127836, 'enUS', 'I want to donate War Supplies to the Mage Tower.', 'I want to donate War Supplies to the Mage Tower.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127852, 'enUS', 'Take me into the Deadmines.', 'Take me into the Deadmines.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127853, 'enUS', 'There is a lot of movement within the Deadmines. \n\nI suspect the place has been infested with all manner of creatures. Shall we investigate?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127854, 'enUS', 'Dodge this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127855, 'enUS', 'Coming at ya!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127856, 'enUS', 'Think fast!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127857, 'enUS', 'Incoming!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127858, 'enUS', 'Hey, catch!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127859, 'enUS', 'What... what happened? This isn\'t the lodge!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127860, 'enUS', 'My head... what happened?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127861, 'enUS', 'Where... where am I?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127862, 'enUS', 'That thing... it got into my head. I couldn\'t stop!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127863, 'enUS', 'I... I feel sick.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127864, 'enUS', 'What... what happened to me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127865, 'enUS', 'This is as far as I can take you. Let\'s hope your companions are strong enough to get you the rest of the way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127866, 'enUS', 'They took the kegs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127867, 'enUS', 'Jona, Joren, go after the Gnomenbrau! A group of troggs carried it off!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127868, 'enUS', 'Soren lad, find the boar that stole our Theramore Pale!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127869, 'enUS', 'I\'ll chase the trolls to their cave and find the Stormhammer Stout!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127870, 'enUS', 'Neither snow nor cataclysm nor demon invasion will stay us from our appointed duty. The mail must flow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127889, 'enUS', 'The Mage Tower is now under construction.', 'The Mage Tower is now under construction.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127890, 'enUS', 'The Mage Tower is under attack!', 'The Mage Tower is under attack!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127891, 'enUS', 'The Mage Tower is destroyed, it will be back up soon.', 'The Mage Tower is destroyed, it will be back up soon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127893, 'enUS', 'Well done. We simply can\'t have improper mail clogging up the system.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127894, 'enUS', 'No need to track down the sender. The tubes will handle that!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127897, 'enUS', '', 'The Burning Legion marches on Highmountain! Our allies have engaged the demons throughout our lands. To turn the tide, champion, you must strike at the heart of their forces!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127898, 'enUS', '', 'Well fought! Our forces are gaining gr...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127899, 'enUS', 'To Thunder Totem! My riders will hold the skies while you sweep the grounds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127900, 'enUS', '', 'The very wildlife of Highmountain rises up against the demons! Shatter the infernals and find the source of their incursion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127901, 'enUS', '', 'The Legion underestimates nature\'s persistence and determination... and yours, I think!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127902, 'enUS', 'We need everyone on the front line. Another wave of demons is incoming!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127903, 'enUS', 'That bought us a brief reprieve. My Illidari can hold the line from here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127904, 'enUS', 'Valarjar of Skyhold! Let these walls resound with cheers for our champion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127905, 'enUS', '', '|TInterface\\Icons\\Ability_Butcher_Whirl.blp:20|t%s begins to cast |cFFFF0000|Hspell:232722|h[Slicing Tornado]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127906, 'enUS', '', '|TInterface\\Icons\\TRADE_ARCHAEOLOGY_SHARK JAWS:20|t%s calls forth the mighty Sarukel to open his |cFFFF0000|Hspell:234621|h[Devouring Maw]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127907, 'enUS', '|TInterface\\Icons\\SPELL_WARLOCK_SOULBURN:20|tThe %s begins to emit a series of |cFFFF0000|Hspell:236011|h[Tormented Cries]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127908, 'enUS', '', '|TInterface\\Icons\\ABILITY_WARLOCK_SOULLINK:20|t%s chains together living essence with |cFFFF0000|Hspell:236449|h[Soulbind]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127909, 'enUS', '', '|TInterface\\Icons\\SPELL_SHADOW_SHADESOFDARKNESS:20|t%s begins to release a mass of |cFFFF0000|Hspell:236075|h[Wailing Souls]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127910, 'enUS', '', 'Together we\'ll shatter the bones of countless demons!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127911, 'enUS', '|TInterface\\Icons\\Spell_Deathknight_Defile:20|tWith no souls in the Corporeal Realm, the %s lashes out with |cFFFF0000|Hspell:236507|h[Quietus]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127912, 'enUS', '', '|TInterface\\Icons\\Spell_Deathknight_Defile:20|tWith no souls in the Spirit Realm, %s lashes out with |cFFFF0000|Hspell:236507|h[Quietus]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127913, 'enUS', 'Oh, am I glad ta see ya! Our brothers and sisters\'re fightin\' down tha ramp, but they\'re out of brew!$b$bThese confounded beasts are stampedin\' through, so we cannae roll the barrels down without someone...$b$bThat gives me an idea...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127914, 'enUS', 'Simple! Ye ride down on tha barrel. $b$bWith yer fancy footwork ye can dodge tha stampede an\' deliver the brew! As me master always said: \'If ye can dodge a horse on a barrel, ye can dodge a punch.\'$b$bMe trainin\' was... exotic. Look, just hop on tha barrel, keep rollin\' till ye reach the patrol, an watch out for the horses on the way down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127915, 'enUS', 'Do I look like a warlock to ye? I haven\'t the foggiest clue why. Maybe this is some sort o\' seasonal stampede, where the dreadsteeds are a-runnin for a migration, or matin\' season, or runnin\' from somethin\'...$b$bOr maybe they\'re just runnin\' through here to stop us from easily deliverin\' our brew!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127916, 'enUS', 'What\'s the plan?', 'What\'s the plan?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127917, 'enUS', 'What\'s causing the stampede, anyway?', 'What\'s causing the stampede, anyway?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127918, 'enUS', 'Fair enough.', 'Fair enough.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127919, 'enUS', '|TInterface\\Icons\\ACHIEVEMENT_BOSS_LORDMARROWGAR:20|tThe %s shudders and retches forth The Desolate Host, overloading the soul conduits!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127920, 'enUS', '', 'Ah, nevermind. Just wait until Mograine sees us flying up. He\'ll get chills!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127921, 'enUS', 'The Soulstone is in place. Use the Arcane Matrix to fire up the city\'s defenses!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127922, 'enUS', 'Business is good.$b$bReal good.$b$b<Nargle tucks a few coins into his pocket.>$b$bMister Noggenfogger will be pleased- keep it up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127923, 'enUS', 'Superbly done, Archmage! I never doubted your success. To the Archmage! To the Tirisgarde!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127928, 'enUS', 'Did it work?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127929, 'enUS', 'There is nowhere you can flee that I cannot reach you, demon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127930, 'enUS', 'Ah, our guests of honor have finally arrived. I look forward to ripping those horns from your skull, Betrayer!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127931, 'enUS', '', 'Illidan! Curse that impetuous demon hunter!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127932, 'enUS', '', 'Illidan left behind a mess! Stay close... we shall carve a path through these demons.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127933, 'enUS', '', 'I will continue the hunt for Illidan. You deal with those abominations.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127934, 'enUS', 'Demons bar the path ahead, but they cannot withstand our fury.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127935, 'enUS', 'I will track the Dreadlord. You deal with the rest of the filth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127936, 'enUS', '', 'Did you really think you could elude me, Illidan? Run again and I\'ll shackle you like the beast you are.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127937, 'enUS', 'Posture if you must, Maiev, but for now, do what you do best and follow me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127938, 'enUS', 'There is nowhere left to run, demon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127939, 'enUS', '', 'So much death here... so much power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127940, 'enUS', '', 'Is this not the site of what you mortals consider your greatest humiliation? I hope you fare better this time...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127941, 'enUS', '', 'I don\'t believe these lands have seen such carnage since the Battle for K\'tanth. Such a long time ago...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127942, 'enUS', 'Run? Fool! I have led you to the slaughter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127943, 'enUS', 'Beware the shadows!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127944, 'enUS', 'You cannot withstand the power of the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127945, 'enUS', 'It amuses me to watch you suffer!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127946, 'enUS', 'I tire of these games! Let us see how you fare without your little trinket.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127947, 'enUS', 'How kind of you to bring the Aegis to me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127948, 'enUS', 'Surrender the shield and I will grant you a quick death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127949, 'enUS', '', 'This was always a place of power. Aegwynn was drawn here, and before her the elves, and before them the trolls. And before them...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127950, 'enUS', '', 'That fel edifice towering over this land pales in comparison to the grandeur of what stood here long ago.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127951, 'enUS', '', 'It was here, in ages past, that the God of the Deep lost a great battle to the God of Seven Heads. But as was so often the case, even defeat ultimately worked in N\'Zoth\'s favor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127952, 'enUS', 'The final... victory... will be ours...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127953, 'enUS', '', 'This trifling skirmish is little more than a distraction. The true battle draws nigh as the circle nears completion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127954, 'enUS', '', 'This conflict is but a shadow of an ancient war that predated the meddling of the titans. Infinite armies clashed in ceaseless battle. But I\'m sure your little war is impressive... in its own way.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127955, 'enUS', '', 'It took ages to make my return to the surface after the Sundering. The naga can be so possessive of artifacts.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127956, 'enUS', '', 'This land has seen so much upheaval. The Cataclysm, the Sundering, the Scouring... you really have no idea.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127957, 'enUS', '', 'You were a good choice. No other wielder has murdered their way across a continent quite like you have. I admire your endurance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127958, 'enUS', '', 'The Light would have your kind remain obedient and stagnant. I offer the gifts of freedom and strength. I hope you remember my kindness.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127959, 'enUS', '', 'Your allies consider me a bad influence. Yet all I\'ve ever done is speed you along the path you chose.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127960, 'enUS', '', 'I can feel the pulse of your world quickening. Soon all will be drenched in its lifeblood.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127961, 'enUS', '', 'A Legion brute obliterating priceless knowledge... imagine that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127962, 'enUS', '', 'Long has this one been a blight upon your world. Not all his schemes came to fruition, but those that did caused untold ruin.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127963, 'enUS', '', 'From birth, pit lords are oversized and stupid. Somehow the Legion manages to magnify both traits.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127964, 'enUS', '', 'At times it seems the naga beg for scraps from the Legion\'s table. But the queen they serve wants far more than that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127965, 'enUS', '', 'I do enjoy seeing the shrine of a great enemy profaned by the spirits of its own worshippers.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127966, 'enUS', '', 'The haughty elves of old had such regard for their own spirits. Such a delicious indignity!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127967, 'enUS', '', 'Invaders messily corrupting other invaders... typical. Far more useful to coax them along the proper path, don\'t you think?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127968, 'enUS', '', 'Let me guide your hand as you battle this vessel. Though it retains but a fraction of the fallen titan\'s power, you will not survive its onslaught without my expertise.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127969, 'enUS', '', 'You must face the Deceiver on his own ground, where he is vulnerable. Come, let us taste his soul!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127970, 'enUS', 'Kil\'jaeden sends his gratitude for delivering the Aegis into our hands.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127971, 'enUS', '', 'Hey you! You\'re the one the tauren was talking about!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127972, 'enUS', '', 'Trying to foil our plot again? Hah!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127973, 'enUS', '', 'As if someone so puny could stop us...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127974, 'enUS', '', 'Besides, if you ever hurt one hair on my sweet tauren\'s head, you\'d have a REAL problem to deal with...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127975, 'enUS', '', 'ME!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127976, 'enUS', '', 'No matter. I already have what I came for.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127977, 'enUS', '', 'You better hope we don\'t meet again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127978, 'enUS', '', 'I don\'t have time for silly catch phrases. So, I\'ll just do a cool exit instead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127979, 'enUS', '', 'Power Leap!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127980, 'enUS', 'This one... is strong. No matter! These souls will serve the master well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127981, 'enUS', '', 'The shadowy figure pulls a vial of goo from the cauldron.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127982, 'enUS', '', 'A remnant of power lingers in this broken temple. It must be ours! Come, enter the circle and take it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127983, 'enUS', 'My axe hungers for flesh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127984, 'enUS', 'Legion! Scorch them to the bone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127985, 'enUS', 'Fiends! Tear down that shield!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127986, 'enUS', 'Infernals! Devastate their ranks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127987, 'enUS', 'Their shield has fallen! Leave no survivors!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127988, 'enUS', 'The full might of the Legion falls upon you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127989, 'enUS', 'Your flesh will burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127990, 'enUS', 'This world... will belong... to the Legion...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127991, 'enUS', 'I will hold this flank. Can you manage to survive without me, warden?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127992, 'enUS', 'Still alive, Maiev?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127993, 'enUS', '', 'I would regret it if you died by another\'s hand, Betrayer!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127994, 'enUS', '', 'Forgive me if my glaive finds your heart, Illidan. With so many demons about, it\'s hard to distinguish you from the rest of this filth.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127995, 'enUS', '', 'Wardens, secure the cathedral! We will hold here while our allies push into the tomb below.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127996, 'enUS', '', 'My mistake. It seems the upstart \"goddess\" still holds sway here. Oh well...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127997, 'enUS', '', 'It is here the Feltotem receive demonic instruction. Cleanse the cavern of their evil and send their masters back to the Twisting Nether!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127998, 'enUS', '', 'Thank you, hero. You\'ve done the tribes of Highmountain a great service this day.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (127999, 'enUS', 'The Maw of the Damned begins to tug at your grip as you approach the Deceiver.', 'The Maw of the Damned begins to tug at your grip as you approach the Deceiver.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128000, 'enUS', 'Your resilient spirit resists the foul fel magic!', 'Your resilient spirit resists the foul fel magic!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128001, 'enUS', 'At long last! Revenge... revenge is mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128002, 'enUS', 'At last I am free!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128003, 'enUS', '', 'Nerus? Explain yourself, uncle!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128004, 'enUS', 'You must understand! I was trying to protect our family...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128005, 'enUS', 'But in the end, the pact I made cursed our ancestors with a fate worse than death.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128006, 'enUS', 'Their spirits are bound to the Legion, forced to do the demons\' bidding.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128007, 'enUS', '', 'This... this is your doing? How could you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128008, 'enUS', 'I am so sorry, Delas. I know it is too much to ask your forgiveness, but I must ask for your help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128009, 'enUS', 'The wreckage of our prior assault on these shores contains supplies vital to our efforts against the Burning Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128010, 'enUS', 'Clear the beach of demonic forces and recover the salvaged supplies for the invasion effort.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128011, 'enUS', '', 'I claim your lives for Azshara!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128012, 'enUS', '', 'Meet your end, mortal filth!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128013, 'enUS', '', 'Time to feed the fish!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128014, 'enUS', '', 'Chumming the waters!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128015, 'enUS', '', 'Ten thousand years haven\'t made mortals any less pathetic!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128016, 'enUS', '', 'Queen Azshara\'s reign is absolute... as ever!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128017, 'enUS', '', 'Oh! The... indignity...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128018, 'enUS', '', 'Such a death... is beneath me...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128019, 'enUS', '', 'Consume them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128020, 'enUS', '', 'Feast upon flesh!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128021, 'enUS', '', 'The sea surges with power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128022, 'enUS', '', 'Death rises to take you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128023, 'enUS', '', 'Suffer the chill of the depths!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128024, 'enUS', '', 'Your souls belong to the deep!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128025, 'enUS', '', 'The depths will devour you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128026, 'enUS', '', 'My pet will swallow you whole!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128027, 'enUS', 'Petty mortal. You gain nothing! You will bow... or you will BURN!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128028, 'enUS', 'Well done. With the demons slain and the supplies recovered, we are one step closer to securing the Broken Shore.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128030, 'enUS', 'I\'m ready to take on Xylem. (100 Nethershards |TINTERFACE\\ICONS\\inv_datacrystal01.blp\n:16|t)', 'I\'m ready to take on Xylem. (100 Nethershards |TINTERFACE\\ICONS\\inv_datacrystal01.blp\n:16|t)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128031, 'enUS', 'I\'m ready to face Raest. (100 Nethershards |TINTERFACE\\ICONS\\inv_datacrystal01.blp\n:16|t)', 'I\'m ready to face Raest. (100 Nethershards |TINTERFACE\\ICONS\\inv_datacrystal01.blp\n:16|t)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128032, 'enUS', '', 'These fools bring the Tidestone to us! Just as our blessed queen foresaw!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128033, 'enUS', '', 'Slay the intruders! Take back what is ours!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128034, 'enUS', '', 'Weaklings! You dare disappoint our queen?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128035, 'enUS', '', 'Incompetents! Must I soil my hands on this rabble?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128036, 'enUS', '', 'Enough of these failures! Now you will face the true might of the naga!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128037, 'enUS', '', 'Harjatan! Pummel their bones to dust!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128038, 'enUS', '', 'Quiet, you mindless oaf! Kill them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128039, 'enUS', '', 'Useless beast! Why the queen was fond of him I\'ll never know!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128040, 'enUS', '', 'Minions! To battle!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128041, 'enUS', '', 'Lend aid to our brutish champion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128042, 'enUS', '', 'You! Fetch me the Tidestone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128043, 'enUS', '', 'More death! More carnage!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128044, 'enUS', 'Bal, lamer zhita!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128045, 'enUS', 'Come, little friends! Crush!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128046, 'enUS', 'Ash\'thero sanguine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128047, 'enUS', 'Time to smash!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128048, 'enUS', 'Harjatan... fail... pretty queen...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128049, 'enUS', 'Harjatan... no fight... so good...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128050, 'enUS', 'I\'m ready to face Kruul. (100 Nethershards |TINTERFACE\\ICONS\\inv_datacrystal01.blp\n:16|t)', 'I\'m ready to face Kruul. (100 Nethershards |TINTERFACE\\ICONS\\inv_datacrystal01.blp\n:16|t)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128051, 'enUS', 'Die for pretty queen!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128052, 'enUS', 'Alana be\'lendor', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128053, 'enUS', '', 'Things are still a bit foggy, but I can stand and fight.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128054, 'enUS', '', 'Take my death gate or open your own. Either way, I\'ll see you in Acherus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128055, 'enUS', 'The Legion has conquered this shore. Soon we shall conquer the Legion in turn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128056, 'enUS', 'This place holds a mere fraction of the Legion\'s might. There are many horrors yet to behold.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128057, 'enUS', 'Do you smell the charred flesh upon the wind? Ah, the scent of home!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128058, 'enUS', 'Your last visit here was nearly the end of you. How fortunate that you now enjoy my company.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128059, 'enUS', 'This place is so like Argus... twisted, shattered, and broken. Almost makes me homesick!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128060, 'enUS', 'Your skills have improved significantly since we first met. Given a few thousand years, you may even rise to the level of my best apprentices.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128061, 'enUS', 'And here we are, the site where fools were vanquished. Remember, courage is no substitute for power.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128062, 'enUS', 'Do you sense it? The lifeblood of your world flows just under the surface of this place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128063, 'enUS', 'I know ancient incantations which could tap into the lifeblood of your world. This would be an ideal place to unleash them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128064, 'enUS', 'This one is powerful, but stupid. It would make a worthless minion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128065, 'enUS', 'Ah, my former master! The time for revenge is at hand!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128066, 'enUS', 'Demon... worshippers?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128067, 'enUS', '%s blinks, looking puzzled. His eyes glow with fel energy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128068, 'enUS', '', 'What\'s... happening? Blood feels... like it\'s boiling... I... I...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128069, 'enUS', 'That fool saw me as little more than a trinket for harvesting power. He has paid for his lack of vision!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128070, 'enUS', '', 'DEMONS! ALL OF YA!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128071, 'enUS', 'I know I have much to answer for.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128072, 'enUS', '', 'Indeed you do, uncle. But this is a start, I suppose...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128073, 'enUS', 'The naga are a corrupted race. They possess arcane skill, but lack the finesse inherent to my people.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128074, 'enUS', 'I detest spirits! Always blathering about their former lives. Consume their souls and be done with them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128075, 'enUS', 'An intriguing use of soul energy. They could have accomplished so much, had they merely applied themselves.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128076, 'enUS', 'Ugh! This place reeks of the titans!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128077, 'enUS', 'Careful! Power still echoes within this ancient golem. It would be prudent to destroy it before the Legion empowers it further.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128078, 'enUS', 'The Deceiver scorned my talents when I still lived. Let us see which of us survives the battle to come!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128079, 'enUS', 'Ah, the skies of home! I did not believe I would see this world again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128080, 'enUS', 'They may never forgive me for what I have done, but at least they will be safe. For now, my family will be safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128081, 'enUS', 'The weight of my decision weighs heavy on my heart, but each day I see their carefree smiles eases that burden just a little.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128082, 'enUS', 'My gift to them is the bliss of ignorance. They will not realize its price until it is too late. What is done is done. I hope I have made the right choice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128083, 'enUS', 'You no take my boat! Birds to da cannons! Monkeys to da sails!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128084, 'enUS', 'Can\'t get past me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128085, 'enUS', 'Nope!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128086, 'enUS', 'Nuh-uh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128087, 'enUS', 'Where you goin\'?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128088, 'enUS', '|TINTERFACE\\ICONS\\SPELL_FEL_INCINERATE.BLP:20|t You have |cFFFF0000|Hspell:235117|h[Unstable Soul]|h|r!', '|TINTERFACE\\ICONS\\SPELL_FEL_INCINERATE.BLP:20|t You have |cFFFF0000|Hspell:235117|h[Unstable Soul]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128089, 'enUS', '', 'Excuse me, $n! Some of those disasters you have previously delayed are now actionable. Please speak with me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128090, 'enUS', 'I\'m ready to face Sigryn. (100 Nethershards |TINTERFACE\\ICONS\\inv_datacrystal01.blp\n:16|t)', 'I\'m ready to face Sigryn. (100 Nethershards |TINTERFACE\\ICONS\\inv_datacrystal01.blp\n:16|t)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128092, 'enUS', 'Gurrg mrggll!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128093, 'enUS', 'Murgg gur gr gr grulgl!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128094, 'enUS', 'Gurg gar gur gal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128095, 'enUS', 'Grulgrug grull murg gruumblg!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128096, 'enUS', 'Murglag!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128097, 'enUS', 'Gurg murglgl!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128098, 'enUS', 'Gurg glurgggll!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128099, 'enUS', 'I\'m ready to face Tugar Bloodtotem. (Free)', 'I\'m ready to face Tugar Bloodtotem. (Free)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128100, 'enUS', 'Send me to Shadowsong. (Free)', 'Send me to Shadowsong. (Free)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128101, 'enUS', 'Aghhhhh! It burns, it burns!', 'Aghhhhh! It burns, it burns!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128102, 'enUS', 'Everything is green, I can\'t see!', 'Everything is green, I can\'t see!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128103, 'enUS', 'My flesh feels like it\'s falling off the bone!', 'My flesh feels like it\'s falling off the bone!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128104, 'enUS', 'AHHHHHH!!!', 'AHHHHHH!!!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128105, 'enUS', 'I can\'t-', 'I can\'t-', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128106, 'enUS', 'Stop!', 'Stop!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128107, 'enUS', 'My bones!', 'My bones!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128108, 'enUS', 'Make it stop!', 'Make it stop!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128109, 'enUS', 'Argh!', 'Argh!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128110, 'enUS', 'You there, help me!', 'You there, help me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128111, 'enUS', 'Just let me-', 'Just let me-', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128112, 'enUS', 'I\'ll talk-', 'I\'ll talk-', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128113, 'enUS', 'Take me to Agatha... (Free)', 'Take me to Agatha... (Free)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128114, 'enUS', '', 'It was an honor to meet you, Grandmaster. I hope to see you again soon!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128115, 'enUS', 'I\'m ready to take on Xylem. (Free)', 'I\'m ready to take on Xylem. (Free)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128116, 'enUS', 'I\'m ready to face Raest. (Free)', 'I\'m ready to face Raest. (Free)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128117, 'enUS', 'I\'m ready to face Kruul. (Free)', 'I\'m ready to face Kruul. (Free)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128118, 'enUS', 'I\'m ready to face Sigryn. (Free)', 'I\'m ready to face Sigryn. (Free)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128119, 'enUS', '|TInterface\\ICONS\\Ability_IronMaidens_ConvulsiveShadows.blp:20|t The Fallen Avatar targets you with |cFFFF0404|Hspell:236604|h[Shadowy Blades]|h|r!', '|TInterface\\ICONS\\Ability_IronMaidens_ConvulsiveShadows.blp:20|t The Fallen Avatar targets you with |cFFFF0404|Hspell:236604|h[Shadowy Blades]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128120, 'enUS', 'The darkness is gone! Thank you, hero.', 'The darkness is gone! Thank you, hero.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128121, 'enUS', 'Where... am I?', 'Where... am I?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128122, 'enUS', 'I can see clearly now!', 'I can see clearly now!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128123, 'enUS', 'I\'m just going to rest for a moment...', 'I\'m just going to rest for a moment...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128124, 'enUS', 'Ouch! Why would you do that to me?', 'Ouch! Why would you do that to me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128125, 'enUS', 'Rude!', 'Rude!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128126, 'enUS', 'That stings!', 'That stings!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128127, 'enUS', 'What in the void-stricken darkness...?', 'What in the void-stricken darkness...?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128128, 'enUS', 'Yikes! I\'m out of here!', 'Yikes! I\'m out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128129, 'enUS', '', 'I\'ll get the flame. I\'ve been looking forward to this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128130, 'enUS', 'Welcome to my peak, $ct.$b$bI am the last remaining ancient watcher of the Drekirjar. I make my home here, tending to the sacred bodies of our allies who gave their lives for our alliance. Skovald may have shattered our alliance with the Thorignir, but there are those of us who do not follow his mandates.$b$bTo this day, the Thorignir die in our joint struggle. It is all I can do to attend them as their bodies return to the snow. With Daumyr as my partner, we protect our charges with our lives.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128131, 'enUS', 'I\'m raising these Thorignir as my soldiers, and Daumyr is next.', 'I\'m raising these Thorignir as my soldiers, and Daumyr is next.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128132, 'enUS', 'You shall not turn my allies into your mindless slaves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128133, 'enUS', 'I am... uh... we... are...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128134, 'enUS', 'The pendulum swings in our favor! Make for the cliffs and dispose of that demon. I will meet you there once we\'ve secured the skies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128135, 'enUS', '', 'Insolent pests! Your efforts amount to NOTHING!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128136, 'enUS', 'What delicious irony! The demons are so quick to turn on one another!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128137, 'enUS', 'Enjoy the chaos...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128138, 'enUS', 'Our shores are ablaze with fel fire! Strike quickly, before the threat moves inland.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128139, 'enUS', 'Well fought! Every victory is crucial in this war.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128140, 'enUS', 'Such depravity! The demons drain the souls of innocents to power their foul magic. Stop this harvest, hero!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128141, 'enUS', 'I have no love of murlocs, but even they don\'t deserve to meet their end in the Legion\'s clutches. Thank you, hero.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128142, 'enUS', '', 'How did the Legion learn about our Storm Brew? We\'ve kept its existence quiet...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128143, 'enUS', 'Your efforts are far too late, pathetic creatures...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128144, 'enUS', 'You thought you could escape by huddling in your puny brewery? The Burning Legion sees all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128145, 'enUS', 'These cowardly demons hide behind their defenses. Take my blessed hammer and slay them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128146, 'enUS', 'Destroy their cannons and bring down their barriers! Let our valiant Valarjar face them in the true spirit of battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128147, 'enUS', 'Fly swiftly, $ct. We will meet you there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128148, 'enUS', 'Well done, hero. The tales of your deeds will be sung within the Halls of Valor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128149, 'enUS', 'I suggest we be quick. The skies are swarming with those foul beasts.\n\nOur opportunity to strike at the heart of the Legion presents itself. We must bring the battle to the frontlines.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128150, 'enUS', 'For the honor of Highmountain. I am ready! [Queue for Scenario]', 'For the honor of Highmountain. I am ready! [Queue for Scenario]', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128151, 'enUS', '', 'Your precious world will burn!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128152, 'enUS', '', '<Daumyr nods to you, a hollow look in her eyes.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128153, 'enUS', 'Time to go to Broken Shore, Daumyr.', 'Time to go to Broken Shore, Daumyr.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128154, 'enUS', 'The azure drakes in the area have pledged to help us fight back against the Legion, but they\'ll need your help. Assist them in any way you can.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128155, 'enUS', 'The drakes are providing us transport up to the Legion ship to deal with the their commander in the area. Once you\'ve dealt with him, they\'ll return to get you before the main assault on the ship begins.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128156, 'enUS', '$n! To me, quickly! We end this now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128157, 'enUS', 'With their commander down and shields disabled, the blue drakes can begin their assault on the ship in earnest. Their sending transport to extract you before they start.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128158, 'enUS', 'Felweaver! Deal with these intruders!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128159, 'enUS', 'Come, champions of this world, so that I may cut you down where you stand!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128160, 'enUS', '', 'Know the torment of a soul divided!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128161, 'enUS', '', 'I did not choose my fate... neither will you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128162, 'enUS', '', 'Storm Brew is amazing! No wonder the Legion fears it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128163, 'enUS', '', 'Grandmaster, you have done better than I expected. Seems I will have to deal with you myself!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128164, 'enUS', '', 'Brewmaster? What are you talking about? We\'ve been searching everywhere for you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128165, 'enUS', '', 'Almai, your naivete is so refreshing. Pity you must die!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128166, 'enUS', '', 'So much for that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128168, 'enUS', 'My work is complete, puny monks. Your order has already fallen!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128169, 'enUS', '', 'What... What just happened? What did she mean? Grandmaster, we must return to the temple at once! Let me know when you\'re ready!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128170, 'enUS', '', 'Brewmaster! NO!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128171, 'enUS', 'I would like to leave the Deadmines.', 'I would like to leave the Deadmines.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128172, 'enUS', 'I fear no beast! The Legion is endless!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128173, 'enUS', 'Back for more? My hounds will tear you to shreds!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128174, 'enUS', 'If this is the best hunter your world has, it is truly doomed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128175, 'enUS', 'Rise, my soldiers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128176, 'enUS', 'Your commander beckons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128177, 'enUS', 'The power courses within you! Destroy these demons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128178, 'enUS', 'Rise! Yes... rise!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128179, 'enUS', 'The time has come! Your destiny is at hand!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128180, 'enUS', 'Let\'s ride.', 'Let\'s ride.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128181, 'enUS', '', 'My duty is one mere mortals cannot comprehend. A sacred charge to serve... to serve the master!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128182, 'enUS', '', 'I am compelled to... ensure... the end of your existence!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128183, 'enUS', 'They are strong, with blood warmer than summer\'s heat. I assure you they will recover in time, but we must do our part and get back out there! Our people need us!', 'They are strong, with blood warmer than summer\'s heat. I assure you they will recover in time, but we must do our part and get back out there! Our people need us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128184, 'enUS', 'Many warriors have sought shelter during a storm, but now is no time for shelter. We must raise our weapons and charge at the heart of nature itself.', 'Many warriors have sought shelter during a storm, but now is no time for shelter. We must raise our weapons and charge at the heart of nature itself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128185, 'enUS', 'I know it hurts now, but years from now we will look back upon this day and remember all we have fought for, all we have given. This is what makes us who we are.', 'I know it hurts now, but years from now we will look back upon this day and remember all we have fought for, all we have given. This is what makes us who we are.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128186, 'enUS', 'We must remain strong.', 'We must remain strong.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128187, 'enUS', 'Raise your head, young warrior. Our journey has only just begun.', 'Raise your head, young warrior. Our journey has only just begun.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128188, 'enUS', 'We must maintain our composure! Show our allies who we truly are, not what those demons expect us to be!', 'We must maintain our composure! Show our allies who we truly are, not what those demons expect us to be!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128189, 'enUS', 'A long wind only blows for so long before the calm sets in. We must remember our training.', 'A long wind only blows for so long before the calm sets in. We must remember our training.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128190, 'enUS', '', 'My family has been making brew for generations, so of course I\'m a natural at it! Still, I\'ve always wanted to try my hand at something different.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128191, 'enUS', '', 'Life in the Valley of Four Winds is usually so peaceful, and that\'s a good thing for most. But for me, it\'s also rather dull. I\'d prefer some excitement now and again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128192, 'enUS', '', 'I can make good brew with my eyes closed. But Mistweaving, now that\'s a challenge. I hope to be great at it one day.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128193, 'enUS', 'To falter in the face of danger is not a flaw nor a weakness, rather an opportunity to rise to the call of battle.', 'To falter in the face of danger is not a flaw nor a weakness, rather an opportunity to rise to the call of battle.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128194, 'enUS', '', 'We need to return to the Temple of Five Dawns! Let me know when you\'re ready!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128198, 'enUS', '', 'The dreadlord attacked me, poisoned my Celebration Brew, and stole my form...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128199, 'enUS', 'It was... not your fault, Almai! But... we must hurry... to find a cure. Some have already succumbed...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128200, 'enUS', 'Marius and his pet warlock managed to reverse one of the demon portals. Pass through and see what damage you can unleash on the other side.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128201, 'enUS', 'Impressive improvisation. If only more of our forces could be entrusted with such missions. Now get out of there before reinforcements find you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128202, 'enUS', '', 'I will go with you, Grandmaster. We have no time to lose!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128203, 'enUS', '', '|TInterface\\Icons\\INV_Misc_Herb_Arrowbloom.blp:20|t%s targets you with |cFFFF0000|Hspell:236304|h[Incorporeal Shot]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128204, 'enUS', '', 'I should be able to reactivate their portal. This place teems with usable souls.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128205, 'enUS', '', 'I will ensure no more of Kruul\'s minions make it through. Suffer well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128206, 'enUS', 'The Xorothian Ritualists are beginning to cast Cataclysm!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128207, 'enUS', 'I grow weary of waiting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128208, 'enUS', 'You cannot stop me, demons! RISE, SOLDIERS!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128209, 'enUS', '', 'Ready to unveil our secret weapon?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128210, 'enUS', '', 'All those demons... bah! No chance of us being overwhelmed, not when we control the power of Daumyr and her brood.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128211, 'enUS', '', 'The Legion will tremble beneath Daumyr\'s shadow. They\'ll plead for mercy as we rain down death and destruction!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128212, 'enUS', '', 'Do you see the Horsemen? Incredible that they\'re holding their own against that throng.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128213, 'enUS', '', 'Another batch down! Send them to their graves!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128214, 'enUS', '', 'The Horsemen kept those demons contained.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128215, 'enUS', '', 'Daumyr made quick work of them. Did you hear their screams? Music to my ears!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128216, 'enUS', '', 'I\'ve never felt so alive!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128217, 'enUS', '|TInterface\\ICONS\\Ability_FelArakkoa_FelDetonation_Green:20|t Watch out! Avoid |cFFFF0404|Hspell:238005|h[Cataclysmic Force Nova]|h|r! in order to stay on the ship!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128218, 'enUS', '', 'Welcome, Slayer $p, to the breeding grounds of the fel bats. We\'ve captured a fine specimen for you to bond with today.$b$bJust inform me when you\'re ready to see it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128219, 'enUS', '', 'Illidari! Bring out the beast!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128220, 'enUS', '', 'A few days ago, this fel bat attacked our camp. It\'s a rare treat to capture a mature specimen, so I insisted it be restrained rather than destroyed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128221, 'enUS', '', 'Taming such a willful beast requires a show of dominance. That is, of course, my specialty... but in this case, I suggest simply pummeling it into submission.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128222, 'enUS', '', 'Quickly, bind the creature!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128223, 'enUS', '', 'My apologies, Slayer, it seems to have escaped. Ah, but see here? A trail of blood. How convenient!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128224, 'enUS', '', 'Tracking it should be simple, thanks to your demon sight. But if it\'s run off to the breeding grounds, I fear you may have some trouble bringing it back. Bats can be such... spirited creatures.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128225, 'enUS', 'I will activate the portal. Once it\'s open, I\'ll need you to place the Calling Stone to draw Arax\'ath and the Wrathsteed through.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128227, 'enUS', 'No doubt he will send his minions to stop us. Maintaining the portal will require all of my strength, so I will rely on you to hold them off.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128228, 'enUS', '|TInterface\\Icons\\Ability_Rogue_BloodyEye:20|tThrashbite\'s |cFFFF0000|Hspell:237726|h[Scornful Gaze]|h|r falls on |cFFFF0000$n|r!', '|TInterface\\Icons\\Ability_Rogue_BloodyEye:20|tThrashbite\'s |cFFFF0000|Hspell:237726|h[Scornful Gaze]|h|r falls on |cFFFF0000$n|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128229, 'enUS', 'What is this sensation? A dark force... reaching into my mind...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128230, 'enUS', '', 'This... doesn\'t seem right. Where am I?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128231, 'enUS', '|TInterface\\Icons\\INV_SPEAR_08.BLP:20|t%s targets |cFFFF0000$n|r with a |cFFFF0000|Hspell:235933|h[Spear of Anguish]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128232, 'enUS', 'Erdris... your magic is weak! We... will... resist you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128233, 'enUS', '', 'I see you\'ve tamed the beast, Slayer $p. Congratulations are in order, my dear!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128234, 'enUS', '', 'Allow us to make the creature presentable so that it may be conferred to you at the Fel Hammer. Speak with Yrdris and he\'ll see you back to the ship.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128235, 'enUS', '', 'I\'ve still some work to do here, but I\'ll meet you back at the Fel Hammer, Slayer $p.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128236, 'enUS', 'I would not waste the time you\'ve been given to complete your task, Farseer $p.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128237, 'enUS', 'I\'m grateful for your aid, Farseer $p. I have no doubt you will resolve this issue swiftly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128239, 'enUS', 'Attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128240, 'enUS', 'The winds carry my will to the far corners of this plane, but even I cannot be everywhere at once. Here in Skywall, certain of my subjects have not fully grasped the totality of my ascension.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128241, 'enUS', 'They foolishly believe Sarsarun to be the rightful heir to the throne. I need you to convince them otherwise.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128242, 'enUS', 'Ha ha ha ha ha!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128243, 'enUS', 'I am unmoved by your babbling, demon. Feel the wrath of a true commander\'s army!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128244, 'enUS', '(CUT/UNUSED)Once they realize your intentions, they will seek to remove you from this plane. I will grant you a blessing that will prevent them from doing so, at least for a time.(CUT/UNUSED)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128245, 'enUS', 'I see the Farseer has edified your allegiances in my direction. Good. Let me clear away any doubts still polluting your minds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128246, 'enUS', 'I am the Windlord now, and all of you bluster about within my domain. But let us not leave this council with a whiff of foul air.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128247, 'enUS', 'Divided, we are scattered to the winds. But united, we are a cyclone of power. Let this council never dissipate again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128248, 'enUS', 'Farseer $p, I will return you to the Heart of Azeroth. Depart with my thanks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128249, 'enUS', 'Once there, I shall present you with a gift to show my appreciation. May it serve you well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128250, 'enUS', 'They\'ll never find this buried here...', 'They\'ll never find this buried here...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128251, 'enUS', 'May Elune guide your soul, my son.', 'May Elune guide your soul, my son.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128252, 'enUS', '<sigh> We lost him too soon.', '<sigh> We lost him too soon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128253, 'enUS', '<sigh> Another sentinel dead.', '<sigh> Another sentinel dead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128254, 'enUS', 'And another! Allying with these \"demons\" was a mistake.', 'And another! Allying with these \"demons\" was a mistake.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128255, 'enUS', 'The Legion hit this area hard. There don\'t seem to be any survivors.', 'The Legion hit this area hard. There don\'t seem to be any survivors.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128256, 'enUS', 'Welcome to the Scribe\'s Sacellum, $c. Can I interest you in some scrolls today?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128257, 'enUS', 'Champion! Your expertise is needed! Prepare for battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128258, 'enUS', 'Might as well start looking for something worth salvaging.', 'Might as well start looking for something worth salvaging.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128259, 'enUS', 'I know I dropped it somewhere around here...', 'I know I dropped it somewhere around here...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128260, 'enUS', 'Where could it be?!', 'Where could it be?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128261, 'enUS', 'Our allies need your aid, champion. You must act at once!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128262, 'enUS', '<yawn> Is it already time to go back to work?', '<yawn> Is it already time to go back to work?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128263, 'enUS', 'I wish I could nap all day.', 'I wish I could nap all day.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128264, 'enUS', 'Looks like that demon\'s poison got me in the end...', 'Looks like that demon\'s poison got me in the end...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128265, 'enUS', 'You were my friend! How could you do this to me? To my FAMILY?', 'You were my friend! How could you do this to me? To my FAMILY?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128266, 'enUS', 'A formidable enemy is close. Strike it down with all the malice it deserves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128267, 'enUS', 'You and your allies are needed, champion. Gather your forces!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128268, 'enUS', 'A well-fought battle. The Legion has learned to fear you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128269, 'enUS', 'Strength of arms is not the only way to strike a blow against the Legion. Well done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128270, 'enUS', 'The downfall of such an enemy will inspire our forces... and seed fear into the demons\' hearts.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128271, 'enUS', 'You and your comrades fought well. Such determination will be crucial in the battles to come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128272, 'enUS', 'Got something that needs blown up? Old Icks has got just the staff for you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128273, 'enUS', '', 'You look like you\'ve lost something. Can I help?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128274, 'enUS', '', 'The energy in this place is malevolent. Let\'s go!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128275, 'enUS', 'Intruders! Killing you will regain my master\'s favor!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128276, 'enUS', 'You will know pain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128277, 'enUS', 'I will delight in your suffering!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128278, 'enUS', 'Come, face your doom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128279, 'enUS', 'Easily broken!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128280, 'enUS', 'Pathetic mortal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128281, 'enUS', 'Another foe felled!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128282, 'enUS', 'I have earned my glory... and robbed these mortals of theirs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128283, 'enUS', 'The master will be pleased.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128284, 'enUS', 'More... agony... to come...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128285, 'enUS', 'Burn! Burn in the fires of my torment!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128286, 'enUS', 'I will flatten you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128287, 'enUS', 'Pummel you to dust!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128288, 'enUS', 'Smash you to pieces!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128289, 'enUS', 'Hold still!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128290, 'enUS', 'Hold still!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128291, 'enUS', 'Stay where you are!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128292, 'enUS', 'Pain rains upon you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128293, 'enUS', 'You dare challenge me?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128294, 'enUS', 'I will break you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128295, 'enUS', 'Let us test your resolve!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128296, 'enUS', 'You cannot withstand me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128297, 'enUS', '%s spits.', '%s spits.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128298, 'enUS', 'Hero! Our foes have infiltrated this area. We\'re relying on you to take them down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128299, 'enUS', 'We have allies in trouble nearby, champion. Please lend your aid!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128300, 'enUS', 'Our scouts report the presence of a deadly enemy nearby. Time to go on the offensive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128301, 'enUS', 'This way, champion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128302, 'enUS', 'This way! Come at once!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128303, 'enUS', 'Gather your allies, champion. A grave threat is at hand, and heroes are needed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128304, 'enUS', 'Well done! We have pushed the Legion back... for now, at least.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128305, 'enUS', 'You work today has saved lives, champion. Take pride in that!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128306, 'enUS', 'Well done! The way you took that target down reminds me of myself in my younger days. My much, much younger days.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128307, 'enUS', 'Once again, I owe you a debt of thanks. I\'d say you\'re making quite a habit of this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128308, 'enUS', 'You and your friends make an impressive fighting force. It is through our unity that the Legion will fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128309, 'enUS', 'Gather your allies, hero. The threat you are about to face will put all your skills to the test!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128310, 'enUS', 'Light preserve us! Our allies are in need. Please, champion, help them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128311, 'enUS', 'Champion! The Light has need of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128312, 'enUS', 'You have brought the Light to this dark place. Thank you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128313, 'enUS', 'You have saved many lives this day, hero. For this noble act, you have my thanks.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128314, 'enUS', 'The Legion cannot stand against the power of the Light. Not when it has champions like you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128315, 'enUS', '', 'My, it looks like Agatha\'s stumbled on another fun little plaything.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128316, 'enUS', 'The way you and your allies persevered is an inspiration to our forces. Proof that together we can save Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128317, 'enUS', '', 'Stand back while we infuse the herbs into the brew...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128318, 'enUS', '', 'There! It is done.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128319, 'enUS', '', 'Worry not, Uhrlia - this one looks spry. Surely $Ghe:she; won\'t give up so easily.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128320, 'enUS', '', 'Let\'s play, $c!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128321, 'enUS', '', 'I do hope this one is more entertaining than the last.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128322, 'enUS', '', 'Agatha... will be... pleased...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128323, 'enUS', '', 'That was... exquisite...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128324, 'enUS', 'Blessings on you, Grandmaster!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128325, 'enUS', 'I was so weak... Thank you for helping me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128326, 'enUS', 'Blech! I never thought I\'d be glad to taste something so vile!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128327, 'enUS', 'I could feel my life slipping away... You have my eternal thanks, Grandmaster!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128328, 'enUS', '', 'Blessings on you, Grandmaster!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128329, 'enUS', '', 'I was so weak... Thank you for helping me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128330, 'enUS', '', 'Blech! I never thought I\'d be glad to taste something so vile!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128331, 'enUS', '', 'I could feel my life slipping away... You have my eternal thanks, Grandmaster!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128332, 'enUS', '%s\'s Fel Bomb detonates!', '%s\'s Fel Bomb detonates!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128333, 'enUS', 'An intruder? Show yourself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128334, 'enUS', 'Did you hear that? It came from over here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128335, 'enUS', 'Nothing gets past the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128336, 'enUS', 'The Legion dispatches its all-seeing eyes to spy on our war efforts. Intolerable! Ensure nothing they have learned can be used against us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128337, 'enUS', 'The Night of the Wilds is upon us once more, friends.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128338, 'enUS', 'On this evening, the spirits of the great beasts of legend rise in these very fields.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128339, 'enUS', 'One among us has been chosen to enter the darkness.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128340, 'enUS', 'To hunt the noble spirits and return them to their honored rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128341, 'enUS', 'Raise your steins to the Huntmaster, our hunter of beasts!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128342, 'enUS', 'Let the feast begin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128343, 'enUS', 'Once you have eaten your fill, hunter, depart the table and enter the wilderness. Your prey awaits!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128344, 'enUS', 'Finding these clever beasts in the darkness will not be easy, but a true hunter is a master of tracking.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128345, 'enUS', 'Hyrtir, the great stag, emerges! Swiftest of the beasts, with horns sharpened to a spear\'s point. Luck be with you, hunter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128346, 'enUS', 'Hyrtir was first pursued by Haldor of the Rocky Cliffs. The spirit of Haldor is with the hunter this night!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128347, 'enUS', 'You follow the trail of Garos, the great worm. Many are her children, and deep are her tunnels.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128348, 'enUS', 'Garos was brought here by Bjorn of the Black Storm. The spirit of Bjorn is with the hunter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128349, 'enUS', 'Ketlingr, the great cat, drinks before you. A lithe, fierce predator, many learned to fear her fangs and claws!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128350, 'enUS', 'Ketlingr was slain by Tor of the Brutal Siege. The spirit of Tor is with the hunter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128351, 'enUS', 'Habrok, the great owl, perches above you. All beasts once feared her talons, and hid during the night from her keen vision.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128352, 'enUS', 'Ranulf of the Screaming Abyss triumphed over Habrok in life. The spirit of Ranulf is with the hunter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128353, 'enUS', 'The spirits hold you in high regard, Huntmaster! The most renowned predators of all our legends have emerged. This is truly the grandest Night of the Wilds I have ever witnessed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128354, 'enUS', 'Behold! Den-mother Fereki, great predator of the earth, and mighty Vetholnir, lord of the skies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128355, 'enUS', 'What is this? A most rare and remarkable spirit has come forth, one that has not graced these fields for countless ages.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128356, 'enUS', 'It is Haukenulfnir, who stalked the wilds when the world was new!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128357, 'enUS', 'She senses your predatory prowess and kinship with beasts. She will test you, hunter, if you approach her.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128358, 'enUS', 'She has found you worthy. The spirit of Haukenulfnir is with the hunter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128359, 'enUS', 'With her spirit now bound to yours, the children of Haukenulfnir will answer your summons in the mortal world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128360, 'enUS', 'Now come back to the fire, good hunter, and let us feast the night away!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128361, 'enUS', 'Do you understand what the tale seeks to teach us? With our companions, we are so much stronger than we are alone.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128362, 'enUS', 'Will you accept the companionship of the Wolfhawk?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128363, 'enUS', 'Ha ha, I think it likes you, Hunter! You and the beast are now bonded, in this realm and the mortal realm. Now, let us return to the feast, and leave our worries for the morning!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128364, 'enUS', 'Let us feast, then! To the Night of the Wilds! To the great beasts! To the Huntmaster!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128365, 'enUS', 'Remain vigilant, champion! We must watch for our enemy, just as they are watching us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128366, 'enUS', 'The eve of our attack is nigh. Have you summoned the forces?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128367, 'enUS', 'I will notify the summoners shortly. They will open the portals on my word only.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128368, 'enUS', 'Good... once they are through, the foolish \"Legionfall Offensive\" will stand no chance. Their demise shall be delivered.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128369, 'enUS', 'I...don\'t feel so good...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128370, 'enUS', 'Fhambar senses something...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128371, 'enUS', 'Fhambar doesn\'t find anything useful...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128372, 'enUS', '|TINTERFACE\\ICONS\\Ability_BossFelLord_FelSpike.blp:20|t Domatrax begins to cast |cFFFF0404|Hspell:234107|h[Chaotic Energy]|h|r! Take cover!', '|TINTERFACE\\ICONS\\Ability_BossFelLord_FelSpike.blp:20|t Domatrax begins to cast |cFFFF0404|Hspell:234107|h[Chaotic Energy]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128373, 'enUS', 'It looks like Fhambar has tracked down your missing mage. I\'ll be taking him back now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128374, 'enUS', 'Good luck with Agatha, $c. You will need it...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128375, 'enUS', '', 'My ward persists! It can protect you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128376, 'enUS', '', 'The chasm is warded against her magic!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128377, 'enUS', '', 'The echo of my power can save you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128378, 'enUS', '', 'He\'s ancient, but tough! He can survive this... Right?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128379, 'enUS', 'It was an honor to fight beside you, Battlelord $n. I will report to Skyhold.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128380, 'enUS', 'Hail to the Battlelord! Lok-tar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128381, 'enUS', 'I need passage to Felwing Ledge.', 'I need passage to Felwing Ledge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128382, 'enUS', 'Iron-Body Ponshu coughs weakly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128383, 'enUS', 'My entire being, even my soul... burned...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128384, 'enUS', 'I saw a vision. A tsunami of demons crashing down upon us... extinguishing every light... save for one.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128385, 'enUS', 'Grandmaster, you are that light! Do not waver in your resolve to fight the Burning Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128386, 'enUS', '', 'It is time for me to leave this realm, my friends. Almai, I believe you have grown beyond the life of a simple brewer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128387, 'enUS', 'May you rest in peace, Brewmaster.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128388, 'enUS', 'I\'m ready, Matron Mother.', 'I\'m ready, Matron Mother.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128389, 'enUS', 'If the Destroyer is alive, she\'s in great danger. No wonder she\'s so troubled...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128390, 'enUS', 'Don\'t worry, lass. We\'ll protect ye. We\'ve got to!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128391, 'enUS', 'Her nightmares... her greatest fears... we\'ve got to help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128392, 'enUS', 'Let us hear what she has to say, Magni.', 'Let us hear what she has to say, Magni.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128393, 'enUS', '', 'Archdruid! The Shrine of Aviana is under attack again, and this time it\'s a full-scale invasion! The Ancient needs our help!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128394, 'enUS', '', 'To hell with the front lines! I\'m going around back to take out their casters and artillery. You\'re welcome to join me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128395, 'enUS', '', 'Did you hear that? Screams from the shrine! We\'ve gotta get back there!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128396, 'enUS', '', 'Die, demon scum!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128397, 'enUS', 'Perish before the might of the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128398, 'enUS', '', 'You wretched... No! The idol!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128399, 'enUS', '', 'Noooo!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128400, 'enUS', '', 'The shrine is lost. Druids, take flight! Retreat!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128401, 'enUS', '', 'What the... Flight Form\'s not working?! With the idol gone, we\'ve lost our wings! Everyone teleport out and regroup at the Dreamgrove!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128402, 'enUS', '', 'I\'ll meet you in Azsuna, Archdruid.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128403, 'enUS', '', 'Let\'s see what\'s going on... discreetly!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128404, 'enUS', 'Legion! With this idol, the cowardly mortals\' floating city will no longer protect them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128405, 'enUS', '', 'Wrong! You die this day, demon!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128406, 'enUS', 'You are too late, little druid! We will use your power of flight to bring down Dalaran!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128407, 'enUS', '', 'Blasphemy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128408, 'enUS', '', 'Over here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128409, 'enUS', '', 'Aviana, please! Help us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128410, 'enUS', 'Enough of this game!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128411, 'enUS', '', 'It is done. Let\'s get this idol back to the Dreamgrove.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128412, 'enUS', '', 'Druids! We were victorious! The Idol of Aviana is once again safe. Archdruid, please do the honors and put the idol back where it belongs.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128413, 'enUS', 'She\'s calmer now, but still troubled. I should be able ta reach her.$b$bAre ye ready, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128414, 'enUS', '', 'Along with Xylem\'s other apprentices, I have been working to pinpoint his location. I believe we have found him in the Eye of Eternity, $ct!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128415, 'enUS', '', 'From that location, Xylem could use the Focusing Iris to wreak untold havoc on Azeroth\'s ley lines.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128416, 'enUS', '', 'The mages of the Kirin Tor can open a temporary portal to his location. You must put an end to Xylem\'s madness... but please, spare his life if you can!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128417, 'enUS', '', 'Archdruid! I need your help!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128418, 'enUS', '', 'I\'ll meet you there. We have to hurry--before it\'s too late!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128421, 'enUS', 'Make sure to study the runes well, $c. I don\'t want to know what will happen if we fail!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128422, 'enUS', 'The Legion may try to wear us down - to erode our allies like rain-swept valleys after a storm, but we will never surrender. It is in our blood to fight until the sun shines upon us once more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128423, 'enUS', 'I am ready to begin.', 'I am ready to begin.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128424, 'enUS', '', 'I command legions, mortal!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128425, 'enUS', '', 'So many treasures, just waiting for me to grab!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128426, 'enUS', 'I draw upon the power of my ancestors!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128427, 'enUS', 'The Drekirjar never yield!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128428, 'enUS', '|TInterface\\Icons\\ACHIEVEMENT_BOSS_SVALASORROWGRAVE.BLP:20|tSeveral Dark Val\'kyr form ranks to |cFFFF0000|Hspell:237761|h[charge]|h|r across the battlefield!', '|TInterface\\Icons\\ACHIEVEMENT_BOSS_SVALASORROWGRAVE.BLP:20|tSeveral Dark Val\'kyr form ranks to |cFFFF0000|Hspell:237761|h[charge]|h|r across the battlefield!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128429, 'enUS', '|TInterface\\Icons\\SPELL_FEL_ELEMENTALDEVASTATION.BLP:20|t%s starts casting |cFFFF0000|Hspell:237952|h[Ancestral Knowledge]|h|r!', '|TInterface\\Icons\\SPELL_FEL_ELEMENTALDEVASTATION.BLP:20|t%s starts casting |cFFFF0000|Hspell:237952|h[Ancestral Knowledge]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128430, 'enUS', '|TInterface\\Icons\\ABILITY_WARRIOR_RAMPAGE.BLP:20|t%s gains |cFFFF0000|Hspell:237947|h[Berserker\'s Rage]|h|r!', '|TInterface\\Icons\\ABILITY_WARRIOR_RAMPAGE.BLP:20|t%s gains |cFFFF0000|Hspell:237947|h[Berserker\'s Rage]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128431, 'enUS', '|TInterface\\Icons\\ABILITY_WHIRLWIND.BLP:20|t%s creates a |cFFFF0000|Hspell:237866|h[Bladestorm]|h|r!', '|TInterface\\Icons\\ABILITY_WHIRLWIND.BLP:20|t%s creates a |cFFFF0000|Hspell:237866|h[Bladestorm]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128432, 'enUS', '|TInterface\\Icons\\ABILITY_WARLOCK_SHADOWFURYTGA.BLP:20|tSeveral |cFFFF0000|Hspell:237908|h[Runes]|h|r appear on the ground nearby.', '|TInterface\\Icons\\ABILITY_WARLOCK_SHADOWFURYTGA.BLP:20|tSeveral |cFFFF0000|Hspell:237908|h[Runes]|h|r appear on the ground nearby.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128433, 'enUS', '|TInterface\\Icons\\SPELL_FEL_ELEMENTALDEVASTATION.BLP:20|t%s channels the |cFFFF0000|Hspell:237945|h[Blood of her Father]|h|r!', '|TInterface\\Icons\\SPELL_FEL_ELEMENTALDEVASTATION.BLP:20|t%s channels the |cFFFF0000|Hspell:237945|h[Blood of her Father]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128434, 'enUS', 'Demonic filth taints this land, champion. We must eradicate it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128435, 'enUS', 'Give the word when you are ready, champion, and we will take to the skies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128436, 'enUS', '', 'The power of my father\'s blood flows through me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128437, 'enUS', 'Behold the speed of my axe!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128438, 'enUS', 'The fury of the storm boils within me! Have you finished your preparations?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128439, 'enUS', 'Greetings, champion. Are you here to answer the call of Odyn?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128440, 'enUS', 'Well done! Our world will never fall so long as it is defended by champions such as you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128441, 'enUS', '', '(UNUSED)Your demon sight will be beneficial in keeping track of its trail of blood.(UNUSED)', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128442, 'enUS', 'Welcome to Felwing Ledge, Slayer $p. The Matron Mother is waiting for you down in the training area, just west of here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128443, 'enUS', '', 'Odyn and the greatest of his hunters await your presence at the Fields of the Eternal hunt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128444, 'enUS', 'I am ready to attend the feast.', 'I am ready to attend the feast.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128446, 'enUS', '', 'This body... my mind... compromised. I have failed the makers!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128447, 'enUS', '', 'End me, mortals! Then rob the Deceiver of his prize!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128448, 'enUS', '', 'I was not the first to place defenses in this temple, but I fear the ancient watcher charged with safeguarding this place... has been compromised. Be wary. I will offer you what protection I can.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128449, 'enUS', '', 'For millennia I was steadfast in my vigil... unwavering. And then his voice called to me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128450, 'enUS', '', 'The unmovable object met the irresistible force, and I found... a new calling.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128451, 'enUS', '', 'What lies below is not for mortal eyes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128452, 'enUS', '', 'You will not disturb the master\'s work!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128453, 'enUS', '', 'A necessary end.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128454, 'enUS', '', 'Flesh is weak!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128455, 'enUS', '', 'My vigil continues. But... who is it I serve?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128456, 'enUS', '', 'Your world\'s hope dies with you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128457, 'enUS', '', 'My vigil ends! Let not... the dark one... awaken...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128458, 'enUS', '', 'He will rise! He... will... reign...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128459, 'enUS', 'Where would I find such a thing?', 'Where would I find such a thing?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128460, 'enUS', '', 'Well, it must be also be nightborne in origin for a harmonious marriage to be achieved. Oh - and it must be newly forged and still unattached. Anything else will result in disaster. \n\nIf the rumors are true, Aethas might be able to help. Supposedly he has taken a rather striking nightborne apprentice! It is quite scandalous.\n\nYou should ask him to introduce you to her.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128461, 'enUS', '', 'Outsider! Sigryn has stormed the Halls of Valor, determined to confront Odyn. In her blind rage, she races headlong to her doom!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128462, 'enUS', '', 'Your Kirin Tor have found the means to convey you into Odyn\'s halls. Hurry! Sigryn must be made to see reason, before it is too late!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128463, 'enUS', '', 'The fate of the Tideskorn rests in your hands. Gods be with you, outsider!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128464, 'enUS', 'Be calm, Aviash. $n is one of our allies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128465, 'enUS', '%s squawks loudly as you approach.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128466, 'enUS', 'What a hunt! This, out here, is the true spirit of the wilds. Let us revel the night away and leave our cares for the morning!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128467, 'enUS', '<Revel the night away and awake in Trueshot Lodge.>', '<Revel the night away and awake in Trueshot Lodge.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128468, 'enUS', 'It was an honor to fight alongside you, Battlelord $n. I will report to Skyhold. See you there!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128469, 'enUS', '', 'I will meet you at Valdisdall in Stormheim. We should be able to reach Skold-Ashil from there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128470, 'enUS', '', 'Movement in the trees! Watch out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128471, 'enUS', '', 'My last moments here were spent escaping the clutches of assassins sent to murder my family.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128472, 'enUS', '', 'Be forewarned. They may still be on the hunt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128473, 'enUS', '', 'It would seem that escaping my past was too much to hope for.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128474, 'enUS', '', 'I will never forget the night they came. My mother fell before my eyes, and they had descended on my brother before I managed to escape.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128475, 'enUS', '', 'Cowardly scum! I would hunt down every one of them, and their masters as well, if I knew who sent them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128476, 'enUS', '', 'Another ambush! Bare your steel!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128477, 'enUS', '', 'It\'s no use, though. They never fail to elude my grasp. I must concentrate on the task at hand.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128478, 'enUS', '', 'I take solace knowing they will be judged by the gods when their time comes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128479, 'enUS', '', 'After that night, I was alone. The last of my bloodline.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128480, 'enUS', 'Grandmaster! Could you spare a moment to listen to an old man\'s tale?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128481, 'enUS', 'Xuen, the White Tiger, one of the August Celestials, had a son named Ban-Lu.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128482, 'enUS', 'Over the centuries, Ban-Lu served as the personal mount of each grandmaster. It was a duty he was honored to fulfil!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128483, 'enUS', 'But Ban-Lu has not been seen since the Legion\'s attack on the Peak of Serenity. It seems the loss of life... affected him deeply.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128484, 'enUS', 'Perhaps if we return there, we may find clues to his whereabouts.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128485, 'enUS', 'Grandmaster! Over here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128486, 'enUS', 'Look for any sign of where Ban-Lu might be.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128487, 'enUS', 'What have you found? Let me see...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128488, 'enUS', 'A trail of paw prints! Hmm, where might they lead...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128489, 'enUS', 'Phew! An exciting journey, but wearying. Please allow me a moment to rest. Perhaps you could ask these grummles whether they\'ve seen Ban-Lu.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128490, 'enUS', 'Wait for me, please! These old bones aren\'t what they used to be.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128491, 'enUS', 'Could you slow down a moment? I do apologize for my languid pace.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128492, 'enUS', 'Where did you go? Oh, wait there. Thank you for showing patience to an old man.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128493, 'enUS', 'Shall we continue the search for Ban-Lu, Grandmaster?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128494, 'enUS', 'It seems we have reached a dead end. I shall meditate on this conundrum.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128495, 'enUS', 'Grandmaster, I have an idea...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128496, 'enUS', 'Grandmaster! Have you found Ban-Lu?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128497, 'enUS', 'Not here? Are you sure? Perhaps you should be more observant, Grandmaster...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128498, 'enUS', 'Filled with sorrow by the death of Grandmaster Hight, I wandered Pandaria to reflect.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128499, 'enUS', 'But in the midst of that reflection, I saw something new and interesting appear... you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128500, 'enUS', 'You have shown patience with Master Bu, honor with the grummles, duty with the Shado-Pan, and faith with the jinyu.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128501, 'enUS', 'It is clear you embody the wisdom, integrity, and tenacity of a grandmaster. Only one test remains... Prove to me that you can fight like a grandmaster!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128502, 'enUS', 'Your skills are impressive indeed, Grandmaster! I would be honored to serve you, as I served the great leaders of our order who came before.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128503, 'enUS', 'Let us return to the Wandering Isle... together!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128504, 'enUS', 'We will not be stopped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128505, 'enUS', 'We must give up! The strength of the Legion will destroy us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128506, 'enUS', '', 'I\'m prepared to take you to the Halls of Valor. Speak with me when your allies have gathered and you are ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128507, 'enUS', 'Where are we going today, Grandmaster?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128508, 'enUS', 'Good to see you again, Grandmaster.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128509, 'enUS', 'You might want to cut back on the mana buns, Grandmaster...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128510, 'enUS', 'Good luck, Grandmaster!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128511, 'enUS', 'Farewell!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128512, 'enUS', 'The lazy yak never gets washed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128513, 'enUS', 'Listen to all voices, Grandmaster. Surrounded only by those who share your views, your mind can never grow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128514, 'enUS', 'Idleness rarely leads to success, Grandmaster.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128515, 'enUS', 'Is it much further, Grandmaster?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128516, 'enUS', 'Remember to finish crossing the river before you tell the crocolisk he has bad breath!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128517, 'enUS', 'To speak of change without being willing to take action is like digging a hole to catch a cloud serpent.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128518, 'enUS', 'A kite cannot fly unless the wind blows against it, Grandmaster.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128519, 'enUS', 'The wise monk chooses their own style, rather than letting popular opinion dictate it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128520, 'enUS', 'The best time to plant a tree is fifty years ago. The second-best time is now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128521, 'enUS', 'Do not concern yourself with failure, Grandmaster. Rather, focus on rising every time you fall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128522, 'enUS', 'The wise brewmaster recognizes the importance of taking a drink... and the importance of stopping before the drink takes the brewmaster.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128523, 'enUS', 'Have I told you the tale of the hozen and the buttercream pie? Hmm... Perhaps best saved for another time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128524, 'enUS', 'Ah, a refreshing swim... Reminds me of the time I swam all the way to Kezan. Grandmaster Rochu wasn\'t happy about how shriveled his paws got!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128525, 'enUS', 'Don\'t worry about me, Grandmaster. I remembered my water breathing potions.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128526, 'enUS', 'You handle this one, squire.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128527, 'enUS', 'Assist me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128528, 'enUS', 'I require... a short respite.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128529, 'enUS', 'Did you take heed of my victory, squire?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128530, 'enUS', 'Another fiend felled by my mighty blows!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128531, 'enUS', 'Well, that was easy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128532, 'enUS', 'I shall slay thee with my mighty sword!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128533, 'enUS', 'Excaliberto! Attack!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128534, 'enUS', 'What a strange looking dragon! No matter!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128535, 'enUS', 'For Doloria!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128536, 'enUS', 'By my might, I slay thee!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128537, 'enUS', 'Why hello again, squire!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128538, 'enUS', 'What ho!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128539, 'enUS', 'We shall slay mighty beasts together!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128540, 'enUS', 'Let us ride!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128541, 'enUS', 'What ho!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128542, 'enUS', 'Yes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128543, 'enUS', 'How may I serve?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128544, 'enUS', 'Very well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128545, 'enUS', 'Oh begone! I have dragons to slay!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128546, 'enUS', 'Farewell, then.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128547, 'enUS', 'Do you know who it is you are addressing?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128548, 'enUS', 'I am Maximillian of Northshire, damsel rescuer extraordinaire. How dare you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128549, 'enUS', 'I say, are you keen to feel the pointy part of my blade?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128550, 'enUS', '', 'I will meet you back at the Hall of the Guardian, Archmage $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128551, 'enUS', 'It is time, my friend. I have located Erdris Thorn in the upper spires of Blackrook Hold, where he plots to raise an undead army!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128552, 'enUS', 'A pair of Bradensbrook\'s fiercest fighters have insisted on joining our campaign. But even with their help, the battle ahead will be treacherous.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128553, 'enUS', 'Come, $n. Let us put Erdris to rest... once and for all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128554, 'enUS', 'Tugar Bloodtotem has resurfaced in Highmountain. With him is an immense worm saturated with fel energy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128555, 'enUS', 'We must put an end to him, along with that nasty pet of his, before they attack Thunder Totem. This will be no easy task.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128556, 'enUS', 'I have given my location to your mages. Once you arrive here, we will enter Tugar\'s cavern and neutralize his threat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128557, 'enUS', '', 'The Prophet has prepared his incantations, and I have meditated long enough. Kruul must die.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128558, 'enUS', '', 'Upon your arrival, we will reopen the portal to the demon\'s domain and finish him... once and for all!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128559, 'enUS', '', 'This mage will teleport you to us. The task ahead will not be easy. Be prepared!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128560, 'enUS', 'Remember, keep your distance from the Nightborne Soulstone as it is siphoning energy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128561, 'enUS', 'Well done. Meet me near Lady Remor\'za\'s tower and I will summon a portal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128562, 'enUS', '', 'C\'mon, Archdruid! My people need our help!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128563, 'enUS', '', 'To the dark depths of Hel with you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128564, 'enUS', 'Yes?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128565, 'enUS', 'What needs conjuring?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128566, 'enUS', 'You require my services?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128567, 'enUS', 'What shall we summon today?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128568, 'enUS', 'The dreadsteeds will be ours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128569, 'enUS', 'Xoroth beckons.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128570, 'enUS', 'The demons will bow to us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128571, 'enUS', 'Fel hooves grant you swiftness.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128572, 'enUS', 'Have you brought me more blood?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128573, 'enUS', 'Shouldn\'t you be fetching materials or something?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128574, 'enUS', 'Why don\'t I ever get to keep the dreadsteed?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128575, 'enUS', '', 'Foolish druids... our distraction has succeeded!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128576, 'enUS', '', 'Hail, outsider.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128577, 'enUS', '', 'What is your purpose?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128578, 'enUS', '', 'My shield is ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128579, 'enUS', '', 'I stand unbroken.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128580, 'enUS', '', 'I will not falter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128581, 'enUS', '', 'Gods go with you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128582, 'enUS', '', 'Fight with honor, outsider.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128583, 'enUS', '', 'I will be hunted no longer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128584, 'enUS', '', 'Stop. Now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128585, 'enUS', '', 'Perhaps my father was right about you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128586, 'enUS', '', 'My bloodline is not known for having even tempers.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128587, 'enUS', '', 'Even the Legion has not been this irritating!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128588, 'enUS', '', 'You know, I might look good in green...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128589, 'enUS', '', 'A fel-fire spear might prove useful right about now...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128590, 'enUS', 'Well met.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128591, 'enUS', 'Hail, adventurer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128592, 'enUS', 'Light\'s blessings, traveller.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128593, 'enUS', 'How can I help?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128594, 'enUS', 'The Light grant you strength.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128595, 'enUS', 'I will protect the realm.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128596, 'enUS', 'My shield is yours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128597, 'enUS', 'I will smite our foes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128598, 'enUS', 'Be wary of dragons. They cannot be trusted!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128599, 'enUS', 'In our time together, my brother and I have seen wars and tests aplenty.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128600, 'enUS', 'I am but one of many who has taken up the lance for Azeroth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128601, 'enUS', 'Greetings.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128602, 'enUS', 'I can always use another apprentice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128603, 'enUS', 'Do you like... trials?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128604, 'enUS', 'Am I here... or am I an illusion?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128605, 'enUS', 'Do try not to die now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128606, 'enUS', 'Go forth, and do... something!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128607, 'enUS', 'Wait, who are you again?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128608, 'enUS', 'Keep up your studies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128609, 'enUS', 'Wait... did I leave the Trial of Fire on? I\'m pretty sure I turned it off...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128610, 'enUS', 'Apprentices..? Apprenticeese..? Apprentici..? Yes, I think that\'s it...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128611, 'enUS', 'You\'ll never become a real mage wearing that outfit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128612, 'enUS', 'I went full shadow... Never go full shadow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128613, 'enUS', '', 'If this was only a distraction, where\'s the main attack? We need to ask Aviana. She\'ll know!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128614, 'enUS', 'We discovered the saboteur\'s corpse near the weapons core. His soul had been drained... Ugh, such a terrible way to die.\n\nThe situation is worse than I let on, I\'m afraid.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128615, 'enUS', 'It seems our allies somehow repelled the attack... stay at the ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128616, 'enUS', 'How did the Legion gain access to Dalaran\'s weapons?', 'How did the Legion gain access to Dalaran\'s weapons?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128617, 'enUS', 'You\'re ordered to report to the battlefield at once. Take out those demons shredding our front lines!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128618, 'enUS', 'The saboteur was from the Horde - one of my own Sunreavers, in fact.\n\nThere are those on the Council who strongly opposed allowing us back into the Kirin Tor in the first place. As you can imagine, this gives them all the ammunition they need to lobby for our expulsion yet again.\n\nAnd if that happens, our truce will shatter and Azeroth will surely fall to the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128619, 'enUS', 'Go on.', 'Go on.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128620, 'enUS', 'The mighty Legion has attacked, and we are barely holding off their siege.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128621, 'enUS', '', 'Even with Kanrethad returned to us, these occultists remain loyal to the Legion. We cannot let them regain control of Felfire Pass. Thin their numbers and absorb their sources of power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128622, 'enUS', '', 'Such a display of power! I\'d wager those occultists regret allying with our enemy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128623, 'enUS', '', 'Felfire Pass is crawling with occultists loyal to the Burning Legion. We cannot let them gain a footing!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128624, 'enUS', '', 'Even with their leader dead, these felborne trappers won\'t stop! Take this manaroot salve and apply it to any wounded animals you see. Save as many as you can!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128625, 'enUS', 'Thunder Totem is under siege! Let us save our comrades before we knock that demon vessel out of the sky.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128626, 'enUS', '', 'You are a true protector of nature. You have my thanks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128627, 'enUS', '', 'My chosen offer the gift of life, champion. Claim their prize and fight on!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128628, 'enUS', '', 'Champions, the Legion seeks to infiltrate Thunder Totem from below. Help push them back!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128629, 'enUS', 'You are a traitor to our people, siding with these demon worshippers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128630, 'enUS', '', 'One of Navarrogg\'s geomancers is sealing the tunnels to stem the flow of Legion enemies. Defend him at all costs!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128631, 'enUS', '', 'Argh!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128632, 'enUS', '', 'Heroes... deal with this... fiend...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128633, 'enUS', '', 'Good work, heroes. Though only two of the tunnels were sealed, every bit helps.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128634, 'enUS', 'We are ready to attack the Legion ship. Mayla, champions, meet me outside Thunder Totem. Time to make them pay!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128635, 'enUS', '', 'Understood. Heroes, lets get to the rendezvous point.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128636, 'enUS', '', 'Look out!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128637, 'enUS', '', 'We must keep moving!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128638, 'enUS', '', 'Once aboard, rescue all the captives you can. When my people are safe, set these explosives and get off the ship... fast!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128639, 'enUS', 'These war eagles will carry us to our destination. Ready to go? Then climb aboard!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128640, 'enUS', 'I leave it to you to sabotage the Legion ship and rescue our allies. The demon commander is mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128641, 'enUS', 'Arrgh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128642, 'enUS', '', 'Felborne trappers are using their twisted magic upon the wildlife here. Take this manaroot salve and apply it to any wounded animals you see. Hurry, before the corruption takes hold!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128643, 'enUS', 'Heh. Is this all this planet can offer? So very weak. I desire a true challenge.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128644, 'enUS', '', 'Hurry, heroes. Lasan needs your help!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128645, 'enUS', 'A close call. Thank you for the help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128646, 'enUS', 'What a battle! Any moment now this ship will explode into tiny pieces. No matter! One of our eagles will see you to safety!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128647, 'enUS', '', 'The fel contagion has found its way to Deadwood Landing once again, High Priest. We must contain this sickness before it infects the mainland.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128648, 'enUS', '', 'A terrible contagion has consumed Deadwood Landing. The harpies that infest the island are carriers. Their corruption must not be allowed to spread!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128649, 'enUS', '', 'Brilliant work! The contagion appears to be contained.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128650, 'enUS', 'Tell me more about Arcanist Ryanna.', 'Tell me more about Arcanist Ryanna.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128651, 'enUS', 'I discovered her quite by accident, really. Er, well, she discovered me.\n\nI got myself into a bit of a.. situation... with a harpy and a banshee, and... oh nevermind.\n\nAnyway, Ryanna freed me with some very impressive spellwork. And then asked to join the Tirisgarde.\n\nHow could I turn her down?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128652, 'enUS', '', 'I\'ll meet you up ahead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128653, 'enUS', '\'Tis good ta see ye, $n. Now that the nightmares are gone, we can find out what she wants ta tell us. Give the word and we\'ll begin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128654, 'enUS', 'She\'s tryin\' ta send me a message... a warning, methinks. But somethin\'s not right with her.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128655, 'enUS', 'Meet me in Sholazar Basin up in Northrend. Together, we\'ll get ta the bottom o\' this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128656, 'enUS', 'A shame Brann didn\'t come with ye. Nothin\' me brother loves more than openin\' titan doors!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128657, 'enUS', 'Enough of this! I will put an end to you myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128658, 'enUS', 'Anomaly detected. Engaging defense protocol Gamma Theta Phi Omicron.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128659, 'enUS', 'Reactivation sequence complete. Initiating sentry protocols.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128660, 'enUS', 'Self-repair mode resumed. External assistance evaluation: appreciated.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128661, 'enUS', 'I sense her fear... her doubt. We\'ve gotta help her! Looks like some o\' the defenses are down. If ye reactivate \'em, it might help snap her out o\' this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128662, 'enUS', 'Ye did it! The nightmares are contained. Keep going, I\'m just ahead in the next chamber.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128663, 'enUS', 'You are BLIND. I WILL FORCE YOUR EYES OPEN.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128664, 'enUS', 'Champion! Azeroth needs our help.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128665, 'enUS', 'Ah, yer gettin\' close. Come ta the Makers\' Overlook, high in the eastern mountains. I\'ll show ye the way inside.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128666, 'enUS', 'Here ya go. This pad\'ll bring ye deep below ground, ta a place no mortal has walked in millennia... the Hall o\' Communion! C\'mon down, I\'ve got a lot to show ye.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128667, 'enUS', 'The titans built these halls ta watch over the soul o\' the world. From this chamber I can hear her, clear as diamond.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128668, 'enUS', 'Lately she\'s seemed... troubled. Hard ta read. I hope ye can help me get through ta her.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128669, 'enUS', 'Somethin\'s triggered the defenses! Careful now, ye\'ve got company!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128670, 'enUS', '', 'I\'m not sure if this will work, but I might as well give it a shot... Stand back!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128671, 'enUS', 'These phantoms must be her dreams... her nightmares. If we help her overcome these horrors, Azeroth should be able ta give us her message.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128672, 'enUS', '', 'The shal\'dorei have been isolated for so many generations. We find it difficult to open our minds to new ways of thinking.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128673, 'enUS', 'Talk ta me, lass. Tell me what\'s got ye all in a tizzy...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128674, 'enUS', '', 'If my elders knew I was consorting with an outlander, I would be banished... Or worse.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128675, 'enUS', 'The Dark Titan comes! We gotta stop him! Argus... the Legion\'s homeworld. She says our destiny lies there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128676, 'enUS', '', 'Aviana! Your children need you. The Druids of the Talon are being slaughtered at the hands of the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128677, 'enUS', 'Her feelin\'s are a jumble. I need ta focus, let her thoughts flow into--UNNGG!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128678, 'enUS', '', 'I have always found magic to be wondrous in all of its forms. Now that the shield has dropped, I cannot wait to master all of it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128679, 'enUS', 'Sands... soaked in pain. Rivers... rivers of blood.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128680, 'enUS', 'How did you meet Aethas?', 'How did you meet Aethas?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128681, 'enUS', '', 'To think my brothers and sisters died to a \"distraction\"... I won\'t stand for it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128682, 'enUS', 'Skies aflame. A sword... in the darkness... the wound... burning...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128683, 'enUS', 'How can we help?', 'How can we help?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128684, 'enUS', '', 'Aviana, your birds bring news from all over Azeroth. Please, help us find the demons\' target! We must avenge our fallen kin!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128685, 'enUS', 'Sar...ger...as...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128686, 'enUS', '', 'Oh! I came upon him in Tel\'anor caught in a harpy\'s bonds without a stitch of clothing! Ha!\n\nThe harpy was so busy arguing with a banshee over who got to play with him first, that they did not notice me at all.\n\nHe looked terribly uncomfortable, so I intervened. And the rest is history!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128687, 'enUS', 'Threat neutralized. Deploying repair matrix.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128688, 'enUS', 'No local threats detected. Commencing assistance appreciation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128689, 'enUS', '', 'Threat signatures neutralized. Facility defenses at 74.39 percent. Repairs in progress.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128690, 'enUS', '', 'Subject: Azeroth. Minor arrhythmia detected, but vitals within acceptable parameters. Incident reports updated.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128697, 'enUS', 'I\'m coming for you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128698, 'enUS', 'Let\'s go.', 'Let\'s go.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128699, 'enUS', '', 'Those who harm my children shall pay!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128700, 'enUS', '', 'Aviana!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128701, 'enUS', 'Spiders! Of all the infernal demons the Legion could dispatch into my realm, why did it have to be spiders?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128702, 'enUS', 'It is imperative that you destroy any clutches of eggs you find. This infestation must not spread!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128703, 'enUS', '', 'Wow... Aviana...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128704, 'enUS', '', 'There\'s no time to lose, Archdruid! I\'m ready to stab some demons!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128705, 'enUS', 'I have faced malevolent naga, angry spirits, and insidious demons. But spiders... unbearable!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128706, 'enUS', 'I have a very sticky situation for you, champion. We need silk harvested from the broods of the Creeping Grotto to use for a rather complex incantation. Can you help?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128707, 'enUS', 'I sense a foul force within Bradensbrook, hero. Search for Legion presence and destroy it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128708, 'enUS', 'Well done, hero! So long as the shadow of the Legion darkens our world, we must remain vigilant.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128709, 'enUS', 'We were having quite a time finding volunteers brave enough to enter that nest of evil. If you need a new pair of shoes after wading through those webs, I have a connection in Dalaran that can offer you a discount!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128710, 'enUS', 'Hey bub! I got a proposition for ya!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128711, 'enUS', 'Now we\'re talkin\'! Just look at that thing go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128712, 'enUS', 'Don\'t worry about our test subjects. I\'m sure they\'re used to these things by now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128713, 'enUS', 'That\'s enough testing, bud. I think it\'s time to start thinking about mass production!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128714, 'enUS', 'Swing back to the Hall of the Guardian, and I\'ll give ya a little something for your troubles.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128715, 'enUS', 'Alright, now the first thing you gotta do is get yourself a hat with a pocket dimension portal inside.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128716, 'enUS', 'Then you just fill it with bunnies, aaaand.... bam!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128717, 'enUS', 'Here, now you try.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128718, 'enUS', 'What the whatnow!?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128719, 'enUS', 'Alright pal, just poke around in there and see what\'s going on with the rabbits.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128720, 'enUS', 'How did a wolf get in there?! Well don\'t just stand around, kill the thing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128721, 'enUS', 'Phew, that was a close one! Rabbits don\'t come cheap, y\'know!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128722, 'enUS', 'Hang tight a sec. I\'ll get you outta there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128723, 'enUS', 'Alright! I\'ll grab my stuff and we can ditch these stiffs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128724, 'enUS', 'See ya later, prestigidi--, um, prestidigigat-- Eh, forget it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128725, 'enUS', 'I sense another infestation of demons nearby. Champion, I am counting upon you to eradicate them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128726, 'enUS', 'Haha! So long, arch-losers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128727, 'enUS', 'It will take time, but thanks to your deeds the land will one day recover. Well done, hero!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128728, 'enUS', 'Your path leads only to ruin. Lay down your weapons and bow before your masters!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128729, 'enUS', 'Don\'t like the show? You can kiss my sparkling enchanted posterior!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128730, 'enUS', 'Oh, coming back for more, I see? Well, ya better be a good tipper, bub!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128732, 'enUS', '', 'Help me! Hurry!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128734, 'enUS', '', 'Come on, let\'s go! We have to save Goldrinn!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128735, 'enUS', 'I had this compass crafted for my father\'s birthday. I gave it to him a few months before my ship ran aground on Pandaria. A lifetime ago...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128736, 'enUS', 'Since this war with the Legion began, I\'ve barely had a chance to think or breathe... or to mourn. Perhaps it\'s time to change that.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128737, 'enUS', 'Thank you for bringing my father\'s compass back to me. It is a reminder of how much I miss him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128738, 'enUS', 'Our guest arrives. Perhaps now there is hope of reaching the king.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128739, 'enUS', 'Anduin refuses to eat or sleep. He ignores every plea to return to the palace. Nothing I say seems to offer comfort.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128740, 'enUS', 'I understand the need to mourn, but the king\'s place is on his throne. The Broken Shore must be our focus. Convince Anduin to end his vigil!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128741, 'enUS', '', 'The portals should be around here somewhere... Keep your eyes open!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128742, 'enUS', '', 'Can you imagine the demons coming through and ending up with a face full of vines? Classic!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128743, 'enUS', '', 'That\'ll show \'em!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128744, 'enUS', '', 'No demons are coming through this portal any time soon!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128745, 'enUS', '', 'They... They\'re trying to corrupt Goldrinn! Quick, Archdruid!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128746, 'enUS', 'King Greymane said you would be coming. Please, go right in.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128747, 'enUS', 'I\'ll need a disguise to move through the city without attracting attention.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128748, 'enUS', 'I think I know just the spell. Follow me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128749, 'enUS', '<Goldrinn lays asleep, peaceful now that the fel energies are gone.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128750, 'enUS', 'Wake up, Goldrinn.', 'Wake up, Goldrinn.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128751, 'enUS', 'You should take out those imps quickly. They\'re not the hardiest creatures, but they can overwhelm you if left unchecked!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128752, 'enUS', 'The Legion will not get away with this!', 'The Legion will not get away with this!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128753, 'enUS', 'Admit that I am right! Break the body and the spirit follows.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128754, 'enUS', 'Physical pain? Bah! Mental anguish is the true weapon. Was it not the key to turning Rakeesh?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128755, 'enUS', 'Bah! It was the suffering I inflicted which... Hold! The mortals come! That fool Goroth has failed again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128756, 'enUS', 'Sooner than expected, but no matter. Our work begins!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128757, 'enUS', '', 'They brought back my cousin\'s body from the Broken Shore last week. I expect a lot more to follow.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128758, 'enUS', 'Pain! Pain beyond measure!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128759, 'enUS', 'Agony impales you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128760, 'enUS', 'At least we\'re safe here in Stormwind. King Wrynn will keep the city safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128761, 'enUS', 'I will cut you to the core!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128762, 'enUS', 'Feed me with your screams!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128763, 'enUS', '', 'Funny, I thought I just heard... No. It must be my imagination playing tricks on me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128764, 'enUS', 'Your suffering will be legend!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128765, 'enUS', 'How much can you withstand?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128766, 'enUS', 'This will hurt... I promise!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128767, 'enUS', 'They\'re right to be afraid. If we fail to stop the Legion on the Broken Shore, the demons will strike us here, at our home and hearth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128768, 'enUS', 'Another broken body!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128769, 'enUS', 'Fragile mortal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128770, 'enUS', 'Too much to endure?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128771, 'enUS', 'Death was too easy an escape for them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128772, 'enUS', 'Their souls shall bear the torment of defeat for all eternity!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128773, 'enUS', 'The pain... of failing... the master...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128774, 'enUS', 'Our suffering... will be... glorious...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128775, 'enUS', 'I offer respite from the darkness! Surrender your will to me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128776, 'enUS', 'Why endure this pain? Kneel to the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128777, 'enUS', 'I will burn away your defiance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128778, 'enUS', 'Resistance will only bring pain!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128779, 'enUS', 'I\'m King Anduin! I\'m gonna kill every demon I see!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128780, 'enUS', 'Your minds will break!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128781, 'enUS', 'Beg for release, mortals!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128782, 'enUS', '', 'Don\'t be Anduin, be Varian! He was way tougher.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128783, 'enUS', 'Yeah, my dad says that too. Okay, I\'m King Varian. Look out, demons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128784, 'enUS', 'I can\'t blame the lad for his choice. My father sacrificed everything to save his people. He was a great king.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128785, 'enUS', 'You are mine now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128786, 'enUS', 'So weak a will!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128787, 'enUS', 'Too delicate to survive!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128788, 'enUS', 'That one\'s mind gave out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128789, 'enUS', 'Mortal minds break so easily. It is unsatisfying.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128790, 'enUS', 'It was their bodies that gave out! The triumph is mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128791, 'enUS', 'This defeat... will haunt me...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128792, 'enUS', 'There is... more pain... to come...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128793, 'enUS', 'Take heed, mortals! Obedient souls serve the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128794, 'enUS', 'Look how eagerly this soul obeys us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128795, 'enUS', 'All my handiwork... ruined!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128796, 'enUS', 'You will pay for that insult!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128797, 'enUS', 'Since you brought my father\'s compass back to me, I\'ve prayed to the Light for guidance, and to know his spirit is at rest. The Light doesn\'t answer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128798, 'enUS', 'Poor soul! And you think I am the cruel one...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128799, 'enUS', 'My people like me, respect me... but they don\'t believe in me. Not like they believed in him.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128800, 'enUS', 'I\'ve heard the voice of the people, but how do my father\'s advisors feel? I fear they do not trust me as they did my father.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128801, 'enUS', 'The young king will become a great leader. I have foreseen it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128802, 'enUS', 'Visions don\'t win wars, Prophet. The boy has a good heart, but he\'s untested.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128803, 'enUS', 'The Light is strong with Anduin. He will grow into his crown. We must have faith.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128804, 'enUS', 'Yes, my precious ones. The fel has brought out our true beauty.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128805, 'enUS', 'Feel your throat tighten!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128806, 'enUS', 'This breath will be your last!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128807, 'enUS', 'We need more than that, and so does he. Anduin wasn\'t there on the Broken Shore. He hasn\'t seen the sacrifice his soldiers made... that they continue to make.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128808, 'enUS', 'I will crush the life from you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128809, 'enUS', 'A king must know the weight of the orders he gives--the price of them. If a compass breaks his spirit, how will he respond when the war arrives on our doorstep?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128810, 'enUS', 'My tendrils will take you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128811, 'enUS', 'These ancient grounds are not for you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128812, 'enUS', 'You disturb nature\'s perfection!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128813, 'enUS', 'Fodder for the compost heap!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128814, 'enUS', 'Food for the worms!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128815, 'enUS', 'They did not show the proper respect to nature.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128816, 'enUS', 'Mortals seldom appreciate true beauty.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128817, 'enUS', 'Now I... shall feed... the soil...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128818, 'enUS', 'My end... was written... in the rings...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128819, 'enUS', 'Thank you, my friend. I know what I must do to learn the weight of my father\'s crown.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128820, 'enUS', 'Little fools wanna outsmart the Legion? Think again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128821, 'enUS', 'Gonna run you dummies over!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128822, 'enUS', 'I must go to the Broken Shore and see for myself the sacrifice he made.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128823, 'enUS', 'I\'LL STOMP YOU!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128824, 'enUS', 'Stand still, wimps!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128825, 'enUS', 'C\'mere and fight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128826, 'enUS', 'Come and get it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128827, 'enUS', 'Books are for losers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128828, 'enUS', 'Dead doofus!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128829, 'enUS', 'Learned your lesson?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128830, 'enUS', 'HA! Who\'s the smart one now?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128831, 'enUS', 'Don\'t need books when I got fists!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128832, 'enUS', 'Didn\'t... think this... through...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128833, 'enUS', 'Shoulda... stayed in... school...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128834, 'enUS', 'I shall find safety to recover. You must find out who is behind this and take them out!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128835, 'enUS', '', 'You heard Goldrinn! We have to find out who\'s leading these attacks now! No time to lose!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128836, 'enUS', 'Focus your attacks. We must not allow the connection to continue!', 'Focus your attacks. We must not allow the connection to continue!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128837, 'enUS', 'Don\'t stop until the connection is severed!', 'Don\'t stop until the connection is severed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128838, 'enUS', 'The energy coming off this thing is incredible.', 'The energy coming off this thing is incredible.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128839, 'enUS', 'We\'ve got to hold the skies a little longer. We cannot afford to fail now.', 'We\'ve got to hold the skies a little longer. We cannot afford to fail now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128840, 'enUS', 'If the tether remains intact much longer Azsuna will be lost!', 'If the tether remains intact much longer Azsuna will be lost!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128841, 'enUS', 'Keep the skies clear or the conduit will never fall!', 'Keep the skies clear or the conduit will never fall!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128842, 'enUS', 'Restrain the beast, and I will summon them to us...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128843, 'enUS', 'The Aegis of Aggramar has been depleted!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128845, 'enUS', 'Come with me to the Summoning Platform where the ritual has been prepared.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128854, 'enUS', '', 'I got everything I needed from this fiend before you got here. I know where their commander is!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128857, 'enUS', 'Look upon the true face of death, and know that your end comes soon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128858, 'enUS', '', 'Come my children and feed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128859, 'enUS', '', 'Feeding time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128860, 'enUS', 'The pain of flesh is fleeting. True torment lasts forever.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128862, 'enUS', 'Your courage will fail.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128866, 'enUS', 'Soon the heart of your world will beat no more!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128867, 'enUS', '', 'That commander\'s going to taste my blade!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128871, 'enUS', 'Mortal! I have been sent by the great Neptulon to bring ruin and judgment upon these fel infused fools that would invade our world.$b$bJoin me in battle, and together we shall quench the flames of their evil.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128872, 'enUS', 'I will not let that happen.', 'I will not let that happen.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128874, 'enUS', 'Thank you, Archdruid. I feared the worst.', 'Thank you, Archdruid. I feared the worst.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128875, 'enUS', 'The commander must be stopped. I believe in you!', 'The commander must be stopped. I believe in you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128876, 'enUS', 'I will return to the Dreamgrove. Thank you!', 'I will return to the Dreamgrove. Thank you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128877, 'enUS', '', 'You don\'t... understand...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128878, 'enUS', 'Thank you, stranger!', 'Thank you, stranger!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128879, 'enUS', 'I feel so weak; the webs were draining away my very essence.', 'I feel so weak; the webs were draining away my very essence.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128880, 'enUS', 'Thank you, hero! I thought my end had come!', 'Thank you, hero! I thought my end had come!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128881, 'enUS', 'I\'m never going to leave Nar\'thalas again!', 'I\'m never going to leave Nar\'thalas again!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128882, 'enUS', 'You have my thanks!', 'You have my thanks!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128883, 'enUS', 'I... live? Thank you friend! Thank you so much!', 'I... live? Thank you friend! Thank you so much!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128884, 'enUS', '', 'I\'ll meet you back in the Dreamgrove, Archdruid. Thanks for bringing me along!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128885, 'enUS', 'A valiant battle earlier, washing away the fires of evil and drowning the aspirations of the fel.$b$bYou did well for one who is so painfully dry.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128886, 'enUS', 'Archmage,the felbound manasabers in the Weeping Terrace must be freed! The abjurists holding them are quite powerful. A well-timed Spell Steal may come in handy!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128887, 'enUS', 'Hero! The naga and their skrog lackeys have harassed my warriors long enough. Teach them the folly of their wicked ways!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128888, 'enUS', 'Glorious carnage indeed! You put those watery pests in their place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128889, 'enUS', 'I will slaughter every last one of your precious Ancients!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128890, 'enUS', 'No! The Legion... will destroy... your world...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128891, 'enUS', 'Neptulon has sent one of his greatest champions to aid our cause, and has asked for you by name. I look forward to seeing what damage the two of you can unleash.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128892, 'enUS', 'Thank you, champion. Things were really beginning to heat up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128893, 'enUS', 'Drown in your failure!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128894, 'enUS', 'By Water be purged!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128895, 'enUS', 'Your fire is quenched!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128896, 'enUS', 'Drown on this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128897, 'enUS', 'Begone, fel thing!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128898, 'enUS', 'You cannot stand against the flood!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128899, 'enUS', 'Fall before the tide of justice!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128900, 'enUS', 'My waters run green with taint. Perhaps a good filtering is in order.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128901, 'enUS', 'Come, little Champion, and let us drown our enemies in the tides of justice!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128902, 'enUS', 'Domatrax rips open additional Fel Portals!', 'Domatrax rips open additional Fel Portals!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128903, 'enUS', 'Intruder! How did you find my lair!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128904, 'enUS', 'It\'s no matter, in a moment I will be gone along with all of my treasure!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128905, 'enUS', '', 'Hey, jerk! I think this shard you brought me is broken. I tried hitting it with a rock and everything!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128906, 'enUS', '', 'Oh, hey. Who are you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128907, 'enUS', 'I\'m Iks\'reeged, Treasure Master! Wait... What is that you are holding?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128908, 'enUS', '', 'The Ancient Shard of Binding begins to radiate with tremendous power.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128909, 'enUS', 'NO! How dare you! I will not bend knee to such a fatuous creature!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128910, 'enUS', 'Arrrg! What have you done?! Shackled to this realm forever? I cannot imagine a worse fate!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128911, 'enUS', '', 'Whoops! So, I guess that\'s what this thing does! Oh well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128912, 'enUS', '', 'Hey! Look at all this treasure!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128913, 'enUS', 'Champion, there is a collection of energy near our camp that seems to be specifically attuned to you. Would you investigate this disturbance?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128914, 'enUS', 'Fascinating! Despite the frequency with which we summon forth elementals and demons, it never occurred to me that the door might swing both ways.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128915, 'enUS', 'Ah! My Summon Champion spell worked. Minion! We must do battle with another of my former masters up ahead. Get to it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128916, 'enUS', 'Minion! Eat this.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128917, 'enUS', 'I speak fer Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128918, 'enUS', 'Well met!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128919, 'enUS', 'These stones have tales ta tell.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128920, 'enUS', 'Fight fer our world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128921, 'enUS', 'Fer Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128922, 'enUS', 'Back ta work!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128923, 'enUS', 'Enough o\' yer nonsense!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128924, 'enUS', 'Me magma\'s fixin\' ta boil!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128925, 'enUS', 'Ye\'re messin\' with the voice o\' the planet! THE PLANET!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128926, 'enUS', 'Now ye done it! IT\'S CLOBBERIN\' TIME!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128927, 'enUS', 'I must know. Follow me to the keep.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128928, 'enUS', 'No luck, eh? King Greymane won\'t be happy, but I\'m sure you did your best.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128929, 'enUS', 'The time for prayer is over. I must know the hearts and minds of my people.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128930, 'enUS', 'I will accompany you to Lion\'s Rest.', 'I will accompany you to Lion\'s Rest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128931, 'enUS', 'Skyhold.', 'Skyhold.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128932, 'enUS', 'To the Battlelord!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128935, 'enUS', 'So... tired...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128936, 'enUS', 'For my masters! For the Legion!', 'For my masters! For the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128937, 'enUS', 'You cannot escape the Legion!', 'You cannot escape the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128938, 'enUS', 'There\'s no point... we\'re doomed! We\'re all doomed!', 'There\'s no point... we\'re doomed! We\'re all doomed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128939, 'enUS', 'Don\'t you see? The Legion has already won!', 'Don\'t you see? The Legion has already won!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128940, 'enUS', 'For the Legion!', 'For the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128941, 'enUS', 'Death to all who oppose the Legion!', 'Death to all who oppose the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128942, 'enUS', 'Erdris? You have been corrupted as well? It saddens me that your eternal slumber has been disturbed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128943, 'enUS', 'The Helm of Domination shows me many things, Deathlord.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128944, 'enUS', 'I have seen a vision... thousands of undead marching to the far reaches of Northrend in search of a great power.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128945, 'enUS', 'Yet when reach out through the cold tendrils of endless ice, I sense nothing there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128946, 'enUS', 'Go now to the far north of Icecrown, and I will show you what I have seen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128949, 'enUS', 'It was here that I saw the undead swarming across an icy shelf, seeking a prize coveted by Arthas.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128950, 'enUS', 'Look ahead... Do you see it?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128951, 'enUS', 'The bones of a dragon most ancient, and powerful beyond reckoning.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128952, 'enUS', 'Yet all that remains here is the sea, and I sense no trace of undead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128953, 'enUS', 'Find a member of the bronze flight, the tenders of history. Do not be merciful.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128954, 'enUS', 'The reds know something. They hide their secrets in the Ruby Sanctum beneath Wyrmrest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128955, 'enUS', 'He has alerted his allies already. They will be expecting you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128956, 'enUS', 'A record of their history must lurk somewhere in this sanctum. Spare who you will, or slay them all... the choice is yours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128957, 'enUS', 'You are empty inside, just like me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128958, 'enUS', 'Fly now into the north. Find this glacier... and your destiny.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128960, 'enUS', 'Continue north, Deathlord, into the blizzard.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128961, 'enUS', 'And hold tight to your reins.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128963, 'enUS', 'It is here... I can feel it. Your supplies have likely frozen. I sense feral undead buried in the ice. It seems the time has come to test your will.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128964, 'enUS', 'Of course, should you fail to survive, know that I shall claim Acherus for my own. Arthas may have sought revenge on the citadel, but I have... other plans.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128965, 'enUS', 'You are getting close. I can sense the spark of life ahead of you. You will bring decay to this lost place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128966, 'enUS', 'I sense many feral undead nearby. They have been feeding on its life energy. Take it from them. Slay them all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128967, 'enUS', 'You have stolen the dragon\'s life energy. Now, bend it to your desires. Pervert it with your darkness.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128968, 'enUS', 'I can feel the unholy energy surging within you. Raise the dragon and claim it as your own.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128969, 'enUS', 'The light of Dalaran burns ever so brightly. But should the darkness gather, our allies will be ready. I\'m sure of it.', 'The light of Dalaran burns ever so brightly. But should the darkness gather, our allies will be ready. I\'m sure of it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128970, 'enUS', 'You\'re right. We do not know what lies ahead. But if the darkness comes, we will face it together.', 'You\'re right. We do not know what lies ahead. But if the darkness comes, we will face it together.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128971, 'enUS', 'Do not let fear of an uncertain future overwhelm you. We do not know what lies ahead.', 'Do not let fear of an uncertain future overwhelm you. We do not know what lies ahead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128972, 'enUS', 'You mustn\'t let the idle mutterings upset you. It\'s times like these we must remain strong.', 'You mustn\'t let the idle mutterings upset you. It\'s times like these we must remain strong.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128973, 'enUS', 'I\'m not sure what he was doing, but his behavior seemed a bit--oh. Ignore my musings. It\'s fine. Everything\'s fine!', 'I\'m not sure what he was doing, but his behavior seemed a bit--oh. Ignore my musings. It\'s fine. Everything\'s fine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128974, 'enUS', 'These Rooksguard are newly risen, and may not have fallen fully into corruption. Perhaps they can yet be saved!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128975, 'enUS', 'As I told you, I don\'t know what they were talking about. But they did look... concerned.', 'As I told you, I don\'t know what they were talking about. But they did look... concerned.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128976, 'enUS', '...yes, but some aren\'t sure what to expect. There is something to be said for leading by example, especially in a time like this.', '...yes, but some aren\'t sure what to expect. There is something to be said for leading by example, especially in a time like this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128977, 'enUS', 'Who knows. The first thing I saw when I looked up was that monstrosity lingering overhead.', 'Who knows. The first thing I saw when I looked up was that monstrosity lingering overhead.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128978, 'enUS', 'Ah... Grandmaster. Almai and Blanche are waiting for you down in the village. I must... rest... a bit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128979, 'enUS', 'NO! You need to listen to me. I\'m telling you--we need to get out of here!', 'NO! You need to listen to me. I\'m telling you--we need to get out of here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128980, 'enUS', 'What if this is it?', 'What if this is it?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128981, 'enUS', 'You have fought well, Farseer $p. I see Thunderaan chooses his champions wisely. I will bow to his power.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128982, 'enUS', '', 'Please hurry, Grandmaster. The order\'s survival depends on your success!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128983, 'enUS', 'The moment we lose faith in the Council is the moment our threads unravel. We must band together, not harbor our fears in events that have not yet occurred.', 'The moment we lose faith in the Council is the moment our threads unravel. We must band together, not harbor our fears in events that have not yet occurred.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128984, 'enUS', 'I am sure the Council will reveal more information as it becomes available. In the meantime, remember that times like these have a way of revealing an individual\'s true character.', 'I am sure the Council will reveal more information as it becomes available. In the meantime, remember that times like these have a way of revealing an individual\'s true character.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128985, 'enUS', 'You look a bit pale. Are you feeling well?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128986, 'enUS', 'Looking tired, have another hard day out in the fields?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128987, 'enUS', 'Lot of people acting strange lately... I hope there isn\'t a sickness going around.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128988, 'enUS', 'I... I couldn\'t stop myself.', 'I... I couldn\'t stop myself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128989, 'enUS', 'They got into my head... into my mind!', 'They got into my head... into my mind!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128990, 'enUS', 'I tried to stop but couldn\'t... it was like I was a puppet.', 'I tried to stop but couldn\'t... it was like I was a puppet.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128991, 'enUS', 'The water... don\'t let the others drink the water!', 'The water... don\'t let the others drink the water!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128992, 'enUS', 'What... what happened to me?', 'What... what happened to me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128993, 'enUS', 'Where am I? Who are you?', 'Where am I? Who are you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128994, 'enUS', 'I\'m hesitant to answer further questions about what\'s going on. I\'m starting to lack confidence in my responses.', 'I\'m hesitant to answer further questions about what\'s going on. I\'m starting to lack confidence in my responses.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128995, 'enUS', 'Welcome, $ct. Let\'s get this assault under way, shall we?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128996, 'enUS', 'I wonder what it means.', 'I wonder what it means.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128997, 'enUS', 'We will divide our forces to hit them from all sides at once.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128998, 'enUS', 'No. You don\'t think... That\'s not possible!', 'No. You don\'t think... That\'s not possible!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (128999, 'enUS', 'Quickly, $ct, we need you on the front lines!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129000, 'enUS', '$n, our assault on the southern beach is being met with heavy resistance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129001, 'enUS', 'I need you spearheading our forces while I deal with these ships!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129002, 'enUS', 'We must take the ruins on the western side of the island. From there we can funnel all of our forces toward the central ship above the soul engine.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129003, 'enUS', 'The Legion will not win this day! We will show them what it means to stand together. For Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129004, 'enUS', 'We must take the beach before we can claim the ruins to the west.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129005, 'enUS', 'Good! They thought they were ready for us, but they\'ve never seen the forces of Azeroth work with such unity!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129006, 'enUS', 'Head up the hill to the west with the other forces. An eredar and his underlings guard the point. You know what to do.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129007, 'enUS', 'Ah! That\'s one ship down.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129008, 'enUS', 'Did you see them? The ones wandering, carrying on conversations with themselves?', 'Did you see them? The ones wandering, carrying on conversations with themselves?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129009, 'enUS', 'Archers! Rain hell upon them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129010, 'enUS', 'It seems you were doomed to fail, Lord Kalgorath.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129011, 'enUS', '$n, the forces of Dread Commander Arganoth lie to the northeast. We must close the portals before our allies are overrun.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129012, 'enUS', 'Their command ship remains. We must destroy it before Dalaran\'s defenses fail! The Council of the Black Harvest has set up a gate for us. You may do the honors, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129013, 'enUS', 'Get ready, $ct. I\'m pulling you out and detonating the bombs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129014, 'enUS', 'Ah, there you are, $ct. I was worried I might have activated the bombs a tad prematurely.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129015, 'enUS', 'But how did it get there? It couldn\'t just appear out of nowhere.', 'But how did it get there? It couldn\'t just appear out of nowhere.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129016, 'enUS', 'Right. We have our orders. Good luck.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129018, 'enUS', 'The warlocks took your world from you, nathrezim! I would not be so arrogant if I were you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129019, 'enUS', 'We have won this day, but a grave threat still looms before us. The Legion\'s master has yet to play his hand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129020, 'enUS', '', 'The Wardens are with you, $ct.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129021, 'enUS', '', 'More portals?! Where the hell is our backup?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129022, 'enUS', '', 'Illidan, how courteous of you to finally arrive! Wardens, see to the real work while Illidan\'s demon hunters caper about with their bats.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129023, 'enUS', '', 'You are too quick to trust Illidan, Prophet. I will ensure he fulfills his duty.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129024, 'enUS', 'Here, these will help. Plant them around the ship. I will activate them from here when ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129025, 'enUS', 'You think a few mortals with gaudy weapons can stop us?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129026, 'enUS', 'You are doomed to fail!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129027, 'enUS', 'I... I can\'t stop it. I\'ve tried to keep myself busy, but my mind wanders back to this feeling... this... I don\'t know what to call it anymore.', 'I... I can\'t stop it. I\'ve tried to keep myself busy, but my mind wanders back to this feeling... this... I don\'t know what to call it anymore.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129028, 'enUS', 'As is the Light.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129029, 'enUS', 'Enough bickering! Stay focused!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129030, 'enUS', 'Perhaps. But not this day, demon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129031, 'enUS', 'Mephistroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129032, 'enUS', 'Illidan, convey the Aegis of Aggramar to the cathedral. I will descend into the tomb with Khadgar and hunt the monster down. Kil\'jaeden\'s reign of destruction will soon be ended!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129033, 'enUS', 'The sky grows closer every day. Is it ever going to end?', 'The sky grows closer every day. Is it ever going to end?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129034, 'enUS', 'Yes, come forth. I could use some entertainment!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129035, 'enUS', 'Silence!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129036, 'enUS', 'Another foolish mortal wishes to face me? So be it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129037, 'enUS', 'You destroy a few ships and claim a victory. It will be your last, I assure you. Savor this moment, for our next meeting will spell your doom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129038, 'enUS', 'How long until our world crumbles?', 'How long until our world crumbles?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129039, 'enUS', 'You can\'t possibly say you aren\'t feeling it. Look around us. This world\'s beginning to doubt itself.', 'You can\'t possibly say you aren\'t feeling it. Look around us. This world\'s beginning to doubt itself.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129040, 'enUS', 'Have you seen the first aid shop? The line is out the door.', 'Have you seen the first aid shop? The line is out the door.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129041, 'enUS', 'No matter what I do, I can\'t seem to stop looking up.', 'No matter what I do, I can\'t seem to stop looking up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129042, 'enUS', 'We will find a way to make sense of it all. And should the time come for action, I am sure our allies will be prepared.', 'We will find a way to make sense of it all. And should the time come for action, I am sure our allies will be prepared.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129043, 'enUS', 'Fear has a way of playing tricks on the mind. You should get some rest, Jenny.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129044, 'enUS', '', 'Will he? How exactly will he keep an endless army of demons from invading and killing everyone?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129045, 'enUS', '', 'This will only take a moment!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129046, 'enUS', '<Lethecus has a far-away look in his eyes.>\n\nOh, yes? I didn\'t see you there.\n\n<Lethecus yawns.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129047, 'enUS', '', 'Here goes nothing. Brace yourselves!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129048, 'enUS', 'Tell me all you know of a glacier to the north where a powerful dragon fell.', 'Tell me all you know of a glacier to the north where a powerful dragon fell.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129049, 'enUS', '', 'Behold our Fel might!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129050, 'enUS', 'No, not that I can recall. I am terribly sorry, I am still in mourning over our dear Ysera\'s passing, I can think of little else right now. Did you ask the bronze flight? They have quite excellent memories.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129051, 'enUS', 'Oh, hello! Thank you for visiting again.\n\nOr is this your first time?\n\nWhat day is it?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129052, 'enUS', '<Trizormu\'s eyes narrow.>\n\nWhat would a death-bringer like yourself want with that information, exactly?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129053, 'enUS', 'Ack! Alright! There used to be... glaciers... large ones... to the north of Icecrown... they floated away years ago... I don\'t know any more, I swear! Speak to... Tariol!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129054, 'enUS', 'Ringing the Large Northern Glacier were, in order of formation, the Medium-Sized Northeastern Glacier, the Small Southern Glacier, the Southwestern Tiny Glacier, the South-Southwestern Tiny Glacier, the South-Southeastern Tiny Glacier, the Most Southern Tiny Glacier, the First and Second Northern Tiny Glaciers That Keep Changing Positions...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129055, 'enUS', '', 'The Slayer remains unconscious. I fear what will happen if--', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129056, 'enUS', '', 'That was a nasty knock on the head. Still, our Slayer is a strong one, my dear. Look!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129057, 'enUS', '...and while we\'re there, naturally we can\'t forget the Cluster of Five: the Northern, Southern, Eastern, and Western Small Ice Chunks That Have Been Technically Designated As Not Glaciers, which is really a funny story...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129058, 'enUS', '', 'What in blazes happened to my chickens?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129059, 'enUS', 'The demons attack my troops before they can recover!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129060, 'enUS', 'Fear not, my lord. I can raise more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129061, 'enUS', 'That demon must be dealt with, $n! Then we can focus on Erdris.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129062, 'enUS', 'Something\'s up with my rabbits! Poor little guys... We gotta do something!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129063, 'enUS', 'I suggest tossing out those rags and changing into something a bit more stylish. You might as well look good as the universe ends.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129064, 'enUS', 'Return to the rest you earned long ago, brother!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129065, 'enUS', 'Shrug off this corruption, my friend!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129066, 'enUS', 'Ah, $n. Just the $c I was looking for. Like my new attire? Eh--who am I kidding? Won\'t matter soon anyway.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129067, 'enUS', 'Only you can end this torment, brother. Fight it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129068, 'enUS', 'And what brings a $c like you around, $n? Fancy some sleek attire for the end of the world as we know it?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129069, 'enUS', 'I... I am free. Thank you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129070, 'enUS', 'Price is no object when it comes to going out in style. I\'m flat broke... not that it\'ll matter soon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129071, 'enUS', 'I will not be a tool of the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129072, 'enUS', 'I don\'t know about you, but I for one refuse to meet my maker in a common mage\'s garb.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129073, 'enUS', 'The end is upon us, $n! At least that\'s what they\'re saying. Might as well look the part.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129074, 'enUS', 'Mind your business, friend.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129075, 'enUS', 'If you don\'t look up then nothing\'s wrong. Carry on!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129076, 'enUS', 'The Burning Legion continues to try to recreate the masterpiece that was the Scourge. \n\nThey turn to their Nathrazim who make puppets of bones. \n\nThey turn to these fallen Spiritwalkers, who simply meddle at these magics.\n\nI am here, on behalf of the Ebon Blade, to observe and judge. And to lend assistance to you, hero, should you require it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129077, 'enUS', 'Welcome, traveler! How are things out there?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129078, 'enUS', 'No, Kerta. I do not think we have anything to worry about. Now focus. We must not keep our guest waiting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129079, 'enUS', 'Welcome. I fear our supplies have run low. We have been filling orders without much of a break since... well, you know.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129080, 'enUS', 'Come in! Kerta and I were just discussing recent events. I would be interested in hearing your take.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129081, 'enUS', 'I am certain the Council is well prepared, Kerta. Now look alive. We have another customer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129082, 'enUS', 'What kind of assistance?', 'What kind of assistance?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129083, 'enUS', '', 'My apologies if I seem a bit on edge. Welcome to the shop.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129084, 'enUS', 'For Northrend, you are looking for Leesha Tannerby, the Dockmaster for the Kraken. It sets sail for Valiance Keep in Borean Tundra from the northern dock in Stormwind Harbor.', 'For Northrend, you are looking for Leesha Tannerby, the Dockmaster for the Kraken. It sets sail for Valiance Keep in Borean Tundra from the northern dock in Stormwind Harbor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129085, 'enUS', 'For the Exodar or Teldrassil located in Kalimdor, seek out the Nayura. She is the Dockmaster for The Bravery, setting sail from the southernmost dock in Stormwind Harbor.', 'For the Exodar or Teldrassil located in Kalimdor, seek out the Nayura. She is the Dockmaster for The Bravery, setting sail from the southernmost dock in Stormwind Harbor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129086, 'enUS', 'If this is a representation of the type of necromancy that the Burning Legion relies on then we are in better shape than I ever imagined.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129087, 'enUS', 'It is so nice to be away from Acherus doing some good, old fashioned field research again with fresh corpses and bone piles.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129088, 'enUS', 'The mages have opened a portal just inside the Mage Tower to assist players getting to the Blasted Lands and on to Draenor. You will see the mage Sparkbright standing by next to it.', 'The mages have opened a portal just inside the Mage Tower to assist players getting to the Blasted Lands and on to Draenor. You will see the mage Sparkbright standing by next to it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129089, 'enUS', 'There is a portal that leads to Hellfire Peninsula in Outland. It is located within the Wizard\'s Sanctum located at the top of the Mage Tower.', 'There is a portal that leads to Hellfire Peninsula in Outland. It is located within the Wizard\'s Sanctum located at the top of the Mage Tower.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129090, 'enUS', 'Say what you will about Arthas, but the former Lich King did have a cold efficiency when it came to dealing with the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129091, 'enUS', 'You are in luck Dalaran is currently located above the Broken Isles and the Kirin Tor have setup a portal located just inside Stormwind Keep.', 'You are in luck Dalaran is currently located above the Broken Isles and the Kirin Tor have setup a portal located just inside Stormwind Keep.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129092, 'enUS', 'Our new allies in Pandaria have allowed us to create a portal to Paw\'don Village in the Jade Forest. It is located on the western edge of the lake next to Stormwind Keep.', 'Our new allies in Pandaria have allowed us to create a portal to Paw\'don Village in the Jade Forest. It is located on the western edge of the lake next to Stormwind Keep.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129094, 'enUS', 'The Deeprun Tram is the fastest route to Ironforge. You will find it located in the Dwarven District.', 'The Deeprun Tram is the fastest route to Ironforge. You will find it located in the Dwarven District.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129095, 'enUS', 'Stormwind is the center of the Alliance and offers many forms of travel to the far reaches of Azeroth and beyond. \n\nWhere are you looking to travel?', 'Stormwind is the center of the Alliance and offers many forms of travel to the far reaches of Azeroth and beyond. \n\nWhere are you looking to travel?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129096, 'enUS', 'The stench of undeath follows you into this temple of life. Do not stay overlong.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129097, 'enUS', 'Tell me all you know of a great dragon that fell on a glacier to the north of Icecrown.', 'Tell me all you know of a great dragon that fell on a glacier to the north of Icecrown.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129098, 'enUS', 'I have no idea what you are talking about. Now if you will excuse me, I have things to attend to. You may direct any additional questions to our steward.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129099, 'enUS', 'You will never find what you seek, death-bringer. Now, you have worn out your welcome at this temple, begone!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129100, 'enUS', 'Your actions stretch the limits of our alliance, death knight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129102, 'enUS', 'These supplies belong to the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129103, 'enUS', 'The Legion will be your end...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129104, 'enUS', 'Ironforge', 'Ironforge', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129105, 'enUS', 'Paw\'don Village in Pandaria', 'Paw\'don Village in Pandaria', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129106, 'enUS', 'Dalaran in the Broken Isles', 'Dalaran in the Broken Isles', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129107, 'enUS', 'Hellfire Peninsula in Outland', 'Hellfire Peninsula in Outland', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129108, 'enUS', 'Blasted Lands and the portal to Draenor', 'Blasted Lands and the portal to Draenor', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129109, 'enUS', 'Teldrassil or the Exodar in Kalimdor', 'Teldrassil or the Exodar in Kalimdor', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129110, 'enUS', 'Borean Tundra in Northrend', 'Borean Tundra in Northrend', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129111, 'enUS', 'Areas opened up in the Cataclysm', 'Areas opened up in the Cataclysm', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129112, 'enUS', '|TInterface\\Icons\\ABILITY_WARLOCK_SHADOWFURYTGA.BLP:20|tOne of the runes |cFFFF0000|Hspell:237908|h[Detonates]|h|r!', '|TInterface\\Icons\\ABILITY_WARLOCK_SHADOWFURYTGA.BLP:20|tOne of the runes |cFFFF0000|Hspell:237908|h[Detonates]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129113, 'enUS', 'Heroes of Azeroth, your class orders have grown mighty throughout our campaign on the Broken Isles. Their strength, their courage, is needed now. The time has come to unite our forces and rid Azeroth of the Burning Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129114, 'enUS', 'Look, these portals are not going to hijack themselves. I need time to do this right or you will just end up in a Legion melting pot or the gut of a Pit Lord. \n\nDo you want to teleport into the gut of a Pit Lord? Oh sure, at first it seems like a clever, unexpected attack. But then you get digested in fel juices and wonder where your great plan went.\n\nLeave me to my work.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129115, 'enUS', 'So far this warlock has proved to be quite useful in deciphering the magics of our enemies. \n\nHe also almost got both of us killed on several occasions but that is a danger I knew I would face when partnering with such a villain.\n\nFor all of his flaws, he has also saved my life several times. For good or ill, we will see this through to the end, together.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129117, 'enUS', 'We will not be able to hold this line for long.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129118, 'enUS', 'Already the vermin stir.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129120, 'enUS', 'Every demon we slay here dies their final death. Do not hold back.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129121, 'enUS', 'We won\'t be able to sustain another attack from one of those creatures. We must bolster our forces.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129122, 'enUS', 'Survivors! We will hold this point, you must find them before the Legion does.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129123, 'enUS', '', '|TInterface\\Icons\\inv_misc_horn_03:20|t%s |cFFFF0000|Hspell:237937|h[Empowers]|h|r $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129124, 'enUS', '', '|TInterface\\Icons\\inv_misc_horn_03:20|t%s |cFFFF0000|Hspell:237937|h[Empowers]|h|r herself and one of her allies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129125, 'enUS', '', '|TInterface\\Icons\\inv_misc_horn_03:20|t%s |cFFFF0000|Hspell:237937|h[Empowers]|h|r all of her council!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129130, 'enUS', 'Remember... the one who hurries to conclusions may arrive first, but may not find the destination pleasant.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129131, 'enUS', 'Do not let the behavior of others influence your ways. We must maintain our composure when faced with the unknown.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129132, 'enUS', 'Do not let the behavior of others influence your own. We must maintain our composure when faced with the unknown.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129133, 'enUS', 'The air speaks to us in many ways, $n. What do these times say to you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129134, 'enUS', 'The zeppelin to Thunder Bluff in central Kalimdor leaves from the western zeppelin tower atop the central plateau in Orgrimmar. Zeppelin master Zelli Hotnozzle will have more information for you there.', 'The zeppelin to Thunder Bluff in central Kalimdor leaves from the western zeppelin tower atop the central plateau in Orgrimmar. Zeppelin master Zelli Hotnozzle will have more information for you there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129135, 'enUS', 'To get to The Undercity or Silvermoon City you should seek out Frezza on the eastern zeppelin tower atop the central plateau in Orgrimmar. He will have more information on when the zeppelin departs for Brill located in the Tirisfal Glades just north of The Undercity. There is a teleporter in entranceway to The Undercity that can take you to Silvermoon City.', 'To get to The Undercity or Silvermoon City you should seek out Frezza on the eastern zeppelin tower atop the central plateau in Orgrimmar. He will have more information on when the zeppelin departs for Brill located in the Tirisfal Glades just north of The Undercity. There is a teleporter in entranceway to The Undercity that can take you to Silvermoon City.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129136, 'enUS', 'Seek out Snurk Bucksquick on the western zeppelin tower atop the central plateau in Ogrimmar. She is the zeppelin master for Stranglethorn Vale and will let you know more info on when the zeppelin will depart for our outpost of Grom\'gol in the southern Eastern Kingdoms.', 'Seek out Snurk Bucksquick on the western zeppelin tower atop the central plateau in Ogrimmar. She is the zeppelin master for Stranglethorn Vale and will let you know more info on when the zeppelin will depart for our outpost of Grom\'gol in the southern Eastern Kingdoms.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129137, 'enUS', 'The mood on these streets darkens like the skies, yet hope can still bring light to us all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129138, 'enUS', 'For Borean Tundra in Norhtrend you will want to take the zeppelin The Mighty Wind. Seek out Greeb Ramrocket on western zeppelin tower for when it departs for Warsong Hold.', 'For Borean Tundra in Norhtrend you will want to take the zeppelin The Mighty Wind. Seek out Greeb Ramrocket on western zeppelin tower for when it departs for Warsong Hold.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129139, 'enUS', 'Stormwind has always been my home. I know its shops and streets as I do the back of my hand.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129140, 'enUS', 'Yet even as I walk these familiar steps, I have never felt more lost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129141, 'enUS', '', 'Over here, $n! Any news? I haven\'t heard much, but some folks around here are acting quite out of sorts.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129142, 'enUS', '', 'Sorry to bother, $n, but have you any news? Guards run by every so often, but they offer little information.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129143, 'enUS', '', 'Wait! Wait! Any updates, $n? Quite a few of us are anxiously awaiting some sort of news.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129144, 'enUS', 'There is a portal that leads to Hellfire Peninsula in Outland. It is located within the Cleft of Shadow, underneath Orgrimmar.', 'There is a portal that leads to Hellfire Peninsula in Outland. It is located within the Cleft of Shadow, underneath Orgrimmar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129145, 'enUS', 'Stop right there! Oh, is that you, $n? Damned looters have been in and out of here all day! Take a look around, but be quick about it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129146, 'enUS', 'Our mages have opened a portal just inside the Cleft of Shadows to assist players getting to the Blasted Lands and on to Draenor.', 'Our mages have opened a portal just inside the Cleft of Shadows to assist players getting to the Blasted Lands and on to Draenor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129147, 'enUS', 'Hands where I can see \'em! Gah! It\'s you again. I\'ve had to chase off so many miscreants lately, I don\'t know what to think anymore.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129148, 'enUS', 'Freeze! Ahh! How many times is this gonna happen? First the guards are checkin\' around, now you... Well, the pickin\'s are slim, but have a look.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129149, 'enUS', '%s sheathes his weapon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129150, 'enUS', 'The Kirin Tor have setup a portal in the Cleft of Shadows to assist players in getting to Dalaran above the Broken Isles.', 'The Kirin Tor have setup a portal in the Cleft of Shadows to assist players in getting to Dalaran above the Broken Isles.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129151, 'enUS', 'Our allies in Pandaria have allowed us to create a portal to Honeydew Village in the Jade Forest. It is located in the middle of the Valley of Honor.', 'Our allies in Pandaria have allowed us to create a portal to Honeydew Village in the Jade Forest. It is located in the middle of the Valley of Honor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129152, 'enUS', 'Undercity or Silvermoon City in the Eastern Kingdoms', 'Undercity or Silvermoon City in the Eastern Kingdoms', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129153, 'enUS', 'Borean Tundra in Northrend', 'Borean Tundra in Northrend', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129154, 'enUS', 'Thunder Bluff in Mulgore', 'Thunder Bluff in Mulgore', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129155, 'enUS', 'Stranglethorn Vale in the Eastern Kingdoms', 'Stranglethorn Vale in the Eastern Kingdoms', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129156, 'enUS', 'Honeydew Village in Pandaria', 'Honeydew Village in Pandaria', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129157, 'enUS', 'Dalaran above the Broken Isles', 'Dalaran above the Broken Isles', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129158, 'enUS', 'Areas opened up in the Cataclysm', 'Areas opened up in the Cataclysm', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129159, 'enUS', 'Hellfire Peninsula in Outland', 'Hellfire Peninsula in Outland', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129160, 'enUS', 'Blasted Lands and the Portal to Draenor', 'Blasted Lands and the Portal to Draenor', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129161, 'enUS', 'Orgrimmar is the heart of the Horde, offering many forms of travel to the far reaches of Azeroth and beyond. \n\nWhere are you looking to travel?', 'Orgrimmar is the heart of the Horde, offering many forms of travel to the far reaches of Azeroth and beyond. \n\nWhere are you looking to travel?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129162, 'enUS', 'I\'ve fished this spot dry, but with the war and all, it\'s just too dangerous to leave the city.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129163, 'enUS', 'I have not returned to Lion\'s Rest since my father\'s memorial was moved here from the keep.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129164, 'enUS', 'The war has consumed all of my energy and attention. I have not had a moment to grieve.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129165, 'enUS', '', 'Stick to what you know, Icks. No point in speculating. Now look alive, we have a guest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129166, 'enUS', '', 'Our patrons are quite fond of our goods. I\'m confident that if the time comes, they will not hesitate to--oh! Welcome to the shop, $n.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129167, 'enUS', '', 'How is it out there, $n? With so many customers, it\'s been a challenge to fill all the orders. I hope all is well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129168, 'enUS', '', 'Why do you like Nethershards so much?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129169, 'enUS', 'It\'s a secret long guarded by ancient secret keepers for secret reasons.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129170, 'enUS', '', 'You don\'t really know what they\'re for, do ya?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129171, 'enUS', 'Nope.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129172, 'enUS', 'Meefi take you to cold Northrend. Her balloon leaves from left tower north of the Ruins of Lordaeron. You go to Vengeance Keep in Howling Fjord to fight Vrykul.', 'Meefi take you to cold Northrend. Her balloon leaves from left tower north of the Ruins of Lordaeron. You go to Vengeance Keep in Howling Fjord to fight Vrykul.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129173, 'enUS', 'Sparkly elves put in orby teleporter in ruins above Undercity. Touch it to take you to their home of Silvermoon City.', 'Sparkly elves put in orby teleporter in ruins above Undercity. Touch it to take you to their home of Silvermoon City.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129174, 'enUS', 'Magic men open portal to Hellfire Peninsula in Outland. Find it in their Mage Quarter.', 'Magic men open portal to Hellfire Peninsula in Outland. Find it in their Mage Quarter.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129175, 'enUS', 'Stranglethorn Vale in the Eastern Kingdoms', 'Stranglethorn Vale in the Eastern Kingdoms', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129176, 'enUS', 'Hellfire Peninsula in Outland', 'Hellfire Peninsula in Outland', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129177, 'enUS', 'Howling Fjord in Northrend', 'Howling Fjord in Northrend', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129178, 'enUS', 'Orgrimmar in Kalimdor', 'Orgrimmar in Kalimdor', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129179, 'enUS', 'Silvermoon City in the Eastern Kingdoms', 'Silvermoon City in the Eastern Kingdoms', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129180, 'enUS', '', 'Are demons ticklish?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129181, 'enUS', 'If you come near me, I\'ll bite your hand off.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129182, 'enUS', '', 'I\'ll take that as a yes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129183, 'enUS', '', 'It looks like fun floating all the time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129184, 'enUS', 'Being eternally suspended in a prison set by a fatuous goblin? It\'s a blast...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129185, 'enUS', '', 'Hey, Iksy. When\'s your birthday? We should have a party!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129186, 'enUS', 'I was spawned into this wretched existence eons ago within the deepest realms of the nether.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129187, 'enUS', '', 'So... Next week?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129188, 'enUS', 'Sure. Next week.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129189, 'enUS', 'Are you ever going to let me go?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129190, 'enUS', '', 'Sure! How do I do that?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129191, 'enUS', 'Really?! Just hand me the Shard of Binding and the spell will be broken!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129192, 'enUS', '', 'Oh that glowy thing? I lost it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129193, 'enUS', 'I hate you...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129194, 'enUS', 'Other Continents', 'Other Continents', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129195, 'enUS', 'Fragile mortals. Bow before the might of the Burning Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129196, 'enUS', 'Your world crumbles around you, mortals. We cannot be stopped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129197, 'enUS', 'Hellfire Peninsula you say. That scorched wasteland in Outland. Well, our host of mages have created a portal to git ye there in the Hall of Mysteries located in the Mystic Ward.', 'Hellfire Peninsula you say. That scorched wasteland in Outland. Well, our host of mages have created a portal to git ye there in the Hall of Mysteries located in the Mystic Ward.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129198, 'enUS', 'Weak flesh and brittle bones... You are no match for the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129199, 'enUS', 'Feel your hearts wrench as the grasp of Sargeras tightens around your world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129200, 'enUS', 'You cannot escape! Our reach is endless!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129201, 'enUS', 'The downfall of your world has begun!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129202, 'enUS', 'Oh, Howling Fjord in Northred. You be in fer a bit of a ride. Seek out Gryth Thurden the Gryphon Master to secure a flight to Menethil Harbor. The Northspear sets sail from there for Valgarde.', 'Oh, Howling Fjord in Northred. You be in fer a bit of a ride. Seek out Gryth Thurden the Gryphon Master to secure a flight to Menethil Harbor. The Northspear sets sail from there for Valgarde.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129203, 'enUS', '', 'I suppose I should ensure the Fel Hammer is being properly repaired. See you there, Sslayer.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129204, 'enUS', 'Time for revenge.', 'Time for revenge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129205, 'enUS', 'You have my thanks, and my mace.', 'You have my thanks, and my mace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129206, 'enUS', 'You came with Magatha? I knew she would free us.', 'You came with Magatha? I knew she would free us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129207, 'enUS', 'I\'m ready to fight.', 'I\'m ready to fight.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129208, 'enUS', 'Ironforge is not the port town of Stormwind but we be offer\'n many a route to other continents.\n\nWhere ye be looking to travel?', 'Ironforge is not the port town of Stormwind but we be offer\'n many a route to other continents.\n\nWhere ye be looking to travel?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129209, 'enUS', 'Hellfire Peninsula in Outland', 'Hellfire Peninsula in Outland', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129210, 'enUS', 'Howling Fjord in Northrend', 'Howling Fjord in Northrend', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129211, 'enUS', 'Stormwind and other Alliance Capitals', 'Stormwind and other Alliance Capitals', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129212, 'enUS', 'We must keep our voices down and act natural, lest we be heard speaking together...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129213, 'enUS', 'It seems Genn and Velen are speaking about me, but I can\'t quite make out the words...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129214, 'enUS', '', 'With the Slayer to lead us, the Legion WILL fall! Let us take this moment to celebrate our victory!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129215, 'enUS', '', 'Now, sharpen your blades for the battles to come! The Illidari will prevail!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129216, 'enUS', '', 'My shackles are in place, hero! Lure a demon into the trap to banish the fiend!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129217, 'enUS', 'Living on an island we rely on the sea and our magical prowess for getting around.\n\nWhere are you looking to travel?', 'Living on an island we rely on the sea and our magical prowess for getting around.\n\nWhere are you looking to travel?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129218, 'enUS', 'To get to Stormwind you must first take the portal to Rut\'theran Village located in the center of the city. Once at the base of Teldrassil, head to the southern dock to catch your ship.', 'To get to Stormwind you must first take the portal to Rut\'theran Village located in the center of the city. Once at the base of Teldrassil, head to the southern dock to catch your ship.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129219, 'enUS', 'The Stormwind ship leaves from the southern dock just over there.', 'The Stormwind ship leaves from the southern dock just over there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129220, 'enUS', 'The Exodar', 'The Exodar', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129221, 'enUS', 'There ship for the Exodar departs from the western dock.', 'There ship for the Exodar departs from the western dock.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129222, 'enUS', 'The Exodar, the ship and home of the Draenei. You will find a portal to the Exodar in the Temple of the Moon.', 'The Exodar, the ship and home of the Draenei. You will find a portal to the Exodar in the Temple of the Moon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129223, 'enUS', 'To get to the barren land of Hellfire Peninsula in Outlands you can take a portal located in the Temple of the Moon.', 'To get to the barren land of Hellfire Peninsula in Outlands you can take a portal located in the Temple of the Moon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129224, 'enUS', 'Other Continents', 'Other Continents', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129225, 'enUS', 'Stormwind City in the Eastern Kingdoms', 'Stormwind City in the Eastern Kingdoms', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129226, 'enUS', 'Hellfire Peninsula in Outland', 'Hellfire Peninsula in Outland', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129227, 'enUS', 'The Command Center has been built! You can now gain access to things.', 'The Command Center has been built! You can now gain access to things.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129228, 'enUS', 'The Command Center has been attacked!  We cant benefit from its glory any more!', 'The Command Center has been attacked!  We cant benefit from its glory any more!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129229, 'enUS', 'The Command Center is ready for construction. Come donate your supplies.', 'The Command Center is ready for construction. Come donate your supplies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129230, 'enUS', 'The Nether Disruptor has been built! You can now gain access to things.', 'The Nether Disruptor has been built! You can now gain access to things.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129231, 'enUS', 'The Nether Disruptor has been attacked!  We cant benefit from its glory any more!', 'The Nether Disruptor has been attacked!  We cant benefit from its glory any more!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129232, 'enUS', 'The Nether Disruptor is ready for construction. Come donate your supplies.', 'The Nether Disruptor is ready for construction. Come donate your supplies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129233, 'enUS', 'Darnassus has a portal to Hellfire Peninsula located in the Temple of the Moon. Follow my directions to the portal up to Darnassus proper. Once there ask another sentinel if you need directions to the Temple.', 'Darnassus has a portal to Hellfire Peninsula located in the Temple of the Moon. Follow my directions to the portal up to Darnassus proper. Once there ask another sentinel if you need directions to the Temple.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129234, 'enUS', 'A portal can be found in the cave beneath Sprit Rise that will take you to the wasteland of Hellfire Peninsula in Outland.', 'A portal can be found in the cave beneath Sprit Rise that will take you to the wasteland of Hellfire Peninsula in Outland.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129235, 'enUS', 'Other Continents', 'Other Continents', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129236, 'enUS', 'Located here in Mulgore we do not have many options for travel but I will help you as best I can. \n\nWhere are you looking to travel?', 'Located here in Mulgore we do not have many options for travel but I will help you as best I can. \n\nWhere are you looking to travel?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129237, 'enUS', 'Hellfire Peninsula in Outland', 'Hellfire Peninsula in Outland', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129238, 'enUS', 'Orgrimmar or the Eastern Kingdoms', 'Orgrimmar or the Eastern Kingdoms', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129239, 'enUS', '', 'The Fel Hammer\'s power reserves run low, Slayer. The fel energy around the Tomb can replenish our power cores and you should be able to ride those geysers up there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129240, 'enUS', '', 'I will add these to our reserves. Thanks again, Slayer!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129241, 'enUS', '', 'Huh? Why would anything be wrong? Move along!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129242, 'enUS', 'Greetings, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129243, 'enUS', '', 'Come in, $n! Leave your worries at the door. The guards have assured us all is well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129244, 'enUS', 'Nothing distracts a worried mind like a bit of toying around. Feel free to browse the shop!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129245, 'enUS', 'Welcome, $n! We have one rule: keep your attitude positive! Don\'t let the all the commotion outside distract you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129246, 'enUS', 'I know these times can be stressful, but don\'t be afraid to let your mind wander to a place of mystery and excitement!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129247, 'enUS', '', 'The skies grow dark, $n. I hope you\'re ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129248, 'enUS', 'Quickly, bolster our ranks! We must push through!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129249, 'enUS', 'Leave none standing! If we fail here, Azeroth burns.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129250, 'enUS', 'The Legion has brought forth some new manner of fiend. Let us test whether it bleeds like all the rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129251, 'enUS', 'The echo of the Guardian Aegwynn appears, just as she did in the cathedral.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129252, 'enUS', '', 'Are you prepared, $n? Some of your allies are not handling the situation so well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129253, 'enUS', 'I have some news for you, $n, everything you\'ve learned about your profession to this point was child\'s play. There was no challenge to it, no fashion, no style, no grace! $b$bNow, I am not merely a Fashion Historian, young $r, I am also a philosopher and--I don\'t mind admitting--something of a style icon in these parts.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129254, 'enUS', '', 'Preparation is key, $n. My goods can be yours--for a price.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129255, 'enUS', '', 'I sealed each level of this temple so that no invader could descend into its depths. I never anticipated that the Legion would find another way to breach its dark heart.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129256, 'enUS', '', 'My protective wards now bar your way. Each seal can only be opened with one of the Pillars of Creation. Be warned: the demons will stop at nothing to prevent your advance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129257, 'enUS', 'Do tell.', 'Do tell.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129258, 'enUS', '', '$n, over here! If you hear any news, please let me know at once. It takes time to pack up my goods.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129259, 'enUS', '', 'Pastries, anyone? Nothing chases away doom and gloom like a fresh, tasty treat.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129260, 'enUS', '', 'So many people coming and going, in and out of the shops, the bank... What\'s going on, exactly? I\'m starting to get nervous.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129261, 'enUS', '', 'I can sense one of the Legion\'s masters working to reanimate the Avatar of Sargeras. Should Kil\'jaeden infuse that husk with the Dark Titan\'s power, I do not know whether any force on this world can stop it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129262, 'enUS', 'Greetings, $n. News of your exploits on the Broken Isles has been reaching us back home in Stormwind. You bring great honor to the Alliance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129263, 'enUS', 'Then we have no time to lose.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129264, 'enUS', 'Heroes, the paths are open. Use the Pillars of Creation to bypass Aegwynn\'s wards before the Legion claims the avatar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129265, 'enUS', 'Rest, friend, and recover your strength. It will be needed for the trials ahead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129266, 'enUS', 'An age has passed since I last walked these halls. I did what needed to be done, though at great cost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129267, 'enUS', 'Be on your guard. I doubt the spirits trapped here will be friendly... or forgiving.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129268, 'enUS', 'Few understood my use of the naga to obtain the Eye of Sargeras, but it was a necessary tactic to secure the future of Azeroth. Let nothing stop you from doing what needs to be done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129269, 'enUS', 'What news of the skies, $n?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129270, 'enUS', 'Any word on what\'s happening out there?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129271, 'enUS', 'How are people coping out there?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129272, 'enUS', '', 'This hammer forged ancient wonders and shaped the earth itself. Today it shatters the bonds of fate that seek to crush us all.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129273, 'enUS', 'My patented Hammerbeard System of Style and Taste is a natural philosophy of flair. Impeccably researched, finely tuned, and well tested by yours truly, the HSST philosophy will be underlying apparatus of your education. $b$bThe dangers will be many, $n, but the rewards are beyond your current capacity to understand!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129274, 'enUS', 'Uh huh.', 'Uh huh.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129275, 'enUS', '', 'My echo has guided you as far as it can, champions. In life, I shouldered too many burdens alone, and such hubris cost me dearly. Only by standing together can you overcome the darkness ahead. Fare well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129276, 'enUS', '', 'From this stone flowed rivers and seas, the very lifeblood of Azeroth. Today it washes clean the darkness that scars our world.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129277, 'enUS', '', 'These tears of perfect beauty embody the dream of what our world could be. Today they purify your hearts for the battle to come.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129278, 'enUS', 'Hey you! Ah, nevermind. We thought you were one of... you know... them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129279, 'enUS', 'Hey, hey. Come right in! Take a load off for a second, then get back out there and make sure we\'re still gonna be here tomorrow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129280, 'enUS', 'Your hands--delicate but powerful, hardened by battle yet skilled--are the only hands that you should trust to create garments or armor worthy of a hero. $b$bMany of the legends of yore that you read of as a child were somewhat lackadaisical with spell and sword but how they dressed--oh how they dressed! This is why we speak of them still. $b$bFew know this and now you are one of them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129281, 'enUS', 'You\'ve thought a lot about my hands.', 'You\'ve thought a lot about my hands.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129282, 'enUS', 'I don\'t know what he was goin\' on about, but it didn\'t sound good. And he ain\'t even the first to do that this week!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129283, 'enUS', '', 'Champion! Our vessel requires power cores charged with the energy of fel geysers. These tattered wings could prove useful should you find yourself aloft. Do not hesitate to deploy them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129284, 'enUS', '', 'The Pillars of Creation are in place. Return to the Cathedral and I will reveal the ancient secret this temple holds.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129285, 'enUS', 'Nevermind that! Think instead of the wasted potential of what you have made thus far! $b$bIt isn\'t all your fault, though you\'ve certainly concentrated on deeds and functionality over style. Under the HSST system, you will remedy these failings and perhaps, at last, have a chance at actually becoming a legend. $b$bThe road is hard and full of peril.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129286, 'enUS', '', 'Most useful! Did you enjoy your taste of what it is like for a demon hunter to glide through the air, hero?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129287, 'enUS', 'Are you wrapping this up soon?', 'Are you wrapping this up soon?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129288, 'enUS', '', 'The elves built this temple upon a site of ancient power, a vault I believed would safeguard the husk of my fallen foe. But with his disciple\'s aid, the Dark Titan threatens to return to this world stronger than before!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129289, 'enUS', 'We will put a stop to Kil\'jaeden\'s plan, Aegwynn. This time we will not fail.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129290, 'enUS', 'Bold words, Khadgar, but victory requires more than courage alone. The armies of your Alliance and Horde had courage... before facing slaughter on the Broken Shore. The Deceiver will turn your fears and doubts against you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129291, 'enUS', 'Archmage, the Felborne in the Weeping Terrace are holding manasabers in thrall. If we free them, these magical creatures may prove useful to us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129292, 'enUS', '', 'Our spies have spotted demons infesting this cave, Shadowblade. We\'ve planted the appropriate supplies. You know what to do!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129293, 'enUS', '', 'That explosion might\'ve been better than the last! Tethys would approve.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129294, 'enUS', 'I\'m starting to wish I\'d chosen another profession.', 'I\'m starting to wish I\'d chosen another profession.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129295, 'enUS', 'Kil\'jaeden hounded my people across the stars, but we are done running. He will pay for his atrocities against our world... and my son.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129296, 'enUS', 'The impatience of youth. $b$bA craftsperson is only as good as his or her materials and the--let\'s face it--garbage you\'ve been using thus far is a core reason why you look more like an accountant from Stormwind than a potential legend. $b$bThink ahead--when they make paintings of you, the cut and weight of your clothing or armor will echo through history. Is that not what you\'ve always wished for?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129297, 'enUS', 'You\'re right, Illidan. Kil\'jaeden and the Legion have defeated each of us in the past. But if we stand together, Azeroth can yet be saved. I know it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129298, 'enUS', '', 'Our rogues could use a hand pulling off a mission in this cavern. These stun bombs should offer just the advantage they need!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129299, 'enUS', '', 'Smashing job! Or would \"stunning\" be more appropriate?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129300, 'enUS', 'You lack appreciation for the gift I am giving you. The gift of a guiding philosophy is one that most go their whole lives without, $n. $b$b<sighs> Look, the point is that you will be judged throughout all time by what you wear and with my research and teaching, you will be able create legendary things that will set you apart from all others. Let us start.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129301, 'enUS', '', 'A trusted friend who lost her life defending this temple. Her rest has been defiled, and now she haunts these halls as a banshee. Release her from this torment!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129302, 'enUS', 'Let us hope this is worth it.', 'Let us hope this is worth it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129303, 'enUS', '', 'The peace earned so long ago has been returned to her. You have my thanks.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129304, 'enUS', '', 'In life, Ryul was a formidable warrior. In death, he is an abomination that has been turned against us. Put him back in his grave.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129305, 'enUS', '', 'Well fought! That should ensure Ryul remains in his grave.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129306, 'enUS', 'The presence of the Tidestone has drawn out the fanatical followers of Queen Azshara, including the elusive Warlord Darjah. An excellent opportunity to remove this threat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129307, 'enUS', '|TINTERFACE\\ICONS\\SPELL_SHADOW_LIFEDRAIN02.BLP:20|t %s begins to cast |cFFFF0404|Hspell:234423|h[Drain Life]|h|r!', '|TINTERFACE\\ICONS\\SPELL_SHADOW_LIFEDRAIN02.BLP:20|t %s begins to cast |cFFFF0404|Hspell:234423|h[Drain Life]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129308, 'enUS', 'The magic of the Felborne is not without a certain... finesse. Such a shame they abused it. They deserved their fate.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129309, 'enUS', 'Well done, heroes. The loss of Warlord Darjah weakens the naga\'s might within the tomb.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129310, 'enUS', 'The Legion\'s presence has stirred an ancient hydra known as Deepmaw. Destroy this creature before its wrath is unleashed upon our world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129311, 'enUS', 'Legends tell of Deepmaw\'s unbridled ferocity. I\'m relieved we had our own legendary heroes to stop this threat!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129312, 'enUS', 'Battlelord! The naga and their skrog lackeys are back to their old tricks. They must be taught proper respect!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129313, 'enUS', 'The power of the tomb has drawn the attention of more than just the Legion. Kelpfist, a greedy and powerful sea giant, has emerged to claim the temple\'s relics for himself. He must be stopped!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129314, 'enUS', 'Gloriously executed as always, Battlelord. I expected no less of you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129315, 'enUS', 'That greedy giant met the end he deserved. Well done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129316, 'enUS', 'That foul stench in the air means the Dread Stalker roams nearby. This vicious pet of the Legion must be put down if we are to reclaim the temple.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129317, 'enUS', 'The Legion bred that monster with a taste for mortal flesh. A fine kill!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129318, 'enUS', 'The demon Dresanoth lurks among the naga. Destroy any foe who stands in your path and scour this filth from our world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129319, 'enUS', 'Well done. We will show the Legion there is nowhere they can hide from annihilation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129320, 'enUS', 'Erdu\'val, one of the Legion\'s most brutal warriors, boasts of his victories. Claim this braggart\'s wretched life!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129321, 'enUS', 'Nothing quiets the prattling of demons like finding one of their champions cold and dead. Well done!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129322, 'enUS', 'Given your thoughts on fashion and flair, please explain the drabness of your robes.', 'Given your thoughts on fashion and flair, please explain the drabness of your robes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129323, 'enUS', '', 'There\'s another demon plot to attack the Ancients! Show those creeps they can\'t mess with nature!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129324, 'enUS', '', 'Stupid demons. They\'ll never learn, will they? They can\'t beat us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129325, 'enUS', 'Fel corruption spreads across the beach. Receive my gift and cleanse the land.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129326, 'enUS', '', 'Our forces have engaged the Legion on the ground floor. We must place the Aegis in the tower before our losses grow insurmountable.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129327, 'enUS', 'That will slow the spread of the fel in this dark place.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129328, 'enUS', '', 'The Legion is plotting attacks on our Ancients from this camp. Help us disrupt their plans!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129329, 'enUS', '', 'That should put a halt to their plans, at least for a while. Thanks for the help!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129330, 'enUS', 'The \"historian\" part of my title requires a certain understated aura of scholarly authority. $b$bThat said, these drab robes are microfiber, moisture wicking, durable, laminated, spun-laced, tufted, antiseptic, crease resistant, shrinkage controlled bespoke robes. $b$bSo there. $b$bAlso note my skin. The moisturizing regime is vigorous.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129331, 'enUS', '', 'These markings appear fresh. Try to stay clear until we get this sorted out, $n. Wouldn\'t want you getting hurt.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129332, 'enUS', '', 'Daumyr is waiting for you, Deathlord. Care to rain down some destruction?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129333, 'enUS', 'Fel corruption spreads across the beach. The Conclave is in need of your aid.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129334, 'enUS', '', 'Wondrous!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129335, 'enUS', '', 'Don\'t get too close, $n. This area was hit recently. It\'s not safe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129336, 'enUS', '', 'The Council asked me to look into these fresh scorch marks. They appear quite recent.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129337, 'enUS', 'We\'ve lost. The Legion broke our lines and pushed us out of the tomb. You\'re all that stands between them and the Aegis. They are coming!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129338, 'enUS', 'Let them come. Maiev and I will hold them on the stairs below.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129339, 'enUS', '', 'Our death knights have unleashed an army of undead drakes to bombard this area. Care to join in on the fun?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129340, 'enUS', '', 'Forgive me if my glaive finds your heart, Illidan. With so many demons about, it\'s hard to distinguish you from the rest of this filth.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129341, 'enUS', '', 'Ah, the cold embrace of death. So liberating!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129342, 'enUS', 'I will hold the skies. See to the Aegis!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129343, 'enUS', 'Alone at last.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129344, 'enUS', '', 'Our allies claim to have outfitted their ship with devastating armaments. Put their weapons to the test!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129345, 'enUS', '', 'Such an inelegant weapon is no substitute for a sharpened glaive, but I suppose it was effective.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129346, 'enUS', '|TINTERFACE\\ICONS\\SPELL_SHADOW_CURSEOFMANNOROTH.BLP:20|t %s begins to cast |cFFFF0404|Hspell:236572|h[Annihilate]|h|r!', '|TINTERFACE\\ICONS\\SPELL_SHADOW_LIFEDRAIN02.BLP:20|t %s begins to cast |cFFFF0404|Hspell:234423|h[Drain Life]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129347, 'enUS', 'What is this?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129348, 'enUS', '', 'Can it be? The Guardian!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129349, 'enUS', '', 'In ages past, this shield defended the young world soul against madness and corruption. Today, it serves those who fight for Azeroth.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129350, 'enUS', '', 'I am Aegwynn, Guardian of Tirisfal. I left behind this echo of my power as a safeguard in the event that the wards I put in place to protect the tomb failed. Indeed, I sense that the master of the Burning Legion now schemes to claim his prize.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129351, 'enUS', '', 'Yet there is still hope. By anchoring the Pillars of Creation in the halls below, you will restore my wards and push back against the invaders. Then you must venture into the depths of the tomb and use the Eye of Aman\'Thul to eradicate the Felstorm and sever the Legion\'s link to Azeroth.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129352, 'enUS', '|TInterface\\ICONS\\Spell_Arcane_PortalDarnassus.blp20|t %s begins to cast |cFFFF0404|Hspell:234676|h[Twisted Reflection]|h|r!', '|TINTERFACE\\ICONS\\SPELL_SHADOW_LIFEDRAIN02.BLP:20|t %s begins to cast |cFFFF0404|Hspell:234423|h[Drain Life]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129353, 'enUS', '', 'But the Pillars alone cannot bring victory. Only a united army founded on strength and courage can conquer the Legion. When you and your allies are prepared for the final battle, my echo will return to aid you. The fate of Azeroth rests in your hands.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129354, 'enUS', '', 'Khadgar will want to hear of this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129355, 'enUS', 'It seems preparations are in order.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129356, 'enUS', 'The winds have blown in your favor this day, Farseer. I shall reconsider my allegiance to Thunderaan.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129357, 'enUS', 'You\'ve impressed me with your vigor, Farseer, and I trust that your faith in Thunderaan is not misplaced. I shall grant my allegiance to his.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129358, 'enUS', 'Hero, we\'ve gotten word that the Felskorn are readying another attack. Please foil their plans before they have a chance to act!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129359, 'enUS', 'Blessings upon you, my friend! The monks are in your debt.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129360, 'enUS', 'Grandmaster! We\'ve gotten word that the Felskorn are readying another attack. Please foil their plans before they have a chance to act!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129361, 'enUS', 'Well done, Grandmaster! The enemy has been kept at bay... for now.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129362, 'enUS', 'Other Continents', 'Other Continents', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129363, 'enUS', 'There is a portal that leads to Hellfire Peninsula in Outland. It is located within the Sunfury Spire.', 'There is a portal that leads to Hellfire Peninsula in Outland. It is located within the Sunfury Spire.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129364, 'enUS', 'To get to Undercity and on to Orgrimmar or Kalimdor, you will need to use the Teleporter located within the Sunfury Spire.', 'To get to Undercity and on to Orgrimmar or Kalimdor, you will need to use the Teleporter located within the Sunfury Spire.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129365, 'enUS', 'Hellfire Peninsula in Outland', 'Hellfire Peninsula in Outland', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129366, 'enUS', 'Undercity or Orgrimmar', 'Undercity or Orgrimmar', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129367, 'enUS', 'What\'s going on? What aren\'t we being told?', 'What\'s going on? What aren\'t we being told?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129368, 'enUS', 'What if something happens?', 'What if something happens?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129369, 'enUS', 'We\'re just looking for answers.', 'We\'re just looking for answers.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129370, 'enUS', 'Do you have any updates?', 'Do you have any updates?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129371, 'enUS', 'We have long sought exclusion from others and so do not have many connections to other lands. As part of our pact with the Horde we do offer travel to the other cities and fronts.\n\nWhere are you looking to travel?', 'We have long sought exclusion from others and so do not have many connections to other lands. As part of our pact with the Horde we do offer travel to the other cities and fronts.\n\nWhere are you looking to travel?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129372, 'enUS', 'What does it mean?', 'What does it mean?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129373, 'enUS', 'Should we be worried?', 'Should we be worried?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129374, 'enUS', 'What\'s being done about people\'s behavior? They\'re frightening the children.', 'What\'s being done about people\'s behavior? They\'re frightening the children.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129375, 'enUS', 'We need answers! We\'re not sure what to expect.', 'We need answers! We\'re not sure what to expect.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129376, 'enUS', 'Please remain calm. Answers will come soon.', 'Please remain calm. Answers will come soon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129377, 'enUS', 'The Council is aware of the situation. They ask that you remain calm until their assessment is complete.', 'The Council is aware of the situation. They ask that you remain calm until their assessment is complete.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129378, 'enUS', 'We are newer to this world but have already started making connection with the major cities of the Alliance.\n\nWhere are you looking to travel?', 'We are newer to this world but have already started making connection with the major cities of the Alliance.\n\nWhere are you looking to travel?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129379, 'enUS', 'We will continue to work closely with the Council until the situation is resolved.', 'We will continue to work closely with the Council until the situation is resolved.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129380, 'enUS', 'We must remain calm until the Council concludes its investigation.', 'We must remain calm until the Council concludes its investigation.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129381, 'enUS', 'If you see any unusual behavior, magical or otherwise, please do not hesitate to report it.', 'If you see any unusual behavior, magical or otherwise, please do not hesitate to report it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129382, 'enUS', 'The Council asks that we remain calm until they have finished their assessment of the situation.', 'The Council asks that we remain calm until they have finished their assessment of the situation.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129383, 'enUS', 'To get to Storwmind you will be traveling by sea. First go up to the surface and Azuremyst Isle. From there go down the right path to the Valaar\'s Berth. The Valaar will take you as far as Rut\'theran Village. A ship departs there for Stormwind and the rest of the Eastern Kingdoms. If you need help getting tot he surface I can give you directions to the entrance as well.', 'To get to Storwmind you will be traveling by sea. First go up to the surface and Azuremyst Isle. From there go down the right path to the Valaar\'s Berth. The Valaar will take you as far as Rut\'theran Village. A ship departs there for Stormwind and the rest of the Eastern Kingdoms. If you need help getting tot he surface I can give you directions to the entrance as well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129384, 'enUS', 'There is a portal to the Night Elf capital of Darnassus that is located in the Vault of Lights.', 'There is a portal to the Night Elf capital of Darnassus that is located in the Vault of Lights.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129385, 'enUS', 'If you wish to venture to our homeland of Outland you can use the portal in the Vault of Lights to get to Hellfire Peninsula.', 'If you wish to venture to our homeland of Outland you can use the portal in the Vault of Lights to get to Hellfire Peninsula.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129386, 'enUS', 'Darnassus', 'Darnassus', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129387, 'enUS', 'Hellfire Peninsula in Outland', 'Hellfire Peninsula in Outland', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129388, 'enUS', 'Stormwind or Ironforge in Eastern Kingdoms', 'Stormwind or Ironforge in Eastern Kingdoms', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129389, 'enUS', 'Other Continents', 'Other Continents', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129390, 'enUS', '\"Fortitude can be found in the land beneath.\"', '\"Fortitude can be found in the land beneath.\"', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129391, 'enUS', '\"There is truth in everything.\"', '\"There is truth in everything.\"', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129392, 'enUS', '\"The wind is swift and dauntless.\"', '\"The wind is swift and dauntless.\"', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129393, 'enUS', '\"The power we wield is the burden of our enemies.\"', '\"The power we wield is the burden of our enemies.\"', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129394, 'enUS', 'The shrine was corrupted by the Legion!', 'The shrine was corrupted by the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129395, 'enUS', 'Outside to Azuremyst Isle', 'Outside to Azuremyst Isle', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129396, 'enUS', 'Oh, you need help reaching the entrance of the Exodar. I forget that many newcomers are not intimately familiar with these hallowed halls. There is a ramp on the upper ring of the Seat of the Naaru that will lead you up to the surface.', 'Oh, you need help reaching the entrance of the Exodar. I forget that many newcomers are not intimately familiar with these hallowed halls. There is a ramp on the upper ring of the Seat of the Naaru that will lead you up to the surface.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129397, 'enUS', '', 'Here to help, $n? I haven\'t been able to diagnose it yet. Their symptoms are all over the place.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129398, 'enUS', '', 'If citizens continue exhibiting these symptoms, we\'ll need to start training additional help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129399, 'enUS', 'Bending Azeroth\'s elements to their will... the Legion has gone too far!', 'Bending Azeroth\'s elements to their will... the Legion has gone too far!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129400, 'enUS', 'Without their power cores they have no control!', 'Without their power cores they have no control!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129401, 'enUS', 'They will accomplish nothing but angering the elements!', 'They will accomplish nothing but angering the elements!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129402, 'enUS', '', 'Please remain calm. We\'ll assist each and every one of you, but we must ask for your patience.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129403, 'enUS', '', '$n! They just keep coming! I\'ve never seen anything like this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129404, 'enUS', '', 'If it\'s too crowded, please step outside. We have additional stations set up and an assistant ready to help.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129405, 'enUS', '', 'We\'ve brought in additional help and we will get to you shortly. Please have patience.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129406, 'enUS', '', 'I don\'t know how much longer we can keep this up, $n. We\'re starting to run short on supplies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129407, 'enUS', '', 'We\'re running out of space for our guests. I don\'t know how much longer we can keep this up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129408, 'enUS', '', 'Have you any news, $n? The symptoms continue to present, but there\'s absolutely no explanation for any of it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129409, 'enUS', '', 'Pretty soon we\'ll have to send people off site for help. I just don\'t think we have the resources here to maintain any semblance of order.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129410, 'enUS', '', 'I do hope this calms down. People have been arriving in a panic.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129411, 'enUS', '', 'I\'ve never seen anything like this before, $n. We\'ll keep at it. Make sure to send anyone presenting similar symptoms our way.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129412, 'enUS', '', 'Hello, $n.  Did you know the Royal Pond was once home to the largest and most beautiful fish in all of Azsuna?  Royalty would often gaze into the waters and watch the graceful movements, trying to forget their heavy burdens.\n\nNow... oh, something is wrong!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129413, 'enUS', 'I... I don\'t feel well.', 'I... I don\'t feel well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129414, 'enUS', 'I feel it coming on again...', 'I feel it coming on again...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129415, 'enUS', 'Please. You have to help us.', 'Please. You have to help us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129416, 'enUS', 'This has to end. I can\'t take much more of this.', 'This has to end. I can\'t take much more of this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129417, 'enUS', 'They say it\'s all in your head, but this... this is different. I swear!', 'They say it\'s all in your head, but this... this is different. I swear!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129418, 'enUS', 'This is all too much to handle. I\'m not sure I\'m going to make it.', 'This is all too much to handle. I\'m not sure I\'m going to make it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129419, 'enUS', 'I just want to feel like myself again.', 'I just want to feel like myself again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129420, 'enUS', 'Is it normal to see stars?', 'Is it normal to see stars?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129421, 'enUS', '', '$n, good to see you! Any updates about what\'s been happening?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129422, 'enUS', '', 'Welcome to the shop! Has the Council chimed in with their findings yet, $n?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129423, 'enUS', '', 'Good to see you again, $n. Come to chat? Looking for supplies? Perhaps just a bit of relief from... whatever\'s going on out there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129424, 'enUS', '', 'Our door is always open, $n. Hopefully the atmosphere in here is calmer than it is out there.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129425, 'enUS', '', 'You\'re safe in here, $n. Ainderu has been adamant about watching the door ever since this all began, but I\'m not concerned.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129426, 'enUS', 'I am ready to launch the assault.', 'I am ready to launch the assault.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129427, 'enUS', 'We have come far together, $n.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129428, 'enUS', 'The final stage of the scenario is not yet complete. You\'ll destroy the legion ship and it will be awesome. Please send any feedback you have so far!', 'The final stage of the scenario is not yet complete. You\'ll destroy the legion ship and it will be awesome. Please send any feedback you have so far!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129429, 'enUS', 'I\'m looking for answers. Have you heard anything new?', 'I\'m looking for answers. Have you heard anything new?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129430, 'enUS', 'I need to know what\'s happening.', 'I need to know what\'s happening.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129431, 'enUS', 'I\'m hoping for some answers.', 'I\'m hoping for some answers.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129432, 'enUS', 'I haven\'t heard anything I didn\'t already know, $n. How about you?', 'I haven\'t heard anything I didn\'t already know, $n. How about you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129433, 'enUS', 'What does it mean? I\'ve never seen anything like this before.', 'What does it mean? I\'ve never seen anything like this before.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129434, 'enUS', 'I keep coming back hoping for an update, but the answer is always the same.', 'I keep coming back hoping for an update, but the answer is always the same.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129435, 'enUS', 'Have you heard anything new today?', 'Have you heard anything new today?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129436, 'enUS', 'I\'m trying to remain positive, but I really don\'t know what to expect.', 'I\'m trying to remain positive, but I really don\'t know what to expect.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129437, 'enUS', '', 'Hear the screams of the forlorn!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129438, 'enUS', '', 'Let their agony wash over you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129439, 'enUS', '', 'Their torment will become your own!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129440, 'enUS', '', 'Life and death... interwoven!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129441, 'enUS', '', 'Ahhh, the ties that bind!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129442, 'enUS', '', 'Can you feel the clutch of doom?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129443, 'enUS', 'The darkness shall claim you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129444, 'enUS', 'Your souls will know despair!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129445, 'enUS', 'Emptiness... desolation...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129446, 'enUS', 'I am confident you can handle your end. Let us hope the others come through.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129447, 'enUS', 'Our fury is unleashed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129448, 'enUS', 'You will be made one with us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129449, 'enUS', 'We will feed upon your suffering!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129450, 'enUS', 'We will reap your souls!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129451, 'enUS', 'You are fuel for the engine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129452, 'enUS', 'Your souls are but fodder!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129453, 'enUS', 'Another for the harvest!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129454, 'enUS', 'That one has joined us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129455, 'enUS', 'More fuel!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129456, 'enUS', 'A rich harvest indeed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129457, 'enUS', 'So many souls to serve us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129458, 'enUS', 'The engine hungers for more!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129459, 'enUS', 'Such... bitter... harvest...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129460, 'enUS', 'The engine... falters...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129461, 'enUS', 'Our work... must continue...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129462, 'enUS', 'Hope dies with you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129463, 'enUS', 'Your struggle has been for naught!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129464, 'enUS', 'Witness our victory!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129465, 'enUS', '', 'As I serve, so shall you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129466, 'enUS', '', 'Let this temple become your tomb!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129467, 'enUS', '', 'Do not resist the master\'s will!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129468, 'enUS', '', 'Your spirit is mine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129469, 'enUS', '', 'I claim you for the master!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129470, 'enUS', '', 'Feed, engine! Feed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129471, 'enUS', '', 'Elune holds no power here. This tomb belongs to the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129472, 'enUS', '', 'Mortal souls... so weak, so pathetic!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129473, 'enUS', '', 'The engine consumes all!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129474, 'enUS', '', 'I should have been stronger. Tell Tyrande... I am sorry.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129475, 'enUS', '', 'I tried to resist them, but failed. At least now I am free.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129476, 'enUS', '', 'Thank you, champions. I pray you cleanse the darkness that haunts this temple.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129477, 'enUS', '', 'Arille, please! Not in front of the guests. We don\'t know what it is yet and there\'s no point in speculation.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129478, 'enUS', '', 'Pull up a chair and take a load off. We know it can be hectic out there, so leave your worries at the door.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129479, 'enUS', '', 'Ah, $n! Maybe you can settle something for us... Arille and I were debating whether or not the Council had finished its assessment. Any word?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129480, 'enUS', '', 'Oh no! Spilled another drink. Can\'t seem to keep my hands steady with all that\'s been going on around here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129481, 'enUS', '', 'Hope everything is well with you, $n. As for me, well...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129482, 'enUS', '', 'Sometimes I wonder if we\'ll ever hear an answer as to why this is happening...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129483, 'enUS', '', 'I hope we receive an update soon. It\'s been a while since the last.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129484, 'enUS', '', 'All of this waiting to find out what\'s happening makes me a nervous wreck.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129485, 'enUS', '', 'Bring any news today? We haven\'t heard any updates in quite some time.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129486, 'enUS', '', 'How are things outside, $n? People have come and gone throughout the day, but nobody seems to know much.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129487, 'enUS', '', 'Welcome! Lucian and I were just discussing recent events. Have any updates for us?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129488, 'enUS', '', 'What\'s going on out there? The noise level\'s grown increasingly louder. I only hope we hear some news soon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129489, 'enUS', '', 'Watch your step, $n! Say, any word from the Council yet?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129490, 'enUS', 'YOU\'RE PRESENCE HERE IS UNWELCOMED!! RETURN NOW FROM WHENCE YOU CAME!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129491, 'enUS', '%s becomes uneasy.', '%s becomes uneasy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129492, 'enUS', '', 'My apologies if the pets are acting up. Something has them riled up.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129493, 'enUS', '', 'Have a care. The pets have been acting out of sorts. It would be wise to steer clear of the pens for a while.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129494, 'enUS', '', 'There, there, my magnificent beasts! Everything will return to normal soon... I hope.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129495, 'enUS', '', 'Try and keep the noise down. These beasts have been on edge.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129496, 'enUS', '', 'What\'s the word on the street, $n?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129497, 'enUS', '', 'Any news, $n?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129498, 'enUS', '', 'We\'re getting pretty restless in here. Any news on what\'s happening?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129499, 'enUS', 'Send me to the Throne of the Four Winds.', 'Send me to the Throne of the Four Winds.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129500, 'enUS', '', 'Hey, $n! Has the Council released any new information?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129501, 'enUS', '', 'The skies aren\'t the only thing full of magic these days! Relax and gander at some of our newest arrivals.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129502, 'enUS', 'Our wings will blacken your sky!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129503, 'enUS', 'I shall use your corpse to feed my charges.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129504, 'enUS', 'Your skies belong to the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129505, 'enUS', '', 'Our wings will blacken your sky!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129506, 'enUS', '', 'Your skies belong to the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129507, 'enUS', '', 'I shall use your corpse to feed my charges.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129508, 'enUS', '', 'If you think I\'m going to let the Doomstone out of my sight, you\'re horribly mistaken. Let\'s get on with it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129509, 'enUS', 'I shall use your corpse to feed my charges.', 'I shall use your corpse to feed my charges.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129510, 'enUS', 'Our wings will blacken your sky!', 'Our wings will blacken your sky!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129511, 'enUS', 'Your skies belong to the Legion!', 'Your skies belong to the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129512, 'enUS', '', 'Trying to make Azeroth\'s earth fight itself? Foolish eredar!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129513, 'enUS', '', 'You know, if you only used the Doomstone, this would soon be over.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129514, 'enUS', '', 'What\'s the point of possessing such a mighty artifact if you fear to use it? The Doomstone would be much more useful in my hands.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129515, 'enUS', '', 'The demons have their fel fire, so they assume they can control Azeroth\'s as well. Oh, how wrong they are!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129516, 'enUS', '', 'The powers of water are generally restorative, the most passive and weak of elements. What did the Legion think to do with it?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129517, 'enUS', '', 'Consider the majesty of earth... so sturdy, protective, immutable. Yet when it but shudders, capable of such grand destruction.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129518, 'enUS', '', 'Can you hear the wind, child? It screams for help, for salvation from the Legion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129519, 'enUS', '', 'Even if captured, my Grimtotem remain ready to fight. Your Earthen Ring, however... weak.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129520, 'enUS', 'Before I tell you about legendary crafting, you must first be grounded in a philosophy of style and flair. This is non-negotiable! Are you ready to begin?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129521, 'enUS', 'Let me hear your philosophy on style.', 'Let me hear your philosophy on style.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129522, 'enUS', 'The end is upon us!', 'The end is upon us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129523, 'enUS', 'Gaze into the skies above! The truth reveals itself at last!', 'Gaze into the skies above! The truth reveals itself at last!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129524, 'enUS', 'Shall we steal the secrets of the Nightborne tailors?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129525, 'enUS', 'I am ready.', 'I am ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129526, 'enUS', '', 'I have complete faith in our leaders. We should not be led astray by the idle banter throughout the city. People cope in different ways.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129527, 'enUS', '', 'We should try to maintain a positive attitude. People have enough to deal with. We need to set an example for others.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129528, 'enUS', '', 'Walking through the streets earlier I could feel my ears growing tired from all the speculation. So much uncertainty around here lately.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129529, 'enUS', '', 'I wonder when the streets will calm down. The city\'s been abuzz ever since this whole thing began.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129530, 'enUS', 'We should listen to our leaders, not the whispered conversations throughout the city. We will know more soon enough.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129531, 'enUS', 'The Council will reveal information soon enough. I\'m sure of it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129532, 'enUS', 'Please! Has anyone seen their parents? They really shouldn\'t be running around right now. It\'s not safe.', 'Please! Has anyone seen their parents? They really shouldn\'t be running around right now. It\'s not safe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129533, 'enUS', 'Where are your parents? You shouldn\'t be running around the city right now.', 'Where are your parents? You shouldn\'t be running around the city right now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129534, 'enUS', 'It\'s not your fault, children, but you really shouldn\'t be out until we\'re certain it\'s safe.', 'It\'s not your fault, children, but you really shouldn\'t be out until we\'re certain it\'s safe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129535, 'enUS', 'The streets are no place for children in times like these. Someone will be by shortly to watch you until we\'re certain the city is safe.', 'The streets are no place for children in times like these. Someone will be by shortly to watch you until we\'re certain the city is safe.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129536, 'enUS', 'There\'s no need to apologize. You\'ve done nothing wrong. We just need to make sure the streets are safe for the both of you.', 'There\'s no need to apologize. You\'ve done nothing wrong. We just need to make sure the streets are safe for the both of you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129537, 'enUS', 'Our lines weaken with each moment. Slaughter their commander before we are overwhelmed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129538, 'enUS', 'Finish... this...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129539, 'enUS', 'Fool! The Legion you know is but a whisper of our true glory.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129540, 'enUS', 'It matters not. It has already begun.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129541, 'enUS', 'You have doomed your world to bathe in the master\'s flame.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129542, 'enUS', 'The way is open. All you know, all you hold dear, will fall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129543, 'enUS', 'Damnable mortals! You scurry through this tomb like vermin, dispatching my underlings and imagining yourselves the equals of the Legion. Heroes... champions... fools!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129544, 'enUS', 'Your puppet\'s strings are cut, Kil\'jaeden!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129545, 'enUS', 'No more shackles! Hold nothing back! Shatter their world and finish this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129546, 'enUS', 'Good luck, Farseer $p. I have faith you will not fail me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129547, 'enUS', 'End... you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129548, 'enUS', 'Mortals... die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129549, 'enUS', 'Burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129550, 'enUS', 'Chaos!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129551, 'enUS', 'Cursed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129552, 'enUS', 'Sever!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129553, 'enUS', 'Keeper...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129554, 'enUS', '', 'Corruption will be cleansed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129555, 'enUS', 'Dead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129556, 'enUS', '', 'That fiend ravages not only your people, but the land itself! If there was ever cause to use the Doomstone, that time is now!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129557, 'enUS', 'Ended!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129558, 'enUS', 'By the Light\'s blessing, our path begins anew.', 'By the Light\'s blessing, our path begins anew.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129559, 'enUS', 'Preparations are nearly complete. A great journey is upon us.', 'Preparations are nearly complete. A great journey is upon us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129560, 'enUS', 'Unsettling as these events may seem, a vision of hope lingers beyond the darkness.', 'Unsettling as these events may seem, a vision of hope lingers beyond the darkness.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129561, 'enUS', 'May we rise once more.', 'May we rise once more.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129562, 'enUS', 'The repairs near completion.', 'The repairs near completion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129563, 'enUS', '...and in the darkness, the Light will prevail.', '...and in the darkness, the Light will prevail.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129564, 'enUS', 'Master... wins.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129565, 'enUS', 'Flesh... fails.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129566, 'enUS', 'It... ends...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129567, 'enUS', 'Still you fight us! Still you deny the inevitable! Your incessant resistance is maddening! Why will you not yield?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129568, 'enUS', 'Have your countless defeats taught you nothing, Deceiver?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129569, 'enUS', 'The vision Sargeras showed you was a lie, Kil\'jaeden. Surrender! Let it end!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129570, 'enUS', 'I have ripped the flame from your world. It now bends to my command!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129571, 'enUS', 'Your power is too great. You cannot control it!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129572, 'enUS', '', 'Such power... such chaos!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129573, 'enUS', '', 'Pull yourself together, child! What is wrong with you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129574, 'enUS', 'Let... it... end...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129575, 'enUS', '', 'That\'s it! Give in to the power!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129576, 'enUS', 'Never! Our crusade will never yield! Savor your shallow victory. We will return again and again, however long it takes, until at last we break you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129577, 'enUS', 'The portal is closing! We can seal the Legion\'s gateway and end this invasion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129578, 'enUS', 'Be on your guard, champion. The Legion is skilled at deception.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129579, 'enUS', 'We may well be walking into a trap.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129580, 'enUS', '', 'Listen--we\'re sure our goods are \"safe\" here, but why take any chances?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129581, 'enUS', '', 'What\'s it to ya?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129582, 'enUS', '', 'Ain\'t ya ever seen someone protectin\' their assets before? Keep movin\'!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129583, 'enUS', '', 'Hold still! We don\'t want none of our loot spillin\' over, do we?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129584, 'enUS', '', 'What\'s it take to get a helpin\' hand \'round here? We was hopin\' to have our stuff back home and safe before... well, y\'know.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129585, 'enUS', '', 'End of the world? Pfft! I\'m tryin\' to get our scratch but these guys are movin\' like snails!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129586, 'enUS', '', 'I sure hope our stash back home is end-of-the-world proof.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129587, 'enUS', 'Please remain calm and maintain an orderly line. A banker will assist you as soon as a spot becomes available.', 'Please remain calm and maintain an orderly line. A banker will assist you as soon as a spot becomes available.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129588, 'enUS', 'Due to the recent influx of patrons, we must remember to maintain a calm, orderly demeanor.', 'Due to the recent influx of patrons, we must remember to maintain a calm, orderly demeanor.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129589, 'enUS', 'If you notice any suspicious activity or unusual behavior, please do not hesitate to inform us.', 'If you notice any suspicious activity or unusual behavior, please do not hesitate to inform us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129590, 'enUS', 'We are here on your behalf. There is no need for panic. Please remember to maintain orderly conduct at all times.', 'We are here on your behalf. There is no need for panic. Please remember to maintain orderly conduct at all times.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129591, 'enUS', 'We ask you to remain patient and calm until further information becomes available.', 'We ask you to remain patient and calm until further information becomes available.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129592, 'enUS', 'This is no longer the Argus I remember.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129593, 'enUS', 'I am ready to search for survivors.', 'I am ready to search for survivors.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129594, 'enUS', 'Prepare yourself, champion. We are surrounded.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129595, 'enUS', 'I will fight by your side.', 'I will fight by your side.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129596, 'enUS', 'Ready to ride with you, Highlord!', 'Ready to ride with you, Highlord!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129597, 'enUS', 'It will be an honor to ride alongside you, Highlord.', 'It will be an honor to ride alongside you, Highlord.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129598, 'enUS', 'Riding into battle with you shall be glorious, Highlord.', 'Riding into battle with you shall be glorious, Highlord.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129599, 'enUS', 'Let us mount up, Highlord and rescue our missing allies.', 'Let us mount up, Highlord and rescue our missing allies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129600, 'enUS', 'You hunted your own people, chasing us across the stars. Never again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129601, 'enUS', 'It was you who lacked vision! Who refused to see the truth of the Legion\'s inevitable victory!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129602, 'enUS', 'You lost faith in the Light. Together, we could have resisted Sargeras and saved our world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129603, 'enUS', 'Did the Light save your son? Was it faith that shone in Rakeesh\'s eyes as they closed for the final time? Or was it hatred for the father who abandoned him?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129604, 'enUS', 'GRRAAAHHHHH!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129605, 'enUS', 'And now your impotent fury has drawn your allies to their doom. Watch as my infinite army slaughters them all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129606, 'enUS', 'No! You will not divide us, Deceiver. I will fight by their side, and they by mine. Together, we will end your crusade!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129611, 'enUS', 'You were fools to follow me here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129612, 'enUS', 'Fall before your master!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129613, 'enUS', 'Who will save you now?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129614, 'enUS', 'Eredar! Show Velen\'s mongrels the folly of resisting us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129615, 'enUS', 'Felborne! Show them the gravity of their defiance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129616, 'enUS', 'Incinerate these weaklings, warlocks!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129617, 'enUS', 'Turn your gaze upon them, Felsworn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129618, 'enUS', 'An inevitable fate!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129619, 'enUS', 'Fall, hero!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129620, 'enUS', 'The Light shall restore you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129621, 'enUS', 'It is not your time!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129622, 'enUS', 'Do not lose faith!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129623, 'enUS', 'Let us judge your battle prowess! Target a foe!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129624, 'enUS', 'Hero! Pick your target well!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129625, 'enUS', 'Demon filth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129626, 'enUS', 'Fall beneath my blades!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129627, 'enUS', 'Insolent mortals! Your fate was sealed the moment you stepped into the Nether!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129628, 'enUS', 'A meaningless struggle! Azeroth shall fall to the Burning Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129629, 'enUS', 'Still you resist! Then let me repay your arrogance!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129630, 'enUS', 'Burn! Burn and die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129631, 'enUS', 'A vessel for my fury!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129632, 'enUS', 'You will all burn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129633, 'enUS', 'Ixallon! Come forth and break them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129634, 'enUS', 'It is a simple matter to show courage against my minions. Can you face the shadows within your own souls as bravely?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129635, 'enUS', 'What is this? Even when your own doubts and fears are made manifest, you will not yield?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129636, 'enUS', 'Confront your own darkness!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129637, 'enUS', 'I sense the corruption inside you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129638, 'enUS', 'You cannot resist your own nature.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129639, 'enUS', 'Your defiance is futile! My strength comes from an infinite army!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129640, 'enUS', 'Kil\'jaeden will draw power from those vessels!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129641, 'enUS', 'Somehow you survived! No matter. Look upon our wonders, you mortals, and despair! Behold the world that shall be your tomb!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129642, 'enUS', 'Your reality is sundered!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129643, 'enUS', 'Chaos will consume you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129644, 'enUS', 'The fel fuels me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129645, 'enUS', 'I will feed upon your ashes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129646, 'enUS', 'Deceiver! We have come to end your invasion of Azeroth. Your master\'s crusade dies here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129647, 'enUS', 'Greetings, Farseer. Are you feeling well again?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129648, 'enUS', 'She lives? No! I will never let her bind me again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129649, 'enUS', 'You have witnessed the destruction the Doomstone can wreak firsthand. You are a trusted warden of Azeroth, but even in your hands the power caused chaos and panic amongst your people. Imagine it in the control of someone with ill intent...$b$bWe cannot let that happen.$b$bWe discussed what to do with this artifact at length while we waited for you to awaken. We considered giving it a new home in one of the elemental planes, but each one provided its own danger.$b$bThere is no safer place for it than here, in the heart of Azeroth. It shall be kept under the protection of the Earthen Ring, never to be used again. Place it on this pedestal, where it shall never move from again.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129650, 'enUS', 'Ah... merely a lingering echo of her power. Nothing to be concerned about, child.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129651, 'enUS', 'You called for me, Neptulon?', 'You called for me, Neptulon?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129652, 'enUS', 'Even her echo prattles on endlessly! Do you see now what I had to deal with?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129653, 'enUS', 'Bah! I am every bit as mighty as the Tidestone, or any of these other musty relics!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129654, 'enUS', 'A dream of perfect beauty... ridiculous! This world knows only pain, child.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129655, 'enUS', '', 'Come back later. I might have another job for you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129656, 'enUS', 'Do not trust her ramblings! I am the only true power here!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129657, 'enUS', 'You are better off without her!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129658, 'enUS', 'Talkin\' to a world soul ain\'t like chattin\' with a mortal. Might be why she needed someone ta become her Speaker.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129659, 'enUS', 'She\'s haunted by the Legion... and the Old Gods. But there be somethin\' more... a greater darkness...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129663, 'enUS', '', 'Oh don\'t worry, Farseer... we will keep an eye on the Doomstone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129664, 'enUS', 'Stay close. We are being watched.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129665, 'enUS', 'That was not a request. <Strangulate him.>', 'That was not a request. <Strangulate him.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129666, 'enUS', 'This hovel feels... familiar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129667, 'enUS', 'This is not our final day. We stand together.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129668, 'enUS', 'Looks like we\'ve got some preparin\' ta do, champion! Ye best tell Khadgar what we\'ve learned. Good luck!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129669, 'enUS', 'What does it mean? Is this the end?', 'What does it mean? Is this the end?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129670, 'enUS', 'What if something attacks the city? What are we supposed to do then?', 'What if something attacks the city? What are we supposed to do then?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129671, 'enUS', 'Show yourselves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129672, 'enUS', 'Velen. At long last, you return to the home you abandoned.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129673, 'enUS', 'You turned your back on your people, Prophet. Your world. Such betrayal should be answered with death.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129675, 'enUS', 'The skies are darker than ever before! Is there no hope?', 'The skies are darker than ever before! Is there no hope?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129676, 'enUS', 'Is this it? Is this what it\'s all been about? There\'s so many things I wanted to do...', 'Is this it? Is this what it\'s all been about? There\'s so many things I wanted to do...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129677, 'enUS', 'I don\'t know what to do anymore!', 'I don\'t know what to do anymore!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129678, 'enUS', 'That voice... Hatuun? How did you survive this hell for so long?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129679, 'enUS', 'We changed, just as Argus did. We endured. What choice did we have?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129680, 'enUS', 'This can\'t be end! It just can\'t!', 'This can\'t be end! It just can\'t!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129681, 'enUS', 'Enough!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129682, 'enUS', 'Is there anything we can do?', 'Is there anything we can do?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129683, 'enUS', 'Stay your weapons! The Prophet and his ally are not our foes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129684, 'enUS', 'What do I tell my children?', 'What do I tell my children?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129685, 'enUS', 'High Exarch Turalyon... we meet at last.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129686, 'enUS', '<Speaking gibberish.>', '<Speaking gibberish.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129687, 'enUS', '<Unintelligible.>', '<Unintelligible.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129688, 'enUS', '<Shaking uncontrollably.>', '<Shaking uncontrollably.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129689, 'enUS', '...in the darkness.', '...in the darkness.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129690, 'enUS', 'The end...', 'The end...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129691, 'enUS', 'I have no wish to fight you, Hatuun. But I cannot allow your bitterness to impede our war against the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129692, 'enUS', 'We must stand together, all of us, if we are to have any chance of defeating the Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129693, 'enUS', 'It has been eons since I have laid eyes on this place. So many memories...$b$b\n<Velen\'s brows knit in pensive thought.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129694, 'enUS', 'Shall we continue?', 'Shall we continue?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129695, 'enUS', '', 'Those insolent eredar still believe they can bend Azeroth\'s earth to their will? Punish them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129696, 'enUS', '', 'And so they return to the earth.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129697, 'enUS', '', 'Thank you, stranger! Those imps came out of nowhere! I don\'t have much left, but you deserve a reward. Look through our supplies and take anything you need.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129698, 'enUS', '', 'My dear Corian... what will I do without you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129699, 'enUS', 'Champion, the very land is rotting beneath our feet. Manifestations of decay proliferate at every turn. They must be eradicated!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129701, 'enUS', 'I can feel the poison leaving my veins. Thank you!', 'I can feel the poison leaving my veins. Thank you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129702, 'enUS', 'Finally... I can breathe again.', 'Finally... I can breathe again.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129703, 'enUS', 'S...s...spiders....', 'S...s...spiders....', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129704, 'enUS', '|TInterface\\Icons\\inv_egg_01:20|tA group of Incubated Eggs are |cFFFF0404|Hspell:240319|h[Hatching]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129705, 'enUS', '', 'My children will feast on the corpses of your allies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129706, 'enUS', '', 'The Legion endures...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129707, 'enUS', '%s has summoned demonic portals!', '%s has summoned demonic portals!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129708, 'enUS', '%s has extended the duration of the demonic portals!', '%s has extended the duration of the demonic portals!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129709, 'enUS', '%s has empowered the demonic portals!', '%s has empowered the demonic portals!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129710, 'enUS', '%s has summoned a demonic lieutenant!', '%s has summoned a demonic lieutenant!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129711, 'enUS', 'Grevious wounds... I must recover...', 'Grevious wounds... I must recover...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129712, 'enUS', 'Unggh...', 'Unggh...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129713, 'enUS', 'I need to recover... Go on without me!', 'I need to recover... Go on without me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129714, 'enUS', 'Hero! I had a slight mishap while flying over this area. I dropped some special coins I was going to give to... uh, nevermind. If you happen to come across them, their return would be most appreciated!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129715, 'enUS', 'You found them? What a relief! I was scrambling to find a replacement gift...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129716, 'enUS', 'I\'m just going to hide back here and wait for all of this to blow over.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129717, 'enUS', '', 'This should be enough! Now let\'s see... Kataract? Katharsis? How do you even say that?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129718, 'enUS', 'So this is where our forces landed on the Broken Shore.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129719, 'enUS', 'This land is scarred and desolate, a terrible place to meet one\'s end.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129720, 'enUS', 'Come, let us press forward.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129721, 'enUS', 'This land is far worse than I had imagined.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129722, 'enUS', 'My father sought to shield me from the war, insisting I stay behind because a Wrynn must always rule in Stormwind. But I saw the truth in his eyes... his fear of losing me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129723, 'enUS', 'When my mother died, I was all he had left of her.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129724, 'enUS', 'Time and time again, I have watched from afar as the heroes of Stormwind fought and died to preserve peace on Azeroth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129725, 'enUS', 'My father was not the first King of Stormwind to die fighting the Burning Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129726, 'enUS', 'My great-grandfather, King Barathen, and my grandfather, King Llane, also paid the ultimate sacrifice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129727, 'enUS', 'The kings of my family have always fought shoulder-to-shield with their soldiers for the freedom of Stormwind.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129728, 'enUS', 'I have lived my life sheltered by the blood of heroes like Tirion and Bolvar... heroes like my father.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129729, 'enUS', 'The time has come for me to do the same. Take me to the place where my father died. I must see it for myself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129730, 'enUS', 'I\'ll ready a ship at once!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129731, 'enUS', 'The Broken Shore is no place to journey alone. We must find Anduin before it\'s too late!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129732, 'enUS', 'Slay the demons and lay waste to their fortifications!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129733, 'enUS', 'Well done. The demons will not soon recover from such devestation.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129734, 'enUS', 'Yes! Let them come! Their bodies will fuel the flames of their burning world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129739, 'enUS', '', 'Defilers! They must be purged from Elune\'s sacred temple!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129740, 'enUS', '', 'Sisters! On your guard!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129741, 'enUS', '', 'My arrows are always ready, Kasparian.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129742, 'enUS', '', 'I claim first blood, sisters!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129743, 'enUS', '', 'Goddess guide your hand!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129744, 'enUS', '', 'You are ever impetuous, Kasparian.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129745, 'enUS', '', 'Your hunt is ended!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129746, 'enUS', '', 'A bit slow on that kill, sister!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129747, 'enUS', '', 'The goddess is pleased, sister!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129748, 'enUS', '', 'My quarry falls!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129749, 'enUS', '', 'A worthy kill, sister!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129750, 'enUS', '', 'Your bow is blessed, sister!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129751, 'enUS', '', 'You should have believed!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129752, 'enUS', '', 'You serve the goddess well, sister!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129753, 'enUS', '', 'Inefficient... but effective.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129754, 'enUS', '', 'Mother Moon has granted us victory!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129755, 'enUS', '', 'The temple is secure. Well fought, sisters!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129756, 'enUS', '', 'Next time, I get to strike first!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129757, 'enUS', '', 'We failed... the goddess...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129758, 'enUS', '', 'No! I... should have... won...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129759, 'enUS', '', 'Elune... forgive us...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129760, 'enUS', '', 'No more dawdling, Kasparian! Victory shall be mine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129761, 'enUS', '', 'I will dismiss these heretics, Yathae!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129762, 'enUS', '', 'I relieve you, sister. Time for my glaive to sing!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129763, 'enUS', '', 'Elune\'s light reaches its zenith!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129764, 'enUS', '', 'Retribution rains down!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129765, 'enUS', '', 'My mark is upon you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129766, 'enUS', '', 'You are in my sights!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129767, 'enUS', '', 'I never miss!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129768, 'enUS', '', 'Let your talons taste their flesh, my pet!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129769, 'enUS', '', 'Goddess, burn the faithless!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129770, 'enUS', '', 'Behold her glory!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129771, 'enUS', '', 'Pay the price for heresy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129772, 'enUS', '', 'Mother Moon, reveal their sins!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129773, 'enUS', '', 'Confess! Repent!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129774, 'enUS', '', 'You are unworthy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129775, 'enUS', '', 'Right on target!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129776, 'enUS', '', 'You are in my way!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129777, 'enUS', '', 'The keenest cut!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129778, 'enUS', '', 'The Night Warrior guides your hand, sister!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129779, 'enUS', '', 'You waste too much time. Destroy them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129780, 'enUS', '', 'Most impressive aim, sister!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129781, 'enUS', '', 'Goddess bless your arrows!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129782, 'enUS', '', 'Mother Moon is pleased, Lunaspyre!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129783, 'enUS', 'I will show no mercy!', 'I will show no mercy!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129784, 'enUS', '', 'Too much preaching! Just kill them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129785, 'enUS', 'Hero, over here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129786, 'enUS', 'This land is grim and brimming with sorrow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129787, 'enUS', 'I had no idea...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129788, 'enUS', '', 'We must hurry, outsider!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129789, 'enUS', '', 'Keep moving! It is not safe here...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129790, 'enUS', '', 'Our foes close in... let us make haste!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129791, 'enUS', 'Father...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129792, 'enUS', 'Let\'s go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129793, 'enUS', 'You have a \"Loaner Demon\" for me?', 'You have a \"Loaner Demon\" for me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129794, 'enUS', 'I don\'t like this demon.', 'I don\'t like this demon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129795, 'enUS', 'What a coincidence. She doesn\'t like you either.\n\nNow get to work before the eredar on the other side of this thing figure out what I\'ve done and try to seal the portal.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129796, 'enUS', 'I wish to cease my experience gains when I reach level 19.', 'I wish to cease my experience gains when I reach level 19.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129797, 'enUS', 'Are you certain you wish to stop gaining experience when you reach level 19?', 'Are you certain you wish to stop gaining experience when you reach level 19?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129798, 'enUS', 'Leave this place, $c!', 'Leave this place, $c!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129799, 'enUS', 'You should not be here.', 'You should not be here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129800, 'enUS', 'My will is not my own.', 'My will is not my own.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129801, 'enUS', 'This is not my choice!', 'This is not my choice!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129802, 'enUS', 'I remain bound, even in death...', 'I remain bound, even in death...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129803, 'enUS', 'My suffering has no end!', 'My suffering has no end!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129804, 'enUS', 'Death does not bring peace.', 'Death does not bring peace.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129805, 'enUS', 'There!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129806, 'enUS', 'My fate was sealed long ago.', 'My fate was sealed long ago.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129807, 'enUS', 'Anduin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129808, 'enUS', 'This changes nothing.', 'This changes nothing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129809, 'enUS', 'Let\'s go.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129810, 'enUS', '', 'These spirits belong to the Burning Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129811, 'enUS', '', 'My death will not break the pact!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129812, 'enUS', 'You will succumb to the Burning Legion. This I vow!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129813, 'enUS', 'This... cannot be...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129814, 'enUS', 'Those bound to me chose this fate for themselves!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129815, 'enUS', '', 'These eredar are using power cores in their amateurish attempts to bend the elements to their will. Teach them such powers are not to be trifled with!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129816, 'enUS', '', 'Those demons presumed to bind the elements. Fools! They have learned the price of such an insult!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129817, 'enUS', 'You are mine!', 'You are mine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129818, 'enUS', 'Nowhere to run!', 'Nowhere to run!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129819, 'enUS', 'Death is only the beginning.', 'Death is only the beginning.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129820, 'enUS', 'Join us in eternity...', 'Join us in eternity...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129821, 'enUS', 'Go to your grave!', 'Go to your grave!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129822, 'enUS', 'Let this torment end!', 'Let this torment end!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129823, 'enUS', 'This... cannot be...', 'This... cannot be...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129824, 'enUS', 'Ah.... at last.', 'Ah.... at last.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129825, 'enUS', 'Vengeance will be mine!', 'Vengeance will be mine!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129826, 'enUS', 'Your world will be purged!', 'Your world will be purged!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129827, 'enUS', 'Your death will be swift and unmerciful.', 'Your death will be swift and unmerciful.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129828, 'enUS', 'Wither before my might!', 'Wither before my might!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129829, 'enUS', 'You dare?!', 'You dare?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129830, 'enUS', 'The Legion\'s work will be done.', 'The Legion\'s work will be done.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129831, 'enUS', 'You will know pain!', 'You will know pain!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129832, 'enUS', 'Take to the skies, my pets! Yaaaaah!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129833, 'enUS', '', 'I am ready to travel to Skold-Ashil.\n\nShall we depart?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129834, 'enUS', 'Kil\'jaeden has sent one of his generals to oversee the Cathedral of Eternal Night. Put an end to her command.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129835, 'enUS', 'Well done. Let the Deceiver rage now that the cathedral is no longer under his control.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129836, 'enUS', 'The Legion has summoned forth a ravenous beast with a taste for mortal flesh. Destroy this monstrosity before it can be unleashed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129837, 'enUS', 'The beast has been put down. Good! Soon we will do the same to Kil\'jaeden.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129838, 'enUS', 'I sense a perverse power rising among the Legion. Destroy it and weaken their grip on the cathedral.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129839, 'enUS', 'A worthy kill. That bloated fiend will never bring forth another demon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129840, 'enUS', 'An inquisitor lurks within the cathedral, turning the souls of our dead against us. Send this filth screaming back into the Nether.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129841, 'enUS', 'That inquisitor delighted in sending its victims to their deaths. Fitting, then, that you forced the fiend to meet its own fate.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129842, 'enUS', 'Kil\'jaeden delights in sending his underlings to defile the cathedral. Remove this foul creature from our world.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129843, 'enUS', 'The Legion seeks to break our will by desecrating this once-sacred temple. Your deeds prove our spirit cannot be broken!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129844, 'enUS', '', 'Elune bless you, brave one. It seems the woods no longer offer us sanctuary from the Legion\'s forces.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129845, 'enUS', '', 'My sisters and I owe you our lives. We don\'t have much but you\'re free to take anything you find useful from our supplies.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129846, 'enUS', '', 'Thank you again. We will not forget your kindness!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129847, 'enUS', 'Salutations, $ct! \n\nAs you may already know, this place is more than just a naval graveyard... it\'s also a massive breeding ground for kraken! I mean just look at all this incubation! It\'s spectacular... and perhaps slightly dangerous.\n\nSince you\'re here to destroy the eggs before they hatch, perhaps you\'d enjoy my newest, relatively-safe invention:\n\nI call it... the Egg-Sploder!\n\nIt almost works perfectly most of the time! What do you say?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129848, 'enUS', 'Sure, I\'ll give it a shot. What could possibly go wrong?', 'Sure, I\'ll give it a shot. What could possibly go wrong?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129849, 'enUS', '', 'Let\'s move!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129850, 'enUS', '', 'As you command, priestess.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129851, 'enUS', 'Activating', 'Activating', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129852, 'enUS', 'This is a test broadcast for gender token testing, $gbrother:sister;! (Do not translate)', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129853, 'enUS', 'Turalyon! I am pleased you continue to thwart the Legion\'s best efforts to kill you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129854, 'enUS', 'What of Alleria? Is she...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129855, 'enUS', 'The last time I saw her was on the Xenedar. She was still... under guard.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129856, 'enUS', 'Alleria\'s out there somewhere, old friend. I know it. And other survivors as well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129857, 'enUS', 'But thousands of demons stand between us and our allies. We must thin the Legion\'s ranks to have any hope of reaching the Xenedar alive.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129858, 'enUS', 'Then that is what we will do, High Exarch.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129859, 'enUS', 'Climbing', 'Climbing', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129860, 'enUS', 'Thank you for saving me!', 'Thank you for saving me!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129861, 'enUS', 'I must report back to camp!', 'I must report back to camp!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129862, 'enUS', 'The demons cannot chain us for long.', 'The demons cannot chain us for long.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129863, 'enUS', '', 'I am Magatha of the Grimtotem tribe, and I will be heard!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129864, 'enUS', 'I have recovered some of the essence you seek. Please, take all of it.', 'I have recovered some of the essence you seek. Please, take all of it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129865, 'enUS', '', 'You\'re in charge here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129866, 'enUS', 'I am the farseer of the Earthen Ring. Who are you?', 'I am the farseer of the Earthen Ring. Who are you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129867, 'enUS', 'Greetings, Grandmaster $n. It\'s nice of you to visit us. We\'re mostly focused on cleaning up the last remnants of the sha in the area.$b$bIf you could assist us with this task, perhaps we can help you with yours. What do you say?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129868, 'enUS', 'You certainly removed a great number of the remaining sha remnants, Grandmaster. We appreciate your help with that matter.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129869, 'enUS', 'How goes the removal of the remaning remnants?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129870, 'enUS', '<The portal hums with energy. Screams and other disturbing noises emanate from beyond.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129871, 'enUS', 'Overload with Nethershards [100]', 'Overload with Nethershards [100]', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129872, 'enUS', 'I\'ve cleaned up the sha.', 'I\'ve cleaned up the sha.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129873, 'enUS', '', 'I am Magatha Grimtotem, of the Grimtotem tribe. I am the Elder Crone, the matriarch of my tribe.$b$bThough Baine may have exiled me from Thunder Bluff, I am the leader that the tauren need. They sit idly by in their tents, waiting for the Alliance and the Legion to effortlessly roll over them. My people and I refuse to let that happen to us. We do what we must to survive.$b$bThe Legion has attacked. The Grimtotem need me. Will you help, or will you get in the way?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129874, 'enUS', 'I\'m ready to clean up the sha.', 'I\'m ready to clean up the sha.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129875, 'enUS', '|CFFF00000CAUTION|R This will summon a dangerous foe that will require multiple heroes to defeat. You will lose 100 Nethershards by destroying this portal.', '|CFFF00000CAUTION|R This will summon a dangerous foe that will require multiple heroes to defeat. You will lose 100 Nethershards by destroying this portal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129884, 'enUS', 'Is there no end to your sanctimonious bluster, Velen? You claim the mantle of prophet, yet you always lacked the vision to see the inevitable destiny of our world... of all worlds.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129885, 'enUS', 'The husk before you was once a vessel for the might of Sargeras. But this temple itself is our prize. The means by which we will reduce your world to cinders!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129886, 'enUS', 'Still... I will not deprive you of this chance to fall before an ancient wonder. Let this avatar be the end of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129887, 'enUS', 'I should warn you that the Egg-Sploder does occasionally cause kraken eggs to hatch instead of explode. A meager price to pay for the convenience of technology, right?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129888, 'enUS', 'We must move quickly. Now that the demons know we are on the surface, the Legion will bring its full might to bear against us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129889, 'enUS', 'Flames!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129890, 'enUS', 'These pylons can only transport a handful of troops at a time, but for now they will suffice.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129891, 'enUS', 'Marked!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129892, 'enUS', 'Claimed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129893, 'enUS', 'Thank you, Grandmaster $p, for your assistance. Unfortunately, Ban-Lu is no longer here. I saw him heading south, but that is all I know.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129894, 'enUS', 'Impale!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129895, 'enUS', '$ct, I\'ve sent a raven to take you to the command ship.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129896, 'enUS', 'Emptiness...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129897, 'enUS', 'Please, walk with me to the shore.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129898, 'enUS', 'I shall commune with the water now...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129899, 'enUS', 'How interesting.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129900, 'enUS', '', 'Eradicate them all!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129901, 'enUS', 'Impus not sure why he ended up here.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129902, 'enUS', 'The rivers call to me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129903, 'enUS', 'The raven looks at you quizzically.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129904, 'enUS', '', 'I seek to cleanse the waters here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129905, 'enUS', '', 'The fish of Suramar are friends to me.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129906, 'enUS', '%s triggers an ancient protective spell!', '%s triggers an ancient protective spell!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129907, 'enUS', 'So, you prove to be as foolish as all the others on this world and seek to face me? Very well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129908, 'enUS', 'Pathetic! My time is wasted here. With the power from the tomb, I will destroy all of you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129909, 'enUS', 'My crag, you die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129910, 'enUS', 'This Dookya\'s pass!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129911, 'enUS', 'Our pass, you go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129912, 'enUS', 'Your shinies, give them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129913, 'enUS', 'The Legion stoops to corrupting the murlocs that call this shore home. Put an end to this befoulment!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129914, 'enUS', 'I have never understood how anyone could find charm in those distasteful creatures. Let us turn our attention to more pressing matters.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129915, 'enUS', 'Fel... murlocs? Is there any race the Legion won\'t corrupt? Take care of this, would you?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129916, 'enUS', 'I confess a certain fondness for murlocs. The fel variety, however... not so much.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129917, 'enUS', 'Harjatan\'s |cFFFF0404|Hspell:232192|h[Commanding Roar]|h|r has drawn the attention of nearby murlocs!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129918, 'enUS', 'For the Legion!', 'For the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129919, 'enUS', 'The fel will consume you!', 'The fel will consume you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129920, 'enUS', 'Your end is near!', 'Your end is near!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129921, 'enUS', 'You will die by my blade!', 'You will die by my blade!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129922, 'enUS', 'We cannot be stopped!', 'We cannot be stopped!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129923, 'enUS', 'I will strike you down!', 'I will strike you down!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129924, 'enUS', 'Fine. Take all my desecrated seaweed.', 'Fine. Take all my desecrated seaweed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129925, 'enUS', 'I have found many of your totem fragments. Please take them.', 'I have found many of your totem fragments. Please take them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129926, 'enUS', 'Well done! With the kraken threat neutralized, we can focus our efforts on defeating the Burning Legion.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129927, 'enUS', 'Looks like I found some of this disgusting globby stuff you wanted.', 'Looks like I found some of this disgusting globby stuff you wanted.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129928, 'enUS', 'Hello pretty bird. Do you want all these shiny things?', 'Hello pretty bird. Do you want all these shiny things?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129929, 'enUS', 'For millennia, the Army of the Light has waged war upon the Burning Legion. Battles. Skirmishes. Sabotage.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129930, 'enUS', 'We were committed to our cause. Either we would end the Legion\'s crusade... or they would end ours.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129931, 'enUS', 'When at last our final assault on Argus began, we knew we might not survive it. And when the Xenedar was hit, it seemed all hope was lost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129932, 'enUS', 'I want to give you all my golden minnows.', 'I want to give you all my golden minnows.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129933, 'enUS', 'Champion, the Legion holds our forces captive, torturing them nigh to death. Rescue them... before their Light fades!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129934, 'enUS', 'You have saved our forces from a terrible fate. The Light thanks you, hero.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129935, 'enUS', 'Mistress Sassz\'ine called in a Darkscale Taskmaster!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129936, 'enUS', '|TINTERFACE\\ICONS\\ability_fixated_state_red.blp:20|t A Razorjaw Gladiator is fixated on you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129970, 'enUS', '<Velen stares straight ahead. He doesn\'t seem to notice you have arrived.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129971, 'enUS', 'Mac\'Aree was once the pinnacle of eredar civilization.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129972, 'enUS', 'A beacon of progress... that drew the malevolence of the universe to our very gates.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129973, 'enUS', 'Wait... something stirs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129974, 'enUS', 'Spirits... of all those who died that day.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129975, 'enUS', 'I can still hear the naaru\'s song of war. There was one who held the way open for us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129976, 'enUS', 'She fell behind. I can feel her presence even now... but her song is bitter, distorted.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129977, 'enUS', 'The last time I laid eyes upon this place, I could think of nothing but escape. Talgath, my most trusted advisor, had betrayed me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129982, 'enUS', 'Champion, the Argus Expedition calls upon you to join our assault on the Legion home world of Argus.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129984, 'enUS', 'Your souls will BURN!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129985, 'enUS', 'Ready your weapon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129987, 'enUS', 'We need not be sticking around here longer than necessary.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129988, 'enUS', 'I am ready to fight by your side.', 'I am ready to fight by your side.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129989, 'enUS', 'Wanna know the key to beating the Legion?\n\nBuildings, mate.\n\nBuildings and more buildings. Crikey!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129990, 'enUS', 'Is that ALL you have to offer? How pathetic...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129991, 'enUS', 'Your flesh is weak! Your bones brittle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129992, 'enUS', 'I\'ve fought your kind for a thousand years, demon. You brag and bluster, then you die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129994, 'enUS', 'Do not relent! We end this beast here and now!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129995, 'enUS', 'I want to go to the Cathedral of Eternal Night (Dungeon).', 'I want to go to the Cathedral of Eternal Night (Dungeon).', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129996, 'enUS', 'I want to go to the Tomb of Sargeras (Raid).', 'I want to go to the Tomb of Sargeras (Raid).', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129997, 'enUS', 'You are no match for our power!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (129999, 'enUS', 'NOT USED', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130000, 'enUS', '', 'Elune aids those who seek to defend the land from the Burning Legion. Step within the moonlight, hero, and obtain her blessing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130001, 'enUS', 'Alleria! Perfect timing, as always.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130002, 'enUS', 'Your people... your worlds... always crumble.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130004, 'enUS', 'Oh, hello, we\'re not done setting up yet.\n\nIf you\'re looking for something a bit more dead and metal than E.T.C., you\'re going to want to check this out.\n\nWould you mind spreading the word for us? It\'s a concert you\'re not going to want to miss!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130005, 'enUS', 'I want to teleport to Harjatan.', 'I want to teleport to Harjatan the Bludger.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130006, 'enUS', 'I want to teleport to Mistress Sassz\'ine.', 'I want to teleport to Mistress Sassz\'ine.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130007, 'enUS', 'I want to teleport to Sisters of the Moon.', 'I want to teleport to Sisters of the Moon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130008, 'enUS', 'I want to teleport to The Desolate Host.', 'I want to teleport to The Desolate Host.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130009, 'enUS', 'I want to teleport to Demonic Inquisition.', 'I want to teleport to Demonic Inquisition.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130010, 'enUS', 'I want to teleport to Maiden of Vigilance.', 'I want to teleport to Maiden of Vigilance.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130011, 'enUS', 'I want to teleport to the Fallen Avatar.', 'I want to teleport to the Fallen Avatar.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130012, 'enUS', 'I want to teleport to Kil\'jaeden.', 'I want to teleport to Kil\'jaeden.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130013, 'enUS', 'Light give me strength!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130014, 'enUS', 'This day will be your last, demon!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130015, 'enUS', '|TINTERFACE\\ICONS\\SPELL_FEL_INCINERATE.BLP:20|t You have |cFFFF0000|Hspell:240209|h[Unstable Soul]|h|r!', '|TINTERFACE\\ICONS\\SPELL_FEL_INCINERATE.BLP:20|t You have |cFFFF0000|Hspell:235117|h[Unstable Soul]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130016, 'enUS', '|TINTERFACE\\ICONS\\Ability_BossFelMagnaron_HandEmpowered.blp:20|t You have |cFFFF0000|Hspell:235271|h[Infusion]|h|r!', '|TINTERFACE\\ICONS\\Ability_BossFelMagnaron_HandEmpowered.blp:20|t Maiden of Vigilance begins casting |cFFFF0000|Hspell:235271|h[Infusion]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130017, 'enUS', '', '%s nocks her arrows with blinding speed.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130018, 'enUS', 'No! No more running! No more hiding! I must finish this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130019, 'enUS', 'Velen! Stop!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130020, 'enUS', 'Sealing the portal buys us nothing, archmage. Whether it takes a few years or millennia, the Legion will return. Over and over again, until too few of us remain to fight them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130021, 'enUS', 'It is time to seize our fate. We must use the portal and follow the Deceiver into the abyss. It is the only way!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130022, 'enUS', 'I agree. It is the only way. Go, champions! We have precious little time to save Velen and return before the portal closes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130023, 'enUS', 'Come. If we are to die in the Nether, we will take the Deceiver with us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130024, 'enUS', 'Azeroth\'s greatest champions await your arrival at Krasus\' Landing when you are ready.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130025, 'enUS', 'NOT USED', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130026, 'enUS', '', 'Left!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130027, 'enUS', '', 'I have made a habit of saving you from the grasp of demons.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130028, 'enUS', 'Light\'s Chosen, salute!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130029, 'enUS', '', 'Are you alright, Turalyon?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130033, 'enUS', 'This battle shall be your last!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130034, 'enUS', 'Facility lockdown confirmed. Engaging threat.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130035, 'enUS', 'Your crusade is ended!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130036, 'enUS', 'Mark an enemy, and I will strike it down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130037, 'enUS', 'The Legion comes! We must protect our homes!', 'The Legion comes! We must protect our homes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130038, 'enUS', 'The demons will never get past us! For the wilds!', 'The demons will never get past us! For the wilds!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130039, 'enUS', 'By fang and claw, I\'ll stop them!', 'By fang and claw, I\'ll stop them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130040, 'enUS', 'Bandu thoribas!', 'Bandu thoribas!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130041, 'enUS', 'Tor ilisar\'thera\'nal!', 'Tor ilisar\'thera\'nal!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130042, 'enUS', 'Be wary, champions! Behold a vision of what is to come!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130043, 'enUS', 'Find me, champions, and I will grant you sight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130044, 'enUS', 'Obliteration beckons!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130045, 'enUS', 'Death rains upon you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130046, 'enUS', 'Smolder! Suffer!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130047, 'enUS', 'Consume them all!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130048, 'enUS', 'Who will share your fate?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130049, 'enUS', 'I will cut you down myself!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130050, 'enUS', 'Be careful, $ct. Demons are out there on the prowl.', 'Be careful, $ct. Demons are out there on the prowl.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130051, 'enUS', 'Give in to your fears! Your desires!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130052, 'enUS', 'Face your destiny!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130053, 'enUS', 'We are ready to depart, Prophet Velen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130054, 'enUS', 'Your lives are petty and insignificant! We are eternal!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130055, 'enUS', 'Velen\'s defiance so long ago paved the way for your downfall.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130056, 'enUS', 'Every world that resisted us has fallen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130057, 'enUS', 'The Burning Legion has come for your souls!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130058, 'enUS', 'Your fate will be no different than all the rest!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130059, 'enUS', 'Give up! There is still time to join us and serve the master!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130060, 'enUS', 'I\'ll get you to those ships, heroes! Take them out, before the Deceiver grows stronger!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130061, 'enUS', 'Slaughter the mortals and bring me their broken corpses!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130062, 'enUS', 'Feel the chaos consume you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130063, 'enUS', 'Limitless destruction is at my command!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130064, 'enUS', 'Your souls will soon know oblivion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130065, 'enUS', 'The leader of these naga has made a pact with the Legion, trading her people\'s lives for power. The fools go willingly, unaware of the price they must pay. Show them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130066, 'enUS', 'The naga must learn that any bargain made with the Legion will end in pain.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130067, 'enUS', '', 'Greetings, $c. I must complete my research.\n\nWill you aid me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130068, 'enUS', 'Tonight we dine on mortal flesh!', '', 26124);
