INSERT INTO `broadcast_text_locale` VALUES (130189, 'enUS', 'I hope you find them.', 'I hope you find them.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130190, 'enUS', 'I\'ve had the same dreams about you!', 'I\'ve had the same dreams about you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130191, 'enUS', 'Yes!', 'Yes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130192, 'enUS', 'I\'m sorry, I just can\'t...', 'I\'m sorry, I just can\'t...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130193, 'enUS', 'Champion, I bring you the favor of Eyir! Approach and obtain her blessing.', 'Champion, I bring you the favor of Eyir! Approach and obtain her blessing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130194, 'enUS', 'Champion, I bring you a gift from Prince Farondis. My spells will allow you to be stronger, deft, and more powerful for a while. Approach and receive your boon.', 'Champion, I bring you a gift from Prince Farondis. My spells will allow you to be stronger, deft, and more powerful for a while. Approach and receive your boon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130195, 'enUS', 'How can I help?', 'How can I help?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130196, 'enUS', 'What is your wish?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130197, 'enUS', '', 'Some of the protective spells have fragmented and begun to fade. It pains me to suggest it, but I think you might be able to catch the fragments with a fishing pole.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130198, 'enUS', '', 'Beautiful isn\'t it? Can you feel its power flowing through you, pulsing as it disrupts the very world around us? I can bend that power to my will if you like...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130199, 'enUS', 'Take me to the Trial of Style!', 'Take me to the Trial of Style!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130200, 'enUS', '', 'Thank you, $n! This is exactly what I need.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130201, 'enUS', '<The portal hums with energy. Screams and other disturbing noises emanate from beyond.>\n\nReturn when you have more Nethershards.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130203, 'enUS', 'Instructor, I don\'t think...', 'Instructor, I don\'t think...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130204, 'enUS', 'Uh... how did that incantation go again? Ru shallora... uh... um... abracadabra?', 'Uh... how did that incantation go again? Ru shallora... uh... um... abracadabra?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130205, 'enUS', 'Don\'t worry students, this demon is perfectly contained in the arcane matrix.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130206, 'enUS', 'Now, if you had been listening in our last class you would know the incantation to cause it to explode in a ball of flame.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130207, 'enUS', 'You there, show the class how the incantation goes.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130208, 'enUS', 'Hurry up! We don\'t have all eternity to wait!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130209, 'enUS', 'Will you adventure with me... forever?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130210, 'enUS', 'Oh dear! This wasn\'t in the lesson plan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130211, 'enUS', 'Annihilate!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130212, 'enUS', 'To arms, Nightwatchers! The Legion comes!', 'To arms, Nightwatchers! The Legion comes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130213, 'enUS', 'The Nightwatch will never forsake our duty!', 'The Nightwatch will never forsake our duty!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130214, 'enUS', 'Bandu Thoribas!', 'Bandu Thoribas!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130215, 'enUS', 'Stand firm, Nightwatchers! Our enemies approach!', 'Stand firm, Nightwatchers! Our enemies approach!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130216, 'enUS', 'For Nar\'thalas!', 'For Nar\'thalas!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130217, 'enUS', 'Focus your attacks. I\'ll hold his attention.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130218, 'enUS', '', 'You cannot evade my volley!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130219, 'enUS', '', 'My arrows strike true!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130220, 'enUS', 'Thank you, friend! As a reward have a bit of my power. May it help you against our foe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130221, 'enUS', 'Impus is making a boat. You help!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130223, 'enUS', 'Farewell.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130224, 'enUS', 'Impus is pleased with minion\'s work.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130225, 'enUS', 'Catch seaweed with fishing pole! Give seaweed to Impus! No profit!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130226, 'enUS', 'A ritual totem was lost in these waters many years ago when a lightning bolt shattered it. I seek to rebuild it so I may  honor my ancestors.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130227, 'enUS', '<Akule studies the fragments carefully.>\n\nYes, these are indeed part of the original totem. You have my thanks, $c.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130228, 'enUS', 'I am collecting the hundreds, maybe thousands, of pieces from the shattered totem. You might have some luck finding them with a fishing pole.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130229, 'enUS', 'The raven looks at you and tilts its head to the side.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130230, 'enUS', 'Corbyn caws with delight.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130231, 'enUS', 'The raven looks at some shiny pebbles near its feet, and then looks at water. You get the distinct feeling that you could retrieve them with a fishing pole.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130232, 'enUS', '', 'Before I was exiled, I was the aquarium keeper in the city. The life that exists in the tank is still my responsibility and I won\'t abandon them!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130233, 'enUS', '', 'Splendid! Here, just put them in my enchanted globe of water, and I\'ll see to the rest.\n\n<Sha\'leth appears to be distracted watching the tiny fish.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130234, 'enUS', '', 'Oh! First and foremost, the koi in are starting to overcrowd. Maybe if you caught some of the minnows, I could release them into the rivers?\n\nBe careful of Magicus, the guardian of the aquarium.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130235, 'enUS', '', 'The corruption chokes our waters and kills the natural life that once thrived there. We must fight back!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130236, 'enUS', '', '<She reaches out and takes the globules while keeping her head turned away in disgust.>\n\nThank you. I will enjoy destroying this.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130237, 'enUS', '', 'No doubt you\'ve already been fighting back the monstrosities that invade our forest. But if we could extract the more... viscous... corruption from the water itself, that would be a great victory as well! I suggest trying a fishing pole.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130238, 'enUS', 'You will pay for your transgressions, warlock!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130239, 'enUS', 'I will crush you, insect!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130240, 'enUS', 'The dreadsteeds are mine, warlock... Mine!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130241, 'enUS', 'Xoroth wil not be... defeated...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130242, 'enUS', '', 'Help! Their trying to get at my supplies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130243, 'enUS', '', 'Thank you so much! Please, have this chest from the cache. You\'ve earned it!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130244, 'enUS', 'You have been spotted and an assassin has been dispatched.', 'You have been spotted and an assassin has been dispatched.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130247, 'enUS', 'Let\'s move.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130248, 'enUS', 'Welcome to the Exodar, $p.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130249, 'enUS', '$p, it is time. Set course for Argus.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130250, 'enUS', 'Test', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130252, 'enUS', 'I\'ve brought you this Armorcrafter\'s Commendation.', 'I\'ve brought you this Armorcrafter\'s Commendation.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130253, 'enUS', 'So you have! Very well, let\'s get started. I\'ll teach you all about the legendary armor you\'ve always dreamed of making!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130254, 'enUS', 'Hold on just a moment, $n. Let\'s finish the armor we\'re already making first.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130255, 'enUS', '|TInterface\\Icons\\SPELL_FIRE_TWILIGHTFLAMEBREATH.BLP:20|t%s begins to cast |cFFFF0000|Hspell:233983|h[Echoing Anguish]|h|r!', '|TInterface\\Icons\\SPELL_FIRE_TWILIGHTFLAMEBREATH.BLP:20|t%s begins to cast |cFFFF0000|Hspell:233983|h[Echoing Anguish]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130256, 'enUS', 'Bl... Bless you... I... I\'ll live, thanks to you!', 'Bl... Bless you... I... I\'ll live, thanks to you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130257, 'enUS', 'Please, take this as your reward.', 'Please, take this as your reward.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130281, 'enUS', 'I\'m here to inquire about the Tri-Disc of Fire.', 'I\'m here to inquire about the Tri-Disc of Fire.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130292, 'enUS', 'Ah yes, I do know where that is. Before Karazhan fell, Khadgar and I had done a bit of research on it back in my sanctum, where it still resides. I\'m afraid we didn\'t have time to learn very much about it.$b$bNormally, it would be trivial to travel to my sanctum, but I\'m afraid the demons in this cellar stand between us and the portal to it.$b$bFight your way through the cellar and you\'ll find the portal to at the far end. The tri-disc should still be there.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130311, 'enUS', '', 'Hmmmm....? Hmmmm.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130312, 'enUS', '', 'Oh, you are cute, but I am already under contract.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130314, 'enUS', '', 'Oh, what delicious morals you have.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130316, 'enUS', '', 'Ooo... I am so cold. Would you be a dear and set yourself on fire for me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130319, 'enUS', '', '<Just winks a lot.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130331, 'enUS', 'Freedom is mine! Your suffering for this insult will be legendary, elf!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130332, 'enUS', '', 'Ooo, a scribe? I do love it when someone writes down my little... dominations. I might know a little secret. A dark secret.\n\nDo you kiss and tell? Because I do.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130333, 'enUS', 'Do you have something for sale?', 'Do you have something for sale?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130334, 'enUS', 'Pretty bird want shiny gold coins?', 'Pretty bird want shiny gold coins?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130335, 'enUS', 'Bless Elune! Thank you, hero!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130336, 'enUS', 'You deserve a reward! Here, I hope this will be of use to you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130337, 'enUS', '', 'Mmm... I do love a good scribe. Be sure to stay... in touch.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130338, 'enUS', 'For the rest of you, let\'s back to the academy. Clearly you all need a few hundred years of further instruction.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130341, 'enUS', 'Now, most importantly: we must decide what type of armor you want to craft. Choose carefully - your commendation only earns you one lesson.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130342, 'enUS', 'I want to make tailored shoulders.\n|TINTERFACE\\ICONS\\INV_SHOULDER_CLOTH_RAIDWARLOCK_L_01.BLP:32|t\n|Hitem:146666:0:0:0:0:0:0:0:110:0|h[Celumbra, the Night\'s Dichotomy]|h', 'I want to make tailored shoulders.\n|TINTERFACE\\ICONS\\INV_SHOULDER_CLOTH_RAIDWARLOCK_L_01.BLP:32|t\n|Hitem:146666:0:0:0:0:0:0:0:110:0|h[Celumbra, the Night\'s Dichotomy]|h', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130343, 'enUS', 'I want to make leather boots.\n|TINTERFACE\\ICONS\\INV_Leather_RaidDemonHunterMythic_Q_02Boots.blp:32|t\n|Hitem:146669:0:0:0:0:0:0:0:110:0|h[The Sentinel\'s Eternal Refuge]|h', 'I want to make leather boots.\n|TINTERFACE\\ICONS\\INV_Leather_RaidDemonHunterMythic_Q_02Boots.blp:32|t\n|Hitem:146669:0:0:0:0:0:0:0:110:0|h[The Sentinel\'s Eternal Refuge]|h', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130344, 'enUS', 'I want to make chain mail gloves.\n|TINTERFACE\\ICONS\\INV_GAUNTLETS_PLATE_RAIDPALADIN_I_01.BLP:32|t\n|Hitem:146668:0:0:0:0:0:0:0:110:0|h[Vigilance Perch]|h', 'I want to make chain mail gloves.\n|TINTERFACE\\ICONS\\INV_GAUNTLETS_PLATE_RAIDPALADIN_I_01.BLP:32|t\n|Hitem:146668:0:0:0:0:0:0:0:110:0|h[Vigilance Perch]|h', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130345, 'enUS', 'I want to make a plate belt.\n|TINTERFACE\\ICONS\\INV_Plate_DraenorCrafted_D_01Buckle.blp:32|t\n|Hitem:146667:0:0:0:0:0:0:0:110:0|h[Rethu\'s Incessant Courage]|h', 'I want to make a plate belt.\n|TINTERFACE\\ICONS\\INV_Plate_DraenorCrafted_D_01Buckle.blp:32|t\n|Hitem:146667:0:0:0:0:0:0:0:110:0|h[Rethu\'s Incessant Courage]|h', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130346, 'enUS', 'Are you certain this is what you want to make? You can only choose one legendary item to craft with this Armorcrafter\'s Commendation.', 'Are you certain this is what you want to make? You can only choose one legendary item to craft with this Armorcrafter\'s Commendation.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130347, 'enUS', 'Ha ha! Stupid frozen ogre.', '', 26124);
