INSERT INTO `broadcast_text_locale` VALUES (130350, 'enUS', 'You there! Won\'t you help us testing flying in the Broken Isles?\n\nTo sweeten the deal, I\'ll also grant you your class mount quest!\n\nWhat do you say?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130351, 'enUS', 'Yes! Grant me Broken Isles Pathfinder I and II and my class mount finale!', 'Yes! Grant me Broken Isles Pathfinder I and II and my class mount finale!', 26124);
