INSERT INTO `broadcast_text_locale` VALUES (130377, 'enUS', 'Too late! I already took the good stuff!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130378, 'enUS', 'You again? You can\'t have my treasure!', '', 26124);
