INSERT INTO `broadcast_text_locale` VALUES (130380, 'enUS', 'Argh! What\'s in the box?! What was in it?!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130390, 'enUS', 'I... I can\'t find my body. I was trapped inside the soul engine and now... where is it? What happened to me?!', 'I... I can\'t find my body. I was trapped inside the soul engine and now... where is it? What happened to me?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130391, 'enUS', 'Where am I? I was fighting on the Broken Shore... can you hear me? By the gods... am I... dead?', 'Where am I? I was fighitng on the Broken Shore... can you hear me? By the gods... am I... dead?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130392, 'enUS', 'No, I can\'t be dead! I have a family... I can\'t leave them behind! How will they survive without me?', 'No, I can\'t be dead! I have a family... I can\'t leave them behind! How will they survive without me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130393, 'enUS', 'Find your way into the Light, my friend. The time has come for you to rest.', 'Find your way into the Light, my friend. The time has come for you to rest.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130394, 'enUS', 'Do you have any rare demonic trinkets in that sack?', 'Do you have any rare demonic trinkets in that sack?', 26124);
