INSERT INTO `broadcast_text_locale` VALUES (130400, 'enUS', 'Treasure Master Iks\'reeged makes a rude gesture at you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130401, 'enUS', 'Have you risen any interesting creatures recently?', 'Have you risen any interesting creatures recently?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130402, 'enUS', 'I\'ve dreamt of this moment for so long, but not once did I imagine it being so... beautiful.', 'I\'ve dreamt of this moment for so long, but not once did I imagine it being so... beautiful.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130403, 'enUS', 'At last the time has come! The final journey is upon us!', 'At last the time has come! The final journey is upon us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130404, 'enUS', 'Open your arms and embrace what lies before you!', 'Open your arms and embrace what lies before you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130405, 'enUS', 'Look to the skies! A great destiny is revealed before us!', 'Look to the skies! A great destiny is revealed before us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130406, 'enUS', 'A great truth reveals itself!', 'A great truth reveals itself!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130408, 'enUS', 'Material goods cannot save you, $n!', 'Material goods cannot save you, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130409, 'enUS', 'Possessions are meaningless! Look to the sky! The end is upon us!', 'Possessions are meaningless! Look to the sky! The end is upon us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130410, 'enUS', 'This place offers only empty promises! Wealth cannot save you!', 'This place offers only empty promises! Wealth cannot save you!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130411, 'enUS', 'Open your eyes, $n! Only lies await you inside. The truth is in the skies!', 'Open your eyes, $n! Only lies await you inside. The truth is in the skies!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130412, 'enUS', 'Marvel at its glory! You cannot hide from the inevitable!', 'Marvel at its glory! You cannot hide from the inevitable!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130413, 'enUS', 'We strike this beast down together.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130414, 'enUS', '', 'Welcome to the Trial of Style: Azeroth\'s finest fashion destination!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130415, 'enUS', '', 'Hurry, High Inquisitor! The enemy is upon us!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130416, 'enUS', 'I hope you brought your wardrobe with you, because today\'s competition looks FIERCE!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130417, 'enUS', '', 'The theme of today\'s competition is... Summer Styles!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130418, 'enUS', 'Ooh, I do love a good pair of shorts.', '', 26124);
