INSERT INTO `broadcast_text_locale` VALUES (130421, 'enUS', 'The competition starts in two minutes. Good luck, good-looking!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130422, 'enUS', '', '$ct, the Felskorn alchemists in this area have been getting more aggressive of late. They recently stole a shipment of our brew. Worse, it appears that they have modified it to enhance themselves and are now spewing it from their spreaders!\n\nThey need to be stopped, but it will not be easy without help. Fortunately the Monkey King loaned us his vaulting staff. Use it to jump over their heads and rain death from above!\n\nWill you help?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130423, 'enUS', 'I\'m ready!', 'I\'m ready!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130424, 'enUS', '|TInterface\\Icons\\ABILITY_WARLOCK_INFERNO.BLP:20|t%s has targeted you with |cFFFF0000|Hspell:238430|h[Bursting Dreadflame]|h|r!', '|TInterface\\Icons\\ABILITY_WARLOCK_INFERNO:20|t%s has targeted you with |cFFFF0000|Hspell:238430|h[Bursting Dreadflame]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130425, 'enUS', '', 'The Burning Legion closes in on Azeroth. We must all do our part!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130426, 'enUS', 'Please... never forget us. Never forget our sacrifice...', 'Please... never forget us. Never forget our sacrifice...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130427, 'enUS', 'Thank you. I will never forget this kindness...', 'Thank you. I will never forget this kindness...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130428, 'enUS', 'At last, this nightmare is at an end...', 'At last, this nightmare is at an end...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130429, 'enUS', 'It was an honor to serve my king...', 'It was an honor to serve my king...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130430, 'enUS', 'I can see it... I can see the Light... thank you.', 'I can see it... I can see the Light... thank you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130431, 'enUS', '', 'To your left, the fabulous $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130432, 'enUS', 'It was an honor to serve Stormwind... to serve my king!', 'It was an honor to serve Stormwind... to serve my king!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130433, 'enUS', 'Thank you for this kindness. I will never forget you...', 'Thank you for this kindness. I will never forget you...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130434, 'enUS', 'And on the right, the inimitable $n!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130435, 'enUS', '', 'Our third place finisher is... $n! Congratulations, $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130436, 'enUS', 'In second place, we have... $n! You looked fabulous up there!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130437, 'enUS', '', 'And finally, our grand transmog champion of the evening. Let\'s hear it for $n!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130438, 'enUS', '', 'Everybody else, keep trying. You\'ll be a champion soon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130439, 'enUS', '', 'That\'s all for today. See you next time!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130440, 'enUS', 'What happened to me? Where is my body?', 'What happened to me? Where is my body?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130441, 'enUS', 'I don\'t want to die...', 'I don\'t want to die...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130442, 'enUS', 'Where am I? I can\'t remember where I am...', 'Where am I? I can\'t remember where I am...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130443, 'enUS', 'Please... make the nightmare stop!', 'Please... make the nightmare stop!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130444, 'enUS', 'I can\'t be dead... someone please help me...', 'I can\'t be dead... someone please help me...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130445, 'enUS', 'If I\'m dead, who will take care of my family?', 'If I\'m dead, who will take care of my family?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130446, 'enUS', 'Come on, let me fight too!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130447, 'enUS', '', 'Oro Junior, stay back there! Your father would be heartbroken if anything were to happen to you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130448, 'enUS', 'But mom, you\'re letting Mycah fight!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130449, 'enUS', 'Don\'t worry little bro, if we get through this then when you\'re older I\'ll teach you how to swing a knife like dad taught me. Deal?', '', 26124);
