INSERT INTO `broadcast_text_locale` VALUES (130466, 'enUS', '', 'The theme of today\'s competition is... Freestyle!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130467, 'enUS', 'Bundle up! Let\'s see what you wear on the frozen shores of Northrend!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130468, 'enUS', 'Take off that makeup and put on that war paint!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130469, 'enUS', 'Be still my heart! Don\'t be afraid to show a little skin!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130470, 'enUS', 'Have a little fun and put together the most garish ensemble you can dream up!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130471, 'enUS', 'Frighten the fashion out of us with your creepiest costumes. Oooh... I\'m getting chills already!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130472, 'enUS', 'Show us your more sinister styles!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130473, 'enUS', 'For the Horde! For the Alliance! Let\'s see your team colors.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130474, 'enUS', 'Pandarian fashion just SCREAMS harmoniousness. Show us your inner peace!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130475, 'enUS', 'Show us your sparkles with your most wizardly wear! And don\'t forget the enchantments!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130476, 'enUS', 'It\'s time to have fun and get a little crazy with your costumes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130477, 'enUS', 'Will you represent Theramore? The Kirin Tor? Talk to Nellie if you need a spare tabard!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130478, 'enUS', 'Will you be a farmer? A chef? A scientist? A pirate? Ooh... I can\'t WAIT to see what they come up with!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130479, 'enUS', 'As you know, epic armor is ALWAYS purple. Wrap yourselves up in the bottom of the rainbow!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130480, 'enUS', 'Pristine white robes and glimmering armor are the order of the day!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130481, 'enUS', 'That\'s right... today\'s contest has no theme. Wear whatever you want!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130482, 'enUS', 'Over here, $p. Keep your head down.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130483, 'enUS', 'Insolent mortal! Come... face your death!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130496, 'enUS', 'I will feast upon your soul.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130497, 'enUS', 'Crunchy bones. So brittle and tasty!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130498, 'enUS', 'Your supple flesh will satisfy my hunger!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130499, 'enUS', 'Feeble and weak! You dare enter our lair?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130500, 'enUS', 'You will kneel or you will die!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130501, 'enUS', 'Our chains hunger for your flesh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130502, 'enUS', 'Your flesh will fuel these pits for eternity!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130503, 'enUS', 'More flesh for the taking!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130504, 'enUS', 'Whatever happens, we stand together.', '', 26124);
