INSERT INTO `broadcast_text_locale` VALUES (130531, 'enUS', '', 'Your invasion of Argus will fail! High Inquisitor Raalgar will see to that.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130532, 'enUS', 'DO NOT RECORD', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130533, 'enUS', 'I can close my eyes to the atrocities of war no longer. I must see the threat of the Burning Legion for myself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130534, 'enUS', '<Link the Vindicaar to the Light\'s Judgment weapon.>', '<Link the Vindicaar to the Light\'s Judgment weapon.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130535, 'enUS', 'The crystal launcher pulses with a subtle energy emanating from deep within the Vindicaar.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130536, 'enUS', 'I am ready.', 'I am ready.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130540, 'enUS', 'Our fight against the Burning Legion continues.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130541, 'enUS', 'I am ready, High Exarch.', 'I am ready, High Exarch.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130543, 'enUS', 'When you are ready. We have much to discuss.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130546, 'enUS', 'But the Light never abandoned us. It called you here and gave us another chance at victory. What we do with that chance... that is up to us.', '', 26124);
