INSERT INTO `broadcast_text_locale` VALUES (130551, 'enUS', 'This ridgeline will guide you to the pits. Stay low. We\'ll regroup shortly.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130554, 'enUS', 'Krokul will now respond to your Flight Master\'s Whistle while on Argus.', 'Krokul will now respond to your Flight Master\'s Whistle while on Argus.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130555, 'enUS', 'So you wanna know everything there is to know about Argus so you can defeat the Legion once and for all?', '', 26124);
