INSERT INTO `broadcast_text_locale` VALUES (130567, 'enUS', '|TInterface\\Icons\\SPELL_FEL_ELEMENTALDEVASTATION.BLP:20|tYou were stunned by |cFFFF0000|Hspell:237903|h[Fel Surge]|h|r! Destroy the totem before it finishes its cast!', '|TInterface\\Icons\\SPELL_FEL_ELEMENTALDEVASTATION.BLP:20|tYou were stunned by |cFFFF0000|Hspell:237903|h[Fel Surge]|h|r! Destroy the totem before it finishes its cast!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130568, 'enUS', '', 'Your efforts have meant nothing, $p!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130570, 'enUS', 'As the portal closes, a sealed door opens further into the Arcatraz.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130576, 'enUS', '|TInterface\\Icons\\CREATUREPORTRAIT_INFERNAL_BALL_02.BLP:20|t%s is casting |cFFFF0000|Hspell:238588|h[Rain of Brimstone]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130577, 'enUS', 'Examining...', 'Examining...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130579, 'enUS', 'Look to the heavens above. See the million points of brilliance in the great dark beyond.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130580, 'enUS', 'Know you are not alone. Our salvation is out there, somewhere.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130581, 'enUS', 'You must hold fast to your resolve. Temptation weakens the mind and spirit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130582, 'enUS', 'Look inside yourself and see the strength your vigil has cultivated.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130584, 'enUS', '|TInterface\\Icons\\Ability_Earthen_Pillar.blp:20|t%s loses a Hardened |cFFFF0000|Hspell:238473|h[Fel Scale]|h|r from colliding with a stalactite!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130585, 'enUS', '|TInterface\\Icons\\spell_shaman_earthquake:20|t%s readies itself to charge!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130586, 'enUS', '|TInterface\\Icons\\inv_qiraj_skinsandworm:20|t%s burrows, the earth  |cFFFF0000|Hspell:238473|h[Hardens]|h|r its scales!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130589, 'enUS', 'Baby, baby, baby! This is the illest, realest, deadest band you\'ve ever seen! They will melt your face, figuratively and literally! Before I met these guys, I was alive and had all my skin! Now I\'m dead and filthy rich!\n\nCatch Blight Boar here, every hour on the half hour!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130590, 'enUS', '', '|TInterface\\Icons\\SPELL_ARCANE_INVOCATION.BLP:20|tYou are afflicted by |cFFFF0000|Hspell:236712|h[Lunar Beacon]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130591, 'enUS', '', '|TInterface\\Icons\\Ability_IronMaidens_ConvulsiveShadows.blp:20|t%s targets you with |cFFFF0000|Hspell:236603|h[Rapid Shot]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130598, 'enUS', '', 'PH', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130599, 'enUS', 'Can you hear them? Chewing, gnashing, wailing...$b$b<Arkhaan shudders.>$b$bIsolon is lost.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130600, 'enUS', 'We should keep going.', 'We should keep going.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130601, 'enUS', 'Pheta thones gamera.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130602, 'enUS', 'We must not give in.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130603, 'enUS', 'Pheta vi acahachi!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130604, 'enUS', 'Our day will come.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130605, 'enUS', '', '|TInterface\\Icons\\SPELL_NATURE_MOONGLOW.BLP:20|tYou are affected by |cFFFF0000|Hspell:236519|h[Moon Burn]|h|r!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130606, 'enUS', 'Be at peace.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130607, 'enUS', 'This way. Arkhaan has a plan.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130608, 'enUS', '', 'Could you elaborate? Even a little?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130609, 'enUS', '', 'Perhaps with some idea of what you hope to achieve...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130610, 'enUS', 'We will stop the whispers. Pointy-ears should know.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130611, 'enUS', '', 'I must confess, Arkhaan, that does not put me at ease.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130612, 'enUS', '', '|TInterface\\Icons\\artifactability_BalanceDruid_FullMoon.blp:20|t |cFFFF0000|Hspell:236357|h[Font of Elune]|h|r has reached full moon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130613, 'enUS', '', '|TInterface\\Icons\\artifactability_BalanceDruid_NewMoon.blp:20|t |cFFFF0000|Hspell:236357|h[Font of Elune]|h|r has reached new moon.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130614, 'enUS', '', 'Are all these creatures...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130615, 'enUS', 'My people. Once.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130616, 'enUS', '', 'How is it you do not share their fate?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130617, 'enUS', 'Arkhaan forgets.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130618, 'enUS', 'We still hear their prayers. Rest, brothers. Rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130619, 'enUS', '', 'At least their suffering is over.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130620, 'enUS', '', 'Where are these things coming from?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130621, 'enUS', 'They want Arkhaan. He promised to save them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130622, 'enUS', 'Arkhaan remembers. Isolon calls to us! Isolon will look to the stars again!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130623, 'enUS', '', 'That is not Isolon calling to you! Wait!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130624, 'enUS', '', 'He is still here. He crossed over into the Void. We may still be able to save him if we hurry.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130626, 'enUS', 'The assortment of valuable things that you adventurers leave behind in your wake is simply astonishing.\n\nAnd profitable.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130627, 'enUS', 'Let me satisfy your curiosity.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130628, 'enUS', 'Show us, please... please...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130629, 'enUS', 'Whispers... too... loud! Aaargghhh!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130630, 'enUS', '', 'Go. I am right behind you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130631, 'enUS', 'Or you could come see a GOOD band! The Blight Boar concert will begin in thirty minutes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130632, 'enUS', 'If you\'re interested in gettin\' down, the Blight Boar concert starts in twenty!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130633, 'enUS', 'Blight Boar is playing their underground show on the east side of the island! Your headliner Blight Boar goes on in ten minutes!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130634, 'enUS', 'I see you\'ve met the viceroy.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130635, 'enUS', '', 'What are your people doing here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130636, 'enUS', 'I am here to find out. Follow, if you can.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130637, 'enUS', '', 'Wait!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130639, 'enUS', 'Gul\'dan has been vanquished, but his master Kil\'jaeden poses an even greater threat.\n\nThe Deceiver will stop at nothing to destroy our world. We must make any sacrifice necessary to end his reign.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130640, 'enUS', 'My rockin\' and rollin\' beauties! You\'re in the right place to melt your face! Allow me to humbly introduce... Blight Booooar!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130641, 'enUS', 'In times such as these, our beliefs are tested. We may be tempted to surrender to an evil that can seem overwhelming... and unstoppable.\n\nBut we must not lose faith in the Light. It will guide us to victory.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130642, 'enUS', '', 'I will never trust the Betrayer or his Illidari lackeys, but I am willing to set aside my misgivings for the sake of the greater good.\n\nBut once the Legion falls, what use will we have for those tainted by its corruption?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130643, 'enUS', 'In times of war, supplies are short.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130644, 'enUS', '', 'I realize this isn\'t much of an inn, but it\'s what we have.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130645, 'enUS', 'Can I interest you in a beverage or two?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130646, 'enUS', 'Welcome, $ct. I have been appointed to distribute these items of interest to your armies and yourself.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130647, 'enUS', '', '$ct, see something you like?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130654, 'enUS', 'The Sentinax recalls its portals and begins recharging.', 'The Sentinax recalls its portals and begins recharging.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130655, 'enUS', 'The Sentinax has finished recharging.', 'The Sentinax has finished recharging.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130659, 'enUS', '<You feel like Manapoof is silently judging you.>', '<You feel like Manapoof is silently judging you.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130660, 'enUS', 'Would you please send me to the Wailing Caverns?', 'Would you please send me to the Wailing Caverns?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130661, 'enUS', 'When the ship from Azeroth arrived, I sensed his presence. I would know it anywhere, even after all the years we\'ve spent apart.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130662, 'enUS', '', 'Arator! He\'s here!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130663, 'enUS', 'Thank you, stranger. I almost met my final death! Please, take these items from my grave as your reward.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130664, 'enUS', 'I admit I am intrigued by the vision of Aegwynn that you encountered in the Cathedral. There is so much I could learn from the mother of my mentor Medivh, even if this manifestation is but an echo of the Guardian she was.\n\nI will continue to study her writings. They may offer insights into what awaits us in the depths of the Tomb of Sargeras.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130665, 'enUS', 'The Cathedral is ours, but the Tomb\'s depths remain in the hands of the Deceiver and his foul minions.\n\nBuild up your forces, $ct. The true battle awaits us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130666, 'enUS', '', 'My wardens hold the Cathedral, but it will all be for naught if Kil\'jaeden awakens his master\'s fallen avatar in the depths of the Tomb.\n\nIt seems Illidan and his followers are still needed... for now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130667, 'enUS', 'Kil\'jaeden hounded my people across the stars, and now does the bidding of his dark master in the depths of the Tomb.\n\nWe will make him pay for his crimes. At long last, the Deceiver will fall!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130668, 'enUS', 'Completing Construction', 'Completing Construction', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130688, 'enUS', '', 'This way.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130689, 'enUS', 'I have something I want to show you, but we mustn\'t be seen. Follow.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130690, 'enUS', '', 'Explain yourself first.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130691, 'enUS', 'Such impatience. I believe you will find the answers you seek on the other side.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130697, 'enUS', 'How industriously they work. A pity they will be overtaken.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130698, 'enUS', '', 'I sense a little disappointment. Was I really your best student?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130699, 'enUS', 'I had hoped to find at least one promising soul on this rock.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130700, 'enUS', '', 'I have not turned away from that path. You know why I resisted.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130701, 'enUS', '', 'I need to finish what I started, Locus-Walker.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130702, 'enUS', 'Can you, though? With your... attachments?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130703, 'enUS', 'They smashed Thrashbite!? Run!!!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130739, 'enUS', 'The Aegis will be ours!', 'The Aegis will be ours!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130740, 'enUS', 'You will fall to the Legion!', 'You will fall to the Legion!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130741, 'enUS', 'You will burn!', 'You will burn!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130742, 'enUS', 'The cathedral is ours!', 'The cathedral is ours!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130743, 'enUS', 'Your relics cannot save you now!', 'Your relics cannot save you now!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130767, 'enUS', '', 'Let\'s get the Forsaken up here on the stage!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130768, 'enUS', '', 'Time for the Forsaken to dance. Their moves will take your breath away.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130769, 'enUS', '', 'Come on up, trolls! Show everyone else how it\'s done!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130770, 'enUS', '', 'Time for a troll dance!  Shoes can\'t hold these feet!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (130771, 'enUS', '', 'Watch your feet! It\'s tauren dance time, and those hooves can hurt.', 26124);
