INSERT INTO `broadcast_text_locale` VALUES (131156, 'enUS', 'Boom! Boom!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131159, 'enUS', 'You haven\'t lived until you\'ve sang shanties at sunrise in the salty mists of the sea.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131161, 'enUS', 'I haven\'t trained at any of the famous academies, but I\'ve sacked all of \'em!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131163, 'enUS', 'When the Bloodsail raided my family\'s fishing boat, they didn\'t just kill everyone I\'ve ever loved.\n\nThey rescued me from a mundane life severely lacking in treasure and grog! A pirate\'s life for me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131256, 'enUS', 'It\'s good to finally meet the famous $p! Me crew tell tales of your legendary sackings of Booty Bay.\n\nEveryone likes to pretend to be a Bloodsail today, but you\'re the real deal. Fly the Black with pride, matey.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131257, 'enUS', 'I will reach Booty Bay just after nightfall. Then, under the cover of darkness, I will make them feel the full wrath of the Bloodsail!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131258, 'enUS', '<Rear Admiral Giggleplanks kicks frantically, but his feet don\'t reach the water.>', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131259, 'enUS', 'Curse this briny stallion to the depths! It won\'t obey my orders. Forward, beast, or I\'ll keelhaul you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131260, 'enUS', 'All of Booty Bay fears my arrival! It has been foretold in the ancient legends!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131261, 'enUS', 'Beware the unbridled wrath of Rear Admiral Giggleplanks! Not even the unusually large rats of Booty Bay will be spared!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131262, 'enUS', 'Chaaarge!\n\n<Rear Admiral Giggleplanks waits for a few moments and then pouts.>\n\nFine, we\'ll wait until the tide pulls us in. That\'ll make it more dramatic anyways.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131263, 'enUS', 'This is the one day all year that the Fleet Admiral lets me off the leash.\n\nThe one day that all of Azeroth fears.\n\nI\'m a wild pirate with wild pirate intentions! Booty Bay will burn... wildly!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131264, 'enUS', 'Hurry up, steed! We must reach Booty Bay by nightfall!\n\nI swear you\'re not even trying.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131265, 'enUS', 'Don\'t judge me. Not on Pirates\' Day!', 'Don\'t judge me. Not on Pirates\' Day!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131266, 'enUS', 'Where be ye ocean steed, matey?', 'Where be ye ocean steed, matey?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131267, 'enUS', 'Avast! Stop poking me inflatable or I be keelhaulin\' ya!', 'Avast! Stop poking me inflatable or I be keelhaulin\' ya!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131268, 'enUS', 'They were all out of the red ones.', 'They were all out of the red ones.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131269, 'enUS', 'Does this make me look fearsome?', 'Does this make me look fearsome?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131270, 'enUS', 'Look at this landlubber without a sea pony! Ha!', 'Look at this landlubber without a sea pony! Ha!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131271, 'enUS', 'Push me towards Booty Bay so I can shout more profanities at \'em!', 'Push me towards Booty Bay so I can shout more profanities at \'em!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131272, 'enUS', 'I\'ve never felt so free!', 'I\'ve never felt so free!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131273, 'enUS', 'If ya need a pig cooked for the barbecue later, just let me know. I do a perfect searing flame.', 'If ya need a pig cooked for the barbecue later, just let me know. I do a perfect searing flame.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131274, 'enUS', 'Cowardly Crosby deserves what\'s comin\' to him.', 'Cowardly Crosby deserves what\'s comin\' to him.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131275, 'enUS', 'Everyone wants to be a Bloodsail, but only a few got the guts to do what it takes.', 'Everyone wants to be a Bloodsail, but only a few got the guts to do what it takes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131276, 'enUS', 'Haven\'t I seen you below deck before? Or maybe it\'s just the grog talkin...', 'Haven\'t I seen you below deck before? Or maybe it\'s just the grog talkin...', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131277, 'enUS', 'Ya like treasure, landlubber? Too bad! It\'s all ours!', 'Ya like treasure, landlubber? Too bad! It\'s all ours!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131278, 'enUS', 'You look familiar... Didn\'t I ransack your coastal villa once?\n\nHa, yeah! I\'m sure it was you! Your servants all ran screaming into the vineyards, right? And then we burnt the whole place to the ground?\n\nGood times.', 'You look familiar... Didn\'t I ransack your coastal villa once?\n\nHa, yeah! I\'m sure it was you! Your servants all ran screaming into the vineyards, right? And then we burnt the whole place to the ground?\n\nGood times.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131279, 'enUS', 'No landlubbers!', 'No landlubbers!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131280, 'enUS', 'Let\'s put the blood in Bloodsail!', 'Let\'s put the blood in Bloodsail!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131281, 'enUS', 'Bloodsail or no sail!', 'Bloodsail or no sail!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131282, 'enUS', 'No one messes with the Bloodsail!', 'No one messes with the Bloodsail!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131283, 'enUS', 'The Bloodsail party\'s never gonna end!', 'The Bloodsail party\'s never gonna end!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131284, 'enUS', 'Hands off the treasure, scallywag.', 'Hands off the treasure, scallywag.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131285, 'enUS', 'No splashing in the water!', 'No splashing in the water!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131286, 'enUS', 'You\'re in Bloodsail territory now.', 'You\'re in Bloodsail territory now.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131287, 'enUS', 'The last vestiges of life in our city are succumbing to the void. The power in this crystal will release them from its grasp.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131288, 'enUS', 'Thank you for freeing those creatures. I remember the days when our children used to play with them. It pains me to see what has happened since.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131301, 'enUS', 'As I was a-walking down  Booty Bay\'s planks...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131307, 'enUS', 'A pretty young lass I chanced to meet...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131308, 'enUS', 'She was round in the counter and bluff in the bow...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131309, 'enUS', 'So I took in all sail and cried, \"Way enough now!\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131310, 'enUS', 'I hailed her in dwarvish, she answered me clear...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131311, 'enUS', '\"I\'m from Ironforge, bound to Bronzebeard!\"', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131312, 'enUS', 'To me way, aye, blow the man down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131313, 'enUS', 'Give me some time to blow the man down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131314, 'enUS', 'Look ahead, look astern, look the weather and the lee...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131315, 'enUS', 'I see a wreck to windward and a lofty ship to lee...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131316, 'enUS', '\"Oh, are you a pirate or a royal fleet?\" cried he...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131317, 'enUS', 'We be pirates! Bloodsail!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131319, 'enUS', 'Blow high, blow low, and so sailed we!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131320, 'enUS', 'A-sailing down the coasts to Booty!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131321, 'enUS', 'Give us your treasure and your ale!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131323, 'enUS', 'We\'ll rant and we\'ll roar, like true Bloodsail pirates!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131324, 'enUS', 'We\'ll rant and we\'ll rave, across the salt seas!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131326, 'enUS', '\'Til we strike soundings in the Cape o\' Stranglethorn!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131328, 'enUS', 'From Arathi to Booty Bay is too far indeed!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131335, 'enUS', 'So it\'s cheer up, my lads. Let your hearts never sink!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131336, 'enUS', 'While the bonny ship, Crimson Whirl, goes a-fishing for the drink.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131337, 'enUS', 'Here\'s a health to the Red Whale...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131338, 'enUS', 'Here\'s a health to the Battler of Westfall...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131339, 'enUS', 'We wear the shirts o\' the white...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131340, 'enUS', 'When we return to Stranglethorn, we\'ll have sweethearts \'til dead!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131341, 'enUS', 'Likewise, the Pippy Swan!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131342, 'enUS', 'And the Ruby, ship of fame!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131343, 'enUS', 'And the pants o\' the red!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131344, 'enUS', 'The keg is empty in the morn...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131345, 'enUS', 'And it\'s mighty drafty around Stranglethorn...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131346, 'enUS', 'Oh, ya pirates and scoundrels...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131347, 'enUS', 'It\'s \'round the Cape we\'ve got to go...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131348, 'enUS', 'Oh, it\'s one more pull and that will do...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131349, 'enUS', 'For we\'re the mateys to kick her through!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131350, 'enUS', 'Go down, ya Bloodsail pirates, go down!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131351, 'enUS', 'Chasing ships wherever we go!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131352, 'enUS', 'He says heave, we say ho!', '', 26124);
