INSERT INTO `broadcast_text_locale` VALUES (131354, 'enUS', 'Please be quiet. Don\'t wake up the moonkin hatchlings!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131355, 'enUS', 'Rest is vital for moonkin hatchlings.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131356, 'enUS', 'After a long day of dancing and Moonfiring, moonkin hatchlings need a break to recharge.', '', 26124);
