INSERT INTO `broadcast_text_locale` VALUES (131358, 'enUS', 'Fleet Admiral Tethys says it\'s time to go...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131359, 'enUS', 'Moonfiring is essential to being a moonkin.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131360, 'enUS', 'It takes great concentration to be able to Moonfire many times in a row.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131361, 'enUS', 'Look at some of these hatchlings. They just can\'t focus long enough to moonfire indefinitely.', '', 26124);
