INSERT INTO `broadcast_text_locale` VALUES (131395, 'enUS', 'Happy Pirates\' Day, Admiral $p! You\'ve earned the right to raise the Black.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131397, 'enUS', 'Already done sackin\' Booty Bay, Admiral? Ha, those mollusks don\'t stand a chance with you at the helm!', '', 26124);
