INSERT INTO `broadcast_text_locale` VALUES (131423, 'enUS', '', 'Stay safe. We will gather the others and prepare for the battle to come.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131432, 'enUS', 'Keep your distance. Those things sting!', 'Keep your distance. Those things sting!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131433, 'enUS', 'I admit it is impressive that the sentinels still patrol after all this time.', 'I admit it is impressive that the sentinels still patrol after all this time.', 26124);
