INSERT INTO `broadcast_text_locale` VALUES (131482, 'enUS', 'Keep it back from us! Use your magic!', 'Keep it back from us! Use your magic!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131483, 'enUS', 'This is taking too long! We have more important things to do than play with this thing.', 'This is taking too long! We have more important things to do than play with this thing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131488, 'enUS', 'It pains me to see them go, Aunt Vereesa. I could not bear to lose them now, not after waiting all these years.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131495, 'enUS', 'The Legion will only fall if we stand together, chieftain. And right now, we have friends facing grave peril.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (131498, 'enUS', 'There is a way. We will use what power we have to help you.', '', 26124);
