INSERT INTO `broadcast_text_locale` VALUES (133032, 'enUS', '|TInterface\\ICONS\\SPELL_WARLOCK_DEMONICPORTAL_PURPLE:20|tA void tear has appeared at the Ruined Overlook!', '', 26124);
