INSERT INTO `broadcast_text_locale` VALUES (133034, 'enUS', 'Time is of the essence. We must not let the Legion capture the Prophet.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (133035, 'enUS', 'We might already be too late! This is dark news. I hear the ritual chant nearby.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (133049, 'enUS', 'Tell me what you know about the stone.', 'Tell me what you know about the stone.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (133050, 'enUS', 'Our people are being used as fodder for vile rituals. Destroy these monstrous creations and help us honor the remains of our dead.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (133051, 'enUS', 'Those fiends capture my people and subject them to heinous rituals. We will no longer be hunted. Help us bring the fight to the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (133052, 'enUS', 'As the Legion encroaches upon the hunting grounds of the panthara, the starving beasts have grown more aggressive. Thin their numbers, before they wipe out our talbuk herds.', '', 26124);
