INSERT INTO `broadcast_text_locale` VALUES (134553, 'enUS', 'It is good to see you again, my friends. When I couldn\'t find you after the devastation of Draenor, I wasn\'t sure what to think.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134554, 'enUS', '', 'You rush to your death. Allow me to oblige you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134555, 'enUS', 'We feared for you as well, Khadgar. But I think part of me knew it would take more than the explosion of a planet to slow you down.', '', 26124);
