INSERT INTO `broadcast_text_locale` VALUES (134605, 'enUS', '', 'Get back to work... or you will know the lash!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134606, 'enUS', '', 'Double your efforts! This land has been useless long enough.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134607, 'enUS', '', 'You can rest when this rock is ours!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134608, 'enUS', '', 'Antorus demands its prize! Mac\'Aree will be ours!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134609, 'enUS', '', 'Your lives are forfeit, should we fail. Secure Mac\'Aree!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134611, 'enUS', 'I won\'t risk our forces on the spire without that warframe backing us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134612, 'enUS', 'It will be ready, High Exarch. You have my word.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134623, 'enUS', 'Was I supposed to energize the projection before I stabilized the effusion? Oh...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134625, 'enUS', 'Maybe if I just invert the core matrix to expel... wait, no. That is how explosions happen.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134626, 'enUS', 'I should have started studying sooner.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134627, 'enUS', 'Krono-ka lith\'tor agnos... or was it lith\'nos agtor? Bah! I will never get this spell right!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134628, 'enUS', '', 'Perhaps we had best discuss the matter in private.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134634, 'enUS', 'Maybe I should have listened to my parents and come a jewelcrafter instead...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134635, 'enUS', 'I will never reach Arc-Consul at this rate.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134636, 'enUS', 'Archimonde learned to do this as an acolyte. How hard could it be?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134640, 'enUS', 'The demons came into our world, their only desire to extinguish all life.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134641, 'enUS', 'Now we will prey upon them!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134642, 'enUS', 'Already their ranks crumble. We must press the attack.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134643, 'enUS', '', 'I go missing for all those years and this is how you treat me?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134644, 'enUS', '', 'Yes, of course I still love him. Must you be so nosey?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134645, 'enUS', '', 'Sisters are complicated. Especially the undead ones.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134653, 'enUS', 'I lend you my strength.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134654, 'enUS', '', 'My void will consume you... if you ask nicely.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134655, 'enUS', 'Come, speak with me.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134657, 'enUS', 'Our time is now, hero.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (134658, 'enUS', 'The Vindicaar is at your disposal.', '', 26124);
