INSERT INTO `broadcast_text_locale` VALUES (136007, 'enUS', '|TInterface\\Icons\\Achievement_Boss_HellFire_FelLord.blp:24|t |TInterface\\Icons\\spell_shadow_summonfelhunter:20|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t', '|TInterface\\Icons\\Achievement_Boss_HellFire_FelLord.blp:24|t |TInterface\\Icons\\spell_shadow_summonfelhunter:20|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136008, 'enUS', '|TInterface\\Icons\\spell_shadow_summonfelhunter:20|t |TInterface\\Icons\\Achievement_Boss_HellFire_Socrethar.blp:24|t', '|TInterface\\Icons\\spell_shadow_summonfelhunter:20|t |TInterface\\Icons\\Achievement_Boss_HellFire_Socrethar.blp:24|t', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136009, 'enUS', '|TInterface\\Icons\\Achievement_Boss_HellFire_FelLord.blp:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t |TInterface\\Icons\\Ability_FelArakkoa_EyeofTerrok.blp:24|t', '|TInterface\\Icons\\Achievement_Boss_HellFire_FelLord.blp:24|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t |TInterface\\Icons\\Ability_FelArakkoa_EyeofTerrok.blp:24|t', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136010, 'enUS', '||TInterface\\Icons\\spell_shadow_summonfelhunter:20|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t |TInterface\\Icons\\Ability_FelArakkoa_EyeofTerrok.blp:24|t', '||TInterface\\Icons\\spell_shadow_summonfelhunter:20|t |TInterface\\Icons\\Achievement_Boss_HellFire_FelReaver.blp:24|t |TInterface\\Icons\\Ability_FelArakkoa_EyeofTerrok.blp:24|t', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136011, 'enUS', 'A bit worn, but still functional. These will do nicely.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136012, 'enUS', 'Armaments from one of our past assaults litter the ridge on the eastern side of the hold. Their recovery would aid our war effort.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136013, 'enUS', 'We will put these to use immediately. Thank you for recovering them.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136014, 'enUS', 'T\'uure, free from the Legion\'s grasp at last? I welcome your presence, torchbearer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136016, 'enUS', 'I see the Alliance answered the call as well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136017, 'enUS', 'Sound effect for troops', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136018, 'enUS', 'Sound effect for troops', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136019, 'enUS', 'Sound effect for troops', 'Sound effect for troops', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136020, 'enUS', '|TInterface\\Icons\\Spell_Warlock_DemonicEmpowerment.blp:20|tKin\'garoth casts |cFFFF0000|Hspell:249740|h[Overcharge]|h|r!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136021, 'enUS', 'Soon comes the awakening of my brother, Argus. Together, our new Pantheon will join the master in breaking your fetid world. But you will not live to see it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136022, 'enUS', 'I have heard all the prattling I can bear. Let us show this lackey of Sargeras that he has no claim to our world!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136023, 'enUS', 'This may be the one place in the universe sacred to the Legion. Demons would pluck out their eyes sooner than gaze upon it.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136024, 'enUS', 'Who could ever imagine such a sacrifice?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136025, 'enUS', 'The souls of the Pantheon are free, but they endured so much sufferin\'. Let me bide with \'em fer a time. Offer a bit o\' comfort.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136026, 'enUS', 'Remain here, Magni. The rest of us will go on.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136027, 'enUS', 'So close now to the end...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136028, 'enUS', 'The heart of Antorus lies before us. The power behind the Legion\'s infinite army.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136029, 'enUS', 'So much agony here... so many voices. It\'s... it\'s nearly overwhelmin\'! But I can feel Eonar urgin\' us forward.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136030, 'enUS', 'At long last, the end is upon us. Press on, my friends.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136031, 'enUS', 'Aaaarggh! Witches! You call that torment? I have endured far worse!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136032, 'enUS', '', 'I tire of that worm\'s babbling! Let me scorch the flesh from his miserable bones!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136033, 'enUS', 'A faint glow emanates from the object.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136034, 'enUS', '', 'Ignore him, sister! Focus on your duty. These souls are nearly ready to join Aggramar in the master\'s service.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136035, 'enUS', 'I feel those demons twistin\' the spirits o\' the titans! Forcin\' \'em ta use their might ta serve the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136036, 'enUS', 'Should they fall to darkness, Sargeras will have the power he needs to claim the final titan.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136037, 'enUS', 'Azeroth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136038, 'enUS', 'Azerite fissures begin to erupt nearby!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136040, 'enUS', 'What is your bidding, master?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136041, 'enUS', 'The circle nears completion. The mortals must not disrupt the rebirth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136042, 'enUS', 'Those who will not serve shall be destroyed. It is the only way.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136043, 'enUS', 'Become the instrument of my wrath once again, Aggramar. End this incursion of the Light.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136044, 'enUS', 'The hour of rebirth draws near. Are the souls prepared?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136045, 'enUS', 'Our kin still resist the true path, master. But they will soon be broken.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136046, 'enUS', 'One still eludes us. Her essence is needed to ensure the victory of my crusade.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136047, 'enUS', 'The Life-Binder will soon reveal herself. She will not escape me, master!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136048, 'enUS', 'When my new Pantheon rises, no power in the universe will stand against the Legion!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136049, 'enUS', '', 'I remember the awakening... how life sprang forth all around us.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136050, 'enUS', '', 'I remember unity... until it was shattered by betrayal.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136051, 'enUS', '', 'I remember fleeing... to keep him from his prize.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136052, 'enUS', '', 'The time for hiding is over. Aid me... or everything ends.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136053, 'enUS', 'Talgath has turned against us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136054, 'enUS', 'No...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136055, 'enUS', 'It cannot end like this. Take the others and go, Prophet. We will buy you time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136056, 'enUS', 'Hatuun! You do not have to do this!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136057, 'enUS', 'Please... keep my family safe.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136058, 'enUS', 'K\'ure! Help us!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136059, 'enUS', 'I will hunt you across the stars, traitor! Even if it takes a thousand years!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136061, 'enUS', 'Swear loyalty to me, pledge the hearts of your people to my cause.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136062, 'enUS', 'Together, we will become Legion, and defeat the hungering void that would consume us all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136063, 'enUS', 'It is little enough to ask, for what he offers us.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136064, 'enUS', 'Such power... And he speaks the truth.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136065, 'enUS', 'I had hoped the others would sense the darkness behind Sargeras\'s words.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136066, 'enUS', 'The ata\'mal crystal soon showed me the horrible truth of what would come to pass if we accepted the titan\'s offer.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136073, 'enUS', 'You see an empty space where a labradorite could fit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136074, 'enUS', 'You see an empty space where a florid malachite could fit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136075, 'enUS', 'You see an empty space where a chemirine could fit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136076, 'enUS', 'You see an empty space where an argulite could fit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136077, 'enUS', 'You see an empty space where a hesselian could fit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136078, 'enUS', 'You see an empty space where a lightsphene could fit.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136079, 'enUS', 'Place a labradorite in the space.', 'Place a labradorite in the space.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136080, 'enUS', 'Place a chemirine in the space.', 'Place a chemirine in the space.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136081, 'enUS', 'Place a florid malachite in the space.', 'Place a florid malachite in the space.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136082, 'enUS', 'Place an argulite in the space.', 'Place an argulite in the space.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136083, 'enUS', 'Place a hesselian in the space.', 'Place a hesselian in the space.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136084, 'enUS', 'Place a lightsphene in the space.', 'Place a lightsphene in the space.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136087, 'enUS', 'The southern skies swell with chaotic energy.', 'The southern skies swell with chaotic energy.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136088, 'enUS', 'Careful! Mortals ain\'t meant ta walk the Dreamweaver\'s path!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136089, 'enUS', 'The demons are turnin\' the Shaper\'s flames against ye!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136090, 'enUS', 'The coven strikes at ye through the storms o\' Golganneth!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136091, 'enUS', 'The coven\'s torments o\' the Highfather have taken form!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136097, 'enUS', 'I\'ll do what I can ta ease the titan spirits while you put an end ta their torturers!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136098, 'enUS', '', 'Archmage Y\'mera waves for you to follow her.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136117, 'enUS', 'Ye did it! Aggramar\'s essence is free! He can stand beside his brethren once more.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136118, 'enUS', 'The very cradle of creation!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136119, 'enUS', 'The titans can sever that connection... but not here. They need ta bring the soul o\' Argus ta their place o\' power... the Seat o\' the Pantheon.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136120, 'enUS', 'Our victories mean nothing. So long as Sargeras has the soul of Argus to fuel his infinite army, Azeroth remains in grave peril.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136121, 'enUS', 'Their final act will harness the last o\' Argus\'s power, to imprison Sargeras... once and for all.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136122, 'enUS', 'Heroes, I can signal the titans ta begin. Once they start their ritual... there\'s no turnin\' back!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136123, 'enUS', 'The tortured soul of Argus has been put ta rest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136124, 'enUS', 'It matters not. We have lost. Look to the skies!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136125, 'enUS', 'Sargeras will soon undo all we have fought for.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136126, 'enUS', 'No.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136127, 'enUS', 'We will use the last glimmer of Argus\'s power to bind him here. The Seat of the Pantheon shall become Sargeras\'s prison... and ours as well.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136128, 'enUS', 'You would condemn yourselves to stop him?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136129, 'enUS', 'Our world must survive... no matter the cost!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136130, 'enUS', 'A sacrifice must be made.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136131, 'enUS', 'Return home, children of Azeroth. Protect the final titan.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136132, 'enUS', 'Prophet... what happened out there?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136133, 'enUS', 'Illidan serves as the Dark Titan\'s jailer. His sacrifice has ended the Legion. At long last, the Burning Crusade... is over.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136134, 'enUS', 'Magni? Are you alright? Magni?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136135, 'enUS', 'A sword... in the darkness... sands... soaked in pain.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136136, 'enUS', 'The wound... burning... burning...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136137, 'enUS', 'Take me to the surface.', 'Take me to the surface.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136138, 'enUS', 'The Vindicaar is yours to command.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136139, 'enUS', '', 'Our home... what has it become?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136142, 'enUS', 'A Pacing Quest Goes Here.', 'A Pacing Quest Goes Here.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136143, 'enUS', 'No, Light please. Not after we have come this far!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136154, 'enUS', 'At last, the corruption ceases!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136155, 'enUS', 'They will not stop their efforts to corrupt me and my bretheren. But, maybe your arrival can turn the tide.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136157, 'enUS', 'Bring me spheres of coalesced flame, that I may empower myself for battle!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136158, 'enUS', 'They are upon us once more! Bring the flame to me!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136171, 'enUS', '', 'Xakaar, what is this delay?!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136172, 'enUS', 'Client Scene: Alleria Peers Into the Void', 'Client Scene: Alleria Peers Into the Void', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136173, 'enUS', '', 'Here we go.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136174, 'enUS', '', 'The key is now reforged. Take it and secure the Armory.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136175, 'enUS', 'As the machine erupts, something stirs beneath the surface.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136180, 'enUS', '', 'At last.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136181, 'enUS', '', 'I serve the end of all things.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136182, 'enUS', '', 'Their will be done.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136183, 'enUS', '', 'I come, Dark Masters.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136184, 'enUS', '', 'I return to the Void.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136185, 'enUS', '', 'Oblivion... calls.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136186, 'enUS', '', 'Understand... this changes... nothing.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136208, 'enUS', 'The intruders are proving to be more resilient than we expected Admiral.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136209, 'enUS', 'The Legion ship has seen you. Escape to avoid being targeted.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136216, 'enUS', '<PH> Intro to Island 3 goes here! For now, just turn in the quest! <PH>', '<PH> Intro to Island 3 goes here! For now, just turn in the quest! <PH>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136244, 'enUS', '', 'Behind me rests the Netherlight Crucible. It is an ancient remnant of this world that once served as a source of considerable magic.$B$BThough its potential power is thought by many to be a gift, empowering it would require great sacrifice... as well as a delicate balance of Light and Shadow.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136245, 'enUS', 'That? Why that\'s just a portal I\'ve conjured to allow safe passage to and from Dalaran.$B$BNever know when we\'re gonna need reinforcements!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136246, 'enUS', '', 'Hello, $n. Everyone aboard has been quite active. You look like you\'ve been busy yourself! If you ever need a place to rest I\'m always here for you.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136247, 'enUS', '', 'Greetings.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136294, 'enUS', 'Client Scene: The Vindicaar goes Gold', 'Client Scene: The Vindicaar goes Gold', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136300, 'enUS', 'This place is full of magic and mystery.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136301, 'enUS', 'What is that portal doing here?', 'What is that portal doing here?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136302, 'enUS', 'What is that behind you?', 'What is that behind you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136303, 'enUS', 'Interesting... And why aren\'t you wearing any shoes?', 'Interesting... And why aren\'t you wearing any shoes?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136304, 'enUS', 'The cold flooring beneath my feet helps me maintain my concentration.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136305, 'enUS', 'I am wearing shoes.', 'I am wearing shoes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136333, 'enUS', 'Don\'t worry about me, I\'ll find a way down... Eventually.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136334, 'enUS', '', 'Oh, look! Another pet has come to play.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136335, 'enUS', '', 'How fortuitous, my pets were getting hungry.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136336, 'enUS', '', 'I can make you immortal. However, you first have to die!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136337, 'enUS', 'You will be first.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136338, 'enUS', 'So eager to die?', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136339, 'enUS', 'I must select one of the countless ways to kill you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136342, 'enUS', 'From Fel... to Fel.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136343, 'enUS', 'Believe you\'re free? We all serve a master.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136344, 'enUS', 'Reborn in Fel!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136389, 'enUS', 'It is good to see you, Ishanah. How are you?', 'It is good to see you, Ishanah. How are you?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136390, 'enUS', '', 'I am well, $n. Thank you for asking.$B$BBefore you venture out to the fields of battle I wanted to make sure you know that my services are always available should you need a place to rest.$B$BBe well.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136391, 'enUS', 'But you\'re sitting down.', 'But you\'re sitting down.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136392, 'enUS', 'Yes, well you got me there. Still comfortable regardless.$B$BNow if you\'ll excuse me, I\'ve got quite a bit of reading to do.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136412, 'enUS', 'I need repairs, Durael.', 'I need repairs, Durael.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136416, 'enUS', 'Choose the Vindicaar power that you want to use on Argus. Only one power can be active at a time.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136433, 'enUS', 'Thank you, hero!', 'Thank you, hero!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136496, 'enUS', '$n prepares a Feast of the Fishes!', '$n prepares a Feast of the Fishes!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136499, 'enUS', '%s\'s pet Corbyn bites your ears, searching for shiny baubles.', '%s\'s pet Corbyn bites your ears, searching for shiny baubles.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136500, 'enUS', '%s\'s pet Corbyn searches your hair for shiny baubles.', '%s\'s pet Corbyn searches your hair for shiny baubles.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136501, 'enUS', '%s\'s pet Corbyn caws loudly in your ears.', '%s\'s pet Corbyn caws loudly in your ears.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136502, 'enUS', '%s\'s pet Corbyn attempts to poke you in the eyes.', '%s\'s pet Corbyn attempts to poke you in the eyes.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136503, 'enUS', '%s\'s pet Corbyn circles your head.', '%s\'s pet Corbyn circles your head.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136504, 'enUS', '%s\'s pet Corbyn pokes around in your bags.', '%s\'s pet Corbyn pokes around in your bags.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136505, 'enUS', 'Corbyn stole a copper coin from %s!', 'Corbyn stole a copper coin from %s!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136506, 'enUS', 'Corbyn couldn\'t find anything shiny on %s.', 'Corbyn couldn\'t find anything shiny on %s.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136540, 'enUS', '|TINTERFACE\\ICONS\\SPELL_FIRE_FELFLAMERING.BLP:20|t Antaen Light-Breaker is casting |cFFFF0000|Hspell:250880|h[Felshock Bombardment]|h|r! Stay in Light\'s Protection!', '|TINTERFACE\\ICONS\\SPELL_FIRE_FELFLAMERING.BLP:20|t Antaen Light-Breaker is casting |cFFFF0000|Hspell:250880|h[Felshock Bombardment]|h|r! Stay in Light\'s Protection!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136541, 'enUS', '', 'I will not be broken.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136542, 'enUS', 'What happened to you in the Seat of the Triumvirate?', 'What happened to you in the Seat of the Triumvirate?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136543, 'enUS', '', 'As the naaru lay dying, L\'ura\'s essence called to me. A being born of the Light had turned to shadow.\n\nIt was a familiar sensation.\n\nThe transformation you witnessed is under my control, to be used when I see fit. I am the same person I was... and yet, something more.\n\nThe use of such power makes some... uneasy. But shadow is a force to be harnessed, not feared.\n\nAs with any weapon, one must maintain constant control when wielding it.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136544, 'enUS', 'I see.', 'I see.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136580, 'enUS', 'I heard the Viceroy finally took care of that meddling priest.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136582, 'enUS', 'A pity he did not kill the elf and her ally, too.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136583, 'enUS', '', 'There is plenty material for the harvest, here. Let none of it go to waste!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136584, 'enUS', 'I will not give Locus-Walker the satisfaction of disrupting our efforts. Capture him at all costs.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136585, 'enUS', 'Do what you like with the fleshling and her ally. They are of no concern.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136586, 'enUS', '', 'Our work is nearly complete. Soon all of Argus will fall to darkness!', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136593, 'enUS', 'He\'s fightin\' through the pain... to let us see...', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136594, 'enUS', 'The voice is alone... weak... but so beautiful!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136595, 'enUS', 'Light.', 'Light.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136596, 'enUS', 'Shadow.', 'Shadow.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136597, 'enUS', '|TINTERFACE\\ICONS\\Item_HolySpark.blp:20|t [Place the Essence of Light within the Netherlight Crucible].', '|TINTERFACE\\ICONS\\Item_HolySpark.blp:20|t [Place the Essence of Light within the Netherlight Crucible].', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136598, 'enUS', '|TINTERFACE\\ICONS\\Inv_Elemental_Primal_Shadow.blp:20|t [Place the Essence of Shadow within the Netherlight Crucible].', '|TINTERFACE\\ICONS\\Inv_Elemental_Primal_Shadow.blp:20|t [Place the Essence of Shadow within the Netherlight Crucible].', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136606, 'enUS', 'I\'ve heard this tale before... <Skip the scenario and begin your journey on Broken Shore.>', 'I\'ve heard this tale before... <Skip the scenario and begin your journey on Broken Shore.>', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136612, 'enUS', 'PH Activate Choice UI', 'PH Activate Choice UI', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136616, 'enUS', 'Thank you for joining us, champion. These are dire times indeed.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136617, 'enUS', 'We all know the Alliance incurred heavy losses in the war against the Legion. And even now, the world bleeds in the aftermath of Sargeras\'s final strike.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136618, 'enUS', 'Meanwhile, the Horde musters its forces and arms for war. Their aggression must not go unanswered.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136619, 'enUS', 'Our future depends on bringing other like-minded allies into the fold. I call upon those of you gathered here for your suggestions.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136620, 'enUS', 'Victory on Argus was only possible because the heroes of Azeroth fought side-by-side with the Army of the Light.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136621, 'enUS', 'Many of our bravest soldiers gave their lives for the cause. Those who remain would be proud to join the Alliance.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136622, 'enUS', 'Among our number are brave initiates about to undertake the arduous path of becoming Lightforged.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136623, 'enUS', 'Thank you, High Exarch. Lightforged soldiers would indeed bring strength to our numbers.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136624, 'enUS', '', 'High King, I remember the days when my people and yours fought together against the Horde.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136625, 'enUS', '', 'I reached out to them in hopes of seeing Silvermoon return to the Alliance. My efforts proved... futile.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136626, 'enUS', '', 'But I did learn of a group of sin\'dorei who have been exiled for studying the void as I have. If I could locate them, their knowledge might aid our cause.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136627, 'enUS', 'Stormwind has suffered at the hands of those who succumbed to the shadow\'s whispers. But if others could master its power as you have, they would make formidable allies.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136628, 'enUS', 'Thank you both for your counsel. I would like to hear our champion\'s perspective on which of these potential allies we should reach out to first.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136629, 'enUS', 'Lady Windrunner, please begin the search for your exiled kin. Perhaps our champion here can assist you.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136630, 'enUS', '', 'We will do our best to locate them, High King.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136631, 'enUS', '', 'This way, champion.', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136656, 'enUS', 'This stuff isn\'t bad, but it\'s a bleedin\' shame just letting the beans sit in water and callin\' that a proper beverage. I need to bring some of this coffee back to the famed Thunderbrew clan and let them have a go at it. Just imagine a good stout, aged with a batch of these beans....', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136658, 'enUS', 'We\'ve been working for years to comprehend the powers the void might provide. Now, we are on the cusp of understanding.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136659, 'enUS', 'What is this place?', 'What is this place?', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136661, 'enUS', '|TInterface\\Icons\\ABILITY_MAGE_DEEPFREEZE.BLP:20|tGet to a Brazier! |cFFFF0000|Hspell:251709|h[Flash Freeze]|h|ris coming!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136682, 'enUS', 'You are not... prepared.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136701, 'enUS', 'We have accomplished our mission. Let us return to the Vindicaar. Much work remains to be done.', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136714, 'enUS', 'I thought I was done for... thank you!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136715, 'enUS', 'Please, save the others!', '', 26124);
INSERT INTO `broadcast_text_locale` VALUES (136716, 'enUS', 'Thank you, Champion... I was sure this was the end.', '', 26124);
